/**
 * ÊÄßËÉΩÊµãËØïÁªÑ‰ª∂
 */
export function PerformanceTestComponent({
  iterations = 1000,
}: {
  iterations?: number;
}) {
  const [isRunning, setIsRunning] = useState(false);
  const [results, setResults] = useState<{
    inefficient: number;
    optimized: number;
  } | null>(null);

  const runPerformanceTest = useCallback(async () => {
    setIsRunning(true);
    setResults(null);

    // ÊµãËØï‰ΩéÊïàÁªÑ‰ª∂
    const inefficientStart = performance.now();
    for (let i = 0; i < iterations; i++) {
      // Ê®°Êãü‰ΩéÊïàÁªÑ‰ª∂ÁöÑËÆ°ÁÆó
      let result = 0;
      for (let j = 0; j < 1000; j++) {
        result += Math.random();
      }
    }
    const inefficientTime = performance.now() - inefficientStart;

    // ÊµãËØï‰ºòÂåñÁªÑ‰ª∂ÔºàÊ®°ÊãüÁºìÂ≠òÔºâ
    const optimizedStart = performance.now();
    let cachedResult = 0;
    for (let j = 0; j < 1000; j++) {
      cachedResult += Math.random();
    }
    for (let i = 0; i < iterations; i++) {
      // Ê®°Êãü‰ΩøÁî®ÁºìÂ≠òÁöÑÁªìÊûú
      const result = cachedResult;
    }
    const optimizedTime = performance.now() - optimizedStart;

    setResults({
      inefficient: inefficientTime,
      optimized: optimizedTime,
    });
    setIsRunning(false);
  }, [iterations]);

  return (
    <Card>
      <CardHeader>
        <CardTitle>ÊÄßËÉΩÊµãËØï</CardTitle>
        <CardDescription>ÊØîËæÉ‰ºòÂåñÂíåÊú™‰ºòÂåñÁªÑ‰ª∂ÁöÑÊÄßËÉΩÂ∑ÆÂºÇ</CardDescription>
      </CardHeader>
      <CardContent>
        <div className='space-y-4'>
          <Button
            onClick={runPerformanceTest}
            disabled={isRunning}
            className='w-full'
          >
            {isRunning ? 'ÊµãËØï‰∏≠...' : `ËøêË°åÊÄßËÉΩÊµãËØï (${iterations} Ê¨°Ëø≠‰ª£)`}
          </Button>

          {results && (
            <div className='grid gap-4 md:grid-cols-2'>
              <div className='rounded-lg border border-red-200 bg-red-50 p-4'>
                <h4 className='font-semibold text-red-700'>Êú™‰ºòÂåñÁªÑ‰ª∂</h4>
                <p className='text-2xl font-bold text-red-600'>
                  {results.inefficient.toFixed(2)}ms
                </p>
              </div>
              <div className='rounded-lg border border-green-200 bg-green-50 p-4'>
                <h4 className='font-semibold text-green-700'>‰ºòÂåñÁªÑ‰ª∂</h4>
                <p className='text-2xl font-bold text-green-600'>
                  {results.optimized.toFixed(2)}ms
                </p>
              </div>
            </div>
          )}

          {results && (
            <div className='text-center'>
              <p className='text-muted-foreground text-sm'>
                ÊÄßËÉΩÊèêÂçá:{' '}
                {(
                  ((results.inefficient - results.optimized) /
                    results.inefficient) *
                  100
                ).toFixed(1)}
                %
              </p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

