export function OptimizedComponent({ count }: { count: number }) {
  // ‰ΩøÁî® useMemo ÁºìÂ≠òÊòÇË¥µËÆ°ÁÆó
  const expensiveCalculation = useMemo(() => {
    let result = 0;
    for (let i = 0; i < DEV_TOOLS_CONSTANTS.REACT_SCAN.MAX_RENDERS; i++) {
      result += Math.random();
    }
    return result;
  }, [count]); // Âè™Âú® count ÂèòÂåñÊó∂ÈáçÊñ∞ËÆ°ÁÆó

  // ‰ΩøÁî® useMemo ÁºìÂ≠òÂØπË±°
  const optimizedObject = useMemo(
    () => ({
      timestamp: Date.now(),
      random: Math.random(),
      count,
    }),
    [count],
  );

  return (
    <Card className='border-green-200 bg-green-50'>
