/**
 * ËÅîÁ≥ªË°®ÂçïAPIÂ∑•ÂÖ∑ÂáΩÊï∞
 * Contact form API utility functions
 */

import { COUNT_FIVE, COUNT_PAIR, MAGIC_36, MAGIC_9, ONE, ZERO } from "@/constants/magic-numbers";
import { MINUTE_MS } from "@/constants/time";
import { logger } from '@/lib/logger';
import { NextRequest } from 'next/server';

// Â∏∏ÈáèÂÆö‰πâ
export const RATE_LIMIT_CONFIG = {
  MAX_REQUESTS: COUNT_FIVE,
  WINDOW_MS: MINUTE_MS,
} as const;

/**
 * ÈÄüÁéáÈôêÂà∂Â≠òÂÇ®
 * Rate limiting storage (in production, use Redis or database)
 */
const rateLimitStore = new Map<string, { count: number; resetTime: number }>();

/**
 * Ê£ÄÊü•ÈÄüÁéáÈôêÂà∂
 * Check rate limiting
 */
export function checkRateLimit(
  ip: string,
  maxRequests = RATE_LIMIT_CONFIG.MAX_REQUESTS,
  windowMs = RATE_LIMIT_CONFIG.WINDOW_MS,
): boolean {
  const now = Date.now();
  const key = ip;

  const current = rateLimitStore.get(key);

  if (!current || now > current.resetTime) {
    rateLimitStore.set(key, { count: ONE, resetTime: now + windowMs });
    return true;
  }

  if (current.count >= maxRequests) {
    return false;
  }

  current.count += ONE;
  rateLimitStore.set(key, current);
  return true;
}

/**
 * È™åËØÅTurnstile token
 * Verify Turnstile token
 */
export async function verifyTurnstile(
  token: string,
  ip: string,
): Promise<boolean> {
  try {
    const secretKey = process.env.TURNSTILE_SECRET_KEY;

    if (!secretKey) {
      logger.warn('Turnstile secret key not configured');
      return false;
    }

    const response = await fetch(
      'https://challenges.cloudflare.com/turnstile/v0/siteverify',
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: new URLSearchParams({
          secret: secretKey,
          response: token,
          remoteip: ip,
        }),
      },
    );

    const result = await response.json();

    if (!result.success) {
      logger.warn('Turnstile verification failed', {
        errorCodes: result['error-codes'],
        ip,
      });
      return false;
    }

    return true;
  } catch (error) {
    logger.error('Turnstile verification error', { error, ip });
    return false;
  }
}

/**
 * Ëé∑ÂèñÂÆ¢Êà∑Á´ØIPÂú∞ÂùÄ
 * Get client IP address
 */
export function getClientIP(request: NextRequest): string {
  const forwarded = request.headers.get('x-forwarded-for');
  const realIP = request.headers.get('x-real-ip');

  if (forwarded) {
    return forwarded.split(',')[ZERO]?.trim() || 'unknown';
  }

  if (realIP) {
    return realIP;
  }

  return 'unknown';
}

/**
 * Ê∏ÖÁêÜËøáÊúüÁöÑÈÄüÁéáÈôêÂà∂ËÆ∞ÂΩï
 * Clean up expired rate limit records
 */
export function cleanupRateLimitStore(): void {
  const now = Date.now();
  for (const [key, value] of rateLimitStore.entries()) {
    if (now > value.resetTime) {
      rateLimitStore.delete(key);
    }
  }
}

/**
 * Ëé∑ÂèñÈÄüÁéáÈôêÂà∂Áä∂ÊÄÅ
 * Get rate limit status
 */
export function getRateLimitStatus(ip: string): {
  remaining: number;
  resetTime: number;
  isLimited: boolean;
} {
  const current = rateLimitStore.get(ip);
  const now = Date.now();

  if (!current || now > current.resetTime) {
    return {
      remaining: RATE_LIMIT_CONFIG.MAX_REQUESTS - ONE,
      resetTime: now + RATE_LIMIT_CONFIG.WINDOW_MS,
      isLimited: false,
    };
  }

  const remaining = Math.max(ZERO, RATE_LIMIT_CONFIG.MAX_REQUESTS - current.count);
  return {
    remaining,
    resetTime: current.resetTime,
    isLimited: remaining === ZERO,
  };
}

