/**
 * ÂèØÊäòÂè†ÂºÄÂèëÂ∑•ÂÖ∑ÂåÖË£ÖÂô®
 *
 * ‰∏∫ÂºÄÂèëÂ∑•ÂÖ∑Êèê‰æõÁªü‰∏ÄÁöÑÊäòÂè†/Â±ïÂºÄÂäüËÉΩ
 * ÊîØÊåÅÈîÆÁõòÂø´Êç∑ÈîÆÂíåËá™Âä®Â∏ÉÂ±ÄÁÆ°ÁêÜ
 */
export function CollapsibleDevTool({
  toolId,
  title,
  children,
  defaultCollapsed = false,
  showToggleButton = true,
  className = '',
}: CollapsibleDevToolProps) {
  const { registerTool, unregisterTool, getClasses, shouldCollapse } =
    useDevToolsLayout();
  const [isCollapsed, setIsCollapsed] = useState(
    defaultCollapsed || shouldCollapse(toolId),
