  useEffect(() => {
    if (process.env.NODE_ENV !== 'development') {
      return undefined;
    }

    const updateLayoutInfo = () => {
      setLayoutInfo(getLayoutInfo());
    };

    updateLayoutInfo();

    // ÂÆöÊúüÊõ¥Êñ∞Â∏ÉÂ±Ä‰ø°ÊÅØ
