'use client';

import React, { useCallback, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';

/**
 * React Scan ÂàÜÊûêÂô®Ê†∏ÂøÉÁªÑ‰ª∂
 * ÂåÖÂê´Âü∫Á°ÄÊï∞ÊçÆËØªÂèñÂíåÁä∂ÊÄÅÁÆ°ÁêÜÂäüËÉΩ
 */

interface ComponentStats {
  name: string;
  renderCount: number;
  lastRenderTime: number;
  averageRenderTime: number;
  totalRenderTime: number;
  isOptimized: boolean;
  warnings: string[];
}

interface ReactScanData {
  components: ComponentStats[];
  totalRenders: number;
  totalTime: number;
  averageTime: number;
  warnings: string[];
}

export function ReactScanAnalyzerCore() {
  const [scanData, setScanData] = useState<ReactScanData | null>(null);
  const [componentStats, _setComponentStats] = useState<ComponentStats[]>([]);
  const [isRecording, setIsRecording] = useState(false);

  // ËØªÂèñ React Scan ÂÜÖÈÉ®Êï∞ÊçÆ
  const readReactScanData = useCallback((): ReactScanData | null => {
    if (typeof window === 'undefined') return null;

    try {
      const reactScan = window.__REACT_SCAN__;
      if (!reactScan) return null;

      const internals = reactScan.ReactScanInternals;
      if (!internals) return null;

      // ÂàÜÊûêÁªÑ‰ª∂Ê∏≤ÊüìÂéÜÂè≤
      const renderHistory: ComponentStats[] = [];

      // Âü∫Á°ÄÊï∞ÊçÆÊî∂ÈõÜ
      const totalRenders = internals.totalRenders || 0;
      const totalTime = internals.totalTime || 0;
      const averageTime = totalRenders > 0 ? totalTime / totalRenders : 0;

      return {
        components: renderHistory,
        totalRenders,
        totalTime,
        averageTime,
        warnings: [],
      };
    } catch (error) {
      console.warn('Failed to read React Scan data:', error);
      return null;
    }
  }, []);

  // ÂºÄÂßãËÆ∞ÂΩï
  const startRecording = useCallback(() => {
    setIsRecording(true);
    const data = readReactScanData();
    if (data) {
      setScanData(data);
      _setComponentStats(data.components);
    }
  }, [readReactScanData]);

  // ÂÅúÊ≠¢ËÆ∞ÂΩï
  const stopRecording = useCallback(() => {
    setIsRecording(false);
    const data = readReactScanData();
    if (data) {
      setScanData(data);
      _setComponentStats(data.components);
    }
  }, [readReactScanData]);

  // Ê∏ÖÈô§Êï∞ÊçÆ
  const clearData = useCallback(() => {
    setScanData(null);
    _setComponentStats([]);
    setIsRecording(false);
  }, []);

  // Ê£ÄÊü• React Scan ÊòØÂê¶ÂèØÁî®
  const isReactScanAvailable = useCallback(() => {
    return typeof window !== 'undefined' && window.__REACT_SCAN__;
  }, []);

  if (!isReactScanAvailable()) {
    return (
      <Card className='p-6'>
        <h3 className='mb-4 text-lg font-semibold'>React Scan Analyzer</h3>
        <p className='text-muted-foreground'>
          React Scan is not available. Please ensure it is properly configured.
        </p>
      </Card>
    );
  }

  return (
    <Card className='p-6'>
      <div className='mb-4 flex items-center justify-between'>
        <h3 className='text-lg font-semibold'>React Scan Analyzer</h3>
        <div className='flex gap-2'>
          {!isRecording ? (
            <Button
              _onClick={startRecording}
              size='sm'
            >
              Start Recording
            </Button>
          ) : (
            <Button
              _onClick={stopRecording}
              size='sm'
              variant='destructive'
            >
              Stop Recording
            </Button>
          )}
          <Button
            _onClick={clearData}
            size='sm'
            variant='outline'
          >
            Clear
          </Button>
        </div>
      </div>

      {scanData && (
        <div className='space-y-4'>
          {/* Âü∫Á°ÄÁªüËÆ°‰ø°ÊÅØ */}
          <div className='grid grid-cols-3 gap-4'>
            <div className='bg-muted rounded-lg p-3'>
              <div className='text-muted-foreground text-sm'>Total Renders</div>
              <div className='text-2xl font-bold'>{scanData.totalRenders}</div>
            </div>
            <div className='bg-muted rounded-lg p-3'>
              <div className='text-muted-foreground text-sm'>Total Time</div>
              <div className='text-2xl font-bold'>
                {scanData.totalTime.toFixed(2)}ms
              </div>
            </div>
            <div className='bg-muted rounded-lg p-3'>
              <div className='text-muted-foreground text-sm'>Average Time</div>
              <div className='text-2xl font-bold'>
                {scanData.averageTime.toFixed(2)}ms
              </div>
            </div>
          </div>

          {/* ÁªÑ‰ª∂ÂàóË°® */}
          {componentStats.length > 0 && (
            <div>
              <h4 className='mb-2 font-semibold'>Component Statistics</h4>
              <div className='space-y-2'>
                {componentStats.slice(0, 10).map((component, index) => (
                  <div
                    key={`${component.name}-${index}`}
                    className='flex items-center justify-between rounded-lg border p-3'
                  >
                    <div>
                      <div className='font-medium'>{component.name}</div>
                      <div className='text-muted-foreground text-sm'>
                        Renders: {component.renderCount} | Avg:{' '}
                        {component.averageRenderTime.toFixed(2)}ms
                      </div>
                    </div>
                    <div className='flex items-center gap-2'>
                      {component.isOptimized ? (
                        <span className='rounded bg-green-100 px-2 py-1 text-xs text-green-800'>
                          Optimized
                        </span>
                      ) : (
                        <span className='rounded bg-yellow-100 px-2 py-1 text-xs text-yellow-800'>
                          Needs Optimization
                        </span>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Ë≠¶Âëä‰ø°ÊÅØ */}
          {scanData.warnings.length > 0 && (
            <div>
              <h4 className='mb-2 font-semibold text-yellow-600'>Warnings</h4>
              <div className='space-y-1'>
                {scanData.warnings.map((warning, index) => (
                  <div
                    key={index}
                    className='rounded bg-yellow-50 p-2 text-sm text-yellow-800'
                  >
                    {warning}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {!scanData && !isRecording && (
        <div className='text-muted-foreground text-center'>
          Click "Start Recording" to begin analyzing React component renders.
        </div>
      )}

      {isRecording && (
        <div className='text-muted-foreground text-center'>
          Recording in progress... Interact with the application to collect
          data.
        </div>
      )}
    </Card>
  );
}

// @ts-nocheck - ÂºÄÂèëÂ∑•ÂÖ∑Ë±ÅÂÖçÔºö‰ªÖÂºÄÂèëÁéØÂ¢É‰ΩøÁî®Ôºå‰∏çÂΩ±ÂìçÁîü‰∫ß‰ª£Á†ÅË¥®Èáè
'use client';

import { useCallback, useEffect, useState } from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import type { ReactScanGlobal } from '@/app/[locale]/react-scan-demo/react-scan-types';

interface ComponentStats {
  name: string;
  renderCount: number;
  renderTime: number;
  avgRenderTime: number;
  efficiency: number;
}

/**
 * React Scan Êï∞ÊçÆÂàÜÊûêÂô® - ÁÆÄÂåñÁâà
 */
export function ReactScanAnalyzer() {
  const [componentStats, _setComponentStats] = useState<ComponentStats[]>([]);
  const [isRecording, setIsRecording] = useState(false);

  // ËØªÂèñ React Scan Êï∞ÊçÆ
  const readReactScanData = useCallback(() => {
    if (typeof window === 'undefined') return [];

    try {
      const globalScan = window.__REACT_SCAN__;
      if (!globalScan) return [];

      const components = Object.entries(globalScan.components || {}).map(
        ([name, data]) => ({
          name,
          renderCount: data.renderCount || 0,
          renderTime: data.renderTime || 0,
          avgRenderTime:
            data.renderCount > 0 ? data.renderTime / data.renderCount : 0,
          efficiency: Math.max(
            0,
            100 - (data.renderTime / data.renderCount || 0) * 10,
          ),
        }),
      );

      return components.sort((a, b) => b.renderTime - a.renderTime);
    } catch (error) {
      console.warn('Failed to read React Scan data:', error);
      return [];
    }
  }, []);

  // Êõ¥Êñ∞Êï∞ÊçÆ
  const updateData = useCallback(() => {
    const newStats = readReactScanData();
    _setComponentStats(newStats);
  }, [readReactScanData]);

  // ÂÆöÊúüÊõ¥Êñ∞Êï∞ÊçÆ
  useEffect(() => {
    if (!isRecording) return;

    const interval = setInterval(updateData, 1000);
    return () => clearInterval(interval);
  }, [isRecording, updateData]);

  // ÊéßÂà∂ÂáΩÊï∞
  const startRecording = () => {
    setIsRecording(true);
    updateData();
  };

  const stopRecording = () => {
    setIsRecording(false);
  };

  const clearData = () => {
    _setComponentStats([]);
    setIsRecording(false);
  };

  const exportData = () => {
    const dataStr = JSON.stringify(componentStats, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `react-scan-data-${new Date().toISOString()}.json`;
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <Card className='w-full'>
      <CardHeader>
        <CardTitle className='flex items-center gap-2'>
          React Scan ÂàÜÊûêÂô®
          <Badge variant={isRecording ? 'destructive' : 'secondary'}>
            {isRecording ? 'ËÆ∞ÂΩï‰∏≠' : 'Â∑≤ÂÅúÊ≠¢'}
          </Badge>
        </CardTitle>
        <CardDescription>ÂàÜÊûê React ÁªÑ‰ª∂ÁöÑÊ∏≤ÊüìÊÄßËÉΩÂíåÊïàÁéá</CardDescription>
      </CardHeader>

      <CardContent className='space-y-4'>
        {/* ÊéßÂà∂Èù¢Êùø */}
        <div className='flex flex-wrap gap-2'>
          <Button
            onClick={isRecording ? stopRecording : startRecording}
            variant={isRecording ? 'destructive' : 'default'}
          >
            {isRecording ? 'ÂÅúÊ≠¢ËÆ∞ÂΩï' : 'ÂºÄÂßãËÆ∞ÂΩï'}
          </Button>
          <Button
            onClick={clearData}
            variant='outline'
          >
            Ê∏ÖÈô§Êï∞ÊçÆ
          </Button>
          <Button
            onClick={exportData}
            variant='outline'
            disabled={!componentStats.length}
          >
            ÂØºÂá∫Êï∞ÊçÆ
          </Button>
        </div>

        {/* Êï∞ÊçÆË°®Ê†º */}
        <Tabs
          defaultValue='stats'
          className='w-full'
        >
          <TabsList>
            <TabsTrigger value='stats'>ÁªÑ‰ª∂ÁªüËÆ°</TabsTrigger>
            <TabsTrigger value='summary'>ÊÄßËÉΩÊëòË¶Å</TabsTrigger>
          </TabsList>

          <TabsContent
            value='stats'
            className='space-y-4'
          >
            {componentStats.length > 0 ? (
              <div className='overflow-x-auto'>
                <table className='w-full border-collapse'>
                  <thead>
                    <tr className='border-b'>
                      <th className='p-2 text-left font-medium'>ÁªÑ‰ª∂ÂêçÁß∞</th>
                      <th className='p-2 text-right font-medium'>Ê∏≤ÊüìÊ¨°Êï∞</th>
                      <th className='p-2 text-right font-medium'>ÊÄªÊó∂Èó¥(ms)</th>
                      <th className='p-2 text-right font-medium'>
                        Âπ≥ÂùáÊó∂Èó¥(ms)
                      </th>
                      <th className='p-2 text-right font-medium'>ÊïàÁéáÂàÜÊï∞</th>
                    </tr>
                  </thead>
                  <tbody>
                    {componentStats.map((stat, index) => (
                      <tr
                        key={`${stat.name}-${index}`}
                        className='hover:bg-muted/50 border-b'
