'use client';

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { MAGIC_0_3 } from "@/constants/decimal";
import { useIntersectionObserver } from '@/hooks/use-intersection-observer';
import {
  techStackCategories,
  techStackData,
  type TechStackCategory,
} from '@/lib/tech-stack-data';
import { ExternalLink } from 'lucide-react';
import { useTranslations } from 'next-intl';
import { useMemo, useState } from 'react';

function TechStackStats({ t }: { t: (_key: string) => string }) {
  const { ref, isVisible } = useIntersectionObserver<HTMLDivElement>({
    threshold: MAGIC_0_3,
    triggerOnce: true,
  });

  return (
    <div
      ref={ref}
      className={`bg-card mt-12 rounded-lg border p-6 transition-all duration-700 ease-out ${
        isVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
      }`}
    >
      <div className='grid grid-cols-2 gap-6 sm:grid-cols-4'>
        <div className='text-center'>
          <div className='text-primary text-2xl font-bold'>
            {techStackData.length}
          </div>
          <div className='text-muted-foreground text-sm'>
            {t('stats.totalTech')}
          </div>
        </div>
        <div className='text-center'>
          <div className='text-primary text-2xl font-bold'>
            {Object.keys(techStackCategories).length}
          </div>
          <div className='text-muted-foreground text-sm'>
            {t('stats.categories')}
          </div>
        </div>
        <div className='text-center'>
          <div className='text-primary text-2xl font-bold'>100%</div>
          <div className='text-muted-foreground text-sm'>
            {t('stats.modern')}
          </div>
        </div>
        <div className='text-center'>
          <div className='text-primary text-2xl font-bold'>A+</div>
          <div className='text-muted-foreground text-sm'>
            {t('stats.quality')}
          </div>
        </div>
      </div>
    </div>
  );
}

// Tech Stack Title Component
function TechStackTitle({
  titleRef,
  titleVisible,
  title,
  subtitle,
}: {
  titleRef: (_node: HTMLDivElement | null) => void;
  titleVisible: boolean;
  title: string;
  subtitle: string;
}) {
  return (
    <div
      ref={titleRef}
      className={`mb-12 text-center transition-all duration-700 ease-out ${
        titleVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
      }`}
    >
      <h2 className='mb-4 text-3xl font-bold tracking-tight sm:text-4xl'>
        {title}
      </h2>
      <p className='text-muted-foreground mx-auto max-w-2xl text-lg'>
        {subtitle}
      </p>
    </div>
