}

// Tech Stack Tabs Component
function TechStackTabs({
  selectedCategory,
  setSelectedCategory,
  tabsRef,
  tabsVisible,
  categorizedTech,
  t,
}: {
  selectedCategory: TechStackCategory;
  setSelectedCategory: (_category: TechStackCategory) => void;
  tabsRef: (_node: HTMLDivElement | null) => void;
  tabsVisible: boolean;
  categorizedTech: Record<TechStackCategory, typeof techStackData>;
  t: (_key: string) => string;
}) {
  return (
    <Tabs
      value={selectedCategory}
      onValueChange={(value) => setSelectedCategory(value as TechStackCategory)}
      className='w-full'
    >
      <div
        ref={tabsRef}
        className={`transition-all delay-200 duration-700 ease-out ${
          tabsVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
        }`}
      >
        <TabsList className='mb-8 grid w-full grid-cols-3 gap-1 sm:grid-cols-5 lg:grid-cols-7'>
          {Object.entries(techStackCategories).map(([key, label]) => (
            <TabsTrigger
              key={key}
              value={key}
              className='text-xs sm:text-sm'
            >
              {label}
            </TabsTrigger>
          ))}
        </TabsList>

        {/* ÊäÄÊúØÊ†àÂÜÖÂÆπ */}
        {Object.entries(categorizedTech).map(([_category, items], index) => (
          <TabsContent
            key={`category-${index}`}
            value={_category}
            className='mt-0'
          >
            <div className='grid gap-4 sm:grid-cols-2 lg:grid-cols-3'>
              {items.map((tech, techIndex) => (
                <Card
                  key={`tech-${techIndex}`}
                  className='group hover:shadow-primary/5 transition-all duration-200 hover:shadow-lg'
                >
                  <CardHeader className='pb-3'>
                    <div className='flex items-center justify-between'>
                      <CardTitle className='text-lg'>{tech.name}</CardTitle>
                      <Badge
                        variant='secondary'
                        className='text-xs'
                      >
                        v{tech.version}
                      </Badge>
                    </div>
                    <CardDescription className='text-sm'>
                      {tech.description}
                    </CardDescription>
                  </CardHeader>
                  {tech.url && (
                    <CardContent className='pt-0'>
                      <Button
                        variant='ghost'
                        size='sm'
                        className='h-8 px-2 text-xs opacity-0 transition-opacity group-hover:opacity-100'
                        asChild
                      >
                        <a
                          href={tech.url}
                          target='_blank'
                          rel='noopener noreferrer'
                          className='flex items-center gap-1'
                        >
                          {t('learnMore')}
                          <ExternalLink className='h-3 w-3' />
                        </a>
                      </Button>
                    </CardContent>
                  )}
                </Card>
              ))}
            </div>
          </TabsContent>
        ))}
      </div>
    </Tabs>
  );
}

export function TechStackSection() {
  const t = useTranslations('home.techStack');
  const [selectedCategory, setSelectedCategory] =
    useState<TechStackCategory>('core');

  // Âä®ÁîªHook
  const { ref: titleRef, isVisible: titleVisible } =
    useIntersectionObserver<HTMLDivElement>({
      threshold: MAGIC_0_3,
      triggerOnce: true,
    });

  const { ref: tabsRef, isVisible: tabsVisible } =
    useIntersectionObserver<HTMLDivElement>({
      threshold: MAGIC_0_3,
      triggerOnce: true,
    });

  const categorizedTech = useMemo(() => {
    return Object.keys(techStackCategories).reduce(
      (acc, category) => {
        acc[category as TechStackCategory] = techStackData.filter(
          (item) => item.category === category,
        );
        return acc;
      },
      {} as Record<TechStackCategory, typeof techStackData>,
    );
  }, []);

  return (
    <section
      id='tech-stack'
      className='py-20'
    >
      <div className='container mx-auto px-4'>
        <div className='mx-auto max-w-6xl'>
          <TechStackTitle
            titleRef={titleRef}
            titleVisible={titleVisible}
            title={t('title')}
            subtitle={t('subtitle')}
          />

          <TechStackTabs
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
            tabsRef={tabsRef}
            tabsVisible={tabsVisible}
            categorizedTech={categorizedTech}
            t={t}
          />

          <TechStackStats t={t} />
        </div>
      </div>
    </section>
  );
}

