'use client';

/* eslint-disable no-case-declarations */
import { MAGIC_2000 } from "@/constants/count";
import { ANGLE_90_DEG, COUNT_FIVE, HTTP_OK, PERCENTAGE_FULL } from "@/constants/magic-numbers";
import { MINUTE_MS } from '@/constants/units';
import {
  I18nPerformanceMonitor,
  preloadTranslations,
} from '@/lib/i18n-performance';
import { logger } from '@/lib/logger';
import { useLocale } from 'next-intl';
import { useEffect } from 'react';

interface TranslationPreloaderProps {
  /**
   * Ë¶ÅÈ¢ÑÂä†ËΩΩÁöÑËØ≠Ë®ÄÂàóË°®
   * ÈªòËÆ§È¢ÑÂä†ËΩΩÂΩìÂâçËØ≠Ë®ÄÂíåËã±Êñá‰Ωú‰∏∫ÂõûÈÄÄ
   */
  locales?: string[];

  /**
   * ÊòØÂê¶ÂêØÁî®ÊÄßËÉΩÁõëÊéß
   */
  enableMonitoring?: boolean;

  /**
   * È¢ÑÂä†ËΩΩÁ≠ñÁï•
   * - 'immediate': Á´ãÂç≥È¢ÑÂä†ËΩΩ
   * - 'idle': Âú®ÊµèËßàÂô®Á©∫Èó≤Êó∂È¢ÑÂä†ËΩΩ
   * - 'interaction': Âú®Áî®Êà∑‰∫§‰∫íÊó∂È¢ÑÂä†ËΩΩ
   */
  strategy?: 'immediate' | 'idle' | 'interaction';

  /**
   * È¢ÑÂä†ËΩΩ‰ºòÂÖàÁ∫ß
   */
  priority?: 'high' | 'normal' | 'low';
}

export function TranslationPreloader({
  locales,
  enableMonitoring = true,
  strategy = 'idle',
  priority: _priority = 'normal',
}: TranslationPreloaderProps) {
  const currentLocale = useLocale();

  useEffect(() => {
    // Á°ÆÂÆöË¶ÅÈ¢ÑÂä†ËΩΩÁöÑËØ≠Ë®Ä
    const targetLocales = locales || [currentLocale, 'en'];

    const preloadWithStrategy = async () => {
      const startTime = performance.now();

      try {
        switch (strategy) {
          case 'immediate':
            await preloadTranslations(targetLocales);
            break;

          case 'idle':
            if ('requestIdleCallback' in window) {
              requestIdleCallback(async () => {
                await preloadTranslations(targetLocales);
              });
            } else {
              // ÂõûÈÄÄÂà∞setTimeout
              setTimeout(async () => {
                await preloadTranslations(targetLocales);
              }, PERCENTAGE_FULL);
            }
            break;

          case 'interaction':
            const handleFirstInteraction = async () => {
              await preloadTranslations(targetLocales);
              // ÁßªÈô§‰∫ã‰ª∂ÁõëÂê¨Âô®
              document.removeEventListener('click', handleFirstInteraction);
              document.removeEventListener('keydown', handleFirstInteraction);
              document.removeEventListener('scroll', handleFirstInteraction);
            };

            document.addEventListener('click', handleFirstInteraction, {
              once: true,
            });
            document.addEventListener('keydown', handleFirstInteraction, {
              once: true,
            });
            document.addEventListener('scroll', handleFirstInteraction, {
              once: true,
            });
            break;
        }

        // ËÆ∞ÂΩïÊÄßËÉΩÊåáÊ†á
        if (enableMonitoring) {
          const loadTime = performance.now() - startTime;
          I18nPerformanceMonitor.recordLoadTime(loadTime);
        }
      } catch (error) {
        logger.error('Translation preload failed', { error: error as Error });
        if (enableMonitoring) {
          I18nPerformanceMonitor.recordError();
        }
      }
    };

    preloadWithStrategy();
  }, [currentLocale, locales, strategy, enableMonitoring]);

  // Ëøô‰∏™ÁªÑ‰ª∂‰∏çÊ∏≤Êüì‰ªª‰ΩïÂÜÖÂÆπ
  return null;
}

/**
 * ÂÖ≥ÈîÆË∑ØÂæÑÁøªËØëÈ¢ÑÂä†ËΩΩÂô®
 * ‰∏ìÈó®Áî®‰∫éÈ¢ÑÂä†ËΩΩÂÖ≥ÈîÆÈ°µÈù¢ÁöÑÁøªËØëÂÜÖÂÆπ
 */
export function CriticalTranslationPreloader() {
  const currentLocale = useLocale();

  useEffect(() => {
    // È¢ÑÂä†ËΩΩÂÖ≥ÈîÆÁøªËØëÂëΩÂêçÁ©∫Èó¥
    const preloadCriticalTranslations = async () => {
      const startTime = performance.now();

      try {
        // È¢ÑÂä†ËΩΩÂΩìÂâçËØ≠Ë®ÄÂíåËã±ÊñáÂõûÈÄÄ
        await preloadTranslations([currentLocale, 'en']);

        const loadTime = performance.now() - startTime;
        I18nPerformanceMonitor.recordLoadTime(loadTime);

        // ËÆ∞ÂΩïÁºìÂ≠òÂëΩ‰∏≠
        I18nPerformanceMonitor.recordCacheHit();
      } catch (error) {
        logger.error('Critical translation preload failed', {
          error: error as Error,
        });
        I18nPerformanceMonitor.recordError();
      }
    };

    // ‰ΩøÁî®È´ò‰ºòÂÖàÁ∫ßÈ¢ÑÂä†ËΩΩ
    if ('scheduler' in window && 'postTask' in (window as any).scheduler) {
      (window as any).scheduler.postTask(preloadCriticalTranslations, {
        priority: 'user-blocking',
      });
    } else {
      // ÂõûÈÄÄÂà∞Á´ãÂç≥ÊâßË°å
      preloadCriticalTranslations();
    }
  }, [currentLocale]);

  return null;
}

/**
 * Êô∫ËÉΩÈ¢ÑÂä†ËΩΩÈÄªËæë
 */
async function performSmartPreload(currentLocale: string) {
  // Ëé∑ÂèñÂΩìÂâçÈ°µÈù¢Ë∑ØÂæÑ
  const currentPath = window.location.pathname;

  // Âü∫‰∫éË∑ØÂæÑÈ¢ÑÊµãÈúÄË¶ÅÁöÑÁøªËØë
  const predictedNamespaces = getPredictedNamespaces(currentPath);

  // È¢ÑÂä†ËΩΩÈ¢ÑÊµãÁöÑÁøªËØëÂÜÖÂÆπ
  for (const namespace of predictedNamespaces) {
    try {
      await preloadTranslations([currentLocale]);
    } catch (error) {
      logger.warn(`Failed to preload namespace ${namespace}`, {
        error: error as Error,
      });
    }
  }
}

/**
 * Ë∞ÉÂ∫¶Êô∫ËÉΩÈ¢ÑÂä†ËΩΩ
 */
function scheduleSmartPreload(preloadFn: () => Promise<void>) {
  // Âú®ÊµèËßàÂô®Á©∫Èó≤Êó∂ÊâßË°åÊô∫ËÉΩÈ¢ÑÂä†ËΩΩ
  if ('requestIdleCallback' in window) {
    requestIdleCallback(preloadFn, { timeout: MAGIC_2000 });
  } else {
    setTimeout(preloadFn, 500);
  }
}

/**
 * Êô∫ËÉΩÁøªËØëÈ¢ÑÂä†ËΩΩÂô®
 * Âü∫‰∫éÁî®Êà∑Ë°å‰∏∫ÂíåÈ°µÈù¢Ë∑ØÁî±Êô∫ËÉΩÈ¢ÑÂä†ËΩΩÁøªËØë
 */
export function SmartTranslationPreloader() {
  const currentLocale = useLocale();

  useEffect(() => {
    const smartPreload = () => performSmartPreload(currentLocale);
    scheduleSmartPreload(smartPreload);
  }, [currentLocale]);

  return null;
}

/**
 * Ê†πÊçÆÈ°µÈù¢Ë∑ØÂæÑÈ¢ÑÊµãÈúÄË¶ÅÁöÑÁøªËØëÂëΩÂêçÁ©∫Èó¥
 */
function getPredictedNamespaces(path: string): string[] {
  const namespaceMap: Record<string, string[]> = {
    '/': ['home', 'hero', 'features'],
    '/about': ['about', 'team', 'company'],
    '/guanyu': ['about', 'team', 'company'],
    '/contact': ['contact', 'form', 'support'],
    '/lianxi': ['contact', 'form', 'support'],
    '/blog': ['blog', 'articles', 'content'],
    '/products': ['products', 'catalog', 'pricing'],
  };

  // Ëé∑ÂèñÂü∫Á°ÄË∑ØÂæÑÔºàÁßªÈô§ËØ≠Ë®ÄÂâçÁºÄÔºâ
  const basePath = path.replace(/^\/[a-z]{2}(?=\/|$)/, '') || '/';

  return namespaceMap[basePath] || ['common'];
}

/**
 * ÊÄßËÉΩÁõëÊéßÈ¢ÑÂä†ËΩΩÂô®
 * ‰∏ìÈó®Áî®‰∫éÁõëÊéßÁøªËØëÊÄßËÉΩÂπ∂‰ºòÂåñÈ¢ÑÂä†ËΩΩÁ≠ñÁï•
 */
export function PerformanceMonitoringPreloader() {
  useEffect(() => {
    // ÂÆöÊúüÊ∏ÖÁêÜËøáÊúüÁºìÂ≠ò
    const cleanupInterval = setInterval(() => {
      // ËøôÈáåÂèØ‰ª•Ê∑ªÂä†ÁºìÂ≠òÊ∏ÖÁêÜÈÄªËæë

      logger.debug('Translation cache cleanup performed');
    }, COUNT_FIVE * MINUTE_MS); // ÊØè5ÂàÜÈíüÊ∏ÖÁêÜ‰∏ÄÊ¨°

    // ÂÆöÊúüÊä•ÂëäÊÄßËÉΩÊåáÊ†á
    const reportingInterval = setInterval(() => {
      const metrics = I18nPerformanceMonitor.getMetrics();

      // Â¶ÇÊûúÊÄßËÉΩÊåáÊ†áÂºÇÂ∏∏ÔºåËÆ∞ÂΩïË≠¶Âëä
      if (metrics.averageLoadTime > HTTP_OK) {
        logger.warn('Translation load time exceeds target', { metrics });
      }

      if (metrics.cacheHitRate < ANGLE_90_DEG) {
        logger.warn('Translation cache hit rate below target', { metrics });
      }
    }, MINUTE_MS); // ÊØèÂàÜÈíüÊ£ÄÊü•‰∏ÄÊ¨°

    return () => {
      clearInterval(cleanupInterval);
      clearInterval(reportingInterval);
    };
  }, []);

  return null;
}

'use client';

import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { MAGIC_2000 } from "@/constants/count";
import { ANIMATION_DURATION_VERY_SLOW } from "@/constants/magic-numbers";
import { Link, usePathname } from '@/i18n/routing';
import { Check, Languages, Loader2 } from 'lucide-react';
import { useLocale, useTranslations } from 'next-intl';
import { memo, useState, useTransition } from 'react';

const TRANSITION_TIMEOUT = ANIMATION_DURATION_VERY_SLOW; // 1 second timeout for language switch

// Custom hook for language switching logic
const useLanguageSwitch = () => {
  const [switchingTo, setSwitchingTo] = useState<string | null>(null);
  const [switchSuccess, setSwitchSuccess] = useState(false);
  const [isPending, startTransition] = useTransition();

  const handleLanguageSwitch = (newLocale: string) => {
    setSwitchingTo(newLocale);
    setSwitchSuccess(false);

    startTransition(() => {
      // The transition will be handled by the Link component
      setTimeout(() => {
        setSwitchingTo(null);
        setSwitchSuccess(true);
        // Hide success indicator after 2 seconds
        setTimeout(() => setSwitchSuccess(false), MAGIC_2000);
      }, TRANSITION_TIMEOUT);
    });
  };

  return {
    switchingTo,
    switchSuccess,
    isPending,
    handleLanguageSwitch,
  };
};

export const LanguageToggle = memo(() => {
  const t = useTranslations('language');
  const locale = useLocale();
  const pathname = usePathname();
  const { switchingTo, isPending, handleLanguageSwitch } = useLanguageSwitch();

  return (
    <DropdownMenu data-testid='language-dropdown-menu'>
      <DropdownMenuTrigger
        asChild
        data-testid='language-dropdown-trigger'
      >
        <Button
          variant='outline'
          size='icon'
          disabled={isPending}
          data-testid='language-toggle-button'
        >
          {isPending ? (
            <Loader2 className='h-[1.2rem] w-[1.2rem] animate-spin' />
          ) : (
            <Languages
              className='h-[1.2rem] w-[1.2rem]'
              data-testid='languages-icon'
            />
          )}
          <span className='sr-only'>{t('toggle')}</span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent
        align='end'
        data-testid='language-dropdown-content'
      >
        <DropdownMenuItem
          asChild
          data-testid='language-dropdown-item'
        >
          <Link
            href={pathname}
            locale='en'
            className='flex items-center'
            onClick={() => handleLanguageSwitch('en')}
            data-testid='language-link-en'
            data-locale='en'
            role='menuitem'
          >
            <span className='bg-muted mr-2 rounded px-1.5 py-0.5 font-mono text-xs'>
              EN
            </span>
            <span>{t('english')}</span>
            {switchingTo === 'en' && (
              <Loader2 className='ml-auto h-4 w-4 animate-spin' />
            )}
            {locale === 'en' && switchingTo !== 'en' && (
              <Check
                className='ml-auto h-4 w-4 text-green-500'
                data-testid='check-icon'
              />
            )}
          </Link>
        </DropdownMenuItem>
        <DropdownMenuItem
          asChild
          data-testid='language-dropdown-item'
        >
          <Link
            href={pathname}
            locale='zh'
            className='flex items-center'
            onClick={() => handleLanguageSwitch('zh')}
            data-testid='language-link-zh'
            data-locale='zh'
            role='menuitem'
          >
            <span className='bg-muted mr-2 rounded px-1.5 py-0.5 font-mono text-xs'>
              ZH
            </span>
            <span>{t('chinese')}</span>
            {switchingTo === 'zh' && (
              <Loader2 className='ml-auto h-4 w-4 animate-spin' />
            )}
            {locale === 'zh' && switchingTo !== 'zh' && (
              <Check
                className='ml-auto h-4 w-4 text-green-500'
                data-testid='check-icon'
              />
            )}
          </Link>
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
});

LanguageToggle.displayName = 'LanguageToggle';

