// Re-export everything from testing library
export * from '@testing-library/react';
export { default as userEvent } from '@testing-library/user-event';

/**
 * Language Switcher Component
 *
 * Enhanced language switching component for the navigation bar.
 * Features smooth transitions, loading states, and accessibility.
 */
'use client';

import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { LOCALES_CONFIG } from '@/config/paths';
import { UI_TIMINGS } from '@/constants/i18n-constants';
import { COUNT_PAIR } from "@/constants/magic-numbers";
import { Link, usePathname } from '@/i18n/routing';
import { NAVIGATION_ARIA } from '@/lib/navigation';
import { cn } from '@/lib/utils';
import { Check, Globe, Languages, Loader2 } from 'lucide-react';
import { useLocale, useTranslations } from 'next-intl';
import { memo, useEffect, useRef, useState, useTransition } from 'react';

/**
 * Language Switcher Component
 *
 * Enhanced language switching component for the navigation bar.
 * Features smooth transitions, loading states, and accessibility.
 */

// Use centralized timing constants to avoid magic numbers
const TRANSITION_DIVISOR = COUNT_PAIR;
const TRANSITION_TIMEOUT =
  UI_TIMINGS.LANGUAGE_SWITCH_ANIMATION / TRANSITION_DIVISOR; // 1 second for transition
const SUCCESS_INDICATOR_TIMEOUT = UI_TIMINGS.SUCCESS_DISPLAY_DURATION; // 2 seconds for success indicator

// Language configuration - using centralized config to avoid hardcoding
const LANGUAGES = [
  {
    code: 'en' as const,
    name: 'English',
    nativeName: LOCALES_CONFIG.displayNames.en,
    flag: 'üá∫üá∏',
  },
  {
    code: 'zh' as const,
    name: 'Chinese',
    nativeName: LOCALES_CONFIG.displayNames.zh,
    flag: 'üá®üá≥',
  },
] as const;

// Custom hook for language switching logic
const useLanguageSwitch = () => {
  const [switchingTo, setSwitchingTo] = useState<string | null>(null);
  const [switchSuccess, setSwitchSuccess] = useState(false);
  const [isPending, startTransition] = useTransition();
  const timersRef = useRef<{
    transition?: ReturnType<typeof setTimeout>;
    success?: ReturnType<typeof setTimeout>;
  }>({});

  // Cleanup timers on unmount
  useEffect(() => {
    const timers = timersRef.current;
    return () => {
      if (timers.transition) {
        clearTimeout(timers.transition);
      }
      if (timers.success) {
        clearTimeout(timers.success);
      }
    };
  }, []);

  const handleLanguageSwitch = (newLocale: string) => {
    // Clear any existing timers
    if (timersRef.current.transition) {
      clearTimeout(timersRef.current.transition);
    }
    if (timersRef.current.success) {
      clearTimeout(timersRef.current.success);
    }

    setSwitchingTo(newLocale);
    setSwitchSuccess(false);

    startTransition(() => {
      // The transition will be handled by the Link component
      timersRef.current.transition = setTimeout(() => {
        setSwitchingTo(null);
        setSwitchSuccess(true);

        // Hide success indicator after timeout
        timersRef.current.success = setTimeout(() => {
          setSwitchSuccess(false);
        }, SUCCESS_INDICATOR_TIMEOUT);
      }, TRANSITION_TIMEOUT);
    });
  };

  return {
    switchingTo,
    switchSuccess,
    isPending,
    handleLanguageSwitch,
  };
};

interface LanguageSwitcherProps {
  className?: string;
  variant?: 'default' | 'compact';
  showLabel?: boolean;
}

export const LanguageSwitcher = memo(
  ({
    className,
    variant = 'default',
    showLabel = false,
  }: LanguageSwitcherProps) => {
    const t = useTranslations('language');
    const locale = useLocale();
    const pathname = usePathname();
    const { switchingTo, isPending, handleLanguageSwitch } =
      useLanguageSwitch();

    const currentLanguage = LANGUAGES.find((lang) => lang.code === locale);
    const isCompact = variant === 'compact';

    return (
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button
            variant='ghost'
            size={isCompact ? 'sm' : 'icon'}
            disabled={isPending}
            className={cn(
              'transition-colors duration-200',
              isCompact && 'gap-1 px-2',
              className,
            )}
            aria-label={NAVIGATION_ARIA.languageSelector}
          >
            {isPending ? (
              <Loader2 className='h-4 w-4 animate-spin' />
            ) : isCompact ? (
              <Globe className='h-4 w-4' />
            ) : (
              <Languages className='h-4 w-4' />
            )}

            {isCompact && currentLanguage && (
              <span className='text-xs font-medium'>
                {currentLanguage.code.toUpperCase()}
              </span>
            )}

            {showLabel && <span className='ml-1 text-sm'>{t('toggle')}</span>}

            <span className='sr-only'>{t('toggle')}</span>
          </Button>
        </DropdownMenuTrigger>

        <DropdownMenuContent
          align='end'
          className='w-48'
          aria-label={t('selectLanguage')}
        >
          {LANGUAGES.map((language) => {
            const isActive = locale === language.code;
            const isSwitching = switchingTo === language.code;

            return (
              <DropdownMenuItem
                key={language.code}
                asChild
              >
                <Link
                  href={pathname}
                  locale={language.code as 'en' | 'zh'}
                  className={cn(
                    'flex w-full cursor-pointer items-center',
                    isActive && 'bg-accent',
                  )}
                  onClick={() => handleLanguageSwitch(language.code)}
                >
                  <span
                    className='mr-2 text-base'
                    role='img'
                    aria-hidden='true'
                  >
                    {language.flag}
                  </span>

                  <div className='flex flex-1 flex-col'>
                    <span className='font-medium'>{language.nativeName}</span>
                    <span className='text-muted-foreground text-xs'>
                      {language.name}
                    </span>
                  </div>

                  {isSwitching && (
                    <Loader2 className='ml-auto h-4 w-4 animate-spin' />
                  )}

                  {isActive && !isSwitching && (
                    <Check className='ml-auto h-4 w-4 text-green-500' />
                  )}
                </Link>
              </DropdownMenuItem>
            );
          })}
        </DropdownMenuContent>
      </DropdownMenu>
    );
  },
);

LanguageSwitcher.displayName = 'LanguageSwitcher';

// Compact version for mobile or space-constrained areas
export const LanguageSwitcherCompact = memo(
  ({ className }: { className?: string }) => {
    const props = {
      variant: 'compact' as const,
      ...(className && { className }),
    };

    return <LanguageSwitcher {...props} />;
  },
);

LanguageSwitcherCompact.displayName = 'LanguageSwitcherCompact';

'use client';

import { ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';
import { Analytics } from '@vercel/analytics/react';
import { useLocale } from 'next-intl';
import React, { useEffect } from 'react';

/**
 * ‰ΩøÁî®ÂÖ®Â±Ä loggerÔºàÂºÄÂèëÁéØÂ¢ÉËæìÂá∫ÔºåÁîü‰∫ßÁéØÂ¢ÉÈùôÈªòÔºâ
 */

interface AnalyticsConfig {
  enableWebVitals: boolean;
  enableI18nTracking: boolean;
  enablePerformanceMonitoring: boolean;
}

const defaultConfig: AnalyticsConfig = {
  enableWebVitals: true,
  enableI18nTracking: true,
  enablePerformanceMonitoring: true,
};

export function EnterpriseAnalytics({
  children,
  config = defaultConfig,
}: {
  children: React.ReactNode;
  config?: AnalyticsConfig;
}) {
  const locale = useLocale();

  useEffect(() => {
    // ÂàùÂßãÂåñ‰ºÅ‰∏öÁ∫ßÁõëÊéß
    if (typeof window === 'undefined') return;

    // Web VitalsÁõëÊéß
    if (config.enableWebVitals) {
      initWebVitals(locale);
    }

    // i18nÊÄßËÉΩÁõëÊéß
    if (config.enableI18nTracking) {
      initI18nTracking(locale);
    }

    // ÊÄßËÉΩÁõëÊéß
    if (config.enablePerformanceMonitoring) {
      initPerformanceMonitoring(locale);
    }
  }, [locale, config]);

  return (
    <>
      {children}
      <Analytics />
    </>
  );
}

function initWebVitals(locale: string): void {
  // Âä®ÊÄÅÂØºÂÖ•web-vitals
  import('web-vitals')
    .then(({ onCLS, onFCP, onLCP, onTTFB, onINP }) => {
      const reportVital = (metric: {
        name: string;
        value: number;
        rating: string;
      }) => {
        // ÂºÄÂèëÁéØÂ¢ÉÊó•Âøó
        logger.warn(
          `[Web Vitals] ${metric.name}: ${metric.value} (${metric.rating}) - Locale: ${locale}`,
        );

        // ÂèëÈÄÅÂà∞Vercel Analytics
        if ('va' in window) {
          try {
            (window as any).va('track', 'Web Vital', {
              metric_name: metric.name,
              metric_value: metric.value,
              metric_rating: metric.rating,
              locale,
            });
          } catch (error) {
            logger.error('Failed to send to Vercel Analytics:', error);
          }
        }
      };

      onCLS(reportVital);
      onFCP(reportVital);
      onLCP(reportVital);
      onTTFB(reportVital);
      onINP(reportVital);
    })
    .catch((error) => {
      logger.error('Failed to load web-vitals:', error);
    });
}

function initI18nTracking(locale: string): void {
  // ÁõëÊéßËØ≠Ë®ÄÂàáÊç¢
  const originalPushState = history.pushState;
  const originalReplaceState = history.replaceState;

  history.pushState = function (...args) {
    trackNavigation(locale);
    return originalPushState.apply(this, args);
  };

  history.replaceState = function (...args) {
    trackNavigation(locale);
    return originalReplaceState.apply(this, args);
  };

  window.addEventListener('popstate', () => {
    trackNavigation(locale);
  });
}

function trackNavigation(locale: string): void {
  logger.warn(
    `[I18n Navigation] Locale: ${locale}, URL: ${window.location.href}`,
  );

  // ÂèëÈÄÅÂØºËà™‰∫ã‰ª∂
  if ('va' in window) {
    try {
      (window as any).va('track', 'Navigation', {
        locale,
        url: window.location.href,
      });
    } catch (error) {
      logger.error('Failed to send navigation event:', error);
    }
  }
}

function initPerformanceMonitoring(locale: string): void {
  // ÁõëÊéßÈ°µÈù¢Âä†ËΩΩÊÄßËÉΩ
  window.addEventListener('load', () => {
    setTimeout(() => {
      const navigation = performance.getEntriesByType(
        'navigation',
      )[ZERO] as PerformanceNavigationTiming;

      if (navigation) {
        const metrics = {
          dns: navigation.domainLookupEnd - navigation.domainLookupStart,
          tcp: navigation.connectEnd - navigation.connectStart,
          request: navigation.responseStart - navigation.requestStart,
          response: navigation.responseEnd - navigation.responseStart,
          dom: navigation.domContentLoadedEventEnd - navigation.responseEnd,
          load: navigation.loadEventEnd - navigation.loadEventStart,
          total: navigation.loadEventEnd - navigation.fetchStart,
        };

        logger.warn(`[Performance] Locale: ${locale}`, metrics);

        // ÂèëÈÄÅÊÄßËÉΩÊåáÊ†á
        if ('va' in window) {
          try {
            (window as any).va('track', 'Performance', {
              ...metrics,
              locale,
            });
          } catch (error) {
            logger.error('Failed to send performance metrics:', error);
          }
        }
      }
    }, ZERO);
  });

  // ÁõëÊéßËµÑÊ∫êÂä†ËΩΩ
  const observer = new PerformanceObserver((list) => {
    const entries = list.getEntries();

    for (const entry of entries) {
      if (entry.entryType === 'resource') {
        const resourceEntry = entry as PerformanceResourceTiming;

        // ÁâπÂà´ÂÖ≥Ê≥®i18nÁõ∏ÂÖ≥ËµÑÊ∫ê
        if (
          resourceEntry.name.includes('/messages/') ||
          resourceEntry.name.includes('locale') ||
          resourceEntry.name.includes('i18n')
        ) {
          logger.warn(
            `[I18n Resource] ${resourceEntry.name}: ${resourceEntry.duration}ms (${resourceEntry.transferSize} bytes) - Locale: ${locale}`,
          );
        }
      }
    }
  });

  observer.observe({ entryTypes: ['resource'] });
}

// ÂØºÂá∫Áî®‰∫éÊâãÂä®ËøΩË∏™ÁöÑÂáΩÊï∞
export const enterpriseAnalytics = {
  trackEvent: (eventName: string, properties: Record<string, unknown> = {}) => {
    if (typeof window === 'undefined') return;

    logger.warn(`[Analytics Event] ${eventName}:`, properties);

    if ('va' in window) {
      try {
        (window as any).va('track', eventName, properties);
      } catch (error) {
        logger.error('Failed to send to Vercel Analytics:', error);
      }
    }

    if ('gtag' in window) {
      try {
        (window as any).gtag('event', eventName, properties);
      } catch (error) {
        logger.error('Failed to send to Google Analytics:', error);
      }
    }
  },

  trackError: (error: Error, context: Record<string, unknown> = {}) => {
    if (typeof window === 'undefined') return;

    logger.error('[Tracked Error]', error, context);

    enterpriseAnalytics.trackEvent('Error', {
      error_message: error.message,
      error_name: error.name,
      error_stack: error.stack,
      ...context,
    });
  },

  trackI18nEvent: (eventType: string, data: Record<string, unknown> = {}) => {
    if (typeof window === 'undefined') return;

    logger.warn(`[I18n Event] ${eventType}:`, data);

    enterpriseAnalytics.trackEvent(`I18n ${eventType}`, data);
  },

  trackLocaleChange: (fromLocale: string, toLocale: string) => {
    enterpriseAnalytics.trackI18nEvent('Locale Change', {
      from_locale: fromLocale,
      to_locale: toLocale,
      source: 'user_action',
    });
  },

  trackPerformance: (metric: string, value: number, locale: string) => {
    enterpriseAnalytics.trackEvent('Performance Metric', {
      metric,
      value,
      locale,
    });
  },
};

