// Ëá™ÂÆö‰πâhookÂ§ÑÁêÜhydration
function useMounted() {
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    // eslint-disable-next-line react-you-might-not-need-an-effect/no-initialize-state
