interface ResponsiveLayoutProps {
  'children': ReactNode;
  'className'?: string;
  'mobileLayout'?: ReactNode;
  'tabletLayout'?: ReactNode;
  'desktopLayout'?: ReactNode;
  // Additional props for testing and flexibility
  'mobileNavigation'?: ReactNode;
  'tabletSidebar'?: ReactNode;
  'desktopSidebar'?: ReactNode;
  'onTouchStart'?: () => void;
  'onTouchEnd'?: () => void;
  'onMouseEnter'?: () => void;
  'onMouseLeave'?: () => void;
  'onLayoutChange'?: (_layout: string) => void;
  'role'?: string;
  'aria-label'?: string;
  'data-testid'?: string;
  'tabIndex'?: number;
}

// Ëé∑ÂèñÂΩìÂâçÂ∏ÉÂ±ÄÁ±ªÂûãÁöÑËæÖÂä©ÂáΩÊï∞
function getCurrentLayoutType(
  isMobile: boolean,
  isTablet: boolean,
): 'mobile' | 'tablet' | 'desktop' {
  if (isMobile) return 'mobile';
  if (isTablet) return 'tablet';
  return 'desktop';
}

// Â∏ÉÂ±ÄÂÜÖÂÆπÈÖçÁΩÆÊé•Âè£
interface LayoutContentConfig {
  mobileLayout?: ReactNode;
  mobileNavigation?: ReactNode;
  tabletLayout?: ReactNode;
  tabletSidebar?: ReactNode;
  desktopLayout?: ReactNode;
  desktopSidebar?: ReactNode;
}

// Ëé∑ÂèñÁâπÂÆöÂ∏ÉÂ±ÄÂÜÖÂÆπÁöÑËæÖÂä©ÂáΩÊï∞
function getSpecificLayoutContent(
  layoutType: 'mobile' | 'tablet' | 'desktop',
  config: LayoutContentConfig,
): ReactNode | null {
  switch (layoutType) {
    case 'mobile':
      return config.mobileLayout || config.mobileNavigation || null;
    case 'tablet':
      return config.tabletLayout || config.tabletSidebar || null;
    case 'desktop':
      return config.desktopLayout || config.desktopSidebar || null;
    default:
      return null;
  }
}

// ÁîüÊàêÂìçÂ∫îÂºèCSSÁ±ªÂêçÁöÑËæÖÂä©ÂáΩÊï∞
function generateResponsiveClasses(
  className: string,
  isMobile: boolean,
  isTablet: boolean,
  isDesktop: boolean,
): string {
  const classes = [className];

  if (isMobile) classes.push('responsive-mobile');
  if (isTablet) classes.push('responsive-tablet');
  if (isDesktop) classes.push('responsive-desktop');

  return classes.filter(Boolean).join(' ').trim();
}

export function ResponsiveLayout({
  children,
  className = '',
  mobileLayout,
  tabletLayout,
  desktopLayout,
  mobileNavigation,
  tabletSidebar,
  desktopSidebar,
  onTouchStart,
  onTouchEnd,
  onMouseEnter,
  onMouseLeave,
  onLayoutChange,
  role,
  'aria-label': ariaLabel,
  'data-testid': dataTestId,
  tabIndex,
}: ResponsiveLayoutProps) {
  const { currentBreakpoint, isBelow, isAbove } = useBreakpoint();

  // ‰ΩøÁî®Ëá™ÂÆö‰πâhookÂ§ÑÁêÜhydration
  const mounted = useMounted();

  // Prevent hydration mismatch
  if (!mounted) {
    return <div className={className}>{children}</div>;
  }

  const isMobile = isBelow('md');
  const isTablet = currentBreakpoint === 'md' || currentBreakpoint === 'lg';
  const isDesktop = isAbove('lg');

  const layoutType = getCurrentLayoutType(isMobile, isTablet);
  const specificContent = getSpecificLayoutContent(layoutType, {
    mobileLayout,
    mobileNavigation,
    tabletLayout,
    tabletSidebar,
    desktopLayout,
    desktopSidebar,
  });

  // Use specific layouts if provided
  if (specificContent) {
    return <div className={className}>{specificContent}</div>;
  }

  // Notify layout change
  if (onLayoutChange) {
    onLayoutChange(layoutType);
  }

  // Default responsive behavior
  const responsiveClasses = generateResponsiveClasses(
    className,
    isMobile,
    isTablet,
    isDesktop,
  );

  return (
    <div
      className={responsiveClasses}
      onTouchStart={onTouchStart}
      onTouchEnd={onTouchEnd}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
      role={role}
      aria-label={ariaLabel}
      data-testid={dataTestId}
      tabIndex={tabIndex}
    >
      {children}
    </div>
  );
}

