import { ONE, ZERO } from "@/constants/magic-numbers";

/**
 * Â§ÑÁêÜÈîÆÁõòÂØºËà™
 */
export const createKeyboardHandler = (
  theme: string | undefined,
  handleThemeChange: (
    newTheme: string,
    buttonElement?: HTMLButtonElement,
  ) => void,
) => {
  return (
    event: React.KeyboardEvent<HTMLButtonElement>,
    _themeValue: string,
  ) => {
    const currentIndex = THEME_OPTIONS.findIndex(
      (option) => option.value === theme,
    );
    let nextIndex = currentIndex;
    const buttonElement = event.currentTarget;

    switch (event.key) {
      case 'ArrowLeft':
      case 'ArrowUp': {
        event.preventDefault();
        nextIndex =
          currentIndex > ZERO ? currentIndex - ONE : THEME_OPTIONS.length - ONE;
        const prevOption = THEME_OPTIONS[nextIndex];
        if (prevOption) {
          handleThemeChange(prevOption.value, buttonElement);
        }
        break;
      }
      case 'ArrowRight':
      case 'ArrowDown': {
        event.preventDefault();
        nextIndex =
          currentIndex < THEME_OPTIONS.length - ONE ? currentIndex + ONE : ZERO;
        const nextOption = THEME_OPTIONS[nextIndex];
        if (nextOption) {
          handleThemeChange(nextOption.value, buttonElement);
        }
        break;
      }
      case 'Home': {
        event.preventDefault();
        const firstOption = THEME_OPTIONS[ZERO];
        if (firstOption) {
          handleThemeChange(firstOption.value, buttonElement);
        }
        break;
      }
      case 'End': {
        event.preventDefault();
        const lastOption = THEME_OPTIONS[THEME_OPTIONS.length - ONE];
        if (lastOption) {
          handleThemeChange(lastOption.value, buttonElement);
        }
        break;
      }
    }
  };
};

'use client';

import React, { useEffect, useState } from 'react';
import { Monitor, Moon, Sun } from 'lucide-react';
import { useTranslations } from 'next-intl';
import { useTheme } from 'next-themes';
import { cn } from '@/lib/utils';

/**
 * ‰∏ªÈ¢òÈÄâÈ°πÈÖçÁΩÆ
 */
const THEME_OPTIONS = [
  {
    value: 'system',
    icon: Monitor,
    labelKey: 'theme.system',
    ariaLabelKey: 'theme.switchToSystem',
  },
  {
    value: 'light',
    icon: Sun,
    labelKey: 'theme.light',
    ariaLabelKey: 'theme.switchToLight',
  },
  {
    value: 'dark',
    icon: Moon,
    labelKey: 'theme.dark',
    ariaLabelKey: 'theme.switchToDark',
  },
] as const;

/**
 * ÁªÑ‰ª∂Â±ûÊÄßÊé•Âè£
 */
export interface VercelThemeToggleProps {
  /** Ëá™ÂÆö‰πâÊ†∑ÂºèÁ±ªÂêç */
  'className'?: string;
  /** ÊòØÂê¶ÊòæÁ§∫Ê†áÈ¢ò */
  'showTitle'?: boolean;
  /** Ëá™ÂÆö‰πâÊµãËØïID */
  'data-testid'?: string;
}

/**
 * Vercel È£éÊ†º‰∏ªÈ¢òÂàáÊç¢ÁªÑ‰ª∂ - ÁÆÄÂåñÁâà
 */
export function VercelThemeToggle({
  className,
  showTitle = true,
  'data-testid': testId = 'vercel-theme-toggle',
}: VercelThemeToggleProps) {
  const { theme, setTheme } = useTheme();
  const [mounted, setMounted] = useState(false);
  const t = useTranslations();

  // Èò≤Ê≠¢ hydration mismatch
  useEffect(() => {
    setMounted(true);
  }, []);

  // Âä†ËΩΩÁä∂ÊÄÅÁöÑÈ™®Êû∂Â±è
  if (!mounted) {
    return (
      <div
        className={cn('space-y-3', className)}
        data-testid={`${testId}-skeleton`}
      >
        {showTitle && (
          <div className='bg-muted h-5 w-16 animate-pulse rounded' />
        )}
        <div className='flex gap-2'>
          {THEME_OPTIONS.map((_, index) => (
            <div
              key={index}
              className='flex animate-pulse items-center gap-2 rounded-md border p-2'
            >
              <div className='bg-muted h-4 w-4 rounded' />
              <div className='bg-muted h-4 w-12 rounded' />
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div
      className={cn('space-y-3', className)}
      data-testid={testId}
    >
      {showTitle && (
        <h3 className='text-foreground text-sm font-medium'>
          {t('theme.title')}
        </h3>
      )}

      <div
        className='flex gap-2'
        role='radiogroup'
        aria-label={t('theme.selectTheme')}
      >
        {THEME_OPTIONS.map((themeOption) => {
          const Icon = themeOption.icon;
          const isActive = theme === themeOption.value;
          const label = t(themeOption.labelKey);
          const ariaLabel = t(themeOption.ariaLabelKey);

          return (
            <button
              key={themeOption.value}
              className={cn(
                'flex items-center gap-2 rounded-md border px-3 py-2 text-sm transition-colors',
                'hover:bg-muted focus-visible:ring-ring focus-visible:ring-2 focus-visible:outline-none',
                isActive
                  ? 'bg-primary text-primary-foreground border-primary'
                  : 'bg-background text-muted-foreground border-border hover:text-foreground',
              )}
              onClick={() => setTheme(themeOption.value)}
              aria-label={ariaLabel}
              role='radio'
              aria-checked={isActive}
              type='button'
              data-testid={`${testId}-${themeOption.value}`}
            >
              <Icon className='h-4 w-4' />
              <span className='font-medium'>{label}</span>
            </button>
          );
        })}
      </div>
    </div>
  );
}

