import { FIVE_SECONDS_MS, MAGIC_10000, ONE, ZERO } from "@/constants/magic-numbers";
import { checkMemoryUsageAlert } from '@/hooks/performance-monitor-utils';
import { logger } from '@/lib/logger';
import React from 'react';
import type {
  PerformanceAlert,
  PerformanceAlertThresholds,
  PerformanceMeasurements,
  PerformanceMetrics,
} from '@/hooks/performance-monitor-types';

/**
 * ÂàõÂª∫ÊÄßËÉΩÊµãÈáèÂáΩÊï∞ÁöÑËæÖÂä©ÂáΩÊï∞
 */
export function usePerformanceMeasurements(
  enableAlerts: boolean,
  alertThresholds: Required<PerformanceAlertThresholds>,
  addAlert: (_alert: Omit<PerformanceAlert, 'id' | 'timestamp'>) => void,
  setMetrics: React.Dispatch<React.SetStateAction<PerformanceMetrics | null>>,
  startTime: React.MutableRefObject<number>,
): PerformanceMeasurements {
  const measureLoadTime = React.useCallback(() => {
    try {
      if (typeof window !== 'undefined' && window.performance) {
        const navigation = performance.getEntriesByType(
          'navigation',
        )[ZERO] as PerformanceNavigationTiming;
        if (navigation) {
          const loadTime = navigation.loadEventEnd - navigation.startTime;

          setMetrics((prev) => ({
            renderTime: ZERO,
            ...(prev || {}),
            loadTime,
          }));

          if (enableAlerts && loadTime > alertThresholds.loadTime) {
            addAlert({
              level: 'warning',
              message: `Slow page load detected: ${Math.round(loadTime)}ms`,
              data: { loadTime },
            });
          }
        }
      }
    } catch (error) {
      logger.warn('Failed to measure load time', { error: error as Error });
    }
  }, [enableAlerts, alertThresholds.loadTime, addAlert, setMetrics]);

  const measureRenderTime = React.useCallback(() => {
    try {
      if (startTime.current !== null) {
        const renderTime = performance.now() - startTime.current;

        setMetrics((prev) => ({
          loadTime: ZERO,
          ...(prev || {}),
          renderTime,
        }));

        if (enableAlerts && renderTime > alertThresholds.renderTime) {
          addAlert({
            level: 'warning',
            message: `Slow render detected: ${Math.round(renderTime)}ms`,
            data: { renderTime },
          });
        }
      }
    } catch (error) {
      logger.warn('Failed to measure render time', { error: error as Error });
    }
  }, [
    enableAlerts,
    alertThresholds.renderTime,
    addAlert,
    setMetrics,
    startTime,
  ]);

  const measureMemoryUsage = React.useCallback(() => {
    try {
      if (typeof window !== 'undefined' && window.performance?.memory) {
        const memoryUsage = window.performance.memory.usedJSHeapSize;

        setMetrics((prev) => ({
          loadTime: ZERO,
          renderTime: ZERO,
          ...(prev || {}),
          memoryUsage,
        }));

        if (enableAlerts) {
          checkMemoryUsageAlert(
            memoryUsage,
            alertThresholds.memoryUsage,
            addAlert,
          );
        }
      }
    } catch (error) {
      logger.warn('Failed to measure memory usage', { error: error as Error });
    }
  }, [enableAlerts, alertThresholds.memoryUsage, addAlert, setMetrics]);

  return {
    measureLoadTime,
    measureRenderTime,
    measureMemoryUsage,
  };
}

/**
 * ÊµãÈáèÁΩëÁªúÂª∂Ëøü
 */
export const measureNetworkLatency = async (): Promise<number | null> => {
  try {
    if (typeof window !== 'undefined' && (navigator as any).connection) {
      // ‰ΩøÁî® Network Information API (Â¶ÇÊûúÂèØÁî®)
      const connection = (navigator as any).connection as {
        rtt?: number;
        effectiveType?: string;
        downlink?: number;
      };
      if (connection.rtt) {
        return connection.rtt;
      }
    }

    // ÂõûÈÄÄÂà∞ÁÆÄÂçïÁöÑpingÊµãËØï
    const startTime = performance.now();
    await fetch('/api/ping', {
      method: 'HEAD',
      cache: 'no-cache',
    });
    const endTime = performance.now();

    return endTime - startTime;
  } catch (error) {
    logger.warn('Failed to measure network latency', { error: error as Error });
    return null;
  }
};

/**
 * ÊµãÈáèÈ¶ñÊ¨°ÂÜÖÂÆπÁªòÂà∂ (FCP)
 */
export const measureFirstContentfulPaint = (): number | null => {
  try {
    if (typeof window !== 'undefined' && window.performance) {
      const paintEntries = performance.getEntriesByType('paint');
      const fcpEntry = paintEntries.find(
        (entry) => entry.name === 'first-contentful-paint',
      );
      return fcpEntry ? fcpEntry.startTime : null;
    }
    return null;
  } catch (error) {
    logger.warn('Failed to measure FCP', { error: error as Error });
    return null;
  }
};

/**
 * ÊµãÈáèÊúÄÂ§ßÂÜÖÂÆπÁªòÂà∂ (LCP)
 */
export const measureLargestContentfulPaint = (): Promise<number | null> => {
  return new Promise((resolve) => {
    try {
      if (typeof window !== 'undefined' && 'PerformanceObserver' in window) {
        const observer = new PerformanceObserver((list) => {
          const entries = list.getEntries();
          const lastEntry = entries[entries.length - ONE];
          resolve(lastEntry ? lastEntry.startTime : null);
          observer.disconnect();
        });

        observer.observe({ entryTypes: ['largest-contentful-paint'] });

        // Ë∂ÖÊó∂Â§ÑÁêÜ
        setTimeout(() => {
          observer.disconnect();
          resolve(null);
        }, FIVE_SECONDS_MS);
      } else {
        resolve(null);
      }
    } catch (error) {
      logger.warn('Failed to measure LCP', { error: error as Error });
      resolve(null);
    }
  });
};

/**
 * ÊµãÈáèÁ¥ØÁßØÂ∏ÉÂ±ÄÂÅèÁßª (CLS)
 */
export const measureCumulativeLayoutShift = (): Promise<number | null> => {
  return new Promise((resolve) => {
    try {
      if (typeof window !== 'undefined' && 'PerformanceObserver' in window) {
        let clsValue = ZERO;

        const observer = new PerformanceObserver((list) => {
          for (const entry of list.getEntries()) {
            const layoutShiftEntry = entry as unknown as {
              hadRecentInput?: boolean;
              value: number;
            };
            if (!layoutShiftEntry.hadRecentInput) {
              clsValue += layoutShiftEntry.value;
            }
          }
        });

        observer.observe({ entryTypes: ['layout-shift'] });

        // Âú®È°µÈù¢ÈöêËóèÊó∂ËøîÂõûÁªìÊûú
        const handleVisibilityChange = () => {
          if (document.visibilityState === 'hidden') {
            observer.disconnect();
            document.removeEventListener(
              'visibilitychange',
              handleVisibilityChange,
            );
            resolve(clsValue);
          }
        };

        document.addEventListener('visibilitychange', handleVisibilityChange);

        // Ë∂ÖÊó∂Â§ÑÁêÜ
        setTimeout(() => {
          observer.disconnect();
          document.removeEventListener(
            'visibilitychange',
            handleVisibilityChange,
          );
          resolve(clsValue);
        }, MAGIC_10000);
      } else {
        resolve(null);
      }
    } catch (error) {
      logger.warn('Failed to measure CLS', { error: error as Error });
      resolve(null);
    }
  });
};

/**
 * ÊµãÈáèÈ¶ñÊ¨°ËæìÂÖ•Âª∂Ëøü (FID)
 */
export const measureFirstInputDelay = (): Promise<number | null> => {
  return new Promise((resolve) => {
    try {
      if (typeof window !== 'undefined' && 'PerformanceObserver' in window) {
        const observer = new PerformanceObserver((list) => {
          const entries = list.getEntries();
          const firstEntry = entries[ZERO];
          if (firstEntry) {
            const firstInputEntry = firstEntry as unknown as {
              processingStart: number;
              startTime: number;
            };
            const fid = firstInputEntry.processingStart - firstEntry.startTime;
            resolve(fid);
          } else {
            resolve(null);
          }
          observer.disconnect();
        });

        observer.observe({ entryTypes: ['first-input'] });

        // Ë∂ÖÊó∂Â§ÑÁêÜ
        setTimeout(() => {
          observer.disconnect();
          resolve(null);
        }, MAGIC_10000);
      } else {
        resolve(null);
      }
    } catch (error) {
      logger.warn('Failed to measure FID', { error: error as Error });
      resolve(null);
    }
  });
};

/**
 * ÁªºÂêàÊÄßËÉΩÊµãÈáèÂáΩÊï∞
 */
export const measureComprehensivePerformance = async (): Promise<
  Partial<PerformanceMetrics>
> => {
  const metrics: Partial<PerformanceMetrics> = {};

  try {
    // ÊµãÈáèÂü∫Êú¨ÊåáÊ†á
    if (typeof window !== 'undefined' && window.performance) {
      // Âä†ËΩΩÊó∂Èó¥
      const navigation = performance.getEntriesByType(
        'navigation',
      )[ZERO] as PerformanceNavigationTiming;
      if (navigation) {
        metrics.loadTime = navigation.loadEventEnd - navigation.startTime;
      }

      // ÂÜÖÂ≠ò‰ΩøÁî®
      if (window.performance.memory) {
        metrics.memoryUsage = window.performance.memory.usedJSHeapSize;
      }
    }

    // ÁΩëÁªúÂª∂Ëøü
    const networkLatency = await measureNetworkLatency();
    if (networkLatency !== null) {
      metrics.networkLatency = networkLatency;
    }

    return metrics;
  } catch (error) {
    logger.warn('Failed to measure comprehensive performance', {
      error: error as Error,
    });
    return {};
  }
};

import { MAGIC_16 } from "@/constants/count";
import { ANIMATION_DURATION_VERY_SLOW, COUNT_PAIR, MAGIC_36, MAGIC_9, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, THREE_SECONDS_MS, ZERO } from "@/constants/magic-numbers";
import React from 'react';
import type {
  MonitoringControls,
  PerformanceAlert,
  PerformanceAlertSystem,
  PerformanceAlertThresholds,
  PerformanceMetrics,
  PerformanceMonitorReturnParams,
  PerformanceMonitorState,
  UsePerformanceMonitorOptions,
} from '@/hooks/performance-monitor-types';

/**
 * ÊÄßËÉΩÁõëÊéßÂ∏∏Èáè
 */
export const PERFORMANCE_CONSTANTS = {
  MEMORY_THRESHOLD_MB: PERCENTAGE_HALF,
  MEMORY_BYTES_PER_MB: 1048576, // 1024 * 1024
  TIME_CALCULATION_FACTOR: MAGIC_36,
  CALCULATION_DIVISOR: COUNT_PAIR,
  CALCULATION_MULTIPLIER: MAGIC_9,
} as const;

/**
 * ÈªòËÆ§ÊÄßËÉΩË≠¶ÂëäÈòàÂÄº
 */
export const DEFAULT_ALERT_THRESHOLDS: Required<PerformanceAlertThresholds> = {
  loadTime: THREE_SECONDS_MS, // 3Áßí
  renderTime: MAGIC_16, // 16ÊØ´Áßí (60fps)
  memoryUsage:
    PERFORMANCE_CONSTANTS.MEMORY_THRESHOLD_MB *
    PERFORMANCE_CONSTANTS.MEMORY_BYTES_PER_MB,
};

/**
 * ËæÖÂä©ÂáΩÊï∞ÔºöÊ£ÄÊü•ÂÜÖÂ≠ò‰ΩøÁî®Âπ∂ÁîüÊàêË≠¶Âëä
 */
export const checkMemoryUsageAlert = (
  memoryUsage: number,
  threshold: number,
  addAlert: (_alert: Omit<PerformanceAlert, 'id' | 'timestamp'>) => void,
) => {
  if (memoryUsage > threshold) {
    addAlert({
      level: 'error',
      message: `High memory usage detected: ${Math.round(memoryUsage / PERFORMANCE_CONSTANTS.MEMORY_BYTES_PER_MB)}MB`,
      data: { memoryUsage },
    });
  }
};

/**
 * ËæÖÂä©ÂáΩÊï∞ÔºöÂàõÂª∫ÊÄßËÉΩË≠¶ÂëäÁ≥ªÁªü
 */
export const createPerformanceAlertSystem = (
  alerts: PerformanceAlert[],
  alertHistory: React.MutableRefObject<PerformanceAlert[]>,
  addAlert: (_alert: Omit<PerformanceAlert, 'id' | 'timestamp'>) => void,
  setAlerts: React.Dispatch<React.SetStateAction<PerformanceAlert[]>>,
): PerformanceAlertSystem => ({
  addAlert,
  getAlerts: () => alerts,
  getAlertHistory: () => alertHistory.current,
  clearAlerts: () => {
    setAlerts([]);
    alertHistory.current = [];
  },
});

/**
 * ËæÖÂä©ÂáΩÊï∞ÔºöÂàõÂª∫ÁõëÊéßÊéßÂà∂ÂáΩÊï∞
 */
export const createMonitoringControls = (
  setIsMonitoring: React.Dispatch<React.SetStateAction<boolean>>,
  _setError: React.Dispatch<React.SetStateAction<string | null>>,
  startTime: React.MutableRefObject<number | null>,
