): MonitoringControls => ({
  startMonitoring: () => {
    setIsMonitoring(true);
    startTime.current = performance.now();
    _setError(null);
  },
  stopMonitoring: () => {
    setIsMonitoring(false);
    startTime.current = null;
  },
  resetMetrics: () => {
    setMetrics(null);
    startTime.current = null;
    _setError(null);
  },
  refreshMetrics: () => {
    // Âà∑Êñ∞ÂΩìÂâçÊåáÊ†á
    _setError(null);
  },
});

/**
 * ËæÖÂä©ÂáΩÊï∞ÔºöÂàõÂª∫ËøîÂõûÂØπË±°
 */
