/**
 * È™åËØÅÂíåÊ∏ÖÁêÜÈÄâÈ°πÁöÑËæÖÂä©ÂáΩÊï∞
 */
export const validateAndSanitizeOptions = (
  options: UsePerformanceMonitorOptions,
): PerformanceMonitorState => {
  // Validate and sanitize options to handle null/undefined/invalid values
  const safeOptions = options || {};

  const {
    enableAlerts = false,
    alertThresholds = {},
    monitoringInterval = ANIMATION_DURATION_VERY_SLOW,
    enableMemoryMonitoring = true,
    enableNetworkMonitoring = false,
    enableRenderTimeMonitoring = true,
    enableLoadTimeMonitoring = true,
    enableAutoBaseline = false,
    maxAlerts = PERCENTAGE_FULL,
  } = safeOptions;

  // Validate alertThresholds with proper fallbacks
  const safeAlertThresholds = alertThresholds || {};
  const validatedAlertThresholds: Required<PerformanceAlertThresholds> = {
    loadTime:
      typeof safeAlertThresholds.loadTime === 'number' &&
      safeAlertThresholds.loadTime > ZERO
        ? safeAlertThresholds.loadTime
        : DEFAULT_ALERT_THRESHOLDS.loadTime,
    renderTime:
      typeof safeAlertThresholds.renderTime === 'number' &&
      safeAlertThresholds.renderTime > ZERO
        ? safeAlertThresholds.renderTime
        : DEFAULT_ALERT_THRESHOLDS.renderTime,
    memoryUsage:
      typeof safeAlertThresholds.memoryUsage === 'number' &&
      safeAlertThresholds.memoryUsage > ZERO
        ? safeAlertThresholds.memoryUsage
        : DEFAULT_ALERT_THRESHOLDS.memoryUsage,
  };

  // Validate monitoringInterval
  const validMonitoringInterval =
    typeof monitoringInterval === 'number' && monitoringInterval > ZERO
      ? Math.max(PERCENTAGE_FULL, monitoringInterval) // Minimum 100ms
      : ANIMATION_DURATION_VERY_SLOW;

  // Validate maxAlerts
  const validMaxAlerts =
    typeof maxAlerts === 'number' && maxAlerts > ZERO
      ? Math.min(ANIMATION_DURATION_VERY_SLOW, Math.max(ONE, maxAlerts)) // Between 1 and 1000
      : PERCENTAGE_FULL;

  return {
    enableAlerts: Boolean(enableAlerts),
    alertThresholds: validatedAlertThresholds,
    enableMemoryMonitoring: Boolean(enableMemoryMonitoring),
    enableNetworkMonitoring: Boolean(enableNetworkMonitoring),
    enableRenderTimeMonitoring: Boolean(enableRenderTimeMonitoring),
    enableLoadTimeMonitoring: Boolean(enableLoadTimeMonitoring),
    enableAutoBaseline: Boolean(enableAutoBaseline),
    maxAlerts: validMaxAlerts,
    monitoringInterval: validMonitoringInterval,
  };
};

/**
 * ÁîüÊàêÂîØ‰∏ÄIDÁöÑËæÖÂä©ÂáΩÊï∞
 */
export const generateAlertId = (): string => {
  return `alert_${Date.now()}_${Math.random().toString(MAGIC_36).substr(COUNT_PAIR, MAGIC_9)}`;
};

/**
 * Ê†ºÂºèÂåñÂÜÖÂ≠ò‰ΩøÁî®ÈáèÁöÑËæÖÂä©ÂáΩÊï∞
 */
export const formatMemoryUsage = (bytes: number): string => {
  const mb = bytes / PERFORMANCE_CONSTANTS.MEMORY_BYTES_PER_MB;
  return `${mb.toFixed(COUNT_PAIR)}MB`;
};

/**
 * Ê†ºÂºèÂåñÊó∂Èó¥ÁöÑËæÖÂä©ÂáΩÊï∞
 */
export const formatTime = (milliseconds: number): string => {
  if (milliseconds < ANIMATION_DURATION_VERY_SLOW) {
    return `${milliseconds.toFixed(COUNT_PAIR)}ms`;
  }
  return `${(milliseconds / ANIMATION_DURATION_VERY_SLOW).toFixed(COUNT_PAIR)}s`;
};

/**
 * Ê£ÄÊü•ÊÄßËÉΩÊåáÊ†áÊòØÂê¶Ë∂ÖËøáÈòàÂÄº
 */
export const checkPerformanceThresholds = (
  metrics: PerformanceMetrics,
  thresholds: Required<PerformanceAlertThresholds>,
  addAlert: (_alert: Omit<PerformanceAlert, 'id' | 'timestamp'>) => void,
): void => {
  // Ê£ÄÊü•Âä†ËΩΩÊó∂Èó¥
  if (metrics.loadTime > thresholds.loadTime) {
    addAlert({
      level: 'warning',
      message: `Slow load time detected: ${formatTime(metrics.loadTime)}`,
      data: { loadTime: metrics.loadTime, threshold: thresholds.loadTime },
    });
  }

  // Ê£ÄÊü•Ê∏≤ÊüìÊó∂Èó¥
  if (metrics.renderTime > thresholds.renderTime) {
    addAlert({
      level: 'warning',
      message: `Slow render time detected: ${formatTime(metrics.renderTime)}`,
      data: {
        renderTime: metrics.renderTime,
        threshold: thresholds.renderTime,
      },
    });
  }

  // Ê£ÄÊü•ÂÜÖÂ≠ò‰ΩøÁî®
  if (metrics.memoryUsage && metrics.memoryUsage > thresholds.memoryUsage) {
    addAlert({
      level: 'error',
      message: `High memory usage detected: ${formatMemoryUsage(metrics.memoryUsage)}`,
      data: {
        memoryUsage: metrics.memoryUsage,
        threshold: thresholds.memoryUsage,
      },
    });
  }
};

import React from 'react';
import { logger } from '@/lib/logger';
import { themeAnalytics } from '@/lib/theme-analytics';
import type {
  ThemeTransitionConfig,
  ViewTransition,
  ViewTransitionAPI,
} from '@/hooks/theme-transition-types';
import {
  calculateEndRadius,
  DEFAULT_CONFIG,
  getClickCoordinates,
  recordThemeTransition,
  supportsViewTransitions,
} from '@/hooks/theme-transition-utils';

/**
 * ÊâßË°å‰∏ªÈ¢òÂàáÊç¢ÁöÑÊ†∏ÂøÉÈÄªËæë
 */
export function executeThemeTransition(
  originalSetTheme: (_theme: string) => void,
  newTheme: string,
  _currentTheme: string = 'unknown',
  _config: ThemeTransitionConfig = DEFAULT_CONFIG,
  _animationSetup?: (_transition: ViewTransition) => void,
): void {
  const startTime = performance.now();

  try {
    // ËÆ∞ÂΩï‰∏ªÈ¢òÂÅèÂ•Ω
    themeAnalytics.recordThemePreference(newTheme);

    // Ê†áËÆ∞ÊÄßËÉΩÂºÄÂßãÁÇπ
    if (typeof window !== 'undefined' && window.performance) {
      performance.mark(`theme-transition-${_currentTheme}-start`);
    }

    // Ê£ÄÊü•ÊòØÂê¶ÊîØÊåÅ View Transitions API
    if (!supportsViewTransitions()) {
      logger.debug('View Transitions API not supported, using fallback');
      originalSetTheme(newTheme);

      recordThemeTransition({
        fromTheme: _currentTheme,
        toTheme: newTheme,
        startTime,
        endTime: performance.now(),
        hasViewTransition: false,
      });
      return;
    }

    // ‰ΩøÁî® View Transitions API
    try {
      const documentWithTransition = document as Document & ViewTransitionAPI;
      const transition = documentWithTransition.startViewTransition(() => {
        originalSetTheme(newTheme);
      });

      // Â¶ÇÊûúÊèê‰æõ‰∫ÜÂä®ÁîªËÆæÁΩÆÂáΩÊï∞ÔºåÂàôË∞ÉÁî®ÂÆÉ
      if (_animationSetup) {
        _animationSetup(transition);
      }

      // ÁõëÂê¨ËΩ¨Êç¢ÂÆåÊàê
      transition.finished
        .then(() => {
          recordThemeTransition({
            fromTheme: _currentTheme,
            toTheme: newTheme,
            startTime,
            endTime: performance.now(),
            hasViewTransition: true,
          });
        })
        .catch((error: Error) => {
          logger.error('View transition failed', { error, newTheme });
          recordThemeTransition({
            fromTheme: _currentTheme,
            toTheme: newTheme,
            startTime,
            endTime: performance.now(),
            hasViewTransition: true,
            error,
          });
        });
    } catch (transitionError) {
      logger.error('Failed to start view transition', {
        error: transitionError,
        newTheme,
      });

      // ÈôçÁ∫ßÂà∞ÊôÆÈÄöÂàáÊç¢
      originalSetTheme(newTheme);
      recordThemeTransition({
        fromTheme: _currentTheme,
        toTheme: newTheme,
        startTime,
        endTime: performance.now(),
        hasViewTransition: false,
        error: transitionError as Error,
      });
    }
  } catch (error) {
    logger.error('Theme transition failed', { error, newTheme });

    // Á°Æ‰øù‰∏ªÈ¢ò‰ªçÁÑ∂Ë¢´ËÆæÁΩÆÔºåÂç≥‰ΩøÂá∫Áé∞ÈîôËØØ
    try {
      originalSetTheme(newTheme);
    } catch (setThemeError) {
      logger.error('Failed to set theme as fallback', {
        error: setThemeError,
        newTheme,
      });
    }

    recordThemeTransition({
      fromTheme: _currentTheme,
      toTheme: newTheme,
      startTime,
      endTime: performance.now(),
      hasViewTransition: false,
      error: error as Error,
    });
  }
}

/**
 * ÊâßË°åÂü∫Á°Ä‰∏ªÈ¢òÂàáÊç¢
 */
export function executeBasicThemeTransition(
  originalSetTheme: (_theme: string) => void,
  newTheme: string,
  _currentTheme?: string,
): void {
  executeThemeTransition(originalSetTheme, newTheme, _currentTheme);
}

/**
 * ÊâßË°åÂúÜÂΩ¢Âä®Áîª‰∏ªÈ¢òÂàáÊç¢
 */
export function executeCircularThemeTransition(
  originalSetTheme: (_theme: string) => void,
  newTheme: string,
  _currentTheme?: string,
  _clickEvent?: React.MouseEvent<HTMLElement>,
): void {
  const { x, y } = getClickCoordinates(_clickEvent);
  const endRadius = calculateEndRadius(x, y);

  const animationSetup = (transition: ViewTransition) => {
    // ËÆæÁΩÆÂúÜÂΩ¢Â±ïÂºÄÂä®Áîª
    transition.ready
      .then(() => {
        const clipPath = [
          `circle(0px at ${x}px ${y}px)`,
          `circle(${endRadius}px at ${x}px ${y}px)`,
        ];

        document.documentElement.animate(
          {
            clipPath,
          },
          {
            duration: DEFAULT_CONFIG.animationDuration,
            easing: DEFAULT_CONFIG.easing,
            pseudoElement: '::view-transition-new(root)',
          },
        );
      })
      .catch((error: Error) => {
        logger.warn('Failed to setup circular animation', { error });
      });
  };

  executeThemeTransition(
    originalSetTheme,
    newTheme,
    _currentTheme,
    DEFAULT_CONFIG,
    animationSetup,
  );
}

'use client';

import React, { useCallback, useMemo, useRef } from 'react';
import { useTheme } from 'next-themes';
import {
  executeBasicThemeTransition,
  executeCircularThemeTransition,
} from '@/hooks/theme-transition-core';
import type { EnhancedThemeHook } from '@/hooks/theme-transition-types';
import { createDebounce, DEFAULT_CONFIG } from '@/hooks/theme-transition-utils';

/**
 * Â¢ûÂº∫ÁöÑ‰∏ªÈ¢òÂàáÊç¢ Hook
 *
 * Êèê‰æõ‰ª•‰∏ãÂäüËÉΩÔºö
 * - Âü∫Á°Ä‰∏ªÈ¢òÂàáÊç¢ÔºàÂ∏¶Èò≤ÊäñÔºâ
 * - ÂúÜÂΩ¢Âä®Áîª‰∏ªÈ¢òÂàáÊç¢ÔºàÂü∫‰∫éÁÇπÂáª‰ΩçÁΩÆÔºâ
 * - View Transitions API ÊîØÊåÅ
 * - Ëá™Âä®ÈôçÁ∫ßÂà∞ÊôÆÈÄöÂàáÊç¢
 * - Èò≤ÊäñÊú∫Âà∂Èò≤Ê≠¢Âø´ÈÄüËøûÁª≠ÂàáÊç¢
 * - ÁºìÂ≠ò View Transitions API ÊîØÊåÅÊ£ÄÊµãÁªìÊûú
 * - Áªü‰∏ÄÁöÑÈîôËØØÂ§ÑÁêÜÂíåÊÄßËÉΩÁõëÊéß
 * - Á±ªÂûãÂÆâÂÖ®ÁöÑ API ËÆæËÆ°
 */
export function useEnhancedTheme(): EnhancedThemeHook {
  const { theme, setTheme: originalSetTheme, ...rest } = useTheme();

  // ‰ΩøÁî® ref Êù•Â≠òÂÇ®Èò≤ÊäñÂáΩÊï∞ÔºåÈÅøÂÖçÈáçÂ§çÂàõÂª∫
  const debouncedSetThemeRef = useRef<((_theme: string) => void) | null>(null);
  const debouncedSetCircularThemeRef = useRef<
    | ((_theme: string, _clickEvent?: React.MouseEvent<HTMLElement>) => void)
    | null
  >(null);

  // ÂàõÂª∫Èò≤ÊäñÁöÑÂü∫Á°Ä‰∏ªÈ¢òÂàáÊç¢ÂáΩÊï∞
  const setTheme = useCallback(
    (newTheme: string) => {
      if (!debouncedSetThemeRef.current) {
        debouncedSetThemeRef.current = createDebounce(
          ((themeToSet: string) => {
            executeBasicThemeTransition(originalSetTheme, themeToSet, theme);
          }) as any,
          DEFAULT_CONFIG.debounceDelay,
        );
      }
      debouncedSetThemeRef.current?.(newTheme);
    },
    [originalSetTheme, theme],
  );

  // ÂàõÂª∫Èò≤ÊäñÁöÑÂúÜÂΩ¢Âä®Áîª‰∏ªÈ¢òÂàáÊç¢ÂáΩÊï∞
  const setCircularTheme = useCallback(
    (newTheme: string, clickEvent?: React.MouseEvent<HTMLElement>) => {
      if (!debouncedSetCircularThemeRef.current) {
        debouncedSetCircularThemeRef.current = createDebounce(
          ((themeToSet: string, event?: React.MouseEvent<HTMLElement>) => {
            executeCircularThemeTransition(
              originalSetTheme,
              themeToSet,
              theme,
              event,
            );
          }) as any,
          DEFAULT_CONFIG.debounceDelay,
        );
      }
      debouncedSetCircularThemeRef.current?.(newTheme, clickEvent);
    },
    [originalSetTheme, theme],
  );

  // ËøîÂõûÂ¢ûÂº∫ÁöÑ‰∏ªÈ¢ò Hook
  return useMemo(
    () => ({
      theme,
      setTheme,
      setCircularTheme,
      themes: rest.themes,
      forcedTheme: rest.forcedTheme,
      resolvedTheme: rest.resolvedTheme,
      systemTheme: rest.systemTheme,
    }),
    [theme, setTheme, setCircularTheme, rest],
  );
}

