/* eslint-disable max-lines-per-function, security/detect-object-injection, no-shadow */
import { ZERO } from "@/constants/magic-numbers";
import { I18nPerformanceMonitor } from '@/lib/i18n-performance';
import { useLocale, useTranslations } from 'next-intl';
import { useCallback, useMemo } from 'react';

interface UseEnhancedTranslationsOptions {
  namespace?: string;
  fallback?: boolean;
  analytics?: boolean;
  preload?: string[];
}

export function useEnhancedTranslations(
  options: UseEnhancedTranslationsOptions = {},
) {
  const {
    namespace,
    fallback = true,
    analytics = true,
    preload = [],
  } = options;
  const t = useTranslations(namespace);
  const locale = useLocale();

  // È¢ÑÂä†ËΩΩÊåáÂÆöÁöÑÁøªËØëÈîÆ - ‰ΩøÁî®useMemoÈÅøÂÖçeffect‰∏≠ÁöÑÊï∞ÊçÆ‰º†ÈÄí
  useMemo(() => {
    if (preload.length > ZERO) {
      preload.forEach((key) => {
        try {
          t(key);
        } catch {
          // ÂøΩÁï•È¢ÑÂä†ËΩΩÈîôËØØ
        }
      });
    }
    return null;
  }, [preload, t]);

  // Â¢ûÂº∫ÁöÑÁøªËØëÂáΩÊï∞
  const enhancedT = useCallback(
    (
      key: string,
      values?: Record<
        string,
        string | number | boolean | Record<string, unknown> | unknown[]
      >,
    ) => {
      const startTime = performance.now();

      try {
        const result = t(key, values as any);

        // ËÆ∞ÂΩïÂä†ËΩΩÊó∂Èó¥
        if (analytics) {
          const loadTime = performance.now() - startTime;
          I18nPerformanceMonitor.recordLoadTime(loadTime);
        }

        // Ê£ÄÊü•ÊòØÂê¶‰ΩøÁî®‰∫ÜÂõûÈÄÄ
        if (result === key && fallback) {
          return `[${locale.toUpperCase()}] ${key}`;
        }

        return result;
      } catch {
        // ËÆ∞ÂΩïÁøªËØëÈîôËØØ
        if (analytics) {
          I18nPerformanceMonitor.recordError();
        }

        return fallback ? `[ERROR] ${key}` : key;
      }
    },
    [t, locale, fallback, analytics],
  );

  // ÊâπÈáèÁøªËØëÂáΩÊï∞
  const batchT = useCallback(
    (
      keys: string[],
      values?: Record<
        string,
        string | number | boolean | Record<string, unknown> | unknown[]
      >,
    ) => {
      return keys.reduce(
        (acc, key) => {
          acc[key] = enhancedT(key, values);
          return acc;
        },
        {} as Record<string, string>,
      );
    },
    [enhancedT],
  );

  // Êù°‰ª∂ÁøªËØëÂáΩÊï∞
  const conditionalT = useCallback(
    (
      condition: boolean,
      trueKey: string,
      falseKey: string,
      values?: Record<
        string,
        string | number | boolean | Record<string, unknown> | unknown[]
      >,
    ) => {
      return enhancedT(condition ? trueKey : falseKey, values);
    },
    [enhancedT],
  );

  // ÂØåÊñáÊú¨ÁøªËØëÂáΩÊï∞
  const richT = useCallback(
    (
      key: string,
      components: Record<string, (chunks: React.ReactNode) => React.ReactNode>,
    ) => {
      try {
        return t.rich(key, components);
      } catch {
        if (analytics) {
          I18nPerformanceMonitor.recordError();
        }
        return fallback ? `[RICH ERROR] ${key}` : key;
      }
    },
    [t, fallback, analytics],
  );

  // ÁøªËØëÂ≠òÂú®Ê£ÄÊü•
  const hasTranslation = useCallback(
    (key: string) => {
      try {
        return t.has(key);
      } catch {
        return false;
      }
    },
    [t],
  );

  // Ê†ºÂºèÂåñÊï∞Â≠ó
  const formatNumber = useCallback(
    (value: number, options?: Intl.NumberFormatOptions) => {
      try {
        return new Intl.NumberFormat(locale, options).format(value);
      } catch {
        return value.toString();
      }
    },
    [locale],
  );

  // Ê†ºÂºèÂåñÊó•Êúü
  const formatDate = useCallback(
    (date: Date, options?: Intl.DateTimeFormatOptions) => {
      try {
        return new Intl.DateTimeFormat(locale, options).format(date);
      } catch {
        return date.toISOString();
      }
    },
    [locale],
  );

  // Ê†ºÂºèÂåñÁõ∏ÂØπÊó∂Èó¥
  const formatRelativeTime = useCallback(
    (value: number, unit: Intl.RelativeTimeFormatUnit) => {
      try {
        return new Intl.RelativeTimeFormat(locale, { numeric: 'auto' }).format(
          value,
          unit,
        );
      } catch {
        return `${value} ${unit}`;
      }
    },
    [locale],
  );

  // Ëé∑ÂèñÊÄßËÉΩÊåáÊ†á
  const getPerformanceMetrics = useCallback(() => {
    return I18nPerformanceMonitor.getMetrics();
  }, []);

  return useMemo(
    () => ({
      t: enhancedT,
      batchT,
      conditionalT,
      richT,
      hasTranslation,
      formatNumber,
      formatDate,
      formatRelativeTime,
      getPerformanceMetrics,
      locale,
      namespace,
    }),
    [
      enhancedT,
      batchT,
      conditionalT,
      richT,
      hasTranslation,
      formatNumber,
      formatDate,
      formatRelativeTime,
      getPerformanceMetrics,
      locale,
      namespace,
    ],
  );
}

// ÊÄßËÉΩÁõëÊéßHook
export function useI18nPerformance() {
  const getMetrics = useCallback(() => {
    return I18nPerformanceMonitor.getMetrics();
  }, []);

  const resetMetrics = useCallback(() => {
    I18nPerformanceMonitor.reset();
  }, []);

  return {
    getMetrics,
    resetMetrics,
  };
}

'use client';

import { MAGIC_0_1 } from "@/constants/decimal";
import { ZERO } from "@/constants/magic-numbers";
import { AccessibilityUtils } from '@/lib/accessibility';
import { logger } from '@/lib/logger';
import { useCallback, useEffect, useRef, useState } from 'react';

/**
 * Intersection Observer Hook ÈÖçÁΩÆÈÄâÈ°π
 */
export interface IntersectionObserverOptions {
  /** Ëß¶ÂèëÈòàÂÄºÔºå0-1‰πãÈó¥ÁöÑÊï∞ÂÄº */
  threshold?: number;
  /** Ê†πÂÖÉÁ¥†ÁöÑÂ§ñËæπË∑ù */
  rootMargin?: string;
  /** ÊòØÂê¶Âè™Ëß¶Âèë‰∏ÄÊ¨° */
  triggerOnce?: boolean;
  /** Ê†πÂÖÉÁ¥†ÔºåÈªòËÆ§‰∏∫ËßÜÂè£ */
  root?: Element | null;
}

/**
 * Hook ËøîÂõûÂÄºÁ±ªÂûã
 */
export interface IntersectionObserverHookReturn<
  T extends HTMLElement = HTMLElement,
> {
  /** Ë¶ÅËßÇÂØüÁöÑÂÖÉÁ¥†ÂºïÁî® - ÂõûË∞ÉÂáΩÊï∞ÂΩ¢Âºè */
  ref: (_node: T | null) => void;
  /** ÂΩìÂâçÊòØÂê¶ÂèØËßÅ */
  isVisible: boolean;
  /** ÊòØÂê¶ÊõæÁªèÂèØËßÅËøá */
  hasBeenVisible: boolean;
}

/**
 * ÈªòËÆ§ÈÖçÁΩÆ
 */
const DEFAULT_OPTIONS: Required<Omit<IntersectionObserverOptions, 'root'>> = {
  threshold: MAGIC_0_1,
  rootMargin: '0px',
  triggerOnce: true,
};

/**
 * ÊªöÂä®Ëß¶ÂèëÂä®ÁîªHook
 *
 * Âü∫‰∫éÂéüÁîüIntersection Observer APIÔºå‰∏∫ÁªÑ‰ª∂Êèê‰æõÊªöÂä®Ëß¶ÂèëÁöÑÂèØËßÅÊÄßÊ£ÄÊµã„ÄÇ
 * ÈõÜÊàêÂèØËÆøÈóÆÊÄßÊîØÊåÅÔºåÈÅµÂæ™È°πÁõÆÁé∞ÊúâHookÊ®°Âºè„ÄÇ
 *
 * @param options - ÈÖçÁΩÆÈÄâÈ°π
 * @returns HookËøîÂõûÂÄºÔºåÂåÖÂê´ref„ÄÅÂèØËßÅÊÄßÁä∂ÊÄÅÁ≠â
 *
 * @example
 * ```tsx
 * function AnimatedComponent() {
 *   const { ref, isVisible } = useIntersectionObserver({
 *     threshold: 0.2,
 *     triggerOnce: true
 *   });
 *
 *   return (
 *     <div
 *       ref={ref}
 *       className={`transition-all duration-700 ${
 *         isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'
 *       }`}
 *     >
 *       Content
 *     </div>
 *   );
 * }
 * ```
 */
/**
 * Â§ÑÁêÜÂèØËßÅÊÄßÁä∂ÊÄÅÁöÑËæÖÂä©ÂáΩÊï∞
 */
function setVisibilityState(
  setIsVisible: (_visible: boolean) => void,
  setHasBeenVisible: (_visible: boolean) => void,
  hasBeenVisibleRef: { current: boolean },
  visible: boolean,
) {
  setIsVisible(visible);
  setHasBeenVisible(visible);
  hasBeenVisibleRef.current = visible;
}

/**
 * ÂàõÂª∫ IntersectionObserver ÁöÑËæÖÂä©ÂáΩÊï∞
 */
function createObserver(
  element: HTMLElement,
  handleIntersection: (
    _entries: IntersectionObserverEntry[],
    _observer: IntersectionObserver,
  ) => void,
  config: IntersectionObserverOptions & {
    threshold: number;
    rootMargin: string;
    triggerOnce: boolean;
  },
  fallbackToVisible: () => void,
): (() => void) | null {
  try {
    const observer = new IntersectionObserver(
      (entries) => {
        handleIntersection(entries, observer);
      },
      {
        threshold: config.threshold,
        rootMargin: config.rootMargin,
        root: config.root || null,
      },
    );

    observer.observe(element);

    return () => {
      observer.unobserve(element);
      observer.disconnect();
    };
  } catch (error) {
    logger.warn('IntersectionObserver error', {
      error: error instanceof Error ? error.message : String(error),
      threshold: config.threshold,
      rootMargin: config.rootMargin,
    });
    fallbackToVisible();
    return null;
  }
}

export function useIntersectionObserver<T extends HTMLElement = HTMLElement>(
  options: IntersectionObserverOptions = {},
): IntersectionObserverHookReturn<T> {
  const ref = useRef<T | null>(null);
  const [isVisible, setIsVisible] = useState(false);
  const [hasBeenVisible, setHasBeenVisible] = useState(false);
  const [element, setElement] = useState<T | null>(null);
  const hasBeenVisibleRef = useRef(false);

  // ÊèêÂèñÊâÄÊúâÈÖçÁΩÆÂÄºÔºåÈÅøÂÖç‰æùËµñÊï¥‰∏™configÂØπË±°
  const threshold = options.threshold ?? DEFAULT_OPTIONS.threshold;
  const rootMargin = options.rootMargin ?? DEFAULT_OPTIONS.rootMargin;
  const triggerOnce = options.triggerOnce ?? DEFAULT_OPTIONS.triggerOnce;
  const root = options.root ?? null;

  const prefersReducedMotion = AccessibilityUtils.prefersReducedMotion();

  const callbackRef = useCallback((node: T | null) => {
    ref.current = node;
    setElement(node);
  }, []);

  const handleIntersection = useCallback(
    (entries: IntersectionObserverEntry[], observer: IntersectionObserver) => {
      const entry = entries.length > ZERO ? entries[ZERO] : null;
      if (!entry) return;

      const isCurrentlyVisible = entry.isIntersecting;
      setIsVisible(isCurrentlyVisible);

      if (isCurrentlyVisible && !hasBeenVisibleRef.current) {
        hasBeenVisibleRef.current = true;
        setHasBeenVisible(true);
      }

      // Âè™ÊúâÂú® triggerOnce ‰∏∫ true ‰∏îÂÖÉÁ¥†ÂΩìÂâçÂèØËßÅÊó∂ÊâçÂÅúÊ≠¢ËßÇÂØü
      if (triggerOnce && isCurrentlyVisible && entry.target) {
        observer.unobserve(entry.target);
      }
    },
    [triggerOnce],
  );

  useEffect(() => {
    // eslint-disable-next-line react-you-might-not-need-an-effect/no-event-handler
    if (!element) return undefined;

    const fallbackToVisible = () =>
      setVisibilityState(
        setIsVisible,
        setHasBeenVisible,
        hasBeenVisibleRef,
        true,
      );

    if (
      prefersReducedMotion ||
      typeof window === 'undefined' ||
      !window.IntersectionObserver
    ) {
      fallbackToVisible();
      return undefined;
    }

    // ÂàõÂª∫ÈÖçÁΩÆÂØπË±°Ôºå‰ΩøÁî®ÊèêÂèñÁöÑÁ®≥ÂÆöÂÄº
    const config = {
      threshold,
      rootMargin,
      triggerOnce,
      root,
    };

    return (
      createObserver(element, handleIntersection, config, fallbackToVisible) ||
      undefined
    );
  }, [
    element,
    threshold,
    rootMargin,
    triggerOnce,
    root,
    handleIntersection,
    prefersReducedMotion,
  ]);

  return {
    ref: callbackRef,
    isVisible,
    hasBeenVisible,
  };
}

/**
 * Â∏¶Âª∂ËøüÁöÑÊªöÂä®Ëß¶ÂèëHook
 *
 * Âú®Âü∫Á°ÄHookÁöÑÂü∫Á°Ä‰∏äÊ∑ªÂä†Âª∂ËøüÂäüËÉΩÔºåÁî®‰∫éÂÆûÁé∞staggerÂä®ÁîªÊïàÊûú
 *
 * @param options - ÈÖçÁΩÆÈÄâÈ°π
 * @param delay - Âª∂ËøüÊó∂Èó¥ÔºàÊØ´ÁßíÔºâ
 * @returns HookËøîÂõûÂÄº
 */
export function useIntersectionObserverWithDelay(
  options: IntersectionObserverOptions = {},
  delay = ZERO,
): IntersectionObserverHookReturn {
  const {
    ref,
    isVisible: baseIsVisible,
    hasBeenVisible,
  } = useIntersectionObserver(options);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    if (baseIsVisible && delay > ZERO) {
      const timer = setTimeout(() => {
        setIsVisible(true);
      }, delay);

      return () => clearTimeout(timer);
    } else if (baseIsVisible) {
      setIsVisible(true);
    } else {
      setIsVisible(false);
    }

    return undefined;
  }, [baseIsVisible, delay]);

  return {
    ref,
    isVisible,
    hasBeenVisible,
  };
}

'use client';

import { ONE, ZERO } from "@/constants/magic-numbers";
import React, { useCallback, useEffect, useMemo, useRef } from 'react';

export interface KeyboardNavigationOptions {
  enabled?: boolean;
  loop?: boolean;
  orientation?: 'horizontal' | 'vertical' | 'both';
  selector?: string;
  onNavigate?: (_element: HTMLElement, _direction: string) => void;
}

export interface UseKeyboardNavigationReturn {
  containerRef: React.RefObject<HTMLElement | null>;
  focusFirst: () => void;
  focusLast: () => void;
  focusNext: () => void;
  focusPrevious: () => void;
  getCurrentFocusIndex: () => number;
  setFocusIndex: (_index: number) => void;
}

export type KeyboardNavigationConfig = Required<KeyboardNavigationOptions>;

const defaultOptions: Required<KeyboardNavigationOptions> = {
  enabled: true,
  loop: true,
  orientation: 'both',
  selector:
    '[tabindex]:not([tabindex="-1"]), button:not([disabled]), input:not([disabled]), select:not([disabled]), textarea:not([disabled]), a[href], [contenteditable="true"]',
  // eslint-disable-next-line no-empty-function
  onNavigate: () => {},
};

// ÁÆ≠Â§¥ÈîÆÂ§ÑÁêÜÈÖçÁΩÆÊé•Âè£
interface ArrowKeyConfig {
  key: string;
  isHorizontal: boolean;
  isVertical: boolean;
  event: KeyboardEvent;
  focusNext: () => void;
  focusPrevious: () => void;
}

// Â§ÑÁêÜÁÆ≠Â§¥ÈîÆÂØºËà™ÁöÑËæÖÂä©ÂáΩÊï∞
function handleArrowKey(config: ArrowKeyConfig): boolean {
  const { key, isHorizontal, isVertical, event, focusNext, focusPrevious } =
    config;
  switch (key) {
    case 'ArrowRight':
      if (isHorizontal) {
        event.preventDefault();
        focusNext();
        return true;
      }
      break;
    case 'ArrowLeft':
      if (isHorizontal) {
        event.preventDefault();
        focusPrevious();
        return true;
      }
      break;
    case 'ArrowDown':
      if (isVertical) {
        event.preventDefault();
        focusNext();
        return true;
      }
      break;
    case 'ArrowUp':
      if (isVertical) {
        event.preventDefault();
        focusPrevious();
        return true;
      }
      break;
    default:
      // ‰∏çÂ§ÑÁêÜÂÖ∂‰ªñÊåâÈîÆ
      break;
  }
  return false;
}

// Â§ÑÁêÜTabÈîÆÁöÑËæÖÂä©ÂáΩÊï∞
function handleTabKey(
  event: KeyboardEvent,
  config: KeyboardNavigationConfig,
  getFocusableElements: () => HTMLElement[],
  getCurrentFocusIndex: () => number,
): boolean {
  const direction = event.shiftKey ? 'previous' : 'next';
  const elements = getFocusableElements();
  const currentIndex = getCurrentFocusIndex();
  const targetElement = event.shiftKey
    ? elements[currentIndex - ONE]
    : elements[currentIndex + ONE];

  if (targetElement) {
    config.onNavigate(targetElement, direction);
    return true;
  }
  return false;
}

/**
 * ÂàõÂª∫ÁÑ¶ÁÇπÁÆ°ÁêÜÂáΩÊï∞
 */
function useFocusManagement(
  containerRef: React.RefObject<HTMLElement | null>,
  config: KeyboardNavigationConfig,
) {
  const getFocusableElements = useCallback((): HTMLElement[] => {
    if (!containerRef.current) return [];

    const elements = containerRef.current.querySelectorAll(config.selector);
    return Array.from(elements) as HTMLElement[];
  }, [containerRef, config.selector]);

  const getCurrentFocusIndex = useCallback((): number => {
    const elements = getFocusableElements();
    const activeElement = document.activeElement as HTMLElement;
    return elements.indexOf(activeElement);
  }, [getFocusableElements]);

  const setFocusIndex = useCallback(
    (index: number): void => {
      const elements = getFocusableElements();
      // ÂÆâÂÖ®ÁöÑÊï∞ÁªÑËÆøÈóÆÔºåÈÅøÂÖçÂØπË±°Ê≥®ÂÖ•
      if (index >= ZERO && index < elements.length && Array.isArray(elements)) {
        const element = elements.at(index);
        if (element) {
          element.focus();
          config.onNavigate(element, 'direct');
        }
      }
    },
    [getFocusableElements, config],
  );

  return {
    getFocusableElements,
    getCurrentFocusIndex,
    setFocusIndex,
  };
}

/**
 * ÂàõÂª∫ÂØºËà™ÂáΩÊï∞
 */
function useNavigationActions(
  getFocusableElements: () => HTMLElement[],
  getCurrentFocusIndex: () => number,
  setFocusIndex: (_index: number) => void,
  config: KeyboardNavigationConfig,
) {
  const focusFirst = useCallback((): void => {
    setFocusIndex(ZERO);
  }, [setFocusIndex]);

  const focusLast = useCallback((): void => {
    const elements = getFocusableElements();
    setFocusIndex(elements.length - ONE);
  }, [getFocusableElements, setFocusIndex]);

  const focusNext = useCallback((): void => {
    const elements = getFocusableElements();
    const currentIndex = getCurrentFocusIndex();
    let nextIndex = currentIndex + ONE;

    if (nextIndex >= elements.length) {
      nextIndex = config.loop ? ZERO : elements.length - ONE;
    }

    setFocusIndex(nextIndex);
  }, [getFocusableElements, getCurrentFocusIndex, setFocusIndex, config.loop]);

  const focusPrevious = useCallback((): void => {
    const elements = getFocusableElements();
    const currentIndex = getCurrentFocusIndex();
    let prevIndex = currentIndex - ONE;

    if (prevIndex < ZERO) {
      prevIndex = config.loop ? elements.length - ONE : ZERO;
    }

    setFocusIndex(prevIndex);
  }, [getFocusableElements, getCurrentFocusIndex, setFocusIndex, config.loop]);

  return {
    focusFirst,
    focusLast,
    focusNext,
    focusPrevious,
  };
}

/**
 * ÂàõÂª∫ÈîÆÁõò‰∫ã‰ª∂Â§ÑÁêÜÂô®
 */
function useKeyboardHandler(
  config: KeyboardNavigationConfig,
  navigationActions: {
    focusNext: () => void;
    focusPrevious: () => void;
    focusFirst: () => void;
    focusLast: () => void;
  },
  getFocusableElements: () => HTMLElement[],
  getCurrentFocusIndex: () => number,
) {
  const handleKeyDown = useCallback(
    (event: KeyboardEvent): void => {
      if (!config.enabled) return;

      const { key } = event;
      const isHorizontal =
        config.orientation === 'horizontal' || config.orientation === 'both';
      const isVertical =
        config.orientation === 'vertical' || config.orientation === 'both';

      // Â§ÑÁêÜÁÆ≠Â§¥ÈîÆ
      const arrowHandled = handleArrowKey({
        key,
        isHorizontal,
        isVertical,
        event,
        focusNext: navigationActions.focusNext,
        focusPrevious: navigationActions.focusPrevious,
      });

      if (arrowHandled) return;

      // Â§ÑÁêÜTabÈîÆ
      const tabHandled = handleTabKey(
        event,
        config,
        getFocusableElements,
        getCurrentFocusIndex,
      );
      if (tabHandled) return;

      // Â§ÑÁêÜHome/EndÈîÆ
      if (key === 'Home') {
        event.preventDefault();
        navigationActions.focusFirst();
      } else if (key === 'End') {
        event.preventDefault();
        navigationActions.focusLast();
      }
    },
    [config, navigationActions, getFocusableElements, getCurrentFocusIndex],
  );

  return { handleKeyDown };
}

export function useKeyboardNavigation(
  options: KeyboardNavigationOptions = {},
): UseKeyboardNavigationReturn {
  const config = useMemo(() => ({ ...defaultOptions, ...options }), [options]);
  const containerRef = useRef<HTMLElement | null>(null);

  const { getFocusableElements, getCurrentFocusIndex, setFocusIndex } =
    useFocusManagement(containerRef, config);

  const navigationActions = useNavigationActions(
    getFocusableElements,
    getCurrentFocusIndex,
    setFocusIndex,
    config,
  );

  const { handleKeyDown } = useKeyboardHandler(
    config,
    navigationActions,
    getFocusableElements,
    getCurrentFocusIndex,
  );

  useEffect(() => {
    const container = containerRef.current;
    if (!container || !config.enabled) return undefined;

    container.addEventListener('keydown', handleKeyDown);
    return () => container.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown, config.enabled]);

  return {
    containerRef,
    ...navigationActions,
    getCurrentFocusIndex,
    setFocusIndex,
  };
}

