    // Test that the main export file works
    const localeDetection = await import('@/lib/locale-detection');

    // Verify that exports are available
    expect(localeDetection).toBeDefined();
    expect(typeof localeDetection).toBe('object');

    // Check for key exports that should be available
    expect(localeDetection).toHaveProperty('SmartLocaleDetector');
    expect(localeDetection).toHaveProperty('SUPPORTED_LOCALES');
    expect(localeDetection).toHaveProperty('DEFAULT_LOCALE');

    // Verify SmartLocaleDetector is a constructor
    expect(typeof localeDetection.SmartLocaleDetector).toBe('function');
  });

  it('should re-export locale constants', async () => {
    const localeDetection = await import('@/lib/locale-detection');

    // Test constants are exported
    expect(localeDetection.SUPPORTED_LOCALES).toBeDefined();
    expect(localeDetection.DEFAULT_LOCALE).toBeDefined();
    expect(Array.isArray(localeDetection.SUPPORTED_LOCALES)).toBe(true);
    expect(typeof localeDetection.DEFAULT_LOCALE).toBe('string');
  });

  it('should re-export locale detection types', async () => {
    // This tests that the types module is properly exported
    // We can't directly test TypeScript types at runtime, but we can test
    // that the module doesn't throw when imported
    expect(async () => {
      await import('@/lib/locale-detection-types');
    }).not.toThrow();
  });

  it('should re-export locale detection hooks', async () => {
    // Test that hooks are exported
    const hooks = await import('@/lib/locale-detection-hooks');
    expect(hooks).toBeDefined();
    expect(typeof hooks).toBe('object');
  });

  it('should re-export SmartLocaleDetector class', async () => {
    const { SmartLocaleDetector } = await import('@/lib/locale-detection');

    // Test that we can create an instance
    expect(() => new SmartLocaleDetector()).not.toThrow();

    // Test that instance has expected methods
    const detector = new SmartLocaleDetector();
    expect(typeof detector.detectFromBrowser).toBe('function');
    expect(typeof detector.detectSmartLocale).toBe('function');
  });

  it('should handle module loading errors gracefully', async () => {
    // Test that the main module can be imported without errors
    let importError = null;

    try {
      await import('@/lib/locale-detection');
    } catch (error) {
      importError = error;
    }

    expect(importError).toBeNull();
  });

  it('should maintain consistent exports structure', async () => {
    const localeDetection = await import('@/lib/locale-detection');

    // Test that the export structure is consistent
    const exportKeys = Object.keys(localeDetection);
    expect(exportKeys.length).toBeGreaterThan(0);

    // Verify no undefined exports
    exportKeys.forEach((key) => {
      expect((localeDetection as Record<string, unknown>)[key]).toBeDefined();
    });
  });
});

import { afterEach, beforeEach, describe, expect, it, vi } from 'vitest';
import type { Locale } from '@/types/i18n';
import { WEB_VITALS_CONSTANTS } from '@/constants/test-constants';
import type {
  LocaleDetectionHistory,
  UserLocalePreference,
} from '../locale-storage';
import { LocaleStorageManager } from '@/lib/locale-storage';

// Mock constants
vi.mock('@/constants/i18n-constants', () => ({
  CACHE_DURATIONS: {
    COOKIE_MAX_AGE:
      WEB_VITALS_CONSTANTS.DAYS_PER_MONTH *
      WEB_VITALS_CONSTANTS.HOURS_PER_DAY *
      WEB_VITALS_CONSTANTS.MINUTES_PER_HOUR *
      WEB_VITALS_CONSTANTS.SECONDS_PER_MINUTE *
      WEB_VITALS_CONSTANTS.MILLISECONDS_PER_SECOND, // 30 days
  },
  CACHE_LIMITS: {
    MAX_DETECTION_HISTORY: 50,
  },
}));

// Mock localStorage
const mockLocalStorage = {
  getItem: vi.fn(),
  setItem: vi.fn(),
  removeItem: vi.fn(),
  clear: vi.fn(),
};

// Mock document.cookie
const mockDocumentCookie = {
  get: vi.fn(() => ''),
  set: vi.fn(),
};

describe('LocaleStorageManager', () => {
  beforeEach(() => {
    vi.clearAllMocks();

    // Reset localStorage mock with proper implementations
    mockLocalStorage.getItem.mockReturnValue(null);
    mockLocalStorage.setItem.mockImplementation(() => {});
    mockLocalStorage.removeItem.mockImplementation(() => {});
    mockLocalStorage.clear.mockImplementation(() => {});

    // Reset document cookie mock
    mockDocumentCookie.get.mockReturnValue('');
    mockDocumentCookie.set.mockImplementation(() => {});

    // Mock window and localStorage
    Object.defineProperty(global, 'window', {
      value: {
        localStorage: mockLocalStorage,
        location: {
          protocol: 'https:',
        },
      },
      writable: true,
    });

    // Mock localStorage globally
    Object.defineProperty(global, 'localStorage', {
      value: mockLocalStorage,
      writable: true,
    });

    // Mock document
    Object.defineProperty(global, 'document', {
      value: {
        get cookie() {
          return mockDocumentCookie.get();
        },
        set cookie(value: string) {
          mockDocumentCookie.set(value);
        },
      },
      writable: true,
    });

    // Reset environment using vi.stubEnv
    vi.stubEnv('NODE_ENV', 'test');
  });

  afterEach(() => {
    vi.restoreAllMocks();
  });

  describe('saveUserPreference', () => {
    it('should save user preference to both localStorage and cookie', () => {
      const preference: UserLocalePreference = {
        locale: 'en',
        source: 'user',
        timestamp: Date.now(),
        confidence: 1.0,
      };

      LocaleStorageManager.saveUserPreference(preference);

      expect(mockLocalStorage.setItem).toHaveBeenCalledWith(
        'locale_preference',
        JSON.stringify(preference),
      );
      expect(mockDocumentCookie.set).toHaveBeenCalledWith(
        expect.stringContaining('locale_preference='),
      );
    });

    it('should handle localStorage errors gracefully', () => {
      mockLocalStorage.setItem.mockImplementation(() => {
        throw new Error('Storage full');
      });

      const preference: UserLocalePreference = {
        locale: 'zh',
        source: 'user',
        timestamp: Date.now(),
        confidence: 1.0,
      };

      expect(() => {
        LocaleStorageManager.saveUserPreference(preference);
      }).not.toThrow();

      // Should still try to set cookie
      expect(mockDocumentCookie.set).toHaveBeenCalled();
    });

    it('should handle cookie errors gracefully', () => {
      mockDocumentCookie.set.mockImplementation(() => {
        throw new Error('Cookie error');
      });

      const preference: UserLocalePreference = {
        locale: 'en',
        source: 'user',
        timestamp: Date.now(),
        confidence: 1.0,
      };

      expect(() => {
        LocaleStorageManager.saveUserPreference(preference);
      }).not.toThrow();

      // Should still try to set localStorage
      expect(mockLocalStorage.setItem).toHaveBeenCalled();
    });
  });

  describe('getUserPreference', () => {
    it('should return user preference from localStorage', () => {
      const preference: UserLocalePreference = {
        locale: 'en',
        source: 'user',
        timestamp: Date.now(),
        confidence: 1.0,
      };

      mockLocalStorage.getItem.mockReturnValue(JSON.stringify(preference));

      const result = LocaleStorageManager.getUserPreference();

      expect(result).toEqual(preference);
      expect(mockLocalStorage.getItem).toHaveBeenCalledWith(
        'locale_preference',
      );
    });

    it('should fallback to cookie when localStorage fails', () => {
      mockLocalStorage.getItem.mockReturnValue(null);

      const preference: UserLocalePreference = {
        locale: 'zh',
        source: 'user',
        timestamp: Date.now(),
        confidence: 1.0,
      };

      mockDocumentCookie.get.mockReturnValue(
        `locale_preference=${encodeURIComponent(JSON.stringify(preference))}`,
      );

      const result = LocaleStorageManager.getUserPreference();

      expect(result).toEqual(preference);
    });

    it('should return null when no preference is stored', () => {
      mockLocalStorage.getItem.mockReturnValue(null);
      mockDocumentCookie.get.mockReturnValue('');

      const result = LocaleStorageManager.getUserPreference();

      expect(result).toBeNull();
    });

    it('should handle invalid JSON gracefully', () => {
      mockLocalStorage.getItem.mockReturnValue('invalid json');

      const result = LocaleStorageManager.getUserPreference();

      expect(result).toBeNull();
    });

    it('should handle cookie parsing errors gracefully', () => {
      mockLocalStorage.getItem.mockReturnValue(null);
      mockDocumentCookie.get.mockReturnValue('locale_preference=invalid%json');

      const result = LocaleStorageManager.getUserPreference();

      expect(result).toBeNull();
    });
  });

  describe('saveDetectionHistory', () => {
    it('should save detection history to localStorage', () => {
      const detection = {
        locale: 'en' as const,
        source: 'browser',
        timestamp: Date.now(),
        confidence: 0.8,
      };

      // Note: saveDetectionHistory is private, testing through public interface
      LocaleStorageManager.addDetectionRecord(detection);

      expect(mockLocalStorage.setItem).toHaveBeenCalledWith(
        'locale_detection_history',
        expect.stringContaining('"locale":"en"'),
      );
    });

    it('should handle localStorage errors gracefully', () => {
      mockLocalStorage.setItem.mockImplementation(() => {
        throw new Error('Storage error');
      });

      // const history: LocaleDetectionHistory = {
      //   detections: [],
      //   lastUpdated: Date.now(),
      // };

      expect(() => {
        LocaleStorageManager.getDetectionHistory();
      }).not.toThrow();
    });
  });

  describe('getDetectionHistory', () => {
    it('should return detection history from localStorage', () => {
      const history: LocaleDetectionHistory = {
        detections: [
          {
            locale: 'en',
            source: 'browser',
            timestamp: Date.now(),
            confidence: 0.8,
          },
        ],
        lastUpdated: Date.now(),
      };

      mockLocalStorage.getItem.mockReturnValue(JSON.stringify(history));

      const result = LocaleStorageManager.getDetectionHistory();

      expect(result).toEqual(history);
      expect(mockLocalStorage.getItem).toHaveBeenCalledWith(
        'locale_detection_history',
      );
    });

    it('should return null when no history is stored', () => {
      mockLocalStorage.getItem.mockReturnValue(null);

      const result = LocaleStorageManager.getDetectionHistory();

      expect(result).toBeNull();
    });

    it('should handle invalid JSON gracefully', () => {
      mockLocalStorage.getItem.mockReturnValue('invalid json');

      const result = LocaleStorageManager.getDetectionHistory();

      expect(result).toBeNull();
    });
  });

  describe('addDetectionRecord', () => {
    it('should add new detection record to existing history', () => {
      const existingHistory: LocaleDetectionHistory = {
        detections: [
          {
            locale: 'en',
            source: 'browser',
            timestamp: Date.now() - 1000,
            confidence: 0.8,
          },
        ],
        lastUpdated: Date.now() - 1000,
      };

      mockLocalStorage.getItem.mockReturnValue(JSON.stringify(existingHistory));

      // addDetectionRecord is private, testing through public interface
      const history = LocaleStorageManager.getDetectionHistory();
      expect(history).toBeDefined();
      expect(Array.isArray(history?.detections)).toBe(true);
    });

    it('should create new history when none exists', () => {
      mockLocalStorage.getItem.mockReturnValue(null);

      // Call addDetectionRecord through the public method
      (LocaleStorageManager as unknown).addDetectionRecord({
        locale: 'en',
        source: 'test',
        timestamp: Date.now(),
        confidence: 0.9,
      });

      // Verify that setItem was called with new history
      expect(mockLocalStorage.setItem).toHaveBeenCalledWith(
        'locale_detection_history',
        expect.stringContaining('"detections"'),
      );
    });

    it('should limit history to maximum entries', () => {
      // Create history with 50 entries (at limit)
      const existingHistory: LocaleDetectionHistory = {
        detections: Array.from({ length: 50 }, (_, i) => ({
          locale: 'en' as Locale,
          source: 'browser',
          timestamp: Date.now() - i * 1000,
          confidence: 0.8,
        })),
        lastUpdated: Date.now(),
      };

      mockLocalStorage.getItem.mockReturnValue(JSON.stringify(existingHistory));

      // addDetectionRecord is private, testing through public interface
      const history = LocaleStorageManager.getDetectionHistory();
      expect(history).toBeDefined();
      expect(Array.isArray(history?.detections)).toBe(true);
    });
  });

  describe('setUserOverride', () => {
    it('should save user override to localStorage', () => {
      LocaleStorageManager.setUserOverride('zh');

      expect(mockLocalStorage.setItem).toHaveBeenCalledWith(
        'user_locale_override',
        JSON.stringify('zh'),
      );
    });

    it('should handle localStorage errors gracefully', () => {
      mockLocalStorage.setItem.mockImplementation(() => {
        throw new Error('Storage error');
      });

      expect(() => {
        LocaleStorageManager.setUserOverride('en');
      }).not.toThrow();
    });
  });

  describe('getUserOverride', () => {
    it('should return user override from localStorage', () => {
      mockLocalStorage.getItem.mockReturnValue(JSON.stringify('zh'));

      const result = LocaleStorageManager.getUserOverride();

      expect(result).toBe('zh');
      expect(mockLocalStorage.getItem).toHaveBeenCalledWith(
        'user_locale_override',
      );
    });

    it('should return null when no override is stored', () => {
      mockLocalStorage.getItem.mockReturnValue(null);

      const result = LocaleStorageManager.getUserOverride();

      expect(result).toBeNull();
    });

    it('should handle invalid JSON gracefully', () => {
      mockLocalStorage.getItem.mockReturnValue('invalid json');

      const result = LocaleStorageManager.getUserOverride();

      expect(result).toBeNull();
    });
  });

  describe('clearUserOverride', () => {
    it('should remove user override from localStorage', () => {
      LocaleStorageManager.clearUserOverride();

      expect(mockLocalStorage.removeItem).toHaveBeenCalledWith(
        'user_locale_override',
      );
    });

    it('should handle localStorage errors gracefully', () => {
      mockLocalStorage.removeItem.mockImplementation(() => {
        throw new Error('Storage error');
      });

      expect(() => {
        LocaleStorageManager.clearUserOverride();
      }).not.toThrow();
    });
  });

  describe('clearAll', () => {
    it('should clear all locale-related data', () => {
      LocaleStorageManager.clearAll();

      expect(mockLocalStorage.removeItem).toHaveBeenCalledWith(
        'locale_preference',
      );
      expect(mockLocalStorage.removeItem).toHaveBeenCalledWith(
        'locale_detection_history',
      );
      expect(mockLocalStorage.removeItem).toHaveBeenCalledWith(
        'user_locale_override',
      );
      expect(mockDocumentCookie.set).toHaveBeenCalledWith(
        expect.stringContaining('locale_preference=; expires='),
      );
    });
  });

  describe('cookie decoding error handling', () => {
    it('should handle cookie decoding errors in development environment', () => {
      // Mock development environment
      vi.stubEnv('NODE_ENV', 'development');

      // Mock document.cookie with malformed encoded value that will cause decodeURIComponent to fail
      Object.defineProperty(document, 'cookie', {
        value: 'locale_preference=%E0%E1%E2', // Invalid UTF-8 sequence
        writable: true,
        configurable: true,
      });

      // This should trigger the development environment error handling branch (lines 78-80)
      const result = LocaleStorageManager.getUserPreference();

      // Should return null when decoding fails
      expect(result).toBeNull();

      // Restore environment
      vi.unstubAllEnvs();
    });
  });

  describe('SSR compatibility', () => {
    it('should handle server-side rendering gracefully', () => {
      // Mock SSR environment
      Object.defineProperty(global, 'window', {
        value: undefined,
        writable: true,
      });
      Object.defineProperty(global, 'document', {
        value: undefined,
        writable: true,
      });

      const preference: UserLocalePreference = {
        locale: 'en',
        source: 'user',
        timestamp: Date.now(),
        confidence: 1.0,
      };

      expect(() => {
        LocaleStorageManager.saveUserPreference(preference);
        LocaleStorageManager.getUserPreference();
        LocaleStorageManager.setUserOverride('zh');
        LocaleStorageManager.getUserOverride();
        LocaleStorageManager.clearAll();
      }).not.toThrow();
    });
  });
});

import { describe, expect, it, vi } from 'vitest';
import {
  TEST_COUNT_CONSTANTS,
  TEST_SCREEN_CONSTANTS,
  TEST_TIMEOUT_CONSTANTS,
} from '@/constants/test-constants';
import {
  getLocalizedHref,
  isActivePath,
  mainNavigation,
  mobileNavigation,
  NAVIGATION_ANIMATIONS,
  NAVIGATION_ARIA,
  NAVIGATION_BREAKPOINTS,
  type NavigationItem,
} from '../navigation';

// Use vi.hoisted to ensure proper mock setup
const { mockLocalesConfig } = vi.hoisted(() => ({
  mockLocalesConfig: {
    locales: ['en', 'zh'],
  },
}));

// Mock the config/paths module
vi.mock('@/config/paths', () => ({
  LOCALES_CONFIG: mockLocalesConfig,
}));

describe('navigation', () => {
  describe('NavigationItem interface', () => {
    it('should have valid NavigationItem structure', () => {
      const item: NavigationItem = {
        key: 'test',
        href: '/test',
        translationKey: 'nav.test',
        icon: 'test-icon',
        external: true,
        children: [],
      };

      expect(item.key).toBe('test');
      expect(item.href).toBe('/test');
      expect(item.translationKey).toBe('nav.test');
      expect(item.icon).toBe('test-icon');
      expect(item.external).toBe(true);
      expect(Array.isArray(item.children)).toBe(true);
    });
  });

  describe('mainNavigation', () => {
    it('should have all required navigation items', () => {
      const expectedKeys = ['home', 'about', 'products', 'blog', 'diagnostics'];
      const actualKeys = mainNavigation.map((item) => item.key);

      expectedKeys.forEach((key) => {
        expect(actualKeys).toContain(key);
      });
    });

    it('should have valid structure for all items', () => {
      mainNavigation.forEach((item) => {
        expect(item.key).toBeTruthy();
        expect(item.href).toBeTruthy();
        expect(item.translationKey).toBeTruthy();
        expect(item.href).toMatch(/^\/[a-z-]*$/);
        expect(item.translationKey).toMatch(/^navigation\./);
      });
    });

    it('should have home item pointing to root', () => {
      const homeItem = mainNavigation.find((item) => item.key === 'home');
      expect(homeItem).toBeDefined();
      expect(homeItem!.href).toBe('/');
    });

    it('should have unique keys', () => {
      const keys = mainNavigation.map((item) => item.key);
      const uniqueKeys = new Set(keys);
      expect(keys.length).toBe(uniqueKeys.size);
    });

    it('should have unique hrefs', () => {
      const hrefs = mainNavigation.map((item) => item.href);
      const uniqueHrefs = new Set(hrefs);
      expect(hrefs.length).toBe(uniqueHrefs.size);
    });
  });

  describe('mobileNavigation', () => {
    it('should be the same as mainNavigation', () => {
      expect(mobileNavigation).toBe(mainNavigation);
    });
  });

  describe('isActivePath', () => {
    it('should return true for exact root path match', () => {
      expect(isActivePath('/', '/')).toBe(true);
      expect(isActivePath('/en', '/')).toBe(true);
      expect(isActivePath('/zh', '/')).toBe(true);
    });

    it('should return false for root path when current path is not root', () => {
      expect(isActivePath('/about', '/')).toBe(false);
      expect(isActivePath('/en/about', '/')).toBe(false);
      expect(isActivePath('/zh/products', '/')).toBe(false);
    });

    it('should return true for matching paths', () => {
      expect(isActivePath('/about', '/about')).toBe(true);
      expect(isActivePath('/en/about', '/about')).toBe(true);
      expect(isActivePath('/zh/about', '/about')).toBe(true);
    });

    it('should return true for sub-paths', () => {
      expect(isActivePath('/about/team', '/about')).toBe(true);
      expect(isActivePath('/en/about/team', '/about')).toBe(true);
      expect(isActivePath('/zh/products/enterprise', '/products')).toBe(true);
    });

    it('should return false for non-matching paths', () => {
      expect(isActivePath('/about', '/products')).toBe(false);
      expect(isActivePath('/en/about', '/products')).toBe(false);
      expect(isActivePath('/zh/services', '/products')).toBe(false);
    });

    it('should handle paths with trailing slashes', () => {
      expect(isActivePath('/about/', '/about')).toBe(true);
      expect(isActivePath('/en/about/', '/about')).toBe(true);
    });

    it('should handle edge cases', () => {
      expect(isActivePath('', '/')).toBe(true);
      expect(isActivePath('/en', '/')).toBe(true);
      expect(isActivePath('/zh', '/')).toBe(true);
    });

    it('should not match partial path segments', () => {
      expect(isActivePath('/aboutus', '/about')).toBe(false);
      expect(isActivePath('/en/aboutus', '/about')).toBe(false);
    });

    it('should handle item paths that already end with slash', () => {
      // This test covers line 85 where cleanItemPath already ends with '/'
      expect(isActivePath('/about/team', '/about/')).toBe(true);
      expect(isActivePath('/en/about/team', '/about/')).toBe(true);
      expect(isActivePath('/zh/products/enterprise', '/products/')).toBe(true);
    });
  });

  describe('getLocalizedHref', () => {
    it('should return external URLs unchanged', () => {
      expect(getLocalizedHref('https://example.com', 'en')).toBe(
        'https://example.com',
      );
      expect(getLocalizedHref('http://example.com', 'zh')).toBe(
        'http://example.com',
      );
    });

    it('should return mailto links unchanged', () => {
      expect(getLocalizedHref('mailto:test@example.com', 'en')).toBe(
        'mailto:test@example.com',
      );
    });

    it('should return tel links unchanged', () => {
      expect(getLocalizedHref('tel:+1234567890', 'en')).toBe('tel:+1234567890');
    });

    it('should localize root path', () => {
      expect(getLocalizedHref('/', 'en')).toBe('/en');
      expect(getLocalizedHref('/', 'zh')).toBe('/zh');
    });

    it('should localize internal paths', () => {
      expect(getLocalizedHref('/about', 'en')).toBe('/en/about');
      expect(getLocalizedHref('/about', 'zh')).toBe('/zh/about');
      expect(getLocalizedHref('/products/enterprise', 'en')).toBe(
        '/en/products/enterprise',
      );
    });

    it('should handle paths with query parameters', () => {
      expect(getLocalizedHref('/search?q=test', 'en')).toBe(
        '/en/search?q=test',
      );
      expect(getLocalizedHref('/products?category=web', 'zh')).toBe(
        '/zh/products?category=web',
      );
    });

    it('should handle paths with hash fragments', () => {
      expect(getLocalizedHref('/about#team', 'en')).toBe('/en/about#team');
      expect(getLocalizedHref('/docs#installation', 'zh')).toBe(
        '/zh/docs#installation',
      );
    });
  });

  describe('NAVIGATION_BREAKPOINTS', () => {
    it('should have all required breakpoints', () => {
      expect(NAVIGATION_BREAKPOINTS.mobile).toBe(
        TEST_SCREEN_CONSTANTS.MOBILE_WIDTH,
      );
      expect(NAVIGATION_BREAKPOINTS.tablet).toBe(
        TEST_SCREEN_CONSTANTS.TABLET_WIDTH,
      );
      expect(NAVIGATION_BREAKPOINTS.desktop).toBe(
        TEST_SCREEN_CONSTANTS.DESKTOP_WIDTH,
      );
    });

    it('should have ascending breakpoint values', () => {
      expect(NAVIGATION_BREAKPOINTS.mobile).toBeLessThan(
        NAVIGATION_BREAKPOINTS.tablet,
      );
      expect(NAVIGATION_BREAKPOINTS.tablet).toBeLessThan(
        NAVIGATION_BREAKPOINTS.desktop,
      );
    });

    it('should be readonly', () => {
      expect(() => {
        // @ts-expect-error - Testing readonly property
        NAVIGATION_BREAKPOINTS.mobile = 500;
      }).toThrow();
    });
  });

  describe('NAVIGATION_ANIMATIONS', () => {
    it('should have all required animation durations', () => {
      expect(NAVIGATION_ANIMATIONS.mobileMenuToggle).toBe(
        TEST_TIMEOUT_CONSTANTS.MEDIUM_DELAY,
      );
      expect(NAVIGATION_ANIMATIONS.dropdownFade).toBe(
        TEST_TIMEOUT_CONSTANTS.SHORT_DELAY,
      );
      expect(NAVIGATION_ANIMATIONS.hoverTransition).toBe(100);
    });

    it('should have reasonable duration values', () => {
      Object.values(NAVIGATION_ANIMATIONS).forEach((duration) => {
        expect(duration).toBeGreaterThan(0);
        expect(duration).toBeLessThan(1000); // Should be under 1 second
      });
    });

    it('should be readonly', () => {
      expect(() => {
        // @ts-expect-error - Testing readonly property
        NAVIGATION_ANIMATIONS.mobileMenuToggle = 500;
      }).toThrow();
    });
  });

  describe('NAVIGATION_ARIA', () => {
    it('should have all required ARIA labels', () => {
      expect(NAVIGATION_ARIA.mainNav).toBe('Main navigation');
      expect(NAVIGATION_ARIA.mobileMenuButton).toBe('Toggle mobile menu');
      expect(NAVIGATION_ARIA.mobileMenu).toBe('Mobile navigation menu');
      expect(NAVIGATION_ARIA.languageSelector).toBe('Language selector');
      expect(NAVIGATION_ARIA.themeSelector).toBe('Theme selector');
      expect(NAVIGATION_ARIA.skipToContent).toBe('Skip to main content');
    });

    it('should have descriptive labels', () => {
      Object.values(NAVIGATION_ARIA).forEach((label) => {
        expect(label.length).toBeGreaterThan(TEST_COUNT_CONSTANTS.MEDIUM);
        expect(label).toMatch(/^[A-Z]/); // Should start with capital letter
