  /**
   * ÊµãËØïTabÈîÆÂæ™ÁéØÂØºËà™
   */
  async testTabNavigation(): Promise<AccessibilityTestResult> {
    const testName = 'TabÈîÆÂæ™ÁéØÂØºËà™ÊµãËØï';

    try {
      // Ëé∑ÂèñÊâÄÊúâÂèØËÅöÁÑ¶ÂÖÉÁ¥†
      const focusableElements = this.getFocusableElements();

      if (focusableElements.length === ZERO) {
        return {
          testName,
          passed: false,
          details: 'Êú™ÊâæÂà∞ÂèØËÅöÁÑ¶ÁöÑÂØºËà™ÂÖÉÁ¥†',
          suggestions: [
            'Á°Æ‰øùÂØºËà™ÂÖÉÁ¥†ÂÖ∑ÊúâÊ≠£Á°ÆÁöÑtabindexÂ±ûÊÄß',
            'Ê£ÄÊü•ÂÖÉÁ¥†ÊòØÂê¶Ë¢´Ê≠£Á°ÆÊ∏≤Êüì',
          ],
        };
      }

      // ÊµãËØïTabÈîÆÂØºËà™È°∫Â∫è
      const tabOrder: HTMLElement[] = [];
      const currentElement = focusableElements[ZERO];

      if (!currentElement) {
        return {
          testName,
          passed: false,
          details: 'Êú™ÊâæÂà∞ÂèØËÅöÁÑ¶ÁöÑÂØºËà™ÂÖÉÁ¥†',
        };
      }

      for (let i = ZERO; i < focusableElements.length; i++) {
        currentElement.focus();
        tabOrder.push(document.activeElement as HTMLElement);

        // Ê®°ÊãüTabÈîÆ
        const tabEvent = new KeyboardEvent('keydown', {
          key: 'Tab',
          bubbles: true,
          cancelable: true,
        });
        document.dispatchEvent(tabEvent);

        // Á≠âÂæÖÁÑ¶ÁÇπÂèòÂåñ
        await new Promise((resolve) => setTimeout(resolve, PERCENTAGE_HALF));
      }

      const isValidTabOrder = this.validateTabOrder(
        tabOrder,
        focusableElements,
      );

      return {
        testName,
        passed: isValidTabOrder,
        details: `ÊµãËØï‰∫Ü${focusableElements.length}‰∏™ÂèØËÅöÁÑ¶ÂÖÉÁ¥†ÔºåTabÈîÆÂØºËà™${isValidTabOrder ? 'Ê≠£Â∏∏' : 'ÂºÇÂ∏∏'}`,
        ...(isValidTabOrder
          ? {}
          : {
              suggestions: [
                'Ê£ÄÊü•tabindexÂ±ûÊÄßËÆæÁΩÆ',
                'Á°Æ‰øùÂÖÉÁ¥†ÊåâÈÄªËæëÈ°∫Â∫èÊéíÂàó',
                'È™åËØÅÈöêËóèÂÖÉÁ¥†‰∏çÂú®TabÂ∫èÂàó‰∏≠',
              ],
            }),
      };
    } catch (_error) {
      // ÂøΩÁï•ÈîôËØØÂèòÈáè
      return {
        testName,
        passed: false,
        details: `ÊµãËØïËøáÁ®ã‰∏≠ÂèëÁîüÈîôËØØ: ${_error}`,
        suggestions: ['Ê£ÄÊü•DOMÁªìÊûÑ', 'Á°Æ‰øùÊµãËØïÁéØÂ¢ÉÊ≠£Á°Æ'],
      };
    }
  }

  /**
   * ÊµãËØïEnter/SpaceÈîÆÊøÄÊ¥ªÂäüËÉΩ
   */
  async testKeyActivation(): Promise<AccessibilityTestResult> {
    const testName = 'Enter/SpaceÈîÆÊøÄÊ¥ªÊµãËØï';

    try {
      const triggers = document.querySelectorAll(
        '[data-slot="navigation-menu-trigger"]',
      );
      const links = document.querySelectorAll('a[href]');

      let activationTests = ZERO;
      let passedTests = ZERO;

      // ÊµãËØï‰∏ãÊãâËèúÂçïËß¶ÂèëÂô®
      for (const trigger of triggers) {
        const element = trigger as HTMLElement;
        element.focus();

        // ÊµãËØïEnterÈîÆ
        const enterResult = await this.testElementKeyActivation(
          element,
          'Enter',
        );
        activationTests += ONE;
        if (enterResult) passedTests += ONE;

        // ÊµãËØïSpaceÈîÆ
        const spaceResult = await this.testElementKeyActivation(element, ' ');
        activationTests += ONE;
        if (spaceResult) passedTests += ONE;
      }

      // ÊµãËØïÂØºËà™ÈìæÊé•
      for (const link of Array.from(links).slice(ZERO, COUNT_TRIPLE)) {
        // ÈôêÂà∂ÊµãËØïÊï∞Èáè
        const element = link as HTMLElement;
        element.focus();

        const enterResult = await this.testElementKeyActivation(
          element,
          'Enter',
        );
        activationTests += ONE;
        if (enterResult) passedTests += ONE;
      }

      const successRate =
        activationTests > ZERO ? passedTests / activationTests : ZERO;

      return {
        testName,
        passed: successRate >= MAGIC_0_8, // 80%ÈÄöËøáÁéá
        details: `ÊµãËØï‰∫Ü${activationTests}‰∏™ÊøÄÊ¥ªÊìç‰ΩúÔºåÊàêÂäüÁéá: ${(successRate * PERCENTAGE_FULL).toFixed(ONE)}%`,
        ...(successRate < MAGIC_0_8
          ? {
              suggestions: [
                'Á°Æ‰øùÊâÄÊúâ‰∫§‰∫íÂÖÉÁ¥†ÊîØÊåÅEnterÈîÆÊøÄÊ¥ª',
                'È™åËØÅSpaceÈîÆÂú®ÊåâÈíÆÂÖÉÁ¥†‰∏äÁöÑÊøÄÊ¥ªÂäüËÉΩ',
                'Ê£ÄÊü•ÈîÆÁõò‰∫ã‰ª∂Â§ÑÁêÜÂô®ÁöÑÂÆûÁé∞',
              ],
            }
          : {}),
      };
    } catch (_error) {
      // ÂøΩÁï•ÈîôËØØÂèòÈáè
      return {
        testName,
        passed: false,
        details: `ÊµãËØïËøáÁ®ã‰∏≠ÂèëÁîüÈîôËØØ: ${_error}`,
        suggestions: ['Ê£ÄÊü•‰∫ã‰ª∂Â§ÑÁêÜÂô®', 'È™åËØÅÂÖÉÁ¥†ÂèØËÆøÈóÆÊÄß'],
      };
    }
  }

  /**
   * ÊµãËØïEscapeÈîÆÂÖ≥Èó≠ÂäüËÉΩ
   */
  async testEscapeKeyClose(): Promise<AccessibilityTestResult> {
    const testName = 'EscapeÈîÆÂÖ≥Èó≠ÊµãËØï';

    try {
      const triggers = document.querySelectorAll(
        '[data-slot="navigation-menu-trigger"]',
      );
      let escapeTests = ZERO;
      let passedTests = ZERO;

      for (const trigger of triggers) {
        const element = trigger as HTMLElement;

        // ÂÖàÊâìÂºÄËèúÂçï
        element.focus();
        element.click();
        await new Promise((resolve) => setTimeout(resolve, PERCENTAGE_FULL));

        // Ê£ÄÊü•ËèúÂçïÊòØÂê¶ÊâìÂºÄ
        const isOpen =
          element.getAttribute('data-state') === 'open' ||
          element.getAttribute('aria-expanded') === 'true';

        if (isOpen) {
          // ÊµãËØïEscapeÈîÆÂÖ≥Èó≠
          const escapeEvent = new KeyboardEvent('keydown', {
            key: 'Escape',
            bubbles: true,
            cancelable: true,
          });
          element.dispatchEvent(escapeEvent);

          await new Promise((resolve) => setTimeout(resolve, PERCENTAGE_FULL));

          // Ê£ÄÊü•ËèúÂçïÊòØÂê¶ÂÖ≥Èó≠
          const isClosed =
            element.getAttribute('data-state') !== 'open' &&
            element.getAttribute('aria-expanded') !== 'true';

          escapeTests += ONE;
          if (isClosed) passedTests += ONE;
        }
      }

      const successRate = escapeTests > ZERO ? passedTests / escapeTests : ZERO;

      return {
        testName,
        passed: successRate >= MAGIC_0_8,
        details: `ÊµãËØï‰∫Ü${escapeTests}‰∏™EscapeÂÖ≥Èó≠Êìç‰ΩúÔºåÊàêÂäüÁéá: ${(successRate * PERCENTAGE_FULL).toFixed(ONE)}%`,
        ...(successRate < MAGIC_0_8
          ? {
              suggestions: [
                'Á°Æ‰øùEscapeÈîÆ‰∫ã‰ª∂Â§ÑÁêÜÂô®Ê≠£Á°ÆÂÆûÁé∞',
                'È™åËØÅËèúÂçïÁä∂ÊÄÅÁÆ°ÁêÜ',
                'Ê£ÄÊü•‰∫ã‰ª∂ÂÜíÊ≥°Âíå‰º†Êí≠',
              ],
            }
          : {}),
      };
    } catch (_error) {
      // ÂøΩÁï•ÈîôËØØÂèòÈáè
      return {
        testName,
        passed: false,
        details: `ÊµãËØïËøáÁ®ã‰∏≠ÂèëÁîüÈîôËØØ: ${_error}`,
        suggestions: ['Ê£ÄÊü•ËèúÂçïÁä∂ÊÄÅÁÆ°ÁêÜ', 'È™åËØÅÈîÆÁõò‰∫ã‰ª∂Â§ÑÁêÜ'],
      };
    }
  }

