      const issues: string[] = [];
      const suggestions: string[] = [];

      // Ê£ÄÊü•ÂØºËà™ÂÆπÂô®
      const navElements = document.querySelectorAll('nav');
      navElements.forEach((nav, index) => {
        if (
          !nav.getAttribute('aria-label') &&
          !nav.getAttribute('aria-labelledby')
        ) {
          issues.push(`ÂØºËà™ÂÆπÂô®${index + ONE}Áº∫Â∞ëaria-labelÊàñaria-labelledby`);
          suggestions.push('‰∏∫ÂØºËà™ÂÆπÂô®Ê∑ªÂä†ÊèèËø∞ÊÄßÁöÑaria-label');
        }
      });

      // Ê£ÄÊü•‰∏ãÊãâËèúÂçïËß¶ÂèëÂô®
      const triggers = document.querySelectorAll(
        '[data-slot="navigation-menu-trigger"]',
      );
      triggers.forEach((trigger, index) => {
        if (!trigger.getAttribute('aria-expanded')) {
          issues.push(`‰∏ãÊãâËèúÂçïËß¶ÂèëÂô®${index + ONE}Áº∫Â∞ëaria-expandedÂ±ûÊÄß`);
          suggestions.push('‰∏∫‰∏ãÊãâËèúÂçïËß¶ÂèëÂô®Ê∑ªÂä†aria-expandedÂ±ûÊÄß');
        }

        if (!trigger.getAttribute('aria-haspopup')) {
          issues.push(`‰∏ãÊãâËèúÂçïËß¶ÂèëÂô®${index + ONE}Áº∫Â∞ëaria-haspopupÂ±ûÊÄß`);
          suggestions.push('‰∏∫‰∏ãÊãâËèúÂçïËß¶ÂèëÂô®Ê∑ªÂä†aria-haspopupÂ±ûÊÄß');
        }
      });

      // Ê£ÄÊü•ÂΩìÂâçÈ°µÈù¢Ê†áËØÜ
      const currentPageLinks = document.querySelectorAll(
        '[aria-current="page"]',
      );
      const allLinks = document.querySelectorAll('a[href]');

      if (currentPageLinks.length === ZERO && allLinks.length > ZERO) {
        issues.push('Êú™ÊâæÂà∞ÂΩìÂâçÈ°µÈù¢ÁöÑaria-current="page"Ê†áËØÜ');
        suggestions.push('‰∏∫ÂΩìÂâçÈ°µÈù¢ÈìæÊé•Ê∑ªÂä†aria-current="page"Â±ûÊÄß');
      }

      return {
        testName,
        passed: issues.length === ZERO,
        details:
          issues.length === ZERO
            ? 'ÊâÄÊúâARIAÂ±ûÊÄßÈÖçÁΩÆÊ≠£Á°Æ'
            : `ÂèëÁé∞${issues.length}‰∏™ARIAÂ±ûÊÄßÈóÆÈ¢ò: ${issues.join('; ')}`,
        ...(suggestions.length > ZERO ? { suggestions } : {}),
      };
    } catch (_error) {
      // ÂøΩÁï•ÈîôËØØÂèòÈáè
      return {
        testName,
        passed: false,
        details: `ÊµãËØïËøáÁ®ã‰∏≠ÂèëÁîüÈîôËØØ: ${_error}`,
        suggestions: ['Ê£ÄÊü•DOMÁªìÊûÑ', 'È™åËØÅARIAÂ±ûÊÄßÈÖçÁΩÆ'],
      };
    }
  }

  /**
   * ËøêË°åÊâÄÊúâÊó†ÈöúÁ¢çÊÄßÊµãËØï
   */
  async runAllTests(): Promise<AccessibilityTestResult[]> {
    this.results = [];

    // ËøêË°åÂêÑÈ°πÊµãËØï
    this.results.push(await this.testTabNavigation());
    this.results.push(await this.testKeyActivation());
    this.results.push(await this.testEscapeKeyClose());
    this.results.push(this.testAriaAttributes());

    return this.results;
  }

  /**
   * Ëé∑ÂèñÊµãËØïÊä•Âëä
   */
  getTestReport(): string {
    const totalTests = this.results.length;
    const passedTests = this.results.filter((r) => r.passed).length;
    const failedTests = totalTests - passedTests;

    let report = `\n=== Êó†ÈöúÁ¢çÊÄßÊµãËØïÊä•Âëä ===\n`;
    report += `ÊÄªÊµãËØïÊï∞: ${totalTests}\n`;
    report += `ÈÄöËøá: ${passedTests}\n`;
    report += `Â§±Ë¥•: ${failedTests}\n`;
    report += `ÈÄöËøáÁéá: ${((passedTests / totalTests) * PERCENTAGE_FULL).toFixed(ONE)}%\n\n`;

    this.results.forEach((result, index) => {
      report += `${index + ONE}. ${result.testName}\n`;
      report += `   Áä∂ÊÄÅ: ${result.passed ? '‚úÖ ÈÄöËøá' : '‚ùå Â§±Ë¥•'}\n`;
      report += `   ËØ¶ÊÉÖ: ${result.details}\n`;
      if (result.suggestions) {
        report += `   Âª∫ËÆÆ: ${result.suggestions.join('; ')}\n`;
      }
      report += '\n';
    });

    return report;
  }

  // ÁßÅÊúâËæÖÂä©ÊñπÊ≥ï
  private getFocusableElements(): HTMLElement[] {
    const selector = [
      'a[href]',
      'button:not([disabled])',
      'input:not([disabled])',
      'select:not([disabled])',
      'textarea:not([disabled])',
      '[tabindex]:not([tabindex="-1"])',
      '[data-slot="navigation-menu-trigger"]',
      '[data-slot="navigation-menu-link"]',
    ].join(', ');

    return Array.from(document.querySelectorAll(selector)) as HTMLElement[];
  }

  private validateTabOrder(
    tabOrder: HTMLElement[],
    focusableElements: HTMLElement[],
  ): boolean {
    // ÁÆÄÂçïÈ™åËØÅÔºöÊ£ÄÊü•ÊòØÂê¶ËÉΩÈÅçÂéÜÊâÄÊúâÂèØËÅöÁÑ¶ÂÖÉÁ¥†
    return tabOrder.length >= focusableElements.length * MAGIC_0_8; // ÂÖÅËÆ∏80%ÁöÑË¶ÜÁõñÁéá
  }

  private async testElementKeyActivation(
    element: HTMLElement,
    key: string,
  ): Promise<boolean> {
    try {
      const initialState = element.getAttribute('data-state');
      const initialExpanded = element.getAttribute('aria-expanded');

      const keyEvent = new KeyboardEvent('keydown', {
        key,
        bubbles: true,
        cancelable: true,
      });

      element.dispatchEvent(keyEvent);
      await new Promise((resolve) => setTimeout(resolve, PERCENTAGE_FULL));

      const newState = element.getAttribute('data-state');
      const newExpanded = element.getAttribute('aria-expanded');

      // Ê£ÄÊü•Áä∂ÊÄÅÊòØÂê¶ÂèëÁîüÂèòÂåñÔºàË°®Á§∫ÊøÄÊ¥ªÊàêÂäüÔºâ
      return initialState !== newState || initialExpanded !== newExpanded;
    } catch {
      return false;
    }
  }
}

// ÂØºÂá∫‰æøÊç∑ÂáΩÊï∞
export async function runAccessibilityTests(): Promise<string> {
  const tester = new AccessibilityTester();
  await tester.runAllTests();
  return tester.getTestReport();
}

