import type { PreloaderMetrics } from '@/lib/i18n-preloader-types';
import {
  adaptiveStrategy,
  batchStrategy,
  memoryAwareStrategy,
  networkAwareStrategy,
  timeAwareStrategy,
} from '@/lib/i18n-preloader-strategies/advanced-strategies';
// ÂØºÂÖ•ÊâÄÊúâÁ≠ñÁï•
import {
  immediateStrategy,
  lazyStrategy,
  priorityStrategy,
  progressiveStrategy,
  smartStrategy,
} from '@/lib/i18n-preloader-strategies/basic-strategies';
import { strategyConfigs } from '@/lib/i18n-preloader-strategies/configs';
import { PreloadStrategyManager } from '@/lib/i18n-preloader-strategies/manager';

/**
 * È¢ÑÂä†ËΩΩÁ≠ñÁï•ÈõÜÂêà
 * Preload strategies collection
 */
export const PreloadStrategies = {
  immediate: immediateStrategy,
  smart: smartStrategy,
  progressive: progressiveStrategy,
  priority: priorityStrategy,
  lazy: lazyStrategy,
  batch: batchStrategy,
  adaptive: adaptiveStrategy,
  networkAware: networkAwareStrategy,
  timeAware: timeAwareStrategy,
  memoryAware: memoryAwareStrategy,
};

/**
 * ÂàõÂª∫Á≠ñÁï•ÁÆ°ÁêÜÂô®
 * Create strategy manager
 */
export function createStrategyManager(): PreloadStrategyManager {
  const manager = new PreloadStrategyManager();

  // Ê≥®ÂÜåÊâÄÊúâÁ≠ñÁï•
  Object.entries(PreloadStrategies).forEach(([name, strategy]) => {
    const config = strategyConfigs[name];
    manager.registerStrategy(name, strategy, config);
  });

  return manager;
}

/**
 * Ëé∑ÂèñÊé®ËçêÁ≠ñÁï•
 * Get recommended strategy
 */
export function getRecommendedStrategy(
  metrics: PreloaderMetrics,
  networkCondition: 'fast' | 'slow' | 'offline' = 'fast',
): string {
  const manager = createStrategyManager();
  return manager.selectBestStrategy(metrics, networkCondition);
}

/**
 * ÁøªËØëÈ¢ÑÂä†ËΩΩÁ≠ñÁï•Â∑•ÂÖ∑ÂáΩÊï∞
 * Translation Preloader Strategy Utility Functions
 */

import { COUNT_PAIR, MAGIC_0_5, MAGIC_0_9, MAGIC_17, MAGIC_18, MAGIC_22, MAGIC_9, ONE, ZERO } from "@/constants/magic-numbers";
import { strategyConfigs } from '@/lib/i18n-preloader-strategies/configs';
import type { PreloaderMetrics } from '@/lib/i18n-preloader-types';

/**
 * Á≠ñÁï•Â∑•ÂÖ∑ÂáΩÊï∞
 * Strategy utility functions
 */
export const StrategyUtils = {
  /**
   * Ê£ÄÊü•ÁΩëÁªúÁä∂ÂÜµ
   * Check network condition
   */
  getNetworkCondition(): 'fast' | 'slow' | 'offline' {
    if (!navigator.onLine) {
      return 'offline';
    }

    const {connection} = (navigator as {
        connection?: { effectiveType?: string; downlink?: number };
      });
    if (connection) {
      const { effectiveType, downlink } = connection;
      if (effectiveType === '4g' && (downlink ?? ZERO) > COUNT_PAIR) {
        return 'fast';
      }
    }

    return 'slow';
  },

  /**
   * Ê£ÄÊü•ÂÜÖÂ≠ò‰ΩøÁî®ÊÉÖÂÜµ
   * Check memory usage
   */
  getMemoryUsage(): number {
    const {memory} = (performance as {
        memory?: { usedJSHeapSize: number; totalJSHeapSize: number };
      });
    if (memory) {
      const { usedJSHeapSize, totalJSHeapSize } = memory;
      return usedJSHeapSize / totalJSHeapSize;
    }
    return MAGIC_0_5; // ÈªòËÆ§ÂÄº
  },

  /**
   * Ëé∑ÂèñÂΩìÂâçÊó∂Èó¥ÊÆµ
   * Get current time period
   */
  getTimePeriod(): 'work' | 'evening' | 'night' {
    const hour = new Date().getHours();
    if (hour >= MAGIC_9 && hour <= MAGIC_17) {
      return 'work';
    }
    if (hour >= MAGIC_18 && hour <= MAGIC_22) {
      return 'evening';
    }
    return 'night';
  },

  /**
   * ËÆ°ÁÆóÁ≠ñÁï•‰ºòÂÖàÁ∫ß
   * Calculate strategy priority
   */
  calculateStrategyPriority(
    strategy: string,
    metrics: PreloaderMetrics,
    conditions: { network: string; memory: number; time: string },
  ): number {
    const config = strategyConfigs[strategy];
    if (!config) return ZERO;

    let score = config.priority;

    // Ê†πÊçÆÊù°‰ª∂Ë∞ÉÊï¥ÂàÜÊï∞
    if (conditions.network === 'fast' && strategy === 'immediate') {
      score += COUNT_PAIR;
    }
    if (conditions.memory < MAGIC_0_5 && strategy === 'lazy') {
      score += ONE;
    }
    if (metrics.successRate > MAGIC_0_9 && strategy === 'smart') {
      score += ONE;
    }

    return score;
  },
};

