/**
 * È¢ÑÂä†ËΩΩÂô®Â∑•ÂéÇÁ±ª
 * Preloader Factory Class
 */

import { ONE } from "@/constants/magic-numbers";
import type { CacheStorage, MetricsCollector } from '@/lib/i18n-cache-types';
import { TranslationPreloader } from '@/lib/i18n-preloader-core';
import type { Messages } from '@/types/i18n';
import type {
  IPreloader,
  PreloaderConfig,
  PreloaderPlugin,
  PreloadEventHandler,
} from '@/lib/i18n-preloader-types';

/**
 * È¢ÑÂä†ËΩΩÂô®Â∑•ÂéÇÁ±ª
 * Preloader factory class
 */
export class PreloaderFactory {
  private static instance: PreloaderFactory;
  private plugins = new Map<string, PreloaderPlugin>();
  private eventHandlers = new Map<string, PreloadEventHandler[]>();

  private constructor() {}

  /**
   * Ëé∑ÂèñÂ∑•ÂéÇÂÆû‰æã
   * Get factory instance
   */
  static getInstance(): PreloaderFactory {
    if (!PreloaderFactory.instance) {
      PreloaderFactory.instance = new PreloaderFactory();
    }
    return PreloaderFactory.instance;
  }

  /**
   * ÂàõÂª∫È¢ÑÂä†ËΩΩÂô®
   * Create preloader
   */
  createPreloader(
    cache: CacheStorage<Messages>,
    metricsCollector: MetricsCollector,
    config?: Partial<PreloaderConfig>,
  ): IPreloader {
    const preloader = new TranslationPreloader(cache, metricsCollector, config);

    // ÂÆâË£ÖÊèí‰ª∂
    this.plugins.forEach((plugin) => {
      plugin.install(preloader);
    });

    // ËÆæÁΩÆ‰∫ã‰ª∂Â§ÑÁêÜÂô®
    this.setupEventHandlers(preloader);

    return preloader;
  }

  /**
   * Ê≥®ÂÜåÊèí‰ª∂
   * Register plugin
   */
  registerPlugin(plugin: PreloaderPlugin): void {
    this.plugins.set(plugin.name, plugin);
  }

  /**
   * Âç∏ËΩΩÊèí‰ª∂
   * Unregister plugin
   */
  unregisterPlugin(name: string): boolean {
    return this.plugins.delete(name);
  }

  /**
   * Ëé∑ÂèñÊèí‰ª∂
   * Get plugin
   */
  getPlugin(name: string): PreloaderPlugin | undefined {
    return this.plugins.get(name);
  }

  /**
   * Ê≥®ÂÜå‰∫ã‰ª∂Â§ÑÁêÜÂô®
   * Register event handler
   */
  on(event: string, handler: PreloadEventHandler): void {
    if (!this.eventHandlers.has(event)) {
      this.eventHandlers.set(event, []);
    }
    this.eventHandlers.get(event)!.push(handler);
  }

  /**
   * ÁßªÈô§‰∫ã‰ª∂Â§ÑÁêÜÂô®
   * Remove event handler
   */
  off(event: string, handler: PreloadEventHandler): void {
    const handlers = this.eventHandlers.get(event);
    if (handlers) {
      const index = handlers.indexOf(handler);
      if (index > -ONE) {
        handlers.splice(index, ONE);
      }
    }
  }

  /**
   * ËÆæÁΩÆ‰∫ã‰ª∂Â§ÑÁêÜÂô®
   * Setup event handlers
   */
  private setupEventHandlers(_preloader: IPreloader): void {
    // ËøôÈáåÂèØ‰ª•ËÆæÁΩÆÈªòËÆ§ÁöÑ‰∫ã‰ª∂Â§ÑÁêÜÈÄªËæë
    // ÂÆûÈôÖÂÆûÁé∞ÈúÄË¶ÅÊ†πÊçÆÂÖ∑‰ΩìÁöÑ‰∫ã‰ª∂Á≥ªÁªüÊù•ÂÆåÊàê
  }
}

/**
 * È¢ÑÂä†ËΩΩÂô®ÁÆ°ÁêÜÂô®
 * Preloader Manager
 */

import type { IPreloader, PreloadState } from '@/lib/i18n-preloader-types';

/**
 * È¢ÑÂä†ËΩΩÂô®ÁÆ°ÁêÜÂô®
 * Preloader manager
 */
export class PreloaderManager {
  private preloaders = new Map<string, IPreloader>();
  private defaultPreloader?: IPreloader | undefined;

  /**
   * Ê≥®ÂÜåÈ¢ÑÂä†ËΩΩÂô®
   * Register preloader
   */
  register(name: string, preloader: IPreloader): void {
    this.preloaders.set(name, preloader);
    if (!this.defaultPreloader) {
      this.defaultPreloader = preloader;
    }
  }

  /**
   * Ëé∑ÂèñÈ¢ÑÂä†ËΩΩÂô®
   * Get preloader
   */
  get(name?: string): IPreloader | undefined {
    if (name) {
      return this.preloaders.get(name);
    }
    return this.defaultPreloader;
  }

  /**
   * ËÆæÁΩÆÈªòËÆ§È¢ÑÂä†ËΩΩÂô®
   * Set default preloader
   */
  setDefault(name: string): boolean {
    const preloader = this.preloaders.get(name);
    if (preloader) {
      this.defaultPreloader = preloader;
      return true;
    }
    return false;
  }

  /**
   * ÁßªÈô§È¢ÑÂä†ËΩΩÂô®
   * Remove preloader
   */
  remove(name: string): boolean {
    const preloader = this.preloaders.get(name);
    if (preloader) {
      preloader.cleanup();
      this.preloaders.delete(name);
      if (this.defaultPreloader === preloader) {
        this.defaultPreloader =
          this.preloaders.values().next().value || undefined;
      }
      return true;
    }
    return false;
  }

  /**
   * Ê∏ÖÁêÜÊâÄÊúâÈ¢ÑÂä†ËΩΩÂô®
   * Cleanup all preloaders
   */
  cleanup(): void {
    this.preloaders.forEach((preloader) => {
      preloader.cleanup();
    });
    this.preloaders.clear();
    this.defaultPreloader = undefined;
  }

  /**
   * Ëé∑ÂèñÊâÄÊúâÈ¢ÑÂä†ËΩΩÂô®Áä∂ÊÄÅ
   * Get all preloader states
   */
  getAllStates(): Record<string, PreloadState> {
    const states: Record<string, PreloadState> = {};
    this.preloaders.forEach((preloader, name) => {
      states[name] = preloader.getPreloadState();
    });
    return states;
  }

  /**
   * ÂÅúÊ≠¢ÊâÄÊúâÈ¢ÑÂä†ËΩΩ
   * Stop all preloading
   */
  stopAll(): void {
    this.preloaders.forEach((preloader) => {
      preloader.stopPreloading();
    });
  }
}

