import { ANIMATION_DURATION_VERY_SLOW, COUNT_TEN, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { DEFAULT_LOCALE, SUPPORTED_LOCALES } from '@/lib/locale-constants';
import type { Locale } from '@/types/i18n';
import {
  COUNTRY_CODE_TO_LOCALE_MAP,
  DETECTION_TIMEOUTS,
  GEO_API_CONFIG,
  LANGUAGE_CODE_TO_LOCALE_MAP,
  TIMEZONE_TO_LOCALE_MAP,
} from '@/lib/locale-detector-constants';

/**
 * Âü∫Á°ÄËØ≠Ë®ÄÊ£ÄÊµãÂô®
 * Base locale detector with fundamental detection methods
 */
export class BaseLocaleDetector {
  /**
   * ÂÆâÂÖ®Âú∞‰ªéËØ≠Ë®ÄÊò†Â∞Ñ‰∏≠Ëé∑ÂèñËØ≠Ë®Ä
   * Safely get locale from language mapping
   * ‰ΩøÁî®ÁôΩÂêçÂçïÈ™åËØÅÔºåÈÅøÂÖç Object Injection Sink
   */
  protected getLocaleFromLanguageCode(
    normalizedLang: string,
  ): Locale | undefined {
    if (!normalizedLang || typeof normalizedLang !== 'string') {
      return undefined;
    }

    const locale = LANGUAGE_CODE_TO_LOCALE_MAP.get(
      normalizedLang.toLowerCase(),
    );
    return locale && SUPPORTED_LOCALES.includes(locale) ? locale : undefined;
  }

  /**
   * ÂÆâÂÖ®Âú∞‰ªéÂú∞ÁêÜ‰ΩçÁΩÆÊò†Â∞Ñ‰∏≠Ëé∑ÂèñËØ≠Ë®Ä
   * Safely get locale from geo mapping
   * ‰ΩøÁî®ÁôΩÂêçÂçïÈ™åËØÅÔºåÈÅøÂÖç Object Injection Sink
   */
  protected getLocaleFromCountryCode(
    countryCode: string | undefined,
  ): Locale | undefined {
    if (!countryCode || typeof countryCode !== 'string') {
      return undefined;
    }

    const locale = COUNTRY_CODE_TO_LOCALE_MAP.get(countryCode.toUpperCase());
    return locale && SUPPORTED_LOCALES.includes(locale) ? locale : undefined;
  }

  /**
   * ÂÆâÂÖ®Âú∞‰ªéÊó∂Âå∫Êò†Â∞Ñ‰∏≠Ëé∑ÂèñËØ≠Ë®Ä
   * Safely get locale from timezone mapping
   */
  protected getLocaleFromTimeZone(
    timeZone: string | undefined,
  ): Locale | undefined {
    if (!timeZone || typeof timeZone !== 'string') {
      return undefined;
    }

    const locale = TIMEZONE_TO_LOCALE_MAP.get(timeZone);
    return locale && SUPPORTED_LOCALES.includes(locale) ? locale : undefined;
  }

  /**
   * ‰ªéÊµèËßàÂô®Ê£ÄÊµãËØ≠Ë®Ä
   * Detect locale from browser
   */
  detectFromBrowser(): Locale {
    try {
      if (typeof navigator === 'undefined') {
        return DEFAULT_LOCALE;
      }

      const languages = navigator.languages || [navigator.language];

      for (const lang of languages) {
        const normalizedLang = lang.toLowerCase();
        const detectedLocale = this.getLocaleFromLanguageCode(normalizedLang);

        if (detectedLocale) {
          return detectedLocale;
        }

        // Â∞ùËØïÊèêÂèñ‰∏ªË¶ÅËØ≠Ë®Ä‰ª£Á†Å (‰æãÂ¶Ç: 'zh-CN' -> 'zh')
        const primaryLang = normalizedLang.split('-')[ZERO];
        const primaryLocale = this.getLocaleFromLanguageCode(primaryLang!);

        if (primaryLocale) {
          return primaryLocale;
        }
      }

      return DEFAULT_LOCALE;
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Browser locale detection failed:', error);
      }
      return DEFAULT_LOCALE;
    }
  }

  /**
   * ‰ªéÂú∞ÁêÜ‰ΩçÁΩÆÊ£ÄÊµãËØ≠Ë®Ä
   * Detect locale from geolocation
   */
  async detectFromGeolocation(): Promise<Locale> {
    try {
      if (typeof navigator === 'undefined' || !navigator.geolocation) {
        return DEFAULT_LOCALE;
      }

      return await new Promise<Locale>((resolve) => {
        const timeoutId = setTimeout(() => {
          resolve(DEFAULT_LOCALE);
        }, DETECTION_TIMEOUTS.GEOLOCATION);

        navigator.geolocation.getCurrentPosition(
          async (position) => {
            clearTimeout(timeoutId);
            try {
              const countryCode = await this.getCountryFromCoordinates(
                position.coords.latitude,
                position.coords.longitude,
              );

              const detectedLocale = this.getLocaleFromCountryCode(countryCode);
              resolve(detectedLocale || DEFAULT_LOCALE);
            } catch (error) {
              if (process.env.NODE_ENV === 'development') {
                console.warn('Geolocation API failed:', error);
              }
              resolve(DEFAULT_LOCALE);
            }
          },
          (error) => {
            clearTimeout(timeoutId);
            if (process.env.NODE_ENV === 'development') {
              console.warn('Geolocation permission denied or failed:', error);
            }
            resolve(DEFAULT_LOCALE);
          },
          {
            timeout: DETECTION_TIMEOUTS.GEOLOCATION,
            enableHighAccuracy: false,
            maximumAge: COUNT_TEN * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW, // 10ÂàÜÈíüÁºìÂ≠ò
          },
        );
      });
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Geolocation detection failed:', error);
      }
      return DEFAULT_LOCALE;
    }
  }

  /**
   * ‰ªéÊó∂Âå∫Ê£ÄÊµãËØ≠Ë®Ä
   * Detect locale from timezone
   */
  detectFromTimeZone(): Locale {
    try {
      if (typeof Intl === 'undefined' || !Intl.DateTimeFormat) {
        return DEFAULT_LOCALE;
      }

      const { timeZone } = Intl.DateTimeFormat().resolvedOptions();
      const detectedLocale = this.getLocaleFromTimeZone(timeZone);

      return detectedLocale || DEFAULT_LOCALE;
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Timezone detection failed:', error);
      }
      return DEFAULT_LOCALE;
    }
  }

  /**
   * ‰ªéIPÂú∞ÂùÄÊ£ÄÊµãËØ≠Ë®Ä
   * Detect locale from IP address
   */
  async detectFromIP(): Promise<Locale> {
    try {
      const countryCode = await this.getCountryFromIP();
      const detectedLocale = this.getLocaleFromCountryCode(countryCode);

      return detectedLocale || DEFAULT_LOCALE;
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('IP-based detection failed:', error);
      }
      return DEFAULT_LOCALE;
    }
  }

  /**
   * Ê†πÊçÆÂùêÊ†áËé∑ÂèñÂõΩÂÆ∂‰ª£Á†Å
   * Get country code from coordinates
   */
  private async getCountryFromCoordinates(
    lat: number,
    lng: number,
  ): Promise<string | undefined> {
    const controller = new AbortController();
    const timeoutId = setTimeout(
      () => controller.abort(),
      DETECTION_TIMEOUTS.NETWORK_REQUEST,
    );

    try {
      // Âú®ÂÆûÈôÖÂ∫îÁî®‰∏≠ÔºåËøôÈáåÂ∫îËØ•Ë∞ÉÁî®ÁúüÂÆûÁöÑÂú∞ÁêÜ‰ΩçÁΩÆAPI
      // ‰æãÂ¶Ç: Google Geocoding API, OpenStreetMap Nominatim, Á≠â
      const response = await fetch(
        `https://api.example.com/geo?lat=${lat}&lng=${lng}`,
        {
          signal: controller.signal,
          headers: {
            Accept: 'application/json',
          },
        },
      );

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}`);
      }

      const data = await response.json();
      return data.country || data.country_code || data.countryCode;
    } catch (error) {
      if (error instanceof Error && error.name === 'AbortError') {
        throw new Error('Geolocation API request timeout');
      }
      throw error;
    } finally {
      clearTimeout(timeoutId);
    }
  }

  /**
   * Ê†πÊçÆIPÂú∞ÂùÄËé∑ÂèñÂõΩÂÆ∂‰ª£Á†Å
   * Get country code from IP address
   */
  private async getCountryFromIP(): Promise<string | undefined> {
    const controller = new AbortController();
    const timeoutId = setTimeout(
      () => controller.abort(),
      DETECTION_TIMEOUTS.NETWORK_REQUEST,
    );

    try {
      // Â∞ùËØïÂ§ö‰∏™IPÂú∞ÁêÜ‰ΩçÁΩÆAPI
      for (const endpoint of GEO_API_CONFIG.ENDPOINTS) {
        try {
          const response = await fetch(endpoint, {
            signal: controller.signal,
            headers: {
              Accept: 'application/json',
            },
          });

          if (!response.ok) {
            continue; // Â∞ùËØï‰∏ã‰∏Ä‰∏™Á´ØÁÇπ
          }

          const data = await response.json();
          const countryCode =
            data.country || data.country_code || data.countryCode;

          if (countryCode) {
            return countryCode;
          }
        } catch (error) {
          // ÁªßÁª≠Â∞ùËØï‰∏ã‰∏Ä‰∏™Á´ØÁÇπ
          continue;
        }
      }

      throw new Error('All IP geolocation endpoints failed');
    } catch (error) {
      if (error instanceof Error && error.name === 'AbortError') {
        throw new Error('IP geolocation request timeout');
      }
      throw error;
    } finally {
      clearTimeout(timeoutId);
    }
  }

  /**
   * È™åËØÅÊ£ÄÊµãÂà∞ÁöÑËØ≠Ë®ÄÊòØÂê¶ÂèóÊîØÊåÅ
   * Validate if detected locale is supported
   */
  protected validateLocale(locale: Locale | undefined): Locale {
    return locale && SUPPORTED_LOCALES.includes(locale)
      ? locale
      : DEFAULT_LOCALE;
  }

  /**
   * Ëé∑ÂèñÊµèËßàÂô®ÊîØÊåÅÁöÑËØ≠Ë®ÄÂàóË°®
   * Get browser supported languages
   */
  getBrowserLanguages(): string[] {
    try {
      if (typeof navigator === 'undefined') {
        return [];
      }

      return navigator.languages
        ? Array.from(navigator.languages)
        : [navigator.language];
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Failed to get browser languages:', error);
      }
      return [];
    }
  }

  /**
   * Ëé∑ÂèñÂΩìÂâçÊó∂Âå∫‰ø°ÊÅØ
   * Get current timezone info
   */
  getTimeZoneInfo(): { timeZone: string; offset: number } | null {
    try {
      if (typeof Intl === 'undefined' || !Intl.DateTimeFormat) {
        return null;
      }

      const { timeZone } = Intl.DateTimeFormat().resolvedOptions();
      const offset = new Date().getTimezoneOffset();

      return { timeZone, offset };
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Failed to get timezone info:', error);
      }
      return null;
    }
  }
}

/**
 * Êô∫ËÉΩËØ≠Ë®ÄÊ£ÄÊµãÁÆóÊ≥ï
 *
 * Êèê‰æõÁªºÂêàÊ£ÄÊµãÈÄªËæë„ÄÅÁΩÆ‰ø°Â∫¶ËÆ°ÁÆó„ÄÅÁªìÊûúÂàÜÊûêÁ≠âÊô∫ËÉΩÊ£ÄÊµãÂäüËÉΩ
 */

import { DEC_0_05, MAGIC_0_1, MAGIC_0_8 } from "@/constants/decimal";
import { ONE, ZERO } from "@/constants/magic-numbers";
import { DEFAULT_LOCALE, SUPPORTED_LOCALES } from '@/lib/locale-constants';
import type { LocaleDetectionResult } from '@/lib/locale-detection-types';
import { BaseLocaleDetector } from '@/lib/locale-detector-base';
import type { DetectionSource } from '@/lib/locale-detector-constants';
import { LocaleStorageManager } from '@/lib/locale-storage';
import { logger } from '@/lib/logger';
import type { Locale } from '@/types/i18n';
import {
  CONFIDENCE_WEIGHTS,
  DETECTION_SOURCES,
  QUALITY_THRESHOLDS,
} from '@/lib/locale-detector-constants';

/**
 * Ê£ÄÊµãÁªìÊûúÊé•Âè£
 * Detection result interface
 */
interface DetectionResult {
  locale: Locale;
  source: DetectionSource;
  weight: number;
  confidence: number;
}

/**
 * Êô∫ËÉΩËØ≠Ë®ÄÊ£ÄÊµãÂô®
 * Smart locale detector with advanced algorithms
 */
export class SmartLocaleDetector extends BaseLocaleDetector {
  /**
   * Êô∫ËÉΩÊ£ÄÊµãÁî®Êà∑ËØ≠Ë®ÄÂÅèÂ•Ω
   * Smart detection of user locale preference
   */
  async detectSmartLocale(): Promise<LocaleDetectionResult> {
    // 1. Ê£ÄÊü•Áî®Êà∑ÊâãÂä®ËÆæÁΩÆ (ÊúÄÈ´ò‰ºòÂÖàÁ∫ß)
    const userOverride = LocaleStorageManager.getUserOverride();
    if (userOverride && SUPPORTED_LOCALES.includes(userOverride)) {
      return {
        locale: userOverride,
        source: DETECTION_SOURCES.USER,
        confidence: CONFIDENCE_WEIGHTS.USER_OVERRIDE,
        details: { userOverride },
      };
    }

    // 2. Ê£ÄÊü•Â≠òÂÇ®ÁöÑÁî®Êà∑ÂÅèÂ•Ω
    const userPreference = LocaleStorageManager.getUserPreference();
    if (userPreference && SUPPORTED_LOCALES.includes(userPreference.locale)) {
      return {
        locale: userPreference.locale,
        source: DETECTION_SOURCES.STORED,
        confidence:
          userPreference.confidence || CONFIDENCE_WEIGHTS.STORED_PREFERENCE,
        details: { userOverride: userPreference.locale },
      };
    }

    // 3. Êî∂ÈõÜÊâÄÊúâÊ£ÄÊµãÁªìÊûú
    const detectionResults = await this.collectAllDetectionResults();

    // 4. Â¶ÇÊûúÊ≤°Êúâ‰ªª‰ΩïÊ£ÄÊµãÁªìÊûúÔºåËøîÂõûÈªòËÆ§ËØ≠Ë®Ä
    if (detectionResults.length === ZERO) {
      return {
        locale: DEFAULT_LOCALE,
        source: DETECTION_SOURCES.DEFAULT,
        confidence: CONFIDENCE_WEIGHTS.DEFAULT_FALLBACK,
        details: { fallbackUsed: true },
      };
    }

    // 5. ÂàÜÊûêÊ£ÄÊµãÁªìÊûúÂπ∂ÈÄâÊã©ÊúÄ‰Ω≥ËØ≠Ë®Ä
    const bestResult = this.analyzeBestLocale(detectionResults);

    return bestResult;
  }

  /**
   * Ê£ÄÊµãÊúÄ‰Ω≥ËØ≠Ë®ÄÂÅèÂ•Ω (ÂÖºÂÆπÊñπÊ≥ïÂêç)
   * Detect best locale preference (compatibility method)
   */
  async detectBestLocale(): Promise<LocaleDetectionResult> {
    // 1. Ê£ÄÊü•Â≠òÂÇ®ÁöÑÁî®Êà∑ÂÅèÂ•Ω
    const userPreference = LocaleStorageManager.getUserPreference();
    if (userPreference && SUPPORTED_LOCALES.includes(userPreference.locale)) {
      return {
        locale: userPreference.locale,
        source: DETECTION_SOURCES.STORED,
        confidence:
          userPreference.confidence || CONFIDENCE_WEIGHTS.STORED_PREFERENCE,
        details: { userOverride: userPreference.locale },
      };
    }

    // 2. Ê£ÄÊü•Áî®Êà∑ÊâãÂä®ËÆæÁΩÆ
    const userOverride = LocaleStorageManager.getUserOverride();
    if (userOverride && SUPPORTED_LOCALES.includes(userOverride)) {
      return {
        locale: userOverride,
        source: DETECTION_SOURCES.USER,
        confidence: CONFIDENCE_WEIGHTS.USER_OVERRIDE,
        details: { userOverride },
      };
    }

    // 3. Êåâ‰ºòÂÖàÁ∫ßÈÄê‰∏™Ê£ÄÊµã
    const geoLocale = await this.detectFromGeolocation();
    if (geoLocale !== DEFAULT_LOCALE) {
      return {
        locale: geoLocale,
        source: DETECTION_SOURCES.GEO,
        confidence: CONFIDENCE_WEIGHTS.GEOLOCATION,
        details: { geoLocale },
      };
    }

    const browserLocale = this.detectFromBrowser();
    if (browserLocale !== DEFAULT_LOCALE) {
      return {
        locale: browserLocale,
        source: DETECTION_SOURCES.BROWSER,
        confidence: CONFIDENCE_WEIGHTS.BROWSER_LANGUAGE,
        details: { browserLocale },
      };
    }

    // 4. ‰ΩøÁî®ÈªòËÆ§ËØ≠Ë®Ä
    return {
      locale: DEFAULT_LOCALE,
      source: DETECTION_SOURCES.DEFAULT,
      confidence: CONFIDENCE_WEIGHTS.DEFAULT_FALLBACK,
      details: { fallbackUsed: true },
    };
  }

  /**
   * Êî∂ÈõÜÊâÄÊúâÊ£ÄÊµãÁªìÊûú
   * Collect all detection results
   */
  private async collectAllDetectionResults(): Promise<DetectionResult[]> {
    const results: DetectionResult[] = [];

    try {
      // Âπ∂Ë°åÊâßË°åÊâÄÊúâÊ£ÄÊµãÊñπÊ≥ï
      const [geoLocale, browserLocale, timeZoneLocale, ipLocale] =
        await Promise.allSettled([
          this.detectFromGeolocation(),
          Promise.resolve(this.detectFromBrowser()),
          Promise.resolve(this.detectFromTimeZone()),
          this.detectFromIP(),
        ]);

      // Â§ÑÁêÜÂú∞ÁêÜ‰ΩçÁΩÆÊ£ÄÊµãÁªìÊûú
      if (
        geoLocale.status === 'fulfilled' &&
        geoLocale.value !== DEFAULT_LOCALE
      ) {
        results.push({
          locale: geoLocale.value,
          source: DETECTION_SOURCES.GEO,
          weight: CONFIDENCE_WEIGHTS.GEOLOCATION,
          confidence: CONFIDENCE_WEIGHTS.GEOLOCATION,
        });
      }

      // Â§ÑÁêÜÊµèËßàÂô®ËØ≠Ë®ÄÊ£ÄÊµãÁªìÊûú
      if (
        browserLocale.status === 'fulfilled' &&
        browserLocale.value !== DEFAULT_LOCALE
      ) {
        results.push({
          locale: browserLocale.value,
          source: DETECTION_SOURCES.BROWSER,
          weight: CONFIDENCE_WEIGHTS.BROWSER_LANGUAGE,
          confidence: CONFIDENCE_WEIGHTS.BROWSER_LANGUAGE,
        });
      }

      // Â§ÑÁêÜÊó∂Âå∫Ê£ÄÊµãÁªìÊûú
      if (
        timeZoneLocale.status === 'fulfilled' &&
        timeZoneLocale.value !== DEFAULT_LOCALE
      ) {
        results.push({
          locale: timeZoneLocale.value,
          source: DETECTION_SOURCES.TIMEZONE,
          weight: CONFIDENCE_WEIGHTS.TIMEZONE,
          confidence: CONFIDENCE_WEIGHTS.TIMEZONE,
        });
      }

      // Â§ÑÁêÜIPÊ£ÄÊµãÁªìÊûú
      if (
        ipLocale.status === 'fulfilled' &&
        ipLocale.value !== DEFAULT_LOCALE
      ) {
        results.push({
          locale: ipLocale.value,
          source: DETECTION_SOURCES.GEO, // IPÊ£ÄÊµãÂΩíÁ±ª‰∏∫Âú∞ÁêÜ‰ΩçÁΩÆÊ£ÄÊµã
          weight: CONFIDENCE_WEIGHTS.GEOLOCATION * MAGIC_0_8, // IPÊ£ÄÊµãÊùÉÈáçÁï•‰Ωé‰∫éGPS
          confidence: CONFIDENCE_WEIGHTS.GEOLOCATION * MAGIC_0_8,
        });
      }
    } catch (error) {
      logger.warn('Error collecting detection results:', error);
    }

    return results;
  }

  /**
   * ÂàÜÊûêÊúÄ‰Ω≥ËØ≠Ë®ÄÈÄâÊã©
   * Analyze best locale choice
   */
  private analyzeBestLocale(
    detectionResults: DetectionResult[],
  ): LocaleDetectionResult {
    // ÁªüËÆ°ÊØèÁßçËØ≠Ë®ÄÁöÑÂá∫Áé∞Ê¨°Êï∞ÂíåÊùÉÈáç
    const localeStats = new Map<
      Locale,
      {
        count: number;
        totalWeight: number;
        sources: DetectionSource[];
        maxConfidence: number;
      }
    >();

    for (const result of detectionResults) {
      const current = localeStats.get(result.locale) || {
        count: ZERO,
        totalWeight: ZERO,
        sources: [],
        maxConfidence: ZERO,
      };

      current.count += ONE;
      current.totalWeight += result.weight;
      current.sources.push(result.source);
      current.maxConfidence = Math.max(
        current.maxConfidence,
        result.confidence,
      );

      localeStats.set(result.locale, current);
    }

    // ËÆ°ÁÆóÊØèÁßçËØ≠Ë®ÄÁöÑÁªºÂêàÂæóÂàÜ
    let bestLocale = DEFAULT_LOCALE;
    let bestScore = ZERO;
    let bestSources: DetectionSource[] = [];
    let bestConfidence: number = CONFIDENCE_WEIGHTS.DEFAULT_FALLBACK;

    for (const [locale, stats] of localeStats.entries()) {
      // ÁªºÂêàÂæóÂàÜ = ÊùÉÈáçÊÄªÂíå + ‰∏ÄËá¥ÊÄßÂ•ñÂä±
      const consistencyBonus =
        stats.count > ONE ? QUALITY_THRESHOLDS.CONSISTENCY_BONUS : ZERO;
      const score = stats.totalWeight + consistencyBonus;

      if (score > bestScore) {
        bestScore = score;
        bestLocale = locale;
        bestSources = stats.sources;

        // ËÆ°ÁÆóÊúÄÁªàÁΩÆ‰ø°Â∫¶
        const baseConfidence = Math.min(
          stats.totalWeight / detectionResults.length,
          QUALITY_THRESHOLDS.HIGH_CONFIDENCE,
        );
        bestConfidence = Math.min(baseConfidence + consistencyBonus, ONE);
      }
    }

    // ÊûÑÂª∫ËØ¶ÁªÜ‰ø°ÊÅØ
    const details: Record<string, string | number | boolean> = {};
    for (const result of detectionResults) {
      if (result.source === DETECTION_SOURCES.GEO) {
        details.geoLocale = result.locale;
      } else if (result.source === DETECTION_SOURCES.BROWSER) {
        details.browserLocale = result.locale;
      } else if (result.source === DETECTION_SOURCES.TIMEZONE) {
        details.timeZoneLocale = result.locale;
      }
    }

    // Ê∑ªÂä†ÁªüËÆ°‰ø°ÊÅØ
    details.detectionStats = JSON.stringify({
      totalSources: detectionResults.length,
      consistentSources: localeStats.get(bestLocale)?.count || ZERO,
      score: bestScore,
    });

    return {
      locale: bestLocale,
      source:
        bestSources.length > QUALITY_THRESHOLDS.MIN_SOURCES_FOR_COMBINED
          ? DETECTION_SOURCES.COMBINED
          : bestSources[ZERO] || DETECTION_SOURCES.DEFAULT,
      confidence: bestConfidence,
      details,
    };
  }

  /**
   * Âø´ÈÄüÊ£ÄÊµãËØ≠Ë®Ä (‰ªÖ‰ΩøÁî®Êú¨Âú∞ÊñπÊ≥ï)
   * Quick locale detection (local methods only)
   */
  detectQuickLocale(): LocaleDetectionResult {
    // 1. Ê£ÄÊü•Áî®Êà∑ËÆæÁΩÆ
    const userOverride = LocaleStorageManager.getUserOverride();
    if (userOverride && SUPPORTED_LOCALES.includes(userOverride)) {
      return {
        locale: userOverride,
        source: DETECTION_SOURCES.USER,
        confidence: CONFIDENCE_WEIGHTS.USER_OVERRIDE,
        details: { userOverride },
      };
    }

    // 2. Ê£ÄÊü•ÊµèËßàÂô®ËØ≠Ë®Ä
    const browserLocale = this.detectFromBrowser();
    if (browserLocale !== DEFAULT_LOCALE) {
      return {
        locale: browserLocale,
        source: DETECTION_SOURCES.BROWSER,
        confidence: CONFIDENCE_WEIGHTS.BROWSER_LANGUAGE,
        details: { browserLocale },
      };
    }

    // 3. Ê£ÄÊü•Êó∂Âå∫
    const timeZoneLocale = this.detectFromTimeZone();
    if (timeZoneLocale !== DEFAULT_LOCALE) {
      return {
        locale: timeZoneLocale,
        source: DETECTION_SOURCES.TIMEZONE,
        confidence: CONFIDENCE_WEIGHTS.TIMEZONE,
        details: { timeZoneLocale },
      };
    }

    // 4. ËøîÂõûÈªòËÆ§ËØ≠Ë®Ä
    return {
      locale: DEFAULT_LOCALE,
      source: DETECTION_SOURCES.DEFAULT,
      confidence: CONFIDENCE_WEIGHTS.DEFAULT_FALLBACK,
      details: { fallbackUsed: true },
    };
  }

  /**
   * Ëé∑ÂèñÊ£ÄÊµãË¥®ÈáèËØÑ‰º∞
   * Get detection quality assessment
   */
  getDetectionQuality(result: LocaleDetectionResult): {
    quality: 'high' | 'medium' | 'low';
    reliability: number;
    recommendations: string[];
  } {
    const { confidence, source, details } = result;
    const recommendations: string[] = [];

    // ËØÑ‰º∞Ë¥®ÈáèÁ≠âÁ∫ß
    let quality: 'high' | 'medium' | 'low';
    if (confidence >= QUALITY_THRESHOLDS.HIGH_CONFIDENCE) {
      quality = 'high';
    } else if (confidence >= QUALITY_THRESHOLDS.MEDIUM_CONFIDENCE) {
      quality = 'medium';
    } else {
      quality = 'low';
    }

    // ËÆ°ÁÆóÂèØÈù†ÊÄß
    let reliability = confidence;
    if (source === DETECTION_SOURCES.COMBINED) {
      reliability += MAGIC_0_1; // ÁªÑÂêàÊ£ÄÊµãÊõ¥ÂèØÈù†
    }
    if ((details?.detectionStats?.consistentSources ?? ZERO) > ONE) {
      reliability += DEC_0_05; // ‰∏ÄËá¥ÊÄßÊ£ÄÊµãÊõ¥ÂèØÈù†
    }
    reliability = Math.min(reliability, ONE);

    // ÁîüÊàêÂª∫ËÆÆ
    if (quality === 'low') {
      recommendations.push('Âª∫ËÆÆÁî®Êà∑ÊâãÂä®ÈÄâÊã©ËØ≠Ë®Ä');
      recommendations.push('ÂèØ‰ª•Â∞ùËØïÂêØÁî®Âú∞ÁêÜ‰ΩçÁΩÆÊùÉÈôê‰ª•ÊèêÈ´òÊ£ÄÊµãÂáÜÁ°ÆÊÄß');
    }
    if (source === DETECTION_SOURCES.DEFAULT) {
      recommendations.push('Ê£ÄÊµãÂ§±Ë¥•Ôºå‰ΩøÁî®‰∫ÜÈªòËÆ§ËØ≠Ë®Ä');
      recommendations.push('Âª∫ËÆÆÊ£ÄÊü•ÊµèËßàÂô®ËØ≠Ë®ÄËÆæÁΩÆ');
    }
    if (!details?.geoLocale && quality !== 'high') {
      recommendations.push('Âú∞ÁêÜ‰ΩçÁΩÆÊ£ÄÊµãÊú™ÊàêÂäüÔºåÂèØËÉΩÂΩ±ÂìçÂáÜÁ°ÆÊÄß');
    }

    return { quality, reliability, recommendations };
  }
}

