/**
 * ËÆ°ÁÆóÂ≠òÂÇ®ÊïàÁéá
 * Calculate storage efficiency
 */
export function calculateStorageEfficiency(stats: StorageStats): number {
  // Âü∫‰∫éÂ§ö‰∏™Âõ†Á¥†ËÆ°ÁÆóÊïàÁéáÂàÜÊï∞ (0-1)
  let efficiency = ONE;

  // Êï∞ÊçÆÊñ∞È≤úÂ∫¶ÊùÉÈáç 40%
  efficiency *= MAGIC_0_6 + DEC_0_4 * stats.freshness;

  // Â≠òÂÇ®Âà©Áî®ÁéáÊùÉÈáç 30%
  const maxReasonableSize = BYTES_PER_KB * BYTES_PER_KB; // 1MB
  const sizeEfficiency = Math.min(
    ONE,
    maxReasonableSize / Math.max(stats.totalSize, ONE),
  );
  efficiency *= MAGIC_0_7 + MAGIC_0_3 * sizeEfficiency;

  // ÂéÜÂè≤ËÆ∞ÂΩïË¥®ÈáèÊùÉÈáç 30%
  const maxReasonableEntries = PERCENTAGE_FULL;
  const historyEfficiency = Math.min(
    ONE,
    maxReasonableEntries / Math.max(stats.historyStats.totalEntries, ONE),
  );
  efficiency *= MAGIC_0_7 + MAGIC_0_3 * historyEfficiency;

  return Math.max(ZERO, Math.min(ONE, efficiency));
}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®ÂàÜÊûê‰∫ã‰ª∂ÂíåÊó•ÂøóÁÆ°ÁêÜ
 * Locale Storage Analytics Events and Logging
 *
 * Ë¥üË¥£‰∫ã‰ª∂ÁõëÂê¨„ÄÅËÆøÈóÆÊó•ÂøóÂíåÈîôËØØÊó•ÂøóÁÆ°ÁêÜ
 */

'use client';

import { MAGIC_6 } from "@/constants/count";
import { ANIMATION_DURATION_VERY_SLOW, COUNT_TEN, HOURS_PER_DAY, ONE, PERCENTAGE_FULL, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { DAYS_PER_WEEK } from "@/constants/time";
import { logger } from '@/lib/logger';
import type {
  StorageEvent,
  StorageEventListener,
} from '@/lib/locale-storage-types';

// ==================== ‰∫ã‰ª∂ÁÆ°ÁêÜ ====================

/**
 * ‰∫ã‰ª∂ÁõëÂê¨Âô®ÁÆ°ÁêÜ
 * Event listener management
 */
export class EventManager {
  private static eventListeners: Map<string, StorageEventListener[]> =
    new Map();

  /**
   * Ê∑ªÂä†‰∫ã‰ª∂ÁõëÂê¨Âô®
   * Add event listener
   */
  static addEventListener(
    eventType: string,
    listener: StorageEventListener,
  ): void {
    if (!this.eventListeners.has(eventType)) {
      this.eventListeners.set(eventType, []);
    }
    this.eventListeners.get(eventType)!.push(listener);
  }

  /**
   * ÁßªÈô§‰∫ã‰ª∂ÁõëÂê¨Âô®
   * Remove event listener
   */
  static removeEventListener(
    eventType: string,
    listener: StorageEventListener,
  ): void {
    const listeners = this.eventListeners.get(eventType);
    if (listeners) {
      const index = listeners.indexOf(listener);
      if (index > -ONE) {
        listeners.splice(index, ONE);
      }
    }
  }

  /**
   * ÁßªÈô§ÊâÄÊúâ‰∫ã‰ª∂ÁõëÂê¨Âô®
   * Remove all event listeners
   */
  static removeAllListeners(eventType?: string): void {
    if (eventType) {
      this.eventListeners.delete(eventType);
    } else {
      this.eventListeners.clear();
    }
  }

  /**
   * Ëß¶Âèë‰∫ã‰ª∂
   * Emit event
   */
  static emitEvent(event: StorageEvent): void {
    const listeners = this.eventListeners.get(event.type);
    if (listeners) {
      listeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error('Error in storage event listener:', error);
        }
      });
    }
  }

  /**
   * Ëé∑Âèñ‰∫ã‰ª∂ÁõëÂê¨Âô®Êï∞Èáè
   * Get event listener count
   */
  static getListenerCount(eventType?: string): number {
    if (eventType) {
      return this.eventListeners.get(eventType)?.length || ZERO;
    }

    let total = ZERO;
    for (const listeners of this.eventListeners.values()) {
      total += listeners.length;
    }
    return total;
  }

  /**
   * Ëé∑ÂèñÊâÄÊúâ‰∫ã‰ª∂Á±ªÂûã
   * Get all event types
   */
  static getEventTypes(): string[] {
    return Array.from(this.eventListeners.keys());
  }
}

