/**
 * ÈîôËØØÊó•ÂøóÊù°ÁõÆ
 * Error log entry
 */
export interface ErrorLogEntry {
  error: string;
  timestamp: number;
  context?: string;
  stack?: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
}

/**
 * ÈîôËØØÊó•ÂøóÁÆ°ÁêÜÂô®
 * Error log manager
 */
export class ErrorLogger {
  private static errorLog: ErrorLogEntry[] = [];
  private static readonly MAX_ERROR_ENTRIES = 500;

  /**
   * ËÆ∞ÂΩïÈîôËØØ
   * Log error
   */
  static logError(
    error: string,
    context?: string,
    severity: ErrorLogEntry['severity'] = 'medium',
    stack?: string,
  ): void {
    const errorEntry: ErrorLogEntry = {
      error,
      timestamp: Date.now(),
      severity,
      ...(context !== undefined && { context }),
      ...(stack !== undefined && { stack }),
    };

    this.errorLog.unshift(errorEntry);

    // ÈôêÂà∂ÈîôËØØÊó•ÂøóÊù°ÁõÆÊï∞Èáè
    if (this.errorLog.length > this.MAX_ERROR_ENTRIES) {
      this.errorLog = this.errorLog.slice(ZERO, this.MAX_ERROR_ENTRIES);
    }

    // Ëß¶ÂèëÈîôËØØ‰∫ã‰ª∂
    EventManager.emitEvent({
      type: 'error_occurred',
      timestamp: Date.now(),
      source: 'analytics-events',
      data: errorEntry,
    });

    // ÂØπ‰∫é‰∏•ÈáçÈîôËØØÔºåÈ¢ùÂ§ñÂ§ÑÁêÜ
    if (severity === 'critical') {
      logger.error('Critical storage error:', error, context);
    }
  }

  /**
   * Ëé∑ÂèñÈîôËØØÊó•Âøó
   * Get error log
   */
  static getErrorLog(limit?: number): ErrorLogEntry[] {
    return limit ? this.errorLog.slice(ZERO, limit) : [...this.errorLog];
  }

  /**
   * Ê∏ÖÁêÜÈîôËØØÊó•Âøó
   * Clear error log
   */
  static clearErrorLog(): void {
    this.errorLog = [];
  }

  /**
   * Ëé∑ÂèñÈîôËØØÁªüËÆ°
   * Get error statistics
   */
  static getErrorStats(): {
    totalErrors: number;
    errorRate: number;
    severityDistribution: Record<string, number>;
    recentErrors: ErrorLogEntry[];
    errorTrends: Array<{ date: string; count: number }>;
  } {
    const total = this.errorLog.length;
    const {totalOperations} = AccessLogger.getAccessStats();
    const errorRate = totalOperations > ZERO ? (total / totalOperations) * PERCENTAGE_FULL : ZERO;

    // ÁªüËÆ°‰∏•ÈáçÁ®ãÂ∫¶ÂàÜÂ∏É
    const severityDistribution: Record<string, number> = {
      low: ZERO,
      medium: ZERO,
      high: ZERO,
      critical: ZERO,
    };

    for (const entry of this.errorLog) {
      if (
        entry.severity &&
        severityDistribution[entry.severity] !== undefined
      ) {
        severityDistribution[entry.severity]! += ONE;
      }
    }

    // Ëé∑ÂèñÊúÄËøëÈîôËØØ
    const recentErrors = this.errorLog.slice(ZERO, COUNT_TEN);

    // ËÆ°ÁÆóÈîôËØØË∂ãÂäøÔºàÊåâÂ§©Ôºâ
    const errorTrends = this.calculateErrorTrends();

    return {
      totalErrors: total,
      errorRate,
      severityDistribution,
      recentErrors,
      errorTrends,
    };
  }

  /**
   * ËÆ°ÁÆóÈîôËØØË∂ãÂäø
   * Calculate error trends
   */
  private static calculateErrorTrends(): Array<{
    date: string;
    count: number;
  }> {
    const trends: Record<string, number> = {};
    const now = new Date();

    // ÂàùÂßãÂåñÊúÄËøë7Â§©ÁöÑÊï∞ÊçÆ
    for (let i = MAGIC_6; i >= ZERO; i--) {
      const date = new Date(now);
      date.setDate(date.getDate() - i);
      const dateStr = date.toISOString().split('T')[ZERO] || date.toISOString();
      trends[dateStr] = ZERO;
    }

    // ÁªüËÆ°ÈîôËØØÊï∞Èáè
    for (const entry of this.errorLog) {
      const date = new Date(entry.timestamp);
      const dateStr = date.toISOString().split('T')[ZERO] || date.toISOString();
      if (Object.prototype.hasOwnProperty.call(trends, dateStr)) {
        trends[dateStr] = (trends[dateStr] || ZERO) + ONE;
      }
    }

    return Object.entries(trends).map(([date, count]) => ({ date, count }));
  }
}

