/**
 * Ê∏ÖÁêÜÂàÜÊûêÊï∞ÊçÆ
 * Cleanup analytics data
 */
export function cleanupAnalyticsData(
  maxAge: number = DAYS_PER_WEEK * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW,
): void {
  const cutoffTime = Date.now() - maxAge;

  // Ê∏ÖÁêÜËÆøÈóÆÊó•Âøó
  const accessLog = AccessLogger.getAccessLog();
  const filteredAccessLog = accessLog.filter(
    (entry) => entry.timestamp > cutoffTime,
  );
  AccessLogger.clearAccessLog();
  filteredAccessLog.forEach((entry) => {
    AccessLogger.logAccess(
      entry.key,
      entry.operation,
      entry.success,
      entry.responseTime,
      entry.error,
    );
  });

  // Ê∏ÖÁêÜÈîôËØØÊó•Âøó
  const errorLog = ErrorLogger.getErrorLog();
  const filteredErrorLog = errorLog.filter(
    (entry) => entry.timestamp > cutoffTime,
  );
  ErrorLogger.clearErrorLog();
  filteredErrorLog.forEach((entry) => {
    ErrorLogger.logError(
      entry.error,
      entry.context,
      entry.severity,
      entry.stack,
    );
  });

  // Ëß¶ÂèëÊ∏ÖÁêÜ‰∫ã‰ª∂
  EventManager.emitEvent({
    type: 'cache_cleared',
    timestamp: Date.now(),
    source: 'analytics-events',
    data: {
      maxAge,
      cutoffTime,
      accessLogCleaned: accessLog.length - filteredAccessLog.length,
      errorLogCleaned: errorLog.length - filteredErrorLog.length,
    },
  });
}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®ÂàÜÊûêÊÄßËÉΩÂíåË∂ãÂäøÂàÜÊûê
 * Locale Storage Analytics Performance and Trends
 *
 * Ë¥üË¥£ÊÄßËÉΩÊåáÊ†áÂàÜÊûê„ÄÅ‰ΩøÁî®Ê®°ÂºèËØÜÂà´ÂíåË∂ãÂäøÂàÜÊûê
 */

'use client';

import { COUNT_23, MAGIC_12, MAGIC_17, MAGIC_18, MAGIC_6, MAGIC_9, MAGIC_95, MAGIC_99 } from "@/constants/count";
import { DEC_0_75, MAGIC_0_2, MAGIC_0_25, MAGIC_0_3, MAGIC_0_7, MAGIC_0_8 } from "@/constants/decimal";
import { ANIMATION_DURATION_VERY_SLOW, COUNT_FIVE, COUNT_PAIR, COUNT_TEN, COUNT_TRIPLE, DAYS_PER_MONTH, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { DAYS_PER_WEEK } from "@/constants/time";
import { calculateStorageStats } from '@/lib/locale-storage-analytics-core';
import type { StorageStats } from '@/lib/locale-storage-types';
import {
  AccessLogger,
  ErrorLogger,
  type AccessLogEntry,
} from '@/lib/locale-storage-analytics-events';

// ==================== ‰ΩøÁî®Ê®°ÂºèÂàÜÊûê ====================

/**
 * ‰ΩøÁî®Ê®°ÂºèÂàÜÊûêÁªìÊûú
 * Usage pattern analysis result
 */
export interface UsagePatterns {
  mostAccessedKeys: Array<{ key: string; count: number }>;
  leastAccessedKeys: Array<{ key: string; count: number }>;
  peakUsageHours: Array<{ hour: number; count: number }>;
  operationDistribution: Record<string, number>;
  averageSessionDuration: number;
  userBehaviorInsights: string[];
}

/**
 * Ëé∑Âèñ‰ΩøÁî®Ê®°Âºè
 * Get usage patterns
 */
export function getUsagePatterns(): UsagePatterns {
  const accessLog = AccessLogger.getAccessLog();
  const accessStats = AccessLogger.getAccessStats();

  // ÂàÜÊûêÊúÄÂ∏∏ËÆøÈóÆÁöÑÈîÆ
  const keyEntries = Object.entries(accessStats.keyCounts)
    .map(([key, count]) => ({ key, count }))
    .sort((a, b) => b.count - a.count);

  const mostAccessedKeys = keyEntries.slice(ZERO, COUNT_FIVE);
  const leastAccessedKeys = keyEntries.slice(-COUNT_FIVE).reverse();

  // ÂàÜÊûêÂ≥∞ÂÄº‰ΩøÁî®Êó∂Èó¥
  const hourlyUsage: Record<number, number> = {};
  for (const entry of accessLog) {
    const hour = new Date(entry.timestamp).getHours();
    hourlyUsage[hour] = (hourlyUsage[hour] || ZERO) + ONE;
  }

  const peakUsageHours = Object.entries(hourlyUsage)
    .map(([hour, count]) => ({ hour: parseInt(hour), count }))
    .sort((a, b) => b.count - a.count)
    .slice(ZERO, COUNT_FIVE);

  // ËÆ°ÁÆóÂπ≥Âùá‰ºöËØùÊåÅÁª≠Êó∂Èó¥
  const averageSessionDuration = calculateAverageSessionDuration(accessLog);

  // ÁîüÊàêÁî®Êà∑Ë°å‰∏∫Ê¥ûÂØü
  const userBehaviorInsights = generateBehaviorInsights(
    accessStats,
    peakUsageHours,
    averageSessionDuration,
  );

  return {
    mostAccessedKeys,
    leastAccessedKeys,
    peakUsageHours,
    operationDistribution: accessStats.operationCounts,
    averageSessionDuration,
    userBehaviorInsights,
  };
}

/**
 * ËÆ°ÁÆóÂπ≥Âùá‰ºöËØùÊåÅÁª≠Êó∂Èó¥
 * Calculate average session duration
 */
function calculateAverageSessionDuration(accessLog: AccessLogEntry[]): number {
  if (accessLog.length < COUNT_PAIR) return ZERO;

  const sessions: number[] = [];
  let sessionStart = accessLog[accessLog.length - ONE]?.timestamp ?? Date.now();
  let lastActivity = sessionStart;

  // ‰ºöËØùÈó¥ÈöîÈòàÂÄºÔºö30ÂàÜÈíü
  const sessionGap = DAYS_PER_MONTH * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW;

  for (let i = accessLog.length - COUNT_PAIR; i >= ZERO; i--) {
    const currentTime = accessLog[i]?.timestamp ?? Date.now();

    if (currentTime - lastActivity > sessionGap) {
      // Êñ∞‰ºöËØùÂºÄÂßã
      sessions.push(lastActivity - sessionStart);
      sessionStart = currentTime;
    }

    lastActivity = currentTime;
  }

  // Ê∑ªÂä†ÊúÄÂêé‰∏Ä‰∏™‰ºöËØù
  sessions.push(lastActivity - sessionStart);

  return sessions.length > ZERO
    ? sessions.reduce((sum, duration) => sum + duration, ZERO) / sessions.length
    : ZERO;
}

/**
 * ÁîüÊàêË°å‰∏∫Ê¥ûÂØü
 * Generate behavior insights
 */
function generateBehaviorInsights(
  accessStats: ReturnType<typeof AccessLogger.getAccessStats>,
  peakUsageHours: Array<{ hour: number; count: number }>,
  averageSessionDuration: number,
): string[] {
  const insights: string[] = [];

  // ÊàêÂäüÁéáÂàÜÊûê
  if (accessStats.successRate < MAGIC_95) {
    insights.push(
      `Â≠òÂÇ®Êìç‰ΩúÊàêÂäüÁéáËæÉ‰Ωé (${accessStats.successRate.toFixed(ONE)}%)ÔºåÂª∫ËÆÆÊ£ÄÊü•Â≠òÂÇ®ÈÖçÁΩÆ`,
    );
  } else if (accessStats.successRate > MAGIC_99) {
    insights.push('Â≠òÂÇ®Êìç‰ΩúÊàêÂäüÁéá‰ºòÁßÄÔºåÁ≥ªÁªüËøêË°åÁ®≥ÂÆö');
  }

  // ÂìçÂ∫îÊó∂Èó¥ÂàÜÊûê
  if (accessStats.averageResponseTime > PERCENTAGE_FULL) {
    insights.push(
      `Âπ≥ÂùáÂìçÂ∫îÊó∂Èó¥ËæÉÊÖ¢ (${accessStats.averageResponseTime.toFixed(ONE)}ms)ÔºåÂèØËÉΩÈúÄË¶Å‰ºòÂåñ`,
    );
  } else if (accessStats.averageResponseTime < COUNT_TEN) {
    insights.push('ÂìçÂ∫îÊó∂Èó¥‰ºòÁßÄÔºåÂ≠òÂÇ®ÊÄßËÉΩËâØÂ•Ω');
  }

  // ‰ΩøÁî®Êó∂Èó¥Ê®°ÂºèÂàÜÊûê
  if (peakUsageHours.length > ZERO) {
    const peakHour = peakUsageHours[ZERO]?.hour ?? MAGIC_12;
    if (peakHour >= MAGIC_9 && peakHour <= MAGIC_17) {
      insights.push('‰∏ªË¶ÅÂú®Â∑•‰ΩúÊó∂Èó¥‰ΩøÁî®ÔºåÁ¨¶ÂêàÂäûÂÖ¨Âú∫ÊôØ');
    } else if (peakHour >= MAGIC_18 && peakHour <= COUNT_23) {
      insights.push('‰∏ªË¶ÅÂú®ÊôöÈó¥‰ΩøÁî®ÔºåÂèØËÉΩ‰∏∫‰∏™‰∫∫Áî®Êà∑');
    }
  }

  // ‰ºöËØùÊåÅÁª≠Êó∂Èó¥ÂàÜÊûê
  const sessionMinutes = averageSessionDuration / (SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW);
  if (sessionMinutes > SECONDS_PER_MINUTE) {
    insights.push('Áî®Êà∑‰ºöËØùÊó∂Èó¥ËæÉÈïøÔºåË°®ÊòéÊ∑±Â∫¶‰ΩøÁî®');
  } else if (sessionMinutes < COUNT_FIVE) {
    insights.push('Áî®Êà∑‰ºöËØùÊó∂Èó¥ËæÉÁü≠ÔºåÂèØËÉΩ‰∏∫Âø´ÈÄüËÆøÈóÆ');
  }

  // Êìç‰ΩúÁ±ªÂûãÂàÜÊûê
  const operations = Object.entries(accessStats.operationCounts);
  const totalOps = operations.reduce((sum, [, count]) => sum + count, ZERO);

  for (const [operation, count] of operations) {
    const percentage = (count / totalOps) * PERCENTAGE_FULL;
    if (percentage > PERCENTAGE_HALF) {
      insights.push(`${operation}Êìç‰ΩúÂç†‰∏ªÂØºÂú∞‰Ωç (${percentage.toFixed(ONE)}%)`);
    }
  }

  return insights;
}

// ==================== ÊÄßËÉΩÊåáÊ†áÂàÜÊûê ====================

/**
 * ÊÄßËÉΩÊåáÊ†á
 * Performance metrics
 */
export interface PerformanceMetrics {
  averageResponseTime: number;
  successRate: number;
  errorRate: number;
  throughput: number;
  efficiency: number;
  recommendations: string[];
}

/**
 * Ëé∑ÂèñÊÄßËÉΩÊåáÊ†á
 * Get performance metrics
 */
export function getPerformanceMetrics(): PerformanceMetrics {
  const accessStats = AccessLogger.getAccessStats();
  const errorStats = ErrorLogger.getErrorStats();
  const stats = calculateStorageStats();

  // ËÆ°ÁÆóÂêûÂêêÈáè (Êìç‰ΩúÊï∞/Â∞èÊó∂)
  const oneHourAgo = Date.now() - SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW;
  const recentOperations = AccessLogger.getAccessLog().filter(
    (entry) => entry.timestamp > oneHourAgo,
  ).length;
  const throughput = recentOperations; // ÊØèÂ∞èÊó∂Êìç‰ΩúÊï∞

  // ËÆ°ÁÆóÊïàÁéáÂàÜÊï∞
  const efficiency = calculateEfficiencyScore(accessStats, errorStats, stats);

  // ÁîüÊàêÊÄßËÉΩÂª∫ËÆÆ
  const recommendations = generatePerformanceRecommendations({
    errorRate: errorStats.errorRate,
    responseTime: accessStats.averageResponseTime,
    successRate: accessStats.successRate,
    efficiency,
  });

  return {
    averageResponseTime: accessStats.averageResponseTime,
    successRate: accessStats.successRate,
    errorRate: errorStats.errorRate,
    throughput,
    efficiency,
    recommendations,
  };
}

/**
 * ËÆ°ÁÆóÊïàÁéáÂàÜÊï∞
 * Calculate efficiency score
 */
function calculateEfficiencyScore(
  accessStats: ReturnType<typeof AccessLogger.getAccessStats>,
  errorStats: ReturnType<typeof ErrorLogger.getErrorStats>,
  storageStats: StorageStats,
): number {
  let score = ONE;

  // ÊàêÂäüÁéáÊùÉÈáç 30%
  score *= MAGIC_0_7 + MAGIC_0_3 * (accessStats.successRate / PERCENTAGE_FULL);

  // ÂìçÂ∫îÊó∂Èó¥ÊùÉÈáç 25%
  const responseTimeScore = Math.max(
    ZERO,
    ONE - accessStats.averageResponseTime / 1000,
  );
  score *= DEC_0_75 + MAGIC_0_25 * responseTimeScore;

  // ÈîôËØØÁéáÊùÉÈáç 25%
  const errorRateScore = Math.max(ZERO, ONE - errorStats.errorRate / PERCENTAGE_FULL);
  score *= DEC_0_75 + MAGIC_0_25 * errorRateScore;

  // Êï∞ÊçÆÊñ∞È≤úÂ∫¶ÊùÉÈáç 20%
  score *= MAGIC_0_8 + MAGIC_0_2 * storageStats.freshness;

  return Math.max(ZERO, Math.min(ONE, score));
}

/**
 * ÁîüÊàêÊÄßËÉΩÂª∫ËÆÆ
 * Generate performance recommendations
 */
function generatePerformanceRecommendations(metrics: {
  errorRate: number;
  responseTime: number;
  successRate: number;
  efficiency: number;
}): string[] {
  const recommendations: string[] = [];

  if (metrics.errorRate > COUNT_FIVE) {
    recommendations.push('ÈîôËØØÁéáËøáÈ´òÔºåÂª∫ËÆÆÊ£ÄÊü•Â≠òÂÇ®Êìç‰ΩúÈÄªËæë');
  }

  if (metrics.responseTime > PERCENTAGE_FULL) {
    recommendations.push('ÂìçÂ∫îÊó∂Èó¥ËæÉÊÖ¢ÔºåËÄÉËôë‰ºòÂåñÂ≠òÂÇ®ËÆøÈóÆÊñπÂºè');
  }

  if (metrics.successRate < MAGIC_95) {
    recommendations.push('ÊàêÂäüÁéáÂÅè‰ΩéÔºåÂª∫ËÆÆÂ¢ûÂä†ÈîôËØØÂ§ÑÁêÜÂíåÈáçËØïÊú∫Âà∂');
  }

  if (metrics.efficiency < MAGIC_0_7) {
    recommendations.push('Êï¥‰ΩìÊïàÁéáÂÅè‰ΩéÔºåÂª∫ËÆÆÂÖ®Èù¢‰ºòÂåñÂ≠òÂÇ®Á≠ñÁï•');
  }

  if (recommendations.length === ZERO) {
    recommendations.push('ÊÄßËÉΩË°®Áé∞ËâØÂ•ΩÔºåÂª∫ËÆÆÁªßÁª≠ÁõëÊéß');
  }

  return recommendations;
}

// ==================== Ë∂ãÂäøÂàÜÊûê ====================

/**
 * ‰ΩøÁî®Ë∂ãÂäø
 * Usage trends
 */
export interface UsageTrends {
  dailyOperations: Array<{ date: string; operations: number }>;
  weeklyGrowth: number;
  monthlyGrowth: number;
  seasonalPatterns: Array<{ period: string; avgOperations: number }>;
  predictions: Array<{ date: string; predictedOperations: number }>;
}

/**
 * Ëé∑Âèñ‰ΩøÁî®Ë∂ãÂäø
 * Get usage trends
 */
export function getUsageTrends(days: number = DAYS_PER_WEEK): UsageTrends {
  const accessLog = AccessLogger.getAccessLog();

  // ËÆ°ÁÆóÊØèÊó•Êìç‰ΩúÊï∞
  const dailyOperations = calculateDailyOperations(accessLog, days);

  // ËÆ°ÁÆóÂ¢ûÈïøÁéá
  const weeklyGrowth = calculateGrowthRate(dailyOperations, DAYS_PER_WEEK);
  const monthlyGrowth = calculateGrowthRate(dailyOperations, DAYS_PER_MONTH);

  // ÂàÜÊûêÂ≠£ËäÇÊÄßÊ®°Âºè
  const seasonalPatterns = analyzeSeasonalPatterns(accessLog);

  // ÁîüÊàêÈ¢ÑÊµã
  const predictions = generatePredictions(dailyOperations, COUNT_TRIPLE);

  return {
    dailyOperations,
    weeklyGrowth,
    monthlyGrowth,
    seasonalPatterns,
    predictions,
  };
}

/**
 * ËÆ°ÁÆóÊØèÊó•Êìç‰ΩúÊï∞
 * Calculate daily operations
 */
function calculateDailyOperations(
  accessLog: AccessLogEntry[],
  days: number,
): Array<{ date: string; operations: number }> {
  const dailyOps: Record<string, number> = {};
  const now = new Date();

  // ÂàùÂßãÂåñÊó•Êúü
  for (let i = days - ONE; i >= ZERO; i--) {
    const date = new Date(now);
    date.setDate(date.getDate() - i);
    const dateStr = date.toISOString().split('T')[ZERO] || date.toISOString();
    dailyOps[dateStr] = ZERO;
  }

  // ÁªüËÆ°Êìç‰ΩúÊï∞
  for (const entry of accessLog) {
    const date = new Date(entry.timestamp);
    const dateStr = date.toISOString().split('T')[ZERO] || date.toISOString();
    if (Object.prototype.hasOwnProperty.call(dailyOps, dateStr)) {
      dailyOps[dateStr] = (dailyOps[dateStr] || ZERO) + ONE;
    }
  }

  return Object.entries(dailyOps).map(([date, operations]) => ({
    date,
    operations,
  }));
}

/**
 * ËÆ°ÁÆóÂ¢ûÈïøÁéá
 * Calculate growth rate
 */
function calculateGrowthRate(
  dailyOperations: Array<{ date: string; operations: number }>,
  period: number,
): number {
  if (dailyOperations.length < period) return ZERO;

  const recent = dailyOperations.slice(-period);
  const previous = dailyOperations.slice(-period * COUNT_PAIR, -period);

  if (previous.length === ZERO) return ZERO;

  const recentAvg =
    recent.reduce((sum, day) => sum + day.operations, ZERO) / recent.length;
  const previousAvg =
    previous.reduce((sum, day) => sum + day.operations, ZERO) / previous.length;

  return previousAvg > ZERO ? ((recentAvg - previousAvg) / previousAvg) * PERCENTAGE_FULL : ZERO;
}

/**
 * ÂàÜÊûêÂ≠£ËäÇÊÄßÊ®°Âºè
 * Analyze seasonal patterns
 */
function analyzeSeasonalPatterns(
  accessLog: AccessLogEntry[],
): Array<{ period: string; avgOperations: number }> {
  const patterns: Record<string, number[]> = {
    Â∑•‰ΩúÊó•: [],
    Âë®Êú´: [],
    ‰∏äÂçà: [],
    ‰∏ãÂçà: [],
    Êôö‰∏ä: [],
  };

  for (const entry of accessLog) {
    const date = new Date(entry.timestamp);
    const dayOfWeek = date.getDay();
    const hour = date.getHours();

    // Â∑•‰ΩúÊó• vs Âë®Êú´
    if (dayOfWeek >= ONE && dayOfWeek <= COUNT_FIVE) {
      patterns['Â∑•‰ΩúÊó•']?.push(ONE);
    } else {
      patterns['Âë®Êú´']?.push(ONE);
    }

    // Êó∂Èó¥ÊÆµ
    if (hour >= MAGIC_6 && hour < MAGIC_12) {
      patterns['‰∏äÂçà']!.push(ONE);
    } else if (hour >= MAGIC_12 && hour < MAGIC_18) {
      patterns['‰∏ãÂçà']!.push(ONE);
    } else {
      patterns['Êôö‰∏ä']!.push(ONE);
    }
  }

  return Object.entries(patterns).map(([period, operations]) => ({
    period,
    avgOperations: operations.length,
  }));
}

/**
 * ÁîüÊàêÈ¢ÑÊµã
 * Generate predictions
 */
function generatePredictions(
  dailyOperations: Array<{ date: string; operations: number }>,
  futureDays: number,
): Array<{ date: string; predictedOperations: number }> {
  if (dailyOperations.length < COUNT_TRIPLE) {
    return [];
  }

  // ÁÆÄÂçïÁ∫øÊÄßË∂ãÂäøÈ¢ÑÊµã
  const recent = dailyOperations.slice(-DAYS_PER_WEEK); // ‰ΩøÁî®ÊúÄËøë7Â§©Êï∞ÊçÆ
  const avgOperations =
    recent.reduce((sum, day) => sum + day.operations, ZERO) / recent.length;

  // ËÆ°ÁÆóË∂ãÂäøÊñúÁéá
  const trend =
    recent.length > ONE
      ? (recent[recent.length - ONE]!.operations - recent[ZERO]!.operations) /
        (recent.length - ONE)
      : ZERO;

  const predictions: Array<{ date: string; predictedOperations: number }> = [];

  for (let i = ONE; i <= futureDays; i++) {
    const futureDate = new Date();
    futureDate.setDate(futureDate.getDate() + i);
    const dateStr =
      futureDate.toISOString().split('T')[ZERO] || futureDate.toISOString();

    const predictedOperations = Math.max(
      ZERO,
      Math.round(avgOperations + trend * i),
    );

    predictions.push({
      date: dateStr,
      predictedOperations,
    });
  }

  return predictions;
}

/**
