/**
 * ÁºìÂ≠òÊù°ÁõÆ
 * Cache entry
 */
interface CacheEntry {
  data: unknown;
  timestamp: number;
}

/**
 * ÁºìÂ≠òÁÆ°ÁêÜÂô®
 * Cache manager
 */
export class CacheManager {
  private static metricsCache: Map<string, CacheEntry> = new Map();
  private static readonly CACHE_TTL = COUNT_FIVE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW; // 5 minutes

  /**
   * Ëé∑ÂèñÁºìÂ≠òÁöÑÊåáÊ†á
   * Get cached metrics
   */
  static getCachedMetrics(key: string): unknown | null {
    const cached = this.metricsCache.get(key);
    if (cached && Date.now() - cached.timestamp < this.CACHE_TTL) {
      return cached.data;
    }
    return null;
  }

  /**
   * ËÆæÁΩÆÁºìÂ≠òÁöÑÊåáÊ†á
   * Set cached metrics
   */
  static setCachedMetrics(key: string, data: unknown): void {
    this.metricsCache.set(key, {
      data,
      timestamp: Date.now(),
    });
  }

  /**
   * ‰ΩøÁºìÂ≠òÂ§±Êïà
   * Invalidate cache
   */
  static invalidateCache(key?: string): void {
    if (key) {
      this.metricsCache.delete(key);
    } else {
      this.metricsCache.clear();
    }
  }

  /**
   * Ê∏ÖÁêÜËøáÊúüÁºìÂ≠ò
   * Clean expired cache
   */
  static cleanExpiredCache(): void {
    const now = Date.now();
    for (const [key, entry] of this.metricsCache.entries()) {
      if (now - entry.timestamp >= this.CACHE_TTL) {
        this.metricsCache.delete(key);
      }
    }
  }

  /**
   * Ëé∑ÂèñÁºìÂ≠òÁªüËÆ°
   * Get cache statistics
   */
  static getCacheStats(): {
    totalEntries: number;
    validEntries: number;
    expiredEntries: number;
    hitRate: number;
    memoryUsage: number;
  } {
    const now = Date.now();
    let validEntries = ZERO;
    let expiredEntries = ZERO;
    let memoryUsage = ZERO;

    for (const [_key, entry] of this.metricsCache.entries()) {
      if (now - entry.timestamp < this.CACHE_TTL) {
        validEntries += ONE;
      } else {
        expiredEntries += ONE;
      }

      // ‰º∞ÁÆóÂÜÖÂ≠ò‰ΩøÁî®Èáè
      memoryUsage += JSON.stringify(entry).length * COUNT_PAIR; // Á≤óÁï•‰º∞ÁÆó
    }

    // ÁÆÄÂçïÁöÑÂëΩ‰∏≠ÁéáËÆ°ÁÆóÔºàÂü∫‰∫éÊúâÊïàÊù°ÁõÆÊØî‰æãÔºâ
    const totalEntries = validEntries + expiredEntries;
    const hitRate = totalEntries > ZERO ? (validEntries / totalEntries) * PERCENTAGE_FULL : ZERO;

    return {
      totalEntries,
      validEntries,
      expiredEntries,
      hitRate,
      memoryUsage,
    };
  }
}

