/**
 * È™åËØÅÂàÜÊûêÊï∞ÊçÆÂÆåÊï¥ÊÄß
 * Validate analytics data integrity
 */
export function validateAnalyticsData(): {
  isValid: boolean;
  issues: string[];
  recommendations: string[];
} {
  const issues: string[] = [];
  const recommendations: string[] = [];

  try {
    // Ê£ÄÊü•Âü∫Á°ÄÊï∞ÊçÆ
    const stats = getStorageStats();
    if (!stats.success) {
      issues.push('Êó†Ê≥ïËé∑ÂèñÂ≠òÂÇ®ÁªüËÆ°‰ø°ÊÅØ');
    }

    const healthCheck = performHealthCheck();
    if (!healthCheck.success) {
      issues.push('Êó†Ê≥ïÊâßË°åÂÅ•Â∫∑Ê£ÄÊü•');
    }

    // Ê£ÄÊü•Êó•ÂøóÊï∞ÊçÆ
    const accessLog = AccessLogger.getAccessLog();
    const errorLog = ErrorLogger.getErrorLog();

    if (accessLog.length === ZERO) {
      issues.push('ËÆøÈóÆÊó•Âøó‰∏∫Á©∫');
      recommendations.push('ÂºÄÂßãËÆ∞ÂΩïÂ≠òÂÇ®Êìç‰Ωú‰ª•Êî∂ÈõÜÂàÜÊûêÊï∞ÊçÆ');
    }

    if (errorLog.length > accessLog.length * MAGIC_0_1) {
      issues.push('ÈîôËØØÁéáËøáÈ´ò');
      recommendations.push('Ê£ÄÊü•Â≠òÂÇ®Êìç‰ΩúÈÄªËæëÔºåÂáèÂ∞ëÈîôËØØÂèëÁîü');
    }

    // Ê£ÄÊü•Êï∞ÊçÆÊó∂ÊïàÊÄß
    const now = Date.now();
    const oneHourAgo = now - SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW;
    const recentAccess = accessLog.filter(
      (entry) => entry.timestamp > oneHourAgo,
    );

    if (recentAccess.length === ZERO && accessLog.length > ZERO) {
      issues.push('ÊúÄËøë‰∏ÄÂ∞èÊó∂Êó†Ê¥ªÂä®ËÆ∞ÂΩï');
      recommendations.push('Ê£ÄÊü•Â∫îÁî®ÊòØÂê¶Ê≠£Â∏∏ËøêË°å');
    }

    // Ê£ÄÊü•ÁºìÂ≠òÁä∂ÊÄÅ
    const cacheStats = CacheManager.getCacheStats();
    if (cacheStats.expiredEntries > cacheStats.validEntries) {
      recommendations.push('Ê∏ÖÁêÜËøáÊúüÁºìÂ≠ò‰ª•ÊèêÈ´òÊÄßËÉΩ');
    }
  } catch (error) {
    issues.push(
      `Êï∞ÊçÆÈ™åËØÅËøáÁ®ã‰∏≠ÂèëÁîüÈîôËØØ: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
    );
  }

  return {
    isValid: issues.length === ZERO,
    issues,
    recommendations,
  };
}

