/**
 * ÂéãÁº©ÂàÜÊûêÊï∞ÊçÆ
 * Compress analytics data
 */
export function compressAnalyticsData(): {
  originalSize: number;
  compressedSize: number;
  compressionRatio: number;
  compressedData: string;
} {
  const originalData = exportAnalyticsDataAsJson();
  const originalSize = originalData.length;

  // ÁÆÄÂçïÁöÑÊï∞ÊçÆÂéãÁº©ÔºöÁßªÈô§‰∏çÂøÖË¶ÅÁöÑÁ©∫ÁôΩÂíåÈáçÂ§ç‰ø°ÊÅØ
  const compressedData = JSON.stringify(exportAnalyticsData());
  const compressedSize = compressedData.length;

  const compressionRatio =
    originalSize > ZERO ? (ONE - compressedSize / originalSize) * PERCENTAGE_FULL : ZERO;

  return {
    originalSize,
    compressedSize,
    compressionRatio,
    compressedData,
  };
}

/**
 * ‰ºòÂåñÂàÜÊûêÊï∞ÊçÆÂ≠òÂÇ®
 * Optimize analytics data storage
 */
export function optimizeAnalyticsStorage(): {
  beforeOptimization: {
    accessLogSize: number;
    errorLogSize: number;
    cacheSize: number;
  };
  afterOptimization: {
    accessLogSize: number;
    errorLogSize: number;
    cacheSize: number;
  };
  optimizationResults: string[];
} {
  const results: string[] = [];

  // ËÆ∞ÂΩï‰ºòÂåñÂâçÁöÑÁä∂ÊÄÅ
  const beforeAccessLog = AccessLogger.getAccessLog();
  const beforeErrorLog = ErrorLogger.getErrorLog();
  const beforeCacheStats = CacheManager.getCacheStats();

  const before = {
    accessLogSize: beforeAccessLog.length,
    errorLogSize: beforeErrorLog.length,
    cacheSize: beforeCacheStats.totalEntries,
  };

  // ÊâßË°å‰ºòÂåñ

  // 1. Ê∏ÖÁêÜËøáÊúüÁºìÂ≠ò
  CacheManager.cleanExpiredCache();
  results.push('Ê∏ÖÁêÜ‰∫ÜËøáÊúüÁºìÂ≠ò');

  // 2. ÂéãÁº©ËÆøÈóÆÊó•ÂøóÔºà‰øùÁïôÊúÄËøë1000Êù°Ôºâ
  if (beforeAccessLog.length > ANIMATION_DURATION_VERY_SLOW) {
    const recentAccessLog = beforeAccessLog.slice(ZERO, ANIMATION_DURATION_VERY_SLOW);
    AccessLogger.clearAccessLog();
    recentAccessLog.forEach((entry) => {
      AccessLogger.logAccess(
        entry.key,
        entry.operation,
        entry.success,
        entry.responseTime,
        entry.error,
      );
    });
    results.push(`ÂéãÁº©ËÆøÈóÆÊó•ÂøóÔºö${beforeAccessLog.length} -> 1000 Êù°`);
  }

  // 3. ÂéãÁº©ÈîôËØØÊó•ÂøóÔºà‰øùÁïôÊúÄËøë500Êù°Ôºâ
  if (beforeErrorLog.length > 500) {
    const recentErrorLog = beforeErrorLog.slice(ZERO, 500);
    ErrorLogger.clearErrorLog();
    recentErrorLog.forEach((entry) => {
      ErrorLogger.logError(
        entry.error,
        entry.context,
        entry.severity,
        entry.stack,
      );
    });
    results.push(`ÂéãÁº©ÈîôËØØÊó•ÂøóÔºö${beforeErrorLog.length} -> 500 Êù°`);
  }

  // ËÆ∞ÂΩï‰ºòÂåñÂêéÁöÑÁä∂ÊÄÅ
  const afterAccessLog = AccessLogger.getAccessLog();
  const afterErrorLog = ErrorLogger.getErrorLog();
  const afterCacheStats = CacheManager.getCacheStats();

  const after = {
    accessLogSize: afterAccessLog.length,
    errorLogSize: afterErrorLog.length,
    cacheSize: afterCacheStats.totalEntries,
  };

  if (results.length === ZERO) {
    results.push('Êó†ÈúÄ‰ºòÂåñÔºåÊï∞ÊçÆÂ∑≤Â§Ñ‰∫éÊúÄ‰Ω≥Áä∂ÊÄÅ');
  }

  return {
    beforeOptimization: before,
    afterOptimization: after,
    optimizationResults: results,
  };
}

