/**
 * Ê†ºÂºèÂåñÂ≠óËäÇÂ§ßÂ∞è
 * Format byte size
 */
export function formatByteSize(bytes: number): string {
  const units = ['B', 'KB', 'MB', 'GB'];
  let size = bytes;
  let unitIndex = ZERO;

  while (size >= BYTES_PER_KB && unitIndex < units.length - ONE) {
    size /= BYTES_PER_KB;
    unitIndex += ONE;
  }

  return `${size.toFixed(COUNT_PAIR)} ${units[unitIndex]}`;
}

/**
 * Ê†ºÂºèÂåñÊåÅÁª≠Êó∂Èó¥
 * Format duration
 */
export function formatDuration(milliseconds: number): string {
  const seconds = Math.floor(milliseconds / ANIMATION_DURATION_VERY_SLOW);
  const minutes = Math.floor(seconds / SECONDS_PER_MINUTE);
  const hours = Math.floor(minutes / SECONDS_PER_MINUTE);
  const days = Math.floor(hours / HOURS_PER_DAY);

  if (days > ZERO) {
    return `${days}Â§© ${hours % HOURS_PER_DAY}Â∞èÊó∂`;
  }
  if (hours > ZERO) {
    return `${hours}Â∞èÊó∂ ${minutes % SECONDS_PER_MINUTE}ÂàÜÈíü`;
  }
  if (minutes > ZERO) {
    return `${minutes}ÂàÜÈíü ${seconds % SECONDS_PER_MINUTE}Áßí`;
  }
  return `${seconds}Áßí`;
}

/**
 * Ê†ºÂºèÂåñÁôæÂàÜÊØî
 * Format percentage
 */
export function formatPercentage(value: number, decimals: number = ONE): string {
  return `${value.toFixed(decimals)}%`;
}

/**
 * ÁîüÊàêÂîØ‰∏ÄID
 * Generate unique ID
 */
export function generateUniqueId(): string {
  return `analytics_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
}

