import { MAGIC_0_8 } from "@/constants/decimal";
import { CACHE_DURATIONS } from '@/constants/i18n-constants';
import { ANIMATION_DURATION_VERY_SLOW, HOURS_PER_DAY, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";

/**
 * Cookie ÈÖçÁΩÆÂ∏∏Èáè
 * Cookie configuration constants
 */
export const COOKIE_CONFIG = {
  maxAge: CACHE_DURATIONS.COOKIE_MAX_AGE / ANIMATION_DURATION_VERY_SLOW, // ËΩ¨Êç¢‰∏∫Áßí
  sameSite: 'lax' as const,
  secure:
    typeof window !== 'undefined' && window.location.protocol === 'https:',
  path: '/',
} as const;

/**
 * Cookie Êìç‰ΩúÂ∑•ÂÖ∑Á±ª
 * Cookie management utility class
 */
export class CookieManager {
  /**
   * ËÆæÁΩÆ Cookie
   * Set cookie with options
   */
  static set(name: string, value: string, options = COOKIE_CONFIG): void {
    if (typeof document === 'undefined') return;

    const optionsStr = Object.entries(options)
      .map(([key, val]) => {
        if (key === 'maxAge') return `max-age=${val}`;
        if (key === 'sameSite') return `SameSite=${val}`;
        if (key === 'secure' && val) return 'Secure';
        if (key === 'path') return `Path=${val}`;
        return '';
      })
      .filter(Boolean)
      .join('; ');

    document.cookie = `${name}=${encodeURIComponent(value)}; ${optionsStr}`;
  }

  /**
   * Ëé∑Âèñ Cookie ÂÄº
   * Get cookie value by name
   */
  static get(name: string): string | null {
    if (typeof document === 'undefined') return null;

    // ‰ΩøÁî®Êõ¥ÂÆâÂÖ®ÁöÑcookieËß£ÊûêÊñπÊ≥ï
    const cookies = document.cookie.split(';');
    for (const cookie of cookies) {
      const [cookieName, ...cookieValueParts] = cookie.trim().split('=');
      if (cookieName !== name) continue;

      const cookieValue = cookieValueParts.join('=');
      if (!cookieValue) return null;

      try {
        return decodeURIComponent(cookieValue);
      } catch {
        // ÈùôÈªòÂ§ÑÁêÜURIËß£Á†ÅÈîôËØØ
        if (process.env.NODE_ENV === 'development') {
          // console.warn('Failed to decode cookie value:', error);
        }
        return null;
      }
    }
    return null;
  }

  /**
   * Âà†Èô§ Cookie
   * Remove cookie by name
   */
  static remove(name: string): void {
    if (typeof document === 'undefined') return;
    document.cookie = `${name}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
  }

  /**
   * Ê£ÄÊü• Cookie ÊòØÂê¶Â≠òÂú®
   * Check if cookie exists
   */
  static exists(name: string): boolean {
    return this.get(name) !== null;
  }

  /**
   * Ëé∑ÂèñÊâÄÊúâ Cookie
   * Get all cookies as key-value pairs
   */
  static getAll(): Record<string, string> {
    if (typeof document === 'undefined') return {};

    const cookies: Record<string, string> = {};
    const cookieStrings = document.cookie.split(';');

    for (const cookie of cookieStrings) {
      const [name, ...valueParts] = cookie.trim().split('=');
      if (!name) continue;

      const value = valueParts.join('=');
      if (!value) continue;

      try {
        cookies[name] = decodeURIComponent(value);
      } catch {
        // ÈùôÈªòÂ§ÑÁêÜËß£Á†ÅÈîôËØØ
        if (process.env.NODE_ENV === 'development') {
          // console.warn(`Failed to decode cookie ${name}:`, error);
        }
      }
    }

    return cookies;
  }

  /**
   * Ê∏ÖÈô§ÊâÄÊúâ Cookie
   * Clear all cookies
   */
  static clearAll(): void {
    if (typeof document === 'undefined') return;

    const cookies = this.getAll();
    Object.keys(cookies).forEach((name) => {
      this.remove(name);
    });
  }

  /**
   * ËÆæÁΩÆÂ∏¶ÊúâËøáÊúüÊó∂Èó¥ÁöÑ Cookie
   * Set cookie with expiration date
   */
  static setWithExpiry(
    name: string,
    value: string,
    expiryDays: number,
    options: Partial<typeof COOKIE_CONFIG> = {},
  ): void {
    const expiryDate = new Date();
    expiryDate.setTime(expiryDate.getTime() + expiryDays * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW);

    const cookieOptions = {
      ...COOKIE_CONFIG,
      ...options,
      expires: expiryDate.toUTCString(),
    };

    const optionsStr = Object.entries(cookieOptions)
      .map(([key, val]) => {
        if (key === 'maxAge') return `max-age=${val}`;
        if (key === 'sameSite') return `SameSite=${val}`;
        if (key === 'secure' && val) return 'Secure';
        if (key === 'path') return `Path=${val}`;
        if (key === 'expires') return `expires=${val}`;
        return '';
      })
      .filter(Boolean)
      .join('; ');

    document.cookie = `${name}=${encodeURIComponent(value)}; ${optionsStr}`;
  }

  /**
   * Ëé∑Âèñ Cookie ÁöÑËøáÊúüÊó∂Èó¥
   * Get cookie expiration time (if available)
   */
  static getExpiry(_name: string): Date | null {
    // Note: JavaScriptÊó†Ê≥ïÁõ¥Êé•Ëé∑ÂèñCookieÁöÑËøáÊúüÊó∂Èó¥
    // Ëøô‰∏™ÊñπÊ≥ï‰∏ªË¶ÅÁî®‰∫éÊñáÊ°£ÂÆåÊï¥ÊÄßÔºåÂÆûÈôÖ‰ΩøÁî®‰∏≠ÈúÄË¶ÅÂú®ËÆæÁΩÆÊó∂ËÆ∞ÂΩï
    if (process.env.NODE_ENV === 'development') {
      console.warn(
        'Cookie expiry cannot be retrieved directly from JavaScript. ' +
          'Consider storing expiry information separately if needed.',
      );
    }
    return null;
  }

  /**
   * Ê£ÄÊü• Cookie ÊòØÂê¶ÊîØÊåÅ
   * Check if cookies are supported
   */
  static isSupported(): boolean {
    if (typeof document === 'undefined') return false;

    try {
      const testKey = '__cookie_test__';
      const testValue = 'test';

      this.set(testKey, testValue);
      const retrieved = this.get(testKey);
      this.remove(testKey);

      return retrieved === testValue;
    } catch {
      return false;
    }
  }

  /**
   * Ëé∑Âèñ Cookie Â§ßÂ∞èÔºàÂ≠óËäÇÔºâ
   * Get cookie size in bytes
   */
  static getSize(name: string): number {
    const value = this.get(name);
    if (!value) return ZERO;

    // ËÆ°ÁÆó Cookie ÁöÑÊÄªÂ§ßÂ∞èÔºàÂêçÁß∞ + ÂÄº + ÂàÜÈöîÁ¨¶Ôºâ
    return new Blob([`${name}=${value}`]).size;
  }

  /**
   * Ëé∑ÂèñÊâÄÊúâ Cookie ÁöÑÊÄªÂ§ßÂ∞è
   * Get total size of all cookies
   */
  static getTotalSize(): number {
    if (typeof document === 'undefined') return ZERO;

    return new Blob([document.cookie]).size;
  }

  /**
   * Ê£ÄÊü•ÊòØÂê¶Êé•Ëøë Cookie Â§ßÂ∞èÈôêÂà∂
   * Check if approaching cookie size limit
   */
  static isNearLimit(threshold: number = MAGIC_0_8): boolean {
    const maxSize = MAGIC_4096; // Ê†áÂáÜ Cookie Â§ßÂ∞èÈôêÂà∂
    const currentSize = this.getTotalSize();

    return currentSize / maxSize > threshold;
  }
}

/**
 * ËØ≠Ë®ÄÊ£ÄÊµãÂéÜÂè≤Ê†∏ÂøÉÁÆ°ÁêÜ
 * Locale Detection History Core Management
 *
 * Ë¥üË¥£ÂéÜÂè≤ËÆ∞ÂΩïÁöÑÂü∫Á°ÄÊìç‰ΩúÔºöÊ∑ªÂä†„ÄÅËé∑Âèñ„ÄÅÊõ¥Êñ∞ÂíåÁºìÂ≠òÁÆ°ÁêÜ
 */

'use client';

import { CACHE_LIMITS } from '@/constants/i18n-constants';
import { ANIMATION_DURATION_VERY_SLOW, COUNT_TEN, DAYS_PER_MONTH, HOURS_PER_DAY, ONE, PERCENTAGE_FULL, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { LocalStorageManager } from '@/lib/locale-storage-local';
import { isLocaleDetectionHistory } from '@/lib/locale-storage-types';
import type { Locale } from '@/types/i18n';
import type {
  LocaleDetectionHistory,
  LocaleDetectionRecord,
  LocaleSource,
  StorageOperationResult,
} from '@/lib/locale-storage-types';

// ==================== ÁºìÂ≠òÁÆ°ÁêÜ ====================

/**
 * ÂéÜÂè≤ËÆ∞ÂΩïÁºìÂ≠òÁÆ°ÁêÜÂô®
 * History cache manager
 */
export class HistoryCacheManager {
  private static cache: LocaleDetectionHistory | null = null;
  private static cacheTimestamp = ZERO;
  private static readonly CACHE_TTL = COUNT_TEN * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW; // 10 minutes

  /**
   * Ëé∑ÂèñÁºìÂ≠òÁöÑÂéÜÂè≤ËÆ∞ÂΩï
   * Get cached history
   */
  static getCachedHistory(): LocaleDetectionHistory | null {
    if (Date.now() - this.cacheTimestamp > this.CACHE_TTL) {
      this.cache = null;
      return null;
    }
    return this.cache;
  }

  /**
   * Êõ¥Êñ∞ÁºìÂ≠ò
   * Update cache
   */
  static updateCache(history: LocaleDetectionHistory): void {
    this.cache = history;
    this.cacheTimestamp = Date.now();
  }

  /**
   * Ê∏ÖÈô§ÁºìÂ≠ò
   * Clear cache
   */
  static clearCache(): void {
    this.cache = null;
    this.cacheTimestamp = ZERO;
  }

  /**
   * Ëé∑ÂèñÁºìÂ≠òÁä∂ÊÄÅ
   * Get cache status
   */
  static getCacheStatus(): {
    isCached: boolean;
    cacheAge: number;
    cacheSize: number;
  } {
    const isCached =
      this.cache !== null && Date.now() - this.cacheTimestamp <= this.CACHE_TTL;
    const cacheAge = Date.now() - this.cacheTimestamp;
    const cacheSize = this.cache ? JSON.stringify(this.cache).length : ZERO;

    return {
      isCached,
      cacheAge,
      cacheSize,
    };
  }
}

// ==================== Ê†∏ÂøÉÂéÜÂè≤ÁÆ°ÁêÜ ====================

/**
 * Ê∑ªÂä†Ê£ÄÊµãËÆ∞ÂΩï
 * Add detection record
 */
export function addDetectionRecord(
  locale: Locale,
  source: LocaleSource,
  confidence: number,
  metadata?: Record<string, unknown>,
): StorageOperationResult<LocaleDetectionHistory> {
  const startTime = Date.now();

  try {
    const detection: LocaleDetectionRecord = {
      locale,
      source,
      timestamp: Date.now(),
      confidence: Math.max(ZERO, Math.min(ONE, confidence)),
      metadata: metadata || {},
    };

    const result = updateDetectionHistory(detection);

    if (result.success) {
      // Ê∏ÖÈô§ÁºìÂ≠ò‰ª•Á°Æ‰øù‰∏ãÊ¨°Ëé∑ÂèñÊúÄÊñ∞Êï∞ÊçÆ
      HistoryCacheManager.clearCache();
    }

    return {
      ...result,
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Ëé∑ÂèñÊ£ÄÊµãÂéÜÂè≤
 * Get detection history
 */
export function getDetectionHistory(): StorageOperationResult<LocaleDetectionHistory> {
  const startTime = Date.now();

  try {
    // È¶ñÂÖàÊ£ÄÊü•ÁºìÂ≠ò
    const cached = HistoryCacheManager.getCachedHistory();
    if (cached) {
      return {
        success: true,
        data: cached,
        source: 'memory',
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    }

    // ‰ªéÂ≠òÂÇ®‰∏≠Ëé∑Âèñ
    const stored = LocalStorageManager.get<LocaleDetectionHistory>(
      'locale_detection_history',
    );

    if (!stored) {
      // ÂàõÂª∫ÈªòËÆ§ÂéÜÂè≤ËÆ∞ÂΩï
      const defaultHistory: LocaleDetectionHistory = {
        detections: [],
        history: [],
        lastUpdated: Date.now(),
        totalDetections: ZERO,
      };

      try {
        LocalStorageManager.set('locale_detection_history', defaultHistory);
        const saveResult = { success: true };

        if (saveResult.success) {
          HistoryCacheManager.updateCache(defaultHistory);
          return {
            success: true,
            data: defaultHistory,
            source: 'localStorage',
            timestamp: Date.now(),
            responseTime: Date.now() - startTime,
          };
        }
      } catch (error) {
        return {
          success: false,
          error:
            error instanceof Error
              ? error.message
              : 'Failed to create default history',
          source: 'localStorage',
          timestamp: Date.now(),
          responseTime: Date.now() - startTime,
        };
      }
    }

    // È™åËØÅÊï∞ÊçÆÊ†ºÂºè
    if (!validateHistoryData(stored)) {
      return {
        success: false,
        error: 'Invalid history data format',
        source: 'localStorage',
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    }

    // Êõ¥Êñ∞ÁºìÂ≠ò
    HistoryCacheManager.updateCache(stored);

    return {
      success: true,
      data: stored,
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Êõ¥Êñ∞Ê£ÄÊµãÂéÜÂè≤
 * Update detection history
 */
export function updateDetectionHistory(
  detection: LocaleDetectionRecord,
): StorageOperationResult<LocaleDetectionHistory> {
  const existingResult = getDetectionHistory();

  if (!existingResult.success) {
    return existingResult;
  }

  const history = existingResult.data!;

  // Ê∑ªÂä†Êñ∞ËÆ∞ÂΩïÂà∞ÂéÜÂè≤ÂºÄÂ§¥
  history.history.unshift(detection);

  // ÈôêÂà∂ÂéÜÂè≤ËÆ∞ÂΩïÊï∞Èáè
  const maxRecords = CACHE_LIMITS.MAX_DETECTION_HISTORY || PERCENTAGE_FULL;
  if (history.history.length > maxRecords) {
    history.history = history.history.slice(ZERO, maxRecords);
  }

  // Êõ¥Êñ∞Êó∂Èó¥Êà≥
  history.lastUpdated = Date.now();

  // ‰øùÂ≠òÂà∞Â≠òÂÇ®
  try {
    LocalStorageManager.set('locale_detection_history', history);
    const saveResult = { success: true };

    if (saveResult.success) {
      // Êõ¥Êñ∞ÁºìÂ≠ò
      HistoryCacheManager.updateCache(history);
      return {
        success: true,
        data: history,
        source: 'localStorage',
        timestamp: Date.now(),
      };
    }
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Failed to save history',
      source: 'localStorage',
      timestamp: Date.now(),
    };
  }

  // ÈªòËÆ§ËøîÂõûÂ§±Ë¥•ÁªìÊûú
  return {
    success: false,
    error: 'Unknown error occurred',
    source: 'localStorage',
    timestamp: Date.now(),
  };
}

/**
 * È™åËØÅÂéÜÂè≤Êï∞ÊçÆÊ†ºÂºè
 * Validate history data format
 */
export function validateHistoryData(
  history: unknown,
): history is LocaleDetectionHistory {
  return isLocaleDetectionHistory(history);
}

/**
 * ÂàõÂª∫ÈªòËÆ§ÂéÜÂè≤ËÆ∞ÂΩï
 * Create default history
 */
export function createDefaultHistory(): LocaleDetectionHistory {
  return {
    detections: [],
    history: [],
    lastUpdated: Date.now(),
    totalDetections: ZERO,
  };
}

/**
 * Ëé∑ÂèñÂéÜÂè≤ËÆ∞ÂΩïÊëòË¶Å
 * Get history summary
 */
export function getHistorySummary(): {
  totalRecords: number;
  lastUpdated: number;
  oldestRecord: number;
  newestRecord: number;
  cacheStatus: ReturnType<typeof HistoryCacheManager.getCacheStatus>;
} {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return {
      totalRecords: ZERO,
      lastUpdated: ZERO,
      oldestRecord: ZERO,
      newestRecord: ZERO,
      cacheStatus: HistoryCacheManager.getCacheStatus(),
    };
  }

  const history = historyResult.data;
  const records = history.history;

  return {
    totalRecords: records.length,
    lastUpdated: history.lastUpdated,
    oldestRecord:
      records.length > ZERO ? records[records.length - ONE]!.timestamp : ZERO,
    newestRecord: records.length > ZERO ? records[ZERO]!.timestamp : ZERO,
    cacheStatus: HistoryCacheManager.getCacheStatus(),
  };
}

/**
 * Ê£ÄÊü•ÂéÜÂè≤ËÆ∞ÂΩïÊòØÂê¶ÈúÄË¶ÅÊ∏ÖÁêÜ
 * Check if history needs cleanup
 */
export function needsCleanup(maxAge: number = DAYS_PER_MONTH * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW): {
  needsCleanup: boolean;
  expiredCount: number;
  totalCount: number;
  recommendations: string[];
} {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return {
      needsCleanup: false,
      expiredCount: ZERO,
      totalCount: ZERO,
      recommendations: ['Êó†Ê≥ïËé∑ÂèñÂéÜÂè≤ËÆ∞ÂΩï'],
    };
  }

  const history = historyResult.data;
  const cutoffTime = Date.now() - maxAge;
  const expiredRecords = history.history.filter(
    (record: any) => record.timestamp < cutoffTime,
  );
  const totalCount = history.history.length;
  const expiredCount = expiredRecords.length;

  const recommendations: string[] = [];

  if (expiredCount > ZERO) {
    recommendations.push(`ÂèëÁé∞ ${expiredCount} Êù°ËøáÊúüËÆ∞ÂΩïÔºåÂª∫ËÆÆÊ∏ÖÁêÜ`);
  }

  if (totalCount > PERCENTAGE_FULL) {
    recommendations.push(`ÂéÜÂè≤ËÆ∞ÂΩïËøáÂ§ö (${totalCount} Êù°)ÔºåÂª∫ËÆÆÊ∏ÖÁêÜÊóßËÆ∞ÂΩï`);
  }

  const maxRecords = CACHE_LIMITS.MAX_DETECTION_HISTORY || PERCENTAGE_FULL;
  if (totalCount > maxRecords) {
    recommendations.push(`Ë∂ÖÂá∫ÊúÄÂ§ßËÆ∞ÂΩïÈôêÂà∂ (${maxRecords})ÔºåÂ∞ÜËá™Âä®Êà™Êñ≠`);
  }

  if (recommendations.length === ZERO) {
    recommendations.push('ÂéÜÂè≤ËÆ∞ÂΩïÁä∂ÊÄÅËâØÂ•ΩÔºåÊó†ÈúÄÊ∏ÖÁêÜ');
  }

  return {
    needsCleanup: expiredCount > ZERO || totalCount > maxRecords,
    expiredCount,
    totalCount,
    recommendations,
  };
}

/**
 * ËØ≠Ë®ÄÊ£ÄÊµãÂéÜÂè≤‰∫ã‰ª∂ÁÆ°ÁêÜ
 * Locale Detection History Event Management
 *
 * Ë¥üË¥£ÂéÜÂè≤ËÆ∞ÂΩïÁõ∏ÂÖ≥ÁöÑ‰∫ã‰ª∂ÁõëÂê¨„ÄÅÂèëÂ∏ÉÂíåÁÆ°ÁêÜÂäüËÉΩ
 */

'use client';

import { COUNT_TEN, ONE, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';
import type {
  StorageEvent,
  StorageEventListener,
} from '@/lib/locale-storage-types';

// ==================== ‰∫ã‰ª∂ÁÆ°ÁêÜÂô® ====================

/**
 * ÂéÜÂè≤‰∫ã‰ª∂ÁÆ°ÁêÜÂô®
 * History event manager
 */
export class HistoryEventManager {
  private static eventListeners: Map<string, StorageEventListener[]> =
    new Map();
  private static eventHistory: StorageEvent[] = [];
  private static readonly MAX_EVENT_HISTORY = PERCENTAGE_FULL;

  /**
   * Ê∑ªÂä†‰∫ã‰ª∂ÁõëÂê¨Âô®
   * Add event listener
   */
  static addEventListener(
    eventType: string,
    listener: StorageEventListener,
  ): void {
    if (!this.eventListeners.has(eventType)) {
      this.eventListeners.set(eventType, []);
    }
    this.eventListeners.get(eventType)!.push(listener);
  }

  /**
   * ÁßªÈô§‰∫ã‰ª∂ÁõëÂê¨Âô®
   * Remove event listener
   */
  static removeEventListener(
    eventType: string,
    listener: StorageEventListener,
  ): void {
    const listeners = this.eventListeners.get(eventType);
    if (listeners) {
      const index = listeners.indexOf(listener);
      if (index > -ONE) {
        listeners.splice(index, ONE);
      }
    }
  }

  /**
   * ÁßªÈô§ÊâÄÊúâ‰∫ã‰ª∂ÁõëÂê¨Âô®
   * Remove all event listeners
   */
  static removeAllListeners(eventType?: string): void {
    if (eventType) {
      this.eventListeners.delete(eventType);
    } else {
      this.eventListeners.clear();
    }
  }

  /**
   * ÂèëÂá∫‰∫ã‰ª∂
   * Emit event
   */
  static emitEvent(event: StorageEvent): void {
    // ËÆ∞ÂΩï‰∫ã‰ª∂ÂéÜÂè≤
    this.recordEvent(event);

    // ÂèëÈÄÅÁªôÁâπÂÆöÁ±ªÂûãÁöÑÁõëÂê¨Âô®
    const listeners = this.eventListeners.get(event.type);
    if (listeners) {
      listeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error(`Error in history event listener for ${event.type}`, {
            error: error as Error,
          });
        }
      });
    }

    // ÂèëÈÄÅÁªôÈÄöÁî®ÁõëÂê¨Âô®
    const allListeners = this.eventListeners.get('*');
    if (allListeners) {
      allListeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error('Error in universal history event listener', {
            error: error as Error,
          });
        }
      });
    }
  }

  /**
   * ËÆ∞ÂΩï‰∫ã‰ª∂ÂéÜÂè≤
   * Record event history
   */
  private static recordEvent(event: StorageEvent): void {
    this.eventHistory.unshift(event);

    // ÈôêÂà∂‰∫ã‰ª∂ÂéÜÂè≤ÈïøÂ∫¶
    if (this.eventHistory.length > this.MAX_EVENT_HISTORY) {
      this.eventHistory = this.eventHistory.slice(ZERO, this.MAX_EVENT_HISTORY);
    }
  }

  /**
   * Ëé∑Âèñ‰∫ã‰ª∂ÂéÜÂè≤
   * Get event history
   */
  static getEventHistory(limit?: number): StorageEvent[] {
    return limit ? this.eventHistory.slice(ZERO, limit) : [...this.eventHistory];
  }

  /**
   * Ê∏ÖÈô§‰∫ã‰ª∂ÂéÜÂè≤
   * Clear event history
   */
  static clearEventHistory(): void {
    this.eventHistory = [];
  }

  /**
   * Ëé∑ÂèñÁõëÂê¨Âô®ÁªüËÆ°
   * Get listener statistics
   */
  static getListenerStats(): {
    totalListeners: number;
    eventTypes: string[];
    listenersByType: Record<string, number>;
  } {
    let totalListeners = ZERO;
    const listenersByType: Record<string, number> = {};

    for (const [eventType, listeners] of this.eventListeners.entries()) {
      const count = listeners.length;
      totalListeners += count;
      listenersByType[eventType] = count;
    }

    return {
      totalListeners,
      eventTypes: Array.from(this.eventListeners.keys()),
      listenersByType,
    };
  }
}

