 * Locale Detection History Maintenance Tools
 */

'use client';

import { CACHE_LIMITS } from '@/constants/i18n-constants';
import { ANIMATION_DURATION_VERY_SLOW, DAYS_PER_MONTH, HOURS_PER_DAY, MAGIC_1_5, ONE, PERCENTAGE_FULL, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { getDetectionHistory } from '@/lib/locale-storage-history-core';
import type { StorageOperationResult } from '@/lib/locale-storage-types';
import {
  cleanupDuplicateDetections,
  cleanupExpiredDetections,
  limitHistorySize,
} from '@/lib/locale-storage-history-maintenance/cleanup';

/**
 * ÊâßË°åÂÆåÊï¥ÁöÑÂéÜÂè≤Áª¥Êä§
 * Perform complete history maintenance
 */
export function performMaintenance(options: {
  cleanupExpired?: boolean;
  maxAge?: number;
  removeDuplicates?: boolean;
  limitSize?: boolean;
  maxRecords?: number;
}): StorageOperationResult<{
  expiredRemoved: number;
  duplicatesRemoved: number;
  sizeReduced: number;
  finalCount: number;
}> {
  const startTime = Date.now();

  try {
    let expiredRemoved = ZERO;
    let duplicatesRemoved = ZERO;
    let sizeReduced = ZERO;

    // Ê∏ÖÁêÜËøáÊúüËÆ∞ÂΩï
    if (options.cleanupExpired !== false) {
      const expiredResult = cleanupExpiredDetections(options.maxAge);
      if (expiredResult.success) {
        expiredRemoved = expiredResult.data || ZERO;
      }
    }

    // ÁßªÈô§ÈáçÂ§çËÆ∞ÂΩï
    if (options.removeDuplicates !== false) {
      const duplicateResult = cleanupDuplicateDetections();
      if (duplicateResult.success) {
        duplicatesRemoved = duplicateResult.data || ZERO;
      }
    }

    // ÈôêÂà∂Â§ßÂ∞è
    if (options.limitSize !== false) {
      const limitResult = limitHistorySize(options.maxRecords);
      if (limitResult.success) {
        sizeReduced = limitResult.data || ZERO;
      }
    }

    // Ëé∑ÂèñÊúÄÁªàËÆ°Êï∞
    const historyResult = getDetectionHistory();
    const finalCount = historyResult.success
      ? historyResult.data!.history.length
      : ZERO;

    return {
      success: true,
      data: {
        expiredRemoved,
        duplicatesRemoved,
        sizeReduced,
        finalCount,
      },
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Ëé∑ÂèñÁª¥Êä§Âª∫ËÆÆ
 * Get maintenance recommendations
 */
export function getMaintenanceRecommendations(): {
  recommendations: string[];
  urgency: 'low' | 'medium' | 'high';
  estimatedBenefit: string;
} {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return {
      recommendations: ['Êó†Ê≥ïËé∑ÂèñÂéÜÂè≤ËÆ∞ÂΩïÔºåÂª∫ËÆÆÊ£ÄÊü•Â≠òÂÇ®Áä∂ÊÄÅ'],
      urgency: 'high',
      estimatedBenefit: 'ÊÅ¢Â§çÊ≠£Â∏∏ÂäüËÉΩ',
    };
  }

  const history = historyResult.data;
  const records = history.history;
  const recommendations: string[] = [];
  let urgency: 'low' | 'medium' | 'high' = 'low';

  // Ê£ÄÊü•ËÆ∞ÂΩïÊï∞Èáè
  const maxRecords = CACHE_LIMITS.MAX_DETECTION_HISTORY || PERCENTAGE_FULL;
  if (records.length > maxRecords * MAGIC_1_5) {
    recommendations.push(`ÂéÜÂè≤ËÆ∞ÂΩïËøáÂ§ö (${records.length})ÔºåÂª∫ËÆÆÊ∏ÖÁêÜ`);
    urgency = 'high';
  } else if (records.length > maxRecords) {
    recommendations.push(`ÂéÜÂè≤ËÆ∞ÂΩïËæÉÂ§ö (${records.length})ÔºåËÄÉËôëÊ∏ÖÁêÜ`);
    urgency = urgency === 'low' ? 'medium' : urgency;
  }

  // Ê£ÄÊü•ËøáÊúüËÆ∞ÂΩï
  const thirtyDaysAgo = Date.now() - DAYS_PER_MONTH * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW;
  const expiredCount = records.filter(
    (r) => r.timestamp < thirtyDaysAgo,
  ).length;

  if (expiredCount > ZERO) {
    recommendations.push(`ÂèëÁé∞ ${expiredCount} Êù°ËøáÊúüËÆ∞ÂΩïÔºåÂª∫ËÆÆÊ∏ÖÁêÜ`);
    urgency = urgency === 'low' ? 'medium' : urgency;
  }

  // Ê£ÄÊü•ÈáçÂ§çËÆ∞ÂΩï
  const uniqueKeys = new Set();
  let duplicateCount = ZERO;

  records.forEach((record) => {
    const key = `${record.locale}-${record.source}-${record.timestamp}`;
    if (uniqueKeys.has(key)) {
      duplicateCount += ONE;
    } else {
      uniqueKeys.add(key);
    }
  });

  if (duplicateCount > ZERO) {
    recommendations.push(`ÂèëÁé∞ ${duplicateCount} Êù°ÈáçÂ§çËÆ∞ÂΩïÔºåÂª∫ËÆÆÊ∏ÖÁêÜ`);
  }

  // Ê£ÄÊü•Êï∞ÊçÆÂÆåÊï¥ÊÄß
  const invalidRecords = records.filter(
    (record) =>
      !record.locale ||
      !record.source ||
      !record.timestamp ||
      record.confidence < ZERO ||
      record.confidence > ONE,
  );

  if (invalidRecords.length > ZERO) {
    recommendations.push(`ÂèëÁé∞ ${invalidRecords.length} Êù°Êó†ÊïàËÆ∞ÂΩïÔºåÂª∫ËÆÆÊ∏ÖÁêÜ`);
    urgency = 'high';
  }

  if (recommendations.length === ZERO) {
    recommendations.push('ÂéÜÂè≤ËÆ∞ÂΩïÁä∂ÊÄÅËâØÂ•ΩÔºåÊó†ÈúÄÁª¥Êä§');
  }

  const estimatedBenefit =
    urgency === 'high'
      ? 'ÊòæËëóÊèêÂçáÊÄßËÉΩÂíåÁ®≥ÂÆöÊÄß'
      : urgency === 'medium'
        ? 'ÊîπÂñÑÂ≠òÂÇ®ÊïàÁéá'
        : '‰øùÊåÅÁ≥ªÁªüÊï¥Ê¥Å';

  return {
    recommendations,
    urgency,
    estimatedBenefit,
  };
}

/**
 * ËØ≠Ë®ÄÊ£ÄÊµãÂéÜÂè≤Êü•ËØ¢ÂíåËøáÊª§
 * Locale Detection History Query and Filtering
 *
 * Ë¥üË¥£ÂéÜÂè≤ËÆ∞ÂΩïÁöÑÊü•ËØ¢„ÄÅËøáÊª§ÂíåÊêúÁ¥¢ÂäüËÉΩ
 */

'use client';

import { ANIMATION_DURATION_VERY_SLOW, COUNT_TEN, HOURS_PER_DAY, ONE, PERCENTAGE_FULL, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { getDetectionHistory } from '@/lib/locale-storage-history-core';
import type { LocaleDetectionRecord } from '@/lib/locale-storage-types';
import type { Locale } from '@/types/i18n';

// ==================== Âü∫Á°ÄÊü•ËØ¢ÂäüËÉΩ ====================

/**
 * Ëé∑ÂèñÊúÄËøëÁöÑÊ£ÄÊµãËÆ∞ÂΩï
 * Get recent detections
 */
export function getRecentDetections(
  limit: number = COUNT_TEN,
): LocaleDetectionRecord[] {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return [];
  }

  return historyResult.data.history.slice(ZERO, limit);
}

/**
 * ÊåâÊù•Ê∫êËé∑ÂèñÊ£ÄÊµãËÆ∞ÂΩï
 * Get detections by source
 */
export function getDetectionsBySource(source: string): LocaleDetectionRecord[] {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return [];
  }

  return historyResult.data.history.filter(
    (record) => record.source === source,
  );
}

/**
 * ÊåâËØ≠Ë®ÄËé∑ÂèñÊ£ÄÊµãËÆ∞ÂΩï
 * Get detections by locale
 */
export function getDetectionsByLocale(locale: Locale): LocaleDetectionRecord[] {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return [];
  }

  return historyResult.data.history.filter(
    (record) => record.locale === locale,
  );
}

/**
 * ÊåâÊó∂Èó¥ËåÉÂõ¥Ëé∑ÂèñÊ£ÄÊµãËÆ∞ÂΩï
 * Get detections by time range
 */
export function getDetectionsByTimeRange(
  startTime: number,
  endTime: number,
): LocaleDetectionRecord[] {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return [];
  }

  return historyResult.data.history.filter(
    (record) => record.timestamp >= startTime && record.timestamp <= endTime,
  );
}

/**
 * ÊåâÁΩÆ‰ø°Â∫¶ËåÉÂõ¥Ëé∑ÂèñÊ£ÄÊµãËÆ∞ÂΩï
 * Get detections by confidence range
 */
export function getDetectionsByConfidence(
  minConfidence: number,
  maxConfidence: number = ONE,
): LocaleDetectionRecord[] {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return [];
  }

  return historyResult.data.history.filter(
    (record) =>
      record.confidence >= minConfidence && record.confidence <= maxConfidence,
  );
}

// ==================== È´òÁ∫ßÊü•ËØ¢ÂäüËÉΩ ====================

/**
 * Êü•ËØ¢Êù°‰ª∂Êé•Âè£
 * Query conditions interface
 */
export interface QueryConditions {
  locale?: Locale;
  source?: string;
  startTime?: number;
  endTime?: number;
  minConfidence?: number;
  maxConfidence?: number;
  limit?: number;
  offset?: number;
  sortBy?: 'timestamp' | 'confidence' | 'locale' | 'source';
  sortOrder?: 'asc' | 'desc';
}

/**
 * Â§çÂêàÊü•ËØ¢Ê£ÄÊµãËÆ∞ÂΩï
 * Complex query for detection records
 */
export function queryDetections(conditions: QueryConditions): {
  records: LocaleDetectionRecord[];
  totalCount: number;
  hasMore: boolean;
} {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return {
      records: [],
      totalCount: ZERO,
      hasMore: false,
    };
  }

  let records = historyResult.data.history;

  // Â∫îÁî®ËøáÊª§Êù°‰ª∂
  if (conditions.locale) {
    records = records.filter((record) => record.locale === conditions.locale);
  }

  if (conditions.source) {
    records = records.filter((record) => record.source === conditions.source);
  }

  if (conditions.startTime !== undefined) {
    records = records.filter(
      (record) => record.timestamp >= conditions.startTime!,
    );
  }

  if (conditions.endTime !== undefined) {
    records = records.filter(
      (record) => record.timestamp <= conditions.endTime!,
    );
  }

  if (conditions.minConfidence !== undefined) {
    records = records.filter(
      (record) => record.confidence >= conditions.minConfidence!,
    );
  }

  if (conditions.maxConfidence !== undefined) {
    records = records.filter(
      (record) => record.confidence <= conditions.maxConfidence!,
    );
  }

  // ÊéíÂ∫è
  if (conditions.sortBy) {
    records.sort((a, b) => {
      let aValue: string | number;
      let bValue: string | number;

      switch (conditions.sortBy) {
        case 'timestamp':
          aValue = a.timestamp;
          bValue = b.timestamp;
          break;
        case 'confidence':
          aValue = a.confidence;
          bValue = b.confidence;
          break;
        case 'locale':
          aValue = a.locale;
          bValue = b.locale;
          break;
        case 'source':
          aValue = a.source;
          bValue = b.source;
          break;
        default:
          return ZERO;
      }

      if (aValue < bValue) {
        return conditions.sortOrder === 'desc' ? ONE : -ONE;
      }
      if (aValue > bValue) {
        return conditions.sortOrder === 'desc' ? -ONE : ONE;
      }
      return ZERO;
    });
  }

  const totalCount = records.length;

  // Â∫îÁî®ÂàÜÈ°µ
  const offset = conditions.offset || ZERO;
  const limit = conditions.limit || totalCount;
  const paginatedRecords = records.slice(offset, offset + limit);
  const hasMore = offset + limit < totalCount;

  return {
    records: paginatedRecords,
    totalCount,
    hasMore,
  };
}

/**
 * ÊêúÁ¥¢Ê£ÄÊµãËÆ∞ÂΩï
 * Search detection records
 */
export function searchDetections(searchTerm: string): LocaleDetectionRecord[] {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return [];
  }

  const term = searchTerm.toLowerCase();

  return historyResult.data.history.filter((record) => {
    // ÊêúÁ¥¢ËØ≠Ë®Ä‰ª£Á†Å
    if (record.locale.toLowerCase().includes(term)) {
      return true;
    }

    // ÊêúÁ¥¢Êù•Ê∫ê
    if (record.source.toLowerCase().includes(term)) {
      return true;
    }

    // ÊêúÁ¥¢ÂÖÉÊï∞ÊçÆ
    if (record.metadata) {
      const metadataStr = JSON.stringify(record.metadata).toLowerCase();
      if (metadataStr.includes(term)) {
        return true;
      }
    }

    return false;
  });
}

