/**
 * React Hook: Â≠òÂÇ®ÂèØÁî®ÊÄßÊ£ÄÊü•
 * React Hook: Storage availability check
 */
export function useStorageAvailability() {
  const [isLocalStorageAvailable, setIsLocalStorageAvailable] = useState(false);
  const [isCookieAvailable, setIsCookieAvailable] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const checkAvailability = async () => {
      try {
        // Âä®ÊÄÅÂØºÂÖ•‰ª•ÈÅøÂÖç SSR ÈóÆÈ¢ò
        const { LocalStorageManager } = await import('./locale-storage-local');
        const { CookieManager } = await import('./locale-storage-cookie');

        setIsLocalStorageAvailable(LocalStorageManager.isAvailable());
        setIsCookieAvailable(CookieManager.isSupported());
      } catch (error) {
        logger.warn('Failed to check storage availability:', error);
        setIsLocalStorageAvailable(false);
        setIsCookieAvailable(false);
      } finally {
        setIsLoading(false);
      }
    };

    checkAvailability();
  }, []);

  return {
    isLocalStorageAvailable,
    isCookieAvailable,
    isAnyStorageAvailable: isLocalStorageAvailable || isCookieAvailable,
    isLoading,
  };
}

/**
 * React Hook: Ëá™Âä®Ê∏ÖÁêÜËøáÊúüÊï∞ÊçÆ
 * React Hook: Auto cleanup expired data
 */
export function useAutoCleanup(
  options: {
    enabled?: boolean;
    intervalMs?: number;
    maxAgeMs?: number;
  } = {},
) {
  const {
    enabled = true,
    intervalMs = SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW, // 1 hour
    maxAgeMs = DAYS_PER_MONTH * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW, // 30 days
  } = options;

  useEffect(() => {
    if (!enabled) return;

    const cleanup = () => {
      try {
        LocaleStorageManager.cleanupExpiredDetections(maxAgeMs);
      } catch (error) {
        logger.warn('Auto cleanup failed:', error);
      }
    };

    // Á´ãÂç≥ÊâßË°å‰∏ÄÊ¨°Ê∏ÖÁêÜ
    cleanup();

    // ËÆæÁΩÆÂÆöÊúüÊ∏ÖÁêÜ
    const intervalId = setInterval(cleanup, intervalMs);

    return () => {
      clearInterval(intervalId);
    };
  }, [enabled, intervalMs, maxAgeMs]);
}

/**
 * React Hook: Â≠òÂÇ®‰∫ã‰ª∂ÁõëÂê¨
 * React Hook: Storage event listener
 */
export function useStorageEvents() {
  const [lastStorageEvent, setLastStorageEvent] = useState<{
    key: string;
    newValue: string | null;
    oldValue: string | null;
    timestamp: number;
  } | null>(null);

  useEffect(() => {
    const handleStorageChange = (event: StorageEvent) => {
      if (event.key && Object.values(STORAGE_KEYS).includes(event.key as any)) {
        setLastStorageEvent({
          key: event.key,
          newValue: event.newValue,
          oldValue: event.oldValue,
          timestamp: Date.now(),
        });
      }
    };

    window.addEventListener('storage', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  return {
    lastStorageEvent,
  };
}

import { MAGIC_0_8 } from "@/constants/decimal";
import { BYTES_PER_KB, COUNT_FIVE, ZERO } from "@/constants/magic-numbers";

'use client';

/**
 * LocalStorage Êìç‰ΩúÂ∑•ÂÖ∑Á±ª
 * LocalStorage management utility class
 */
export class LocalStorageManager {
  /**
   * ËÆæÁΩÆ LocalStorage È°π
   * Set item in localStorage
   */
  static set(key: string, value: unknown): void {
    if (typeof window === 'undefined') return;
    try {
      localStorage.setItem(key, JSON.stringify(value));
    } catch {
      // ÈùôÈªòÂ§ÑÁêÜlocalStorageÈîôËØØÔºåÈÅøÂÖçÂú®Áîü‰∫ßÁéØÂ¢É‰∏≠ËæìÂá∫Êó•Âøó
      if (process.env.NODE_ENV === 'development') {
        // Âú®ÂºÄÂèëÁéØÂ¢É‰∏≠ÂèØ‰ª•‰ΩøÁî®Ë∞ÉËØïÂô®ÊàñÂÖ∂‰ªñÊó•ÂøóÊñπÊ°à
        // console.warn('Failed to save to localStorage:', error);
      }
    }
  }

  /**
   * Ëé∑Âèñ LocalStorage È°π
   * Get item from localStorage
   */
  static get<T>(key: string): T | null {
    if (typeof window === 'undefined') return null;
    try {
      const item = localStorage.getItem(key);
      return item ? JSON.parse(item) : null;
    } catch {
      // ÈùôÈªòÂ§ÑÁêÜlocalStorageÈîôËØØÔºåÈÅøÂÖçÂú®Áîü‰∫ßÁéØÂ¢É‰∏≠ËæìÂá∫Êó•Âøó
      if (process.env.NODE_ENV === 'development') {
        // Âú®ÂºÄÂèëÁéØÂ¢É‰∏≠ÂèØ‰ª•‰ΩøÁî®Ë∞ÉËØïÂô®ÊàñÂÖ∂‰ªñÊó•ÂøóÊñπÊ°à
        // console.warn('Failed to read from localStorage:', error);
      }
      return null;
    }
  }

  /**
   * Âà†Èô§ LocalStorage È°π
   * Remove item from localStorage
   */
  static remove(key: string): void {
    if (typeof window === 'undefined') return;
    try {
      localStorage.removeItem(key);
    } catch {
      // ÈùôÈªòÂ§ÑÁêÜlocalStorageÈîôËØØÔºåÈÅøÂÖçÂú®Áîü‰∫ßÁéØÂ¢É‰∏≠ËæìÂá∫Êó•Âøó
      if (process.env.NODE_ENV === 'development') {
        // Âú®ÂºÄÂèëÁéØÂ¢É‰∏≠ÂèØ‰ª•‰ΩøÁî®Ë∞ÉËØïÂô®ÊàñÂÖ∂‰ªñÊó•ÂøóÊñπÊ°à
        // console.warn('Failed to remove from localStorage:', error);
      }
    }
  }

  /**
   * Ê£ÄÊü• LocalStorage È°πÊòØÂê¶Â≠òÂú®
   * Check if item exists in localStorage
   */
  static exists(key: string): boolean {
    if (typeof window === 'undefined') return false;
    try {
      return localStorage.getItem(key) !== null;
    } catch {
      return false;
    }
  }

  /**
   * Ëé∑ÂèñÊâÄÊúâ LocalStorage È°π
   * Get all localStorage items
   */
  static getAll(): Record<string, unknown> {
    if (typeof window === 'undefined') return {};

    const items: Record<string, unknown> = {};

    try {
      for (let i = ZERO; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key) {
          const value = this.get(key);
          if (value !== null) {
            items[key] = value;
          }
        }
      }
    } catch {
      if (process.env.NODE_ENV === 'development') {
        // console.warn('Failed to get all localStorage items:', error);
      }
    }

    return items;
  }

  /**
   * Ê∏ÖÈô§ÊâÄÊúâ LocalStorage È°π
   * Clear all localStorage items
   */
  static clear(): void {
    if (typeof window === 'undefined') return;
    try {
      localStorage.clear();
    } catch {
      if (process.env.NODE_ENV === 'development') {
        // console.warn('Failed to clear localStorage:', error);
      }
    }
  }

  /**
   * Ëé∑Âèñ LocalStorage ‰ΩøÁî®ÁöÑÂ≠òÂÇ®Á©∫Èó¥Â§ßÂ∞è
   * Get localStorage usage size in bytes
   */
  static getUsageSize(): number {
    if (typeof window === 'undefined') return ZERO;

    let totalSize = ZERO;

    try {
      for (let i = ZERO; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key) {
          const value = localStorage.getItem(key);
          if (value) {
            totalSize += new Blob([key + value]).size;
          }
        }
      }
    } catch {
      if (process.env.NODE_ENV === 'development') {
        // console.warn('Failed to calculate localStorage usage:', error);
      }
    }

    return totalSize;
  }

  /**
   * Ëé∑ÂèñÁâπÂÆöÈ°πÁöÑÂ§ßÂ∞è
   * Get size of specific item in bytes
   */
  static getItemSize(key: string): number {
    if (typeof window === 'undefined') return ZERO;

    try {
      const value = localStorage.getItem(key);
      if (!value) return ZERO;

      return new Blob([key + value]).size;
    } catch {
      return ZERO;
    }
  }

  /**
   * Ê£ÄÊü• LocalStorage ÊòØÂê¶ÂèØÁî®
   * Check if localStorage is available
   */
  static isAvailable(): boolean {
    if (typeof window === 'undefined') return false;

    try {
      const testKey = '__localStorage_test__';
      const testValue = 'test';

      localStorage.setItem(testKey, testValue);
      const retrieved = localStorage.getItem(testKey);
      localStorage.removeItem(testKey);

      return retrieved === testValue;
    } catch {
      return false;
    }
  }

  /**
   * Ëé∑Âèñ LocalStorage Ââ©‰ΩôÁ©∫Èó¥Ôºà‰º∞ÁÆóÔºâ
   * Get estimated remaining localStorage space
   */
  static getRemainingSpace(): number {
    if (!this.isAvailable()) return ZERO;

    const maxSize = COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB; // 5MB ‰º∞ÁÆóÈôêÂà∂
    const usedSize = this.getUsageSize();

    return Math.max(ZERO, maxSize - usedSize);
  }

  /**
   * Ê£ÄÊü•ÊòØÂê¶Êé•ËøëÂ≠òÂÇ®ÈôêÂà∂
   * Check if approaching storage limit
   */
  static isNearLimit(threshold: number = MAGIC_0_8): boolean {
    if (!this.isAvailable()) return true;

    const maxSize = COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB; // 5MB ‰º∞ÁÆóÈôêÂà∂
    const usedSize = this.getUsageSize();

    return usedSize / maxSize > threshold;
  }

  /**
   * ËÆæÁΩÆÂ∏¶ÊúâËøáÊúüÊó∂Èó¥ÁöÑÈ°π
   * Set item with expiration time
   */
  static setWithExpiry(key: string, value: unknown, expiryMs: number): void {
    const item = {
      value,
      expiry: Date.now() + expiryMs,
    };

    this.set(key, item);
  }

  /**
   * Ëé∑ÂèñÂ∏¶ÊúâËøáÊúüÊó∂Èó¥Ê£ÄÊü•ÁöÑÈ°π
   * Get item with expiry check
   */
  static getWithExpiry<T>(key: string): T | null {
    const item = this.get<{ value: T; expiry: number }>(key);

    if (!item) return null;

    // Ê£ÄÊü•ÊòØÂê¶ËøáÊúü
    if (Date.now() > item.expiry) {
      this.remove(key);
      return null;
    }

    return item.value;
  }

  /**
   * ÊâπÈáèËÆæÁΩÆÂ§ö‰∏™È°π
   * Set multiple items in batch
   */
  static setMultiple(items: Record<string, unknown>): void {
    Object.entries(items).forEach(([key, value]) => {
      this.set(key, value);
    });
  }

  /**
   * ÊâπÈáèËé∑ÂèñÂ§ö‰∏™È°π
   * Get multiple items in batch
   */
  static getMultiple<T>(keys: string[]): Record<string, T | null> {
    const result: Record<string, T | null> = {};

    keys.forEach((key) => {
      result[key] = this.get<T>(key);
    });

    return result;
  }

  /**
   * ÊâπÈáèÂà†Èô§Â§ö‰∏™È°π
   * Remove multiple items in batch
   */
  static removeMultiple(keys: string[]): void {
    keys.forEach((key) => {
      this.remove(key);
    });
  }

  /**
   * ÊåâÂâçÁºÄËé∑ÂèñÈ°π
   * Get items by key prefix
   */
  static getByPrefix<T>(prefix: string): Record<string, T> {
    const items: Record<string, T> = {};

    if (typeof window === 'undefined') return items;

    try {
      for (let i = ZERO; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key && key.startsWith(prefix)) {
          const value = this.get<T>(key);
          if (value !== null) {
            items[key] = value;
          }
        }
      }
    } catch {
      if (process.env.NODE_ENV === 'development') {
        // console.warn('Failed to get items by prefix:', error);
      }
    }

    return items;
  }

  /**
   * ÊåâÂâçÁºÄÂà†Èô§È°π
   * Remove items by key prefix
   */
  static removeByPrefix(prefix: string): void {
    if (typeof window === 'undefined') return;

    const keysToRemove: string[] = [];

    try {
      for (let i = ZERO; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key && key.startsWith(prefix)) {
          keysToRemove.push(key);
        }
      }

      keysToRemove.forEach((key) => {
        this.remove(key);
      });
    } catch {
      if (process.env.NODE_ENV === 'development') {
        // console.warn('Failed to remove items by prefix:', error);
      }
    }
  }
}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®Ê∏ÖÁêÜÊìç‰Ωú
 * Locale Storage Cleanup Operations
 *
 * Ë¥üË¥£Â≠òÂÇ®Êï∞ÊçÆÁöÑÊ∏ÖÁêÜÂíåËøáÊúüÊï∞ÊçÆÂ§ÑÁêÜ
 */

'use client';

import { ANIMATION_DURATION_VERY_SLOW, DAYS_PER_MONTH, HOURS_PER_DAY, ONE, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { MINUTE_MS } from "@/constants/time";
import { CookieManager } from '@/lib/locale-storage-cookie';
import { LocalStorageManager } from '@/lib/locale-storage-local';
import { STORAGE_KEYS } from '@/lib/locale-storage-types';
import { logger } from '@/lib/logger';
import type {
  LocaleDetectionHistory,
  StorageOperationResult,
  UserLocalePreference,
} from '@/lib/locale-storage-types';

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®Ê∏ÖÁêÜÁÆ°ÁêÜÂô®
 * Locale storage cleanup manager
 */
export class LocaleCleanupManager {
  /**
   * Ê∏ÖÈô§ÊâÄÊúâÂ≠òÂÇ®Êï∞ÊçÆ
   * Clear all storage data
   */
  static clearAll(): StorageOperationResult {
    try {
      // Ê∏ÖÈô§ Cookies
      Object.values(STORAGE_KEYS).forEach((key) => {
        CookieManager.remove(key);
      });

      // Ê∏ÖÈô§ localStorage
      Object.values(STORAGE_KEYS).forEach((key) => {
        LocalStorageManager.remove(key);
      });

      return {
        success: true,
        timestamp: Date.now(),
      };
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ',
      };
    }
  }

  /**
   * Ê∏ÖÁêÜËøáÊúüÁöÑÊ£ÄÊµãËÆ∞ÂΩï
   * Clean up expired detection records
   */
  static cleanupExpiredDetections(
    maxAgeMs: number = DAYS_PER_MONTH * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW,
  ): StorageOperationResult {
    try {
      const historyData = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );

      if (!historyData || !Array.isArray(historyData.detections)) {
        return {
          success: true,
          timestamp: Date.now(),
        };
      }

      const now = Date.now();
      const originalCount = historyData.detections.length;

      // ËøáÊª§ÊéâËøáÊúüÁöÑÊ£ÄÊµãËÆ∞ÂΩï
      const validDetections = historyData.detections.filter(
        (detection) => now - detection.timestamp <= maxAgeMs,
      );

      const cleanedCount = originalCount - validDetections.length;

      if (cleanedCount > ZERO) {
        const updatedHistory: LocaleDetectionHistory = {
          detections: validDetections,
          history: validDetections,
          lastUpdated: now,
          totalDetections: validDetections.length,
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
          updatedHistory,
        );

        return {
          success: true,
          timestamp: Date.now(),
          data: { cleanedCount, remainingCount: validDetections.length },
        };
      }

      return {
        success: true,
        timestamp: Date.now(),
        data: { cleanedCount: ZERO, remainingCount: originalCount },
      };
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ',
      };
    }
  }

  /**
   * Ê∏ÖÁêÜÁâπÂÆöÁ±ªÂûãÁöÑÂ≠òÂÇ®Êï∞ÊçÆ
   * Clean up specific type of storage data
   */
  static clearSpecificData(
    dataType: keyof typeof STORAGE_KEYS,
  ): StorageOperationResult {
    try {
      const key = STORAGE_KEYS[dataType];

      // ‰ªé‰∏§‰∏™Â≠òÂÇ®‰∏≠ÁßªÈô§
      CookieManager.remove(key);
      LocalStorageManager.remove(key);

      return {
        success: true,
        timestamp: Date.now(),
        data: { dataType, key },
      };
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ',
      };
    }
  }

  /**
   * Ê∏ÖÁêÜÊó†ÊïàÁöÑÁî®Êà∑ÂÅèÂ•ΩÊï∞ÊçÆ
   * Clean up invalid user preference data
   */
  static cleanupInvalidPreferences(): StorageOperationResult {
    try {
      let cleanedItems = ZERO;
      const issues: string[] = [];

      // Ê£ÄÊü•Âπ∂Ê∏ÖÁêÜlocalStorage‰∏≠ÁöÑÂÅèÂ•ΩÊï∞ÊçÆ
      const localPreference = LocalStorageManager.get<UserLocalePreference>(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );

      if (localPreference && !this.isValidPreferenceData(localPreference)) {
        LocalStorageManager.remove(STORAGE_KEYS.LOCALE_PREFERENCE);
        cleanedItems += ONE;
        issues.push('Â∑≤Ê∏ÖÁêÜlocalStorage‰∏≠ÁöÑÊó†ÊïàÂÅèÂ•ΩÊï∞ÊçÆ');
      }

      // Ê£ÄÊü•Âπ∂Ê∏ÖÁêÜCookie‰∏≠ÁöÑÂÅèÂ•ΩÊï∞ÊçÆ
      const cookiePreference = CookieManager.get(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );
      if (cookiePreference) {
        try {
          const parsed = JSON.parse(cookiePreference) as UserLocalePreference;
          if (!this.isValidPreferenceData(parsed)) {
            CookieManager.remove(STORAGE_KEYS.LOCALE_PREFERENCE);
            cleanedItems += ONE;
            issues.push('Â∑≤Ê∏ÖÁêÜCookie‰∏≠ÁöÑÊó†ÊïàÂÅèÂ•ΩÊï∞ÊçÆ');
          }
        } catch {
          CookieManager.remove(STORAGE_KEYS.LOCALE_PREFERENCE);
          cleanedItems += ONE;
          issues.push('Â∑≤Ê∏ÖÁêÜCookie‰∏≠ÁöÑÊ†ºÂºèÈîôËØØÂÅèÂ•ΩÊï∞ÊçÆ');
        }
      }

      return {
        success: true,
        timestamp: Date.now(),
        data: { cleanedItems, issues },
      };
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ',
      };
    }
  }

  /**
   * Ê∏ÖÁêÜÈáçÂ§çÁöÑÊ£ÄÊµãËÆ∞ÂΩï
   * Clean up duplicate detection records
   */
  static cleanupDuplicateDetections(): StorageOperationResult {
    try {
      const historyData = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );

      if (!historyData || !Array.isArray(historyData.detections)) {
        return {
          success: true,
          timestamp: Date.now(),
        };
      }

      const originalCount = historyData.detections.length;
      const seen = new Set<string>();
      const uniqueDetections = historyData.detections.filter((detection) => {
        const key = `${detection.locale}-${detection.source}-${Math.floor(detection.timestamp / MINUTE_MS)}`; // ÊåâÂàÜÈíüÂàÜÁªÑ
        if (seen.has(key)) {
          return false;
        }
        seen.add(key);
        return true;
      });

      const duplicateCount = originalCount - uniqueDetections.length;

      if (duplicateCount > ZERO) {
        const updatedHistory: LocaleDetectionHistory = {
          detections: uniqueDetections,
          history: uniqueDetections,
          lastUpdated: Date.now(),
          totalDetections: uniqueDetections.length,
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
          updatedHistory,
        );

        return {
          success: true,
          timestamp: Date.now(),
          data: { duplicateCount, remainingCount: uniqueDetections.length },
        };
      }

      return {
        success: true,
        timestamp: Date.now(),
        data: { duplicateCount: ZERO, remainingCount: originalCount },
      };
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ',
      };
    }
  }

  /**
   * È™åËØÅÂÅèÂ•ΩÊï∞ÊçÆÊòØÂê¶ÊúâÊïà
   * Validate if preference data is valid
   */
  private static isValidPreferenceData(
    preference: UserLocalePreference,
  ): boolean {
    if (!preference || typeof preference !== 'object') return false;

    const requiredFields = ['locale', 'source', 'timestamp', 'confidence'];
    const hasAllFields = requiredFields.every((field) => field in preference);

    if (!hasAllFields) return false;

    // È™åËØÅÂ≠óÊÆµÁ±ªÂûã
    if (typeof preference.locale !== 'string') return false;
    if (typeof preference.source !== 'string') return false;
    if (typeof preference.timestamp !== 'number') return false;
    if (typeof preference.confidence !== 'number') return false;

    // È™åËØÅÂÄºÁöÑÂêàÁêÜÊÄß
    if (preference.confidence < ZERO || preference.confidence > ONE) return false;
    if (preference.timestamp > Date.now() || preference.timestamp < ZERO)
      return false;

    return true;
  }

  /**
   * Ëé∑ÂèñÊ∏ÖÁêÜÁªüËÆ°‰ø°ÊÅØ
   * Get cleanup statistics
   */
  static getCleanupStats(): {
    totalItems: number;
    expiredDetections: number;
    invalidPreferences: number;
    duplicateDetections: number;
  } {
    let totalItems = ZERO;
    let expiredDetections = ZERO;
    let invalidPreferences = ZERO;
    let duplicateDetections = ZERO;

    try {
      // ÁªüËÆ°ÊÄªÈ°πÁõÆÊï∞
      Object.values(STORAGE_KEYS).forEach((key) => {
        if (LocalStorageManager.get(key) !== null) totalItems += ONE;
        if (CookieManager.get(key) !== null) totalItems += ONE;
      });

      // ÁªüËÆ°ËøáÊúüÊ£ÄÊµãËÆ∞ÂΩï
      const historyData = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );
      if (historyData?.detections) {
        const now = Date.now();
        const maxAge = DAYS_PER_MONTH * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW; // 30Â§©
        expiredDetections = historyData.detections.filter(
          (detection) => now - detection.timestamp > maxAge,
        ).length;

        // ÁªüËÆ°ÈáçÂ§çÊ£ÄÊµãËÆ∞ÂΩï
        const seen = new Set<string>();
        historyData.detections.forEach((detection) => {
          const key = `${detection.locale}-${detection.source}-${Math.floor(detection.timestamp / MINUTE_MS)}`;
          if (seen.has(key)) {
            duplicateDetections += ONE;
          } else {
            seen.add(key);
          }
        });
      }

      // ÁªüËÆ°Êó†ÊïàÂÅèÂ•ΩÊï∞ÊçÆ
      const localPreference = LocalStorageManager.get<UserLocalePreference>(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );
      if (localPreference && !this.isValidPreferenceData(localPreference)) {
        invalidPreferences += ONE;
      }

      const cookiePreference = CookieManager.get(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );
      if (cookiePreference) {
        try {
          const parsed = JSON.parse(cookiePreference) as UserLocalePreference;
          if (!this.isValidPreferenceData(parsed)) {
            invalidPreferences += ONE;
          }
        } catch {
          invalidPreferences += ONE;
        }
      }
    } catch (error) {
      logger.warn('Ëé∑ÂèñÊ∏ÖÁêÜÁªüËÆ°‰ø°ÊÅØÊó∂Âá∫Èîô', { error: error as Error });
    }

    return {
      totalItems,
      expiredDetections,
      invalidPreferences,
      duplicateDetections,
    };
  }
}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®ÂØºÂÖ•ÂØºÂá∫Êìç‰Ωú
 * Locale Storage Import/Export Operations
 *
 * Ë¥üË¥£Â≠òÂÇ®Êï∞ÊçÆÁöÑÂØºÂÖ•„ÄÅÂØºÂá∫ÂíåÂ§á‰ªΩÊÅ¢Â§ç
 */

'use client';

import { COUNT_FIVE, COUNT_PAIR, ONE, ZERO } from "@/constants/magic-numbers";
import { CookieManager } from '@/lib/locale-storage-cookie';
import { LocalStorageManager } from '@/lib/locale-storage-local';
import { LocaleValidationManager } from '@/lib/locale-storage-maintenance-validation';
import { STORAGE_KEYS } from '@/lib/locale-storage-types';
import { logger } from '@/lib/logger';
import type { Locale } from '@/types/i18n';
import type {
  LocaleDetectionHistory,
  StorageOperationResult,
  UserLocalePreference
} from '@/lib/locale-storage-types';

/**
 * ÂØºÂá∫Êï∞ÊçÆÊé•Âè£
 * Export data interface
 */
export interface ExportData {
  preference?: UserLocalePreference;
  override?: Locale;
  history?: LocaleDetectionHistory;
  version: string;
  timestamp: number;
  metadata: {
    userAgent: string;
    exportedBy: string;
    dataIntegrity: string;
  };
}

/**
 * ÂØºÂÖ•Êï∞ÊçÆÊé•Âè£
 * Import data interface
 */
export interface ImportData extends ExportData {}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®ÂØºÂÖ•ÂØºÂá∫ÁÆ°ÁêÜÂô®
 * Locale storage import/export manager
 */
export class LocaleImportExportManager {
  /**
   * ÂØºÂá∫ÊâÄÊúâÂ≠òÂÇ®Êï∞ÊçÆ
   * Export all storage data
   */
  static exportData(): ExportData {
    const preference = LocalStorageManager.get<UserLocalePreference>(
      STORAGE_KEYS.LOCALE_PREFERENCE,
    );
    const override = LocalStorageManager.get<Locale>(
      STORAGE_KEYS.USER_LOCALE_OVERRIDE,
    );
    const history = LocalStorageManager.get<LocaleDetectionHistory>(
      STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
    );

    const result: ExportData = {
      version: '1.0.0',
      timestamp: Date.now(),
      metadata: {
        userAgent:
          typeof navigator !== 'undefined' ? navigator.userAgent : 'Unknown',
        exportedBy: 'LocaleMaintenanceManager',
        dataIntegrity: this.calculateDataChecksum(),
      },
    };

    // Âè™ÊúâÂΩìÂÄºÂ≠òÂú®Êó∂ÊâçËÆæÁΩÆÂèØÈÄâÂ±ûÊÄß
    if (preference) result.preference = preference;
    if (override) result.override = override;
    if (history) result.history = history;

    return result;
  }

  /**
   * ÂØºÂÖ•Â≠òÂÇ®Êï∞ÊçÆ
   * Import storage data
   */
  static importData(data: ImportData): StorageOperationResult {
    try {
      let importedItems = ZERO;
      const errors: string[] = [];

      // È™åËØÅÂØºÂÖ•Êï∞ÊçÆÁâàÊú¨
      if (data.version && data.version !== '1.0.0') {
        return {
          success: false,
          timestamp: Date.now(),
          error: `‰∏çÊîØÊåÅÁöÑÊï∞ÊçÆÁâàÊú¨: ${data.version}`,
        };
      }

      // ÂØºÂÖ•Áî®Êà∑ÂÅèÂ•ΩÊï∞ÊçÆ
      if (data.preference) {
        if (LocaleValidationManager.validatePreferenceData(data.preference)) {
          LocalStorageManager.set(
            STORAGE_KEYS.LOCALE_PREFERENCE,
            data.preference,
          );
          CookieManager.set(
            STORAGE_KEYS.LOCALE_PREFERENCE,
            JSON.stringify(data.preference),
          );
          importedItems += ONE;
        } else {
          errors.push('Áî®Êà∑ÂÅèÂ•ΩÊï∞ÊçÆÊ†ºÂºèÊó†Êïà');
        }
      }

      // ÂØºÂÖ•Áî®Êà∑Ë¶ÜÁõñËÆæÁΩÆ
      if (data.override) {
        if (typeof data.override === 'string') {
          LocalStorageManager.set(
            STORAGE_KEYS.USER_LOCALE_OVERRIDE,
            data.override,
          );
          CookieManager.set(STORAGE_KEYS.USER_LOCALE_OVERRIDE, data.override);
          importedItems += ONE;
        } else {
          errors.push('Áî®Êà∑Ë¶ÜÁõñËÆæÁΩÆÊ†ºÂºèÊó†Êïà');
        }
      }

      // ÂØºÂÖ•Ê£ÄÊµãÂéÜÂè≤Êï∞ÊçÆ
      if (data.history) {
        if (LocaleValidationManager.validateHistoryData(data.history)) {
          LocalStorageManager.set(
            STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
            data.history,
          );
          importedItems += ONE;
        } else {
          errors.push('Ê£ÄÊµãÂéÜÂè≤Êï∞ÊçÆÊ†ºÂºèÊó†Êïà');
        }
      }

      return {
        success: errors.length === ZERO,
        timestamp: Date.now(),
        data: { importedItems, errors },
        ...(errors.length > ZERO && {
          error: `ÂØºÂÖ•ÂÆåÊàêÔºå‰ΩÜÊúâ ${errors.length} ‰∏™ÈîôËØØ`,
        }),
      };
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ',
      };
    }
  }

  /**
   * ÂØºÂá∫‰∏∫JSONÂ≠óÁ¨¶‰∏≤
   * Export as JSON string
   */
  static exportAsJson(): string {
    const data = this.exportData();
    return JSON.stringify(data, null, COUNT_PAIR);
  }

  /**
   * ‰ªéJSONÂ≠óÁ¨¶‰∏≤ÂØºÂÖ•
   * Import from JSON string
   */
  static importFromJson(jsonString: string): StorageOperationResult {
    try {
      const data = JSON.parse(jsonString) as ImportData;
      return this.importData(data);
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'JSONËß£ÊûêÈîôËØØ',
      };
    }
  }

  /**
   * ÂàõÂª∫Â§á‰ªΩ
   * Create backup
   */
  static createBackup(): StorageOperationResult {
    try {
      const exportData = this.exportData();
      const backupKey = `locale_backup_${Date.now()}`;

      // Â∞ÜÂ§á‰ªΩÂ≠òÂÇ®Âà∞localStorageÔºà‰ΩøÁî®ÁâπÊÆäÂâçÁºÄÔºâ
      LocalStorageManager.set(backupKey, exportData);

      return {
        success: true,
        timestamp: Date.now(),
        data: { backupKey, backupData: exportData },
      };
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ',
      };
    }
  }

  /**
   * ÊÅ¢Â§çÂ§á‰ªΩ
   * Restore backup
   */
  static restoreBackup(backupKey: string): StorageOperationResult {
    try {
      const backupData = LocalStorageManager.get<ExportData>(backupKey);

      if (!backupData) {
        return {
          success: false,
          timestamp: Date.now(),
          error: 'Â§á‰ªΩÊï∞ÊçÆ‰∏çÂ≠òÂú®',
        };
      }

      const importResult = this.importData(backupData);
      if (importResult.success) {
        return {
          success: true,
          timestamp: Date.now(),
          data: importResult.data,
        };
      }
      return importResult;
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ',
      };
    }
  }

  /**
   * ÂàóÂá∫ÊâÄÊúâÂ§á‰ªΩ
   * List all backups
   */
  static listBackups(): Array<{
    key: string;
    timestamp: number;
    size: number;
    isValid: boolean;
  }> {
    const backups: Array<{
      key: string;
      timestamp: number;
      size: number;
      isValid: boolean;
    }> = [];

    try {
      // ÈÅçÂéÜlocalStorageÊü•ÊâæÂ§á‰ªΩ
      if (typeof localStorage !== 'undefined') {
        for (let i = ZERO; i < localStorage.length; i++) {
          const key = localStorage.key(i);
          if (key && key.startsWith('locale_backup_')) {
            try {
              const data = localStorage.getItem(key);
              if (data) {
                const parsed = JSON.parse(data) as ExportData;
                backups.push({
                  key,
                  timestamp: parsed.timestamp || ZERO,
                  size: data.length,
                  isValid: this.validateExportData(parsed),
                });
              }
            } catch {
              // ÂøΩÁï•Êó†ÊïàÁöÑÂ§á‰ªΩÊï∞ÊçÆ
              backups.push({
                key,
                timestamp: ZERO,
                size: ZERO,
                isValid: false,
              });
            }
          }
        }
      }
    } catch (error) {
      logger.warn('ÂàóÂá∫Â§á‰ªΩÊó∂Âá∫Èîô', { error: error as Error });
    }

    // ÊåâÊó∂Èó¥Êà≥ÊéíÂ∫èÔºàÊúÄÊñ∞ÁöÑÂú®ÂâçÔºâ
    return backups.sort((a, b) => b.timestamp - a.timestamp);
  }

  /**
   * Âà†Èô§Â§á‰ªΩ
   * Delete backup
   */
  static deleteBackup(backupKey: string): StorageOperationResult {
    try {
      if (!backupKey.startsWith('locale_backup_')) {
        return {
          success: false,
          timestamp: Date.now(),
          error: 'Êó†ÊïàÁöÑÂ§á‰ªΩÈîÆÂêç',
        };
      }

      LocalStorageManager.remove(backupKey);

      return {
        success: true,
        timestamp: Date.now(),
        data: { deletedKey: backupKey },
      };
    } catch (error) {
      return {
        success: false,
        timestamp: Date.now(),
        error: error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ',
      };
    }
  }

  /**
   * Ê∏ÖÁêÜÊóßÂ§á‰ªΩ
   * Clean up old backups
   */
  static cleanupOldBackups(maxBackups: number = COUNT_FIVE): StorageOperationResult {
    try {
      const backups = this.listBackups();

      if (backups.length <= maxBackups) {
        return {
          success: true,
          error: 'Ê≤°ÊúâÈúÄË¶ÅÊ∏ÖÁêÜÁöÑÊóßÂ§á‰ªΩ',
          timestamp: Date.now(),
          data: { totalBackups: backups.length, maxBackups },
        };
      }

      const backupsToDelete = backups.slice(maxBackups);
      let deletedCount = ZERO;

      backupsToDelete.forEach((backup) => {
        try {
          LocalStorageManager.remove(backup.key);
          deletedCount += ONE;
        } catch {
          // ÂøΩÁï•Âà†Èô§Â§±Ë¥•ÁöÑÂ§á‰ªΩ
        }
      });

      return {
        success: true,
        error: `Â∑≤Ê∏ÖÁêÜ ${deletedCount} ‰∏™ÊóßÂ§á‰ªΩ`,
        timestamp: Date.now(),
        data: { deletedCount, remainingBackups: backups.length - deletedCount },
      };
    } catch (error) {
      return {
        success: false,
        error: `Ê∏ÖÁêÜÊóßÂ§á‰ªΩÂ§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * È™åËØÅÂØºÂá∫Êï∞ÊçÆ
   * Validate export data
   */
  private static validateExportData(data: ExportData): boolean {
    try {
      // Ê£ÄÊü•Âü∫Êú¨ÁªìÊûÑ
      if (!data || typeof data !== 'object') return false;
      if (typeof data.timestamp !== 'number') return false;

      // È™åËØÅÂÅèÂ•ΩÊï∞ÊçÆÔºàÂ¶ÇÊûúÂ≠òÂú®Ôºâ
      if (
        data.preference &&
        !LocaleValidationManager.validatePreferenceData(data.preference)
      ) {
        return false;
      }

      // È™åËØÅÂéÜÂè≤Êï∞ÊçÆÔºàÂ¶ÇÊûúÂ≠òÂú®Ôºâ
      if (
        data.history &&
        !LocaleValidationManager.validateHistoryData(data.history)
      ) {
        return false;
      }

      // È™åËØÅË¶ÜÁõñËÆæÁΩÆÔºàÂ¶ÇÊûúÂ≠òÂú®Ôºâ
      if (data.override && typeof data.override !== 'string') {
        return false;
      }

      return true;
    } catch {
      return false;
    }
  }

  /**
   * ËÆ°ÁÆóÊï∞ÊçÆÊ†°È™åÂíå
   * Calculate data checksum
   */
  private static calculateDataChecksum(): string {
    try {
      const preference = LocalStorageManager.get(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );
      const override = LocalStorageManager.get(
        STORAGE_KEYS.USER_LOCALE_OVERRIDE,
      );
      const history = LocalStorageManager.get(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );

      const dataString = JSON.stringify({ preference, override, history });

      // ÁÆÄÂçïÁöÑÊ†°È™åÂíåËÆ°ÁÆóÔºàÂÆûÈôÖÂ∫îÁî®‰∏≠ÂèØËÉΩÈúÄË¶ÅÊõ¥Âº∫ÁöÑÁÆóÊ≥ïÔºâ
      let hash = ZERO;
      for (let i = ZERO; i < dataString.length; i++) {
        const char = dataString.charCodeAt(i);
        hash = (hash << COUNT_FIVE) - hash + char;
        hash = hash & hash; // ËΩ¨Êç¢‰∏∫32‰ΩçÊï¥Êï∞
      }

      return Math.abs(hash).toString(16);
    } catch {
      return 'unknown';
    }
  }

  /**
   * Ëé∑ÂèñÂØºÂá∫ÁªüËÆ°‰ø°ÊÅØ
   * Get export statistics
   */
  static getExportStats(): {
    totalItems: number;
    hasPreference: boolean;
    hasOverride: boolean;
    hasHistory: boolean;
    historyRecords: number;
    dataSize: number;
    lastModified: number;
  } {
    const preference = LocalStorageManager.get<UserLocalePreference>(
      STORAGE_KEYS.LOCALE_PREFERENCE,
    );
    const override = LocalStorageManager.get<Locale>(
      STORAGE_KEYS.USER_LOCALE_OVERRIDE,
    );
    const history = LocalStorageManager.get<LocaleDetectionHistory>(
      STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
    );

    const exportData = this.exportData();
    const dataSize = JSON.stringify(exportData).length;

    let lastModified = ZERO;
    if (preference?.timestamp)
      lastModified = Math.max(lastModified, preference.timestamp);
    if (history?.lastUpdated)
      lastModified = Math.max(lastModified, history.lastUpdated);

    return {
      totalItems: [preference, override, history].filter(Boolean).length,
      hasPreference: Boolean(preference),
      hasOverride: Boolean(override),
      hasHistory: Boolean(history),
      historyRecords: history?.detections?.length || ZERO,
      dataSize,
      lastModified,
    };
  }
}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®Áª¥Êä§Êìç‰Ωú
 * Locale Storage Maintenance Operations
 *
 * Ë¥üË¥£Â≠òÂÇ®Êï∞ÊçÆÁöÑÁª¥Êä§„ÄÅÂéãÁº©Âíå‰ºòÂåñÊìç‰Ωú
 */

'use client';

import { ANIMATION_DURATION_VERY_SLOW, COUNT_TEN, COUNT_TRIPLE, DAYS_PER_MONTH, HOURS_PER_DAY, HTTP_OK, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { LocalStorageManager } from '@/lib/locale-storage-local';
import { LocaleCleanupManager } from '@/lib/locale-storage-maintenance-cleanup';
import { LocaleValidationManager } from '@/lib/locale-storage-maintenance-validation';
import { STORAGE_KEYS } from '@/lib/locale-storage-types';
import type {
  LocaleDetectionHistory,
  MaintenanceOptions,
  StorageOperationResult,
  UserLocalePreference,
} from '@/lib/locale-storage-types';

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®Áª¥Êä§Êìç‰ΩúÁÆ°ÁêÜÂô®
 * Locale storage maintenance operations manager
 */
export class LocaleMaintenanceOperationsManager {
  /**
   * ÊâßË°åÂ≠òÂÇ®Áª¥Êä§
   * Perform storage maintenance
   */
  static performMaintenance(
    options: MaintenanceOptions = {},
  ): StorageOperationResult {
    const {
      cleanupExpired = true,
      maxDetectionAge = DAYS_PER_MONTH * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW, // 30Â§©
      validateData = true,
      compactStorage = true,
      fixSyncIssues = true,
      cleanupDuplicates = true,
      cleanupInvalid = true,
    } = options;

    try {
      const results: string[] = [];
      let totalOperations = ZERO;
      let successfulOperations = ZERO;

      // Ê∏ÖÁêÜËøáÊúüÊ£ÄÊµãËÆ∞ÂΩï
      if (cleanupExpired) {
        totalOperations += ONE;
        const cleanupResult =
          LocaleCleanupManager.cleanupExpiredDetections(maxDetectionAge);
        if (cleanupResult.success) {
          successfulOperations += ONE;
          results.push(
            `Ê∏ÖÁêÜËøáÊúüËÆ∞ÂΩïÊàêÂäüÔºåÂà†Èô§‰∫Ü ${cleanupResult.data || ZERO} Êù°ËÆ∞ÂΩï`,
          );
        } else {
          results.push(
            `Ê∏ÖÁêÜËøáÊúüËÆ∞ÂΩïÂ§±Ë¥•: ${cleanupResult.error || 'Êú™Áü•ÈîôËØØ'}`,
          );
        }
      }

      // Ê∏ÖÁêÜÈáçÂ§çËÆ∞ÂΩï
      if (cleanupDuplicates) {
        totalOperations += ONE;
        const duplicateResult =
          LocaleCleanupManager.cleanupDuplicateDetections();
        if (duplicateResult.success) {
          successfulOperations += ONE;
          results.push(
            `Ê∏ÖÁêÜÈáçÂ§çËÆ∞ÂΩïÊàêÂäüÔºåÂà†Èô§‰∫Ü ${duplicateResult.data || ZERO} Êù°ËÆ∞ÂΩï`,
          );
        } else {
          results.push(
            `Ê∏ÖÁêÜÈáçÂ§çËÆ∞ÂΩïÂ§±Ë¥•: ${duplicateResult.error || 'Êú™Áü•ÈîôËØØ'}`,
          );
        }
      }

      // Ê∏ÖÁêÜÊó†ÊïàÊï∞ÊçÆ
      if (cleanupInvalid) {
        totalOperations += ONE;
        const invalidResult = LocaleCleanupManager.cleanupInvalidPreferences();
        if (invalidResult.success) {
          successfulOperations += ONE;
          results.push(
            `Ê∏ÖÁêÜÊó†ÊïàÊï∞ÊçÆÊàêÂäüÔºåÂà†Èô§‰∫Ü ${invalidResult.data || ZERO} Êù°ËÆ∞ÂΩï`,
          );
        } else {
          results.push(
            `Ê∏ÖÁêÜÊó†ÊïàÊï∞ÊçÆÂ§±Ë¥•: ${invalidResult.error || 'Êú™Áü•ÈîôËØØ'}`,
          );
        }
      }

      // È™åËØÅÊï∞ÊçÆÂÆåÊï¥ÊÄß
      if (validateData) {
        totalOperations += ONE;
        const validationResult =
          LocaleValidationManager.validateStorageIntegrity();
        if (validationResult.success) {
          successfulOperations += ONE;
          results.push(`Êï∞ÊçÆÈ™åËØÅÊàêÂäü`);
        } else {
          results.push(`Êï∞ÊçÆÈ™åËØÅÂ§±Ë¥•: ${validationResult.error || 'Êú™Áü•ÈîôËØØ'}`);
        }
      }

      // ‰øÆÂ§çÂêåÊ≠•ÈóÆÈ¢ò
      if (fixSyncIssues) {
        totalOperations += ONE;
        const syncResult = LocaleValidationManager.fixSyncIssues();
        if (syncResult.success) {
          successfulOperations += ONE;
          results.push(`‰øÆÂ§çÂêåÊ≠•ÈóÆÈ¢òÊàêÂäü`);
        } else {
          results.push(`‰øÆÂ§çÂêåÊ≠•ÈóÆÈ¢òÂ§±Ë¥•: ${syncResult.error || 'Êú™Áü•ÈîôËØØ'}`);
        }
      }

      // ÂéãÁº©Â≠òÂÇ®Á©∫Èó¥
      if (compactStorage) {
        totalOperations += ONE;
        const compactResult = this.compactStorage();
        if (compactResult.success) {
          successfulOperations += ONE;
          results.push(`ÂéãÁº©Â≠òÂÇ®ÊàêÂäü`);
        } else {
          results.push(`ÂéãÁº©Â≠òÂÇ®Â§±Ë¥•: ${compactResult.error || 'Êú™Áü•ÈîôËØØ'}`);
        }
      }

      return {
        success: successfulOperations === totalOperations,
        error: `Áª¥Êä§ÂÆåÊàê: ${successfulOperations}/${totalOperations} Êìç‰ΩúÊàêÂäü`,
        timestamp: Date.now(),
        data: {
          totalOperations,
          successfulOperations,
          results,
          options,
        },
      };
    } catch (error) {
      return {
        success: false,
        error: `ÊâßË°åÁª¥Êä§Â§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * ÂéãÁº©Â≠òÂÇ®Á©∫Èó¥
   * Compact storage space
   */
  static compactStorage(): StorageOperationResult {
    try {
      let compactedItems = ZERO;

      // ÈáçÊñ∞Â∫èÂàóÂåñÊâÄÊúâÊï∞ÊçÆ‰ª•ÁßªÈô§Â§ö‰ΩôÁ©∫Ê†º
      Object.values(STORAGE_KEYS).forEach((key) => {
        const data = LocalStorageManager.get(key);
        if (data !== null) {
          // ÈáçÊñ∞ËÆæÁΩÆÊï∞ÊçÆÔºåËøô‰ºöËß¶ÂèëÈáçÊñ∞Â∫èÂàóÂåñ
          LocalStorageManager.set(key, data);
          compactedItems += ONE;
        }
      });

      return {
        success: true,
        error: `Â∑≤ÂéãÁº© ${compactedItems} È°πÂ≠òÂÇ®Êï∞ÊçÆ`,
        timestamp: Date.now(),
        data: { compactedItems },
      };
    } catch (error) {
      return {
        success: false,
        error: `ÂéãÁº©Â≠òÂÇ®Â§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * ‰ºòÂåñÊ£ÄÊµãÂéÜÂè≤Êï∞ÊçÆ
   * Optimize detection history data
   */
  static optimizeDetectionHistory(): StorageOperationResult {
    try {
      const historyData = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );

      if (!historyData || !Array.isArray(historyData.detections)) {
        return {
          success: true,
          error: 'Ê≤°ÊúâÊ£ÄÊµãÂéÜÂè≤Êï∞ÊçÆÈúÄË¶Å‰ºòÂåñ',
          timestamp: Date.now(),
        };
      }

      const originalCount = historyData.detections.length;

      // ÊåâÊó∂Èó¥ÊéíÂ∫èÔºàÊúÄÊñ∞ÁöÑÂú®ÂâçÔºâ
      const sortedDetections = historyData.detections.sort(
        (a, b) => b.timestamp - a.timestamp,
      );

      // ‰øùÁïôÊúÄËøëÁöÑ100Êù°ËÆ∞ÂΩï
      const maxRecords = PERCENTAGE_FULL;
      const optimizedDetections = sortedDetections.slice(ZERO, maxRecords);

      const removedCount = originalCount - optimizedDetections.length;

      if (removedCount > ZERO) {
        const updatedHistory: LocaleDetectionHistory = {
          detections: optimizedDetections,
          history: optimizedDetections,
          lastUpdated: Date.now(),
          totalDetections: optimizedDetections.length,
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
          updatedHistory,
        );

        return {
          success: true,
          error: `Â∑≤‰ºòÂåñÊ£ÄÊµãÂéÜÂè≤ÔºåÁßªÈô§ ${removedCount} Êù°ÊóßËÆ∞ÂΩï`,
          timestamp: Date.now(),
          data: { removedCount, remainingCount: optimizedDetections.length },
        };
      }

      return {
        success: true,
        error: 'Ê£ÄÊµãÂéÜÂè≤Êï∞ÊçÆÂ∑≤ÊòØÊúÄ‰ºòÁä∂ÊÄÅ',
        timestamp: Date.now(),
        data: { removedCount: ZERO, remainingCount: originalCount },
      };
    } catch (error) {
      return {
        success: false,
        error: `‰ºòÂåñÊ£ÄÊµãÂéÜÂè≤Â§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * ÈáçÂª∫Â≠òÂÇ®Á¥¢Âºï
   * Rebuild storage index
   */
  static rebuildStorageIndex(): StorageOperationResult {
    try {
      let rebuiltItems = ZERO;
      const actions: string[] = [];

      // ÈáçÂª∫ÂÅèÂ•ΩÊï∞ÊçÆÁ¥¢Âºï
      const preference = LocalStorageManager.get<UserLocalePreference>(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );
      if (preference) {
        // Á°Æ‰øùÊï∞ÊçÆÁªìÊûÑÂÆåÊï¥
        const rebuiltPreference: UserLocalePreference = {
          locale: preference.locale,
          source: preference.source,
          timestamp: preference.timestamp,
          confidence: preference.confidence,
          ...(preference.metadata && { metadata: preference.metadata }),
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_PREFERENCE,
          rebuiltPreference,
        );
        rebuiltItems += ONE;
        actions.push('ÈáçÂª∫ÂÅèÂ•ΩÊï∞ÊçÆÁ¥¢Âºï');
      }

      // ÈáçÂª∫ÂéÜÂè≤Êï∞ÊçÆÁ¥¢Âºï
      const history = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );
      if (history) {
        // Á°Æ‰øùÊï∞ÊçÆÁªìÊûÑÂÆåÊï¥Âπ∂ÊåâÊó∂Èó¥ÊéíÂ∫è
        const filteredDetections = history.detections
          .filter(
            (detection) => detection && detection.locale && detection.source,
          )
          .sort((a, b) => b.timestamp - a.timestamp);

        const rebuiltHistory: LocaleDetectionHistory = {
          detections: filteredDetections,
          history: filteredDetections,
          lastUpdated: Date.now(),
          totalDetections: filteredDetections.length,
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
          rebuiltHistory,
        );
        rebuiltItems += ONE;
        actions.push('ÈáçÂª∫ÂéÜÂè≤Êï∞ÊçÆÁ¥¢Âºï');
      }

      return {
        success: true,
        error: `Â∑≤ÈáçÂª∫ ${rebuiltItems} È°πÂ≠òÂÇ®Á¥¢Âºï`,
        timestamp: Date.now(),
        data: { rebuiltItems, actions },
      };
    } catch (error) {
      return {
        success: false,
        error: `ÈáçÂª∫Â≠òÂÇ®Á¥¢ÂºïÂ§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * ÊâßË°åÊ∑±Â∫¶Áª¥Êä§
   * Perform deep maintenance
   */
  static performDeepMaintenance(): StorageOperationResult {
    try {
      const results: string[] = [];
      let totalOperations = ZERO;
      let successfulOperations = ZERO;

      // ÊâßË°åÊ†áÂáÜÁª¥Êä§
      totalOperations += ONE;
      const standardResult = this.performMaintenance({
        cleanupExpired: true,
        validateData: true,
        compactStorage: true,
        fixSyncIssues: true,
        cleanupDuplicates: true,
        cleanupInvalid: true,
      });
      if (standardResult.success) {
        successfulOperations += ONE;
        results.push('Ê†áÂáÜÁª¥Êä§ÂÆåÊàê');
      } else {
        results.push(`Ê†áÂáÜÁª¥Êä§Â§±Ë¥•: ${standardResult.error || 'Êú™Áü•ÈîôËØØ'}`);
      }

      // ‰ºòÂåñÊ£ÄÊµãÂéÜÂè≤
      totalOperations += ONE;
      const optimizeResult = this.optimizeDetectionHistory();
      if (optimizeResult.success) {
        successfulOperations += ONE;
        results.push(`‰ºòÂåñÂéÜÂè≤ÊàêÂäü`);
      } else {
        results.push(`‰ºòÂåñÂéÜÂè≤Â§±Ë¥•: ${optimizeResult.error || 'Êú™Áü•ÈîôËØØ'}`);
      }

      // ÈáçÂª∫Â≠òÂÇ®Á¥¢Âºï
      totalOperations += ONE;
      const rebuildResult = this.rebuildStorageIndex();
      if (rebuildResult.success) {
        successfulOperations += ONE;
        results.push(`ÈáçÂª∫Á¥¢ÂºïÊàêÂäü`);
      } else {
        results.push(`ÈáçÂª∫Á¥¢ÂºïÂ§±Ë¥•: ${rebuildResult.error || 'Êú™Áü•ÈîôËØØ'}`);
      }

      return {
        success: successfulOperations === totalOperations,
        error: `Ê∑±Â∫¶Áª¥Êä§ÂÆåÊàê: ${successfulOperations}/${totalOperations} Êìç‰ΩúÊàêÂäü`,
        timestamp: Date.now(),
        data: {
          totalOperations,
          successfulOperations,
          results,
        },
      };
    } catch (error) {
      return {
        success: false,
        error: `ÊâßË°åÊ∑±Â∫¶Áª¥Êä§Â§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * Ëé∑ÂèñÁª¥Êä§Âª∫ËÆÆ
   * Get maintenance recommendations
   */
  static getMaintenanceRecommendations(): {
    recommendations: string[];
    priority: 'low' | 'medium' | 'high';
    estimatedTime: string;
  } {
    const recommendations: string[] = [];
    let priority: 'low' | 'medium' | 'high' = 'low';

    try {
      // Ê£ÄÊü•Ê∏ÖÁêÜÁªüËÆ°
      const cleanupStats = LocaleCleanupManager.getCleanupStats();

      if (cleanupStats.expiredDetections > PERCENTAGE_HALF) {
        recommendations.push(
          `Ê∏ÖÁêÜ ${cleanupStats.expiredDetections} Êù°ËøáÊúüÊ£ÄÊµãËÆ∞ÂΩï`,
        );
        priority = 'medium';
      }

      if (cleanupStats.duplicateDetections > COUNT_TEN) {
        recommendations.push(
          `Ê∏ÖÁêÜ ${cleanupStats.duplicateDetections} Êù°ÈáçÂ§çÊ£ÄÊµãËÆ∞ÂΩï`,
        );
        priority = 'medium';
      }

      if (cleanupStats.invalidPreferences > ZERO) {
        recommendations.push(
          `‰øÆÂ§ç ${cleanupStats.invalidPreferences} È°πÊó†ÊïàÂÅèÂ•ΩÊï∞ÊçÆ`,
        );
        priority = 'high';
      }

      // Ê£ÄÊü•È™åËØÅÁªüËÆ°
      const validationSummary = LocaleValidationManager.getValidationSummary();

      if (validationSummary.invalidKeys > ZERO) {
        recommendations.push(
          `‰øÆÂ§ç ${validationSummary.invalidKeys} È°πÊó†ÊïàÊï∞ÊçÆ`,
        );
        priority = 'high';
      }

      if (validationSummary.syncIssues > ZERO) {
        recommendations.push(`‰øÆÂ§ç ${validationSummary.syncIssues} ‰∏™ÂêåÊ≠•ÈóÆÈ¢ò`);
        priority = 'medium';
      }

      // Ê£ÄÊü•ÂéÜÂè≤Êï∞ÊçÆÂ§ßÂ∞è
      const historyData = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );
      if (historyData?.detections && historyData.detections.length > HTTP_OK) {
        recommendations.push('‰ºòÂåñÊ£ÄÊµãÂéÜÂè≤Êï∞ÊçÆÔºàËÆ∞ÂΩïËøáÂ§öÔºâ');
        if (priority === 'low') priority = 'medium';
      }

      if (recommendations.length === ZERO) {
        recommendations.push('Â≠òÂÇ®Áä∂ÊÄÅËâØÂ•ΩÔºåÊó†ÈúÄÁâπÊÆäÁª¥Êä§');
      }

      // ‰º∞ÁÆóÁª¥Êä§Êó∂Èó¥
      let estimatedTime = '< 1ÂàÜÈíü';
      if (recommendations.length > COUNT_TRIPLE) {
        estimatedTime = '1-2ÂàÜÈíü';
      }
      if (priority === 'high') {
        estimatedTime = '2-5ÂàÜÈíü';
      }

      return {
        recommendations,
        priority,
        estimatedTime,
      };
    } catch (error) {
      return {
        recommendations: ['Ëé∑ÂèñÁª¥Êä§Âª∫ËÆÆÊó∂Âá∫ÈîôÔºåÂª∫ËÆÆÊâßË°åÂü∫Á°ÄÁª¥Êä§'],
        priority: 'medium',
        estimatedTime: '1-2ÂàÜÈíü',
      };
    }
  }
}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®È™åËØÅÊìç‰Ωú
 * Locale Storage Validation Operations
 *
 * Ë¥üË¥£Â≠òÂÇ®Êï∞ÊçÆÁöÑÂÆåÊï¥ÊÄßÈ™åËØÅÂíåÂêåÊ≠•Áä∂ÊÄÅÊ£ÄÊü•
 */

'use client';

import { ONE, ZERO } from "@/constants/magic-numbers";
import { MINUTE_MS } from "@/constants/time";
import { CookieManager } from '@/lib/locale-storage-cookie';
import { LocalStorageManager } from '@/lib/locale-storage-local';
import { STORAGE_KEYS } from '@/lib/locale-storage-types';
import type { Locale } from '@/types/i18n';
import type {
  LocaleDetectionHistory,
  StorageOperationResult,
  UserLocalePreference,
  ValidationResult,
} from '@/lib/locale-storage-types';

/**
 * Â≠òÂÇ®È™åËØÅÊï∞ÊçÆÁªìÊûÑ
 * Storage validation data structure
 */
interface StorageValidationData {
  hasLocalData: boolean;
  hasCookieData: boolean;
  localDataValid: boolean;
  cookieDataValid: boolean;
}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®È™åËØÅÁÆ°ÁêÜÂô®
 * Locale storage validation manager
 */
export class LocaleValidationManager {
  /**
   * È™åËØÅÂ≠òÂÇ®Êï∞ÊçÆÂÆåÊï¥ÊÄß
   * Validate storage data integrity
   */
  static validateStorageIntegrity(): StorageOperationResult {
    const issues: string[] = [];

    try {
      // È™åËØÅÁî®Êà∑ÂÅèÂ•ΩÊï∞ÊçÆ
      const preference = LocalStorageManager.get<UserLocalePreference>(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );
      if (preference && !this.validatePreferenceData(preference)) {
        issues.push('Áî®Êà∑ÂÅèÂ•ΩÊï∞ÊçÆÊ†ºÂºèÊó†Êïà');
      }

      // È™åËØÅÊ£ÄÊµãÂéÜÂè≤Êï∞ÊçÆ
      const history = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );
      if (history && !this.validateHistoryData(history)) {
        issues.push('Ê£ÄÊµãÂéÜÂè≤Êï∞ÊçÆÊ†ºÂºèÊó†Êïà');
      }

      // È™åËØÅÂ≠òÂÇ®ÂêåÊ≠•Áä∂ÊÄÅ
      const syncIssues = this.validateStorageSync();
      issues.push(...syncIssues);

      if (issues.length === ZERO) {
        return {
          success: true,
          timestamp: Date.now(),
          data: { issues },
        };
      }
        return {
          success: false,
          error: `ÂèëÁé∞ ${issues.length} ‰∏™ÈóÆÈ¢ò`,
          timestamp: Date.now(),
          data: { issues },
        };

    } catch (error) {
      return {
        success: false,
        error: `È™åËØÅÂ≠òÂÇ®ÂÆåÊï¥ÊÄßÂ§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * È™åËØÅÁî®Êà∑ÂÅèÂ•ΩÊï∞ÊçÆ
   * Validate user preference data
   */
  static validatePreferenceData(preference: UserLocalePreference): boolean {
    if (!preference || typeof preference !== 'object') return false;

    const requiredFields = ['locale', 'source', 'timestamp', 'confidence'];
    const hasAllFields = requiredFields.every((field) => field in preference);

    if (!hasAllFields) return false;

    // È™åËØÅÂ≠óÊÆµÁ±ªÂûã
    if (typeof preference.locale !== 'string') return false;
    if (typeof preference.source !== 'string') return false;
    if (typeof preference.timestamp !== 'number') return false;
    if (typeof preference.confidence !== 'number') return false;

    // È™åËØÅÂÄºÁöÑÂêàÁêÜÊÄß
    if (preference.confidence < ZERO || preference.confidence > ONE) return false;
    if (preference.timestamp > Date.now() || preference.timestamp < ZERO)
      return false;

    return true;
  }

  /**
   * È™åËØÅÊ£ÄÊµãÂéÜÂè≤Êï∞ÊçÆ
   * Validate detection history data
   */
  static validateHistoryData(history: LocaleDetectionHistory): boolean {
    if (!history || typeof history !== 'object') return false;
    if (!Array.isArray(history.detections)) return false;
    if (typeof history.lastUpdated !== 'number') return false;

    return history.detections.every(
      (detection) =>
        typeof detection.locale === 'string' &&
        typeof detection.source === 'string' &&
        typeof detection.timestamp === 'number' &&
        typeof detection.confidence === 'number' &&
        detection.confidence >= ZERO &&
        detection.confidence <= ONE,
    );
  }

  /**
   * È™åËØÅÂ≠òÂÇ®ÂêåÊ≠•Áä∂ÊÄÅ
   * Validate storage synchronization
   */
  static validateStorageSync(): string[] {
    const issues: string[] = [];

    // Ê£ÄÊü•ÂÖ≥ÈîÆÊï∞ÊçÆÁöÑÂêåÊ≠•Áä∂ÊÄÅ
    const localPreference = LocalStorageManager.get<UserLocalePreference>(
      STORAGE_KEYS.LOCALE_PREFERENCE,
    );
    const cookiePreference = CookieManager.get(STORAGE_KEYS.LOCALE_PREFERENCE);

    if (localPreference && !cookiePreference) {
      issues.push('Áî®Êà∑ÂÅèÂ•ΩÂú®localStorage‰∏≠Â≠òÂú®‰ΩÜCookie‰∏≠Áº∫Â§±');
    }

    const localOverride = LocalStorageManager.get<Locale>(
      STORAGE_KEYS.USER_LOCALE_OVERRIDE,
    );
    const cookieOverride = CookieManager.get(STORAGE_KEYS.USER_LOCALE_OVERRIDE);

    if (localOverride && !cookieOverride) {
      issues.push('Áî®Êà∑Ë¶ÜÁõñËÆæÁΩÆÂú®localStorage‰∏≠Â≠òÂú®‰ΩÜCookie‰∏≠Áº∫Â§±');
    }

    return issues;
  }

  /**
   * È™åËØÅÁâπÂÆöÂ≠òÂÇ®ÈîÆÁöÑÊï∞ÊçÆ
   * Validate data for specific storage key
   */
  static validateSpecificData(
    key: keyof typeof STORAGE_KEYS,
  ): ValidationResult<StorageValidationData> {
    try {
      const storageKey = STORAGE_KEYS[key];
      const localData = LocalStorageManager.get(storageKey);
      const cookieData = CookieManager.get(storageKey);

      const result: ValidationResult<StorageValidationData> = {
        isValid: true,
        errors: [],
        warnings: [],
        data: {
          hasLocalData: localData !== null,
          hasCookieData: cookieData !== null,
          localDataValid: true,
          cookieDataValid: true,
        },
      };

      // È™åËØÅlocalStorageÊï∞ÊçÆ
      if (localData !== null) {
        if (key === 'LOCALE_PREFERENCE') {
          const isValid = this.validatePreferenceData(
            localData as UserLocalePreference,
          );
          result.data!.localDataValid = isValid;
          if (!isValid) {
            result.isValid = false;
            result.errors.push('localStorage‰∏≠ÁöÑÂÅèÂ•ΩÊï∞ÊçÆÊ†ºÂºèÊó†Êïà');
          }
        } else if (key === 'LOCALE_DETECTION_HISTORY') {
          const isValid = this.validateHistoryData(
            localData as LocaleDetectionHistory,
          );
          result.data!.localDataValid = isValid;
          if (!isValid) {
            result.isValid = false;
            result.errors.push('localStorage‰∏≠ÁöÑÂéÜÂè≤Êï∞ÊçÆÊ†ºÂºèÊó†Êïà');
          }
        }
      }

      // È™åËØÅCookieÊï∞ÊçÆ
      if (cookieData !== null) {
        try {
          if (key === 'LOCALE_PREFERENCE') {
            const parsed = JSON.parse(cookieData) as UserLocalePreference;
            const isValid = this.validatePreferenceData(parsed);
            result.data!.cookieDataValid = isValid;
            if (!isValid) {
              result.isValid = false;
              result.errors.push('Cookie‰∏≠ÁöÑÂÅèÂ•ΩÊï∞ÊçÆÊ†ºÂºèÊó†Êïà');
            }
          }
        } catch {
          result.isValid = false;
          result.data!.cookieDataValid = false;
          result.errors.push('CookieÊï∞ÊçÆJSONÊ†ºÂºèÈîôËØØ');
        }
      }

      // Ê£ÄÊü•ÂêåÊ≠•Áä∂ÊÄÅ
      if (localData !== null && cookieData === null) {
        result.warnings.push('Êï∞ÊçÆ‰ªÖÂ≠òÂú®‰∫élocalStorage‰∏≠ÔºåCookie‰∏≠Áº∫Â§±');
      } else if (localData === null && cookieData !== null) {
        result.warnings.push('Êï∞ÊçÆ‰ªÖÂ≠òÂú®‰∫éCookie‰∏≠ÔºålocalStorage‰∏≠Áº∫Â§±');
      }

      return result;
    } catch (error) {
      return {
        isValid: false,
        errors: [
          `È™åËØÅÂ§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        ],
        warnings: [],
        data: {
          hasLocalData: false,
          hasCookieData: false,
          localDataValid: false,
          cookieDataValid: false,
        },
      };
    }
  }

  /**
   * È™åËØÅÊâÄÊúâÂ≠òÂÇ®Êï∞ÊçÆ
   * Validate all storage data
   */
  static validateAllData(): Record<string, ValidationResult> {
    const results: Record<string, ValidationResult> = {};

    Object.keys(STORAGE_KEYS).forEach((key) => {
      results[key] = this.validateSpecificData(
        key as keyof typeof STORAGE_KEYS,
      );
    });

    return results;
  }

  /**
   * Ê£ÄÊü•Êï∞ÊçÆ‰∏ÄËá¥ÊÄß
   * Check data consistency
   */
  static checkDataConsistency(): StorageOperationResult {
    try {
      const issues: string[] = [];
      const warnings: string[] = [];

      // Ê£ÄÊü•ÂÅèÂ•ΩÊï∞ÊçÆ‰∏ÄËá¥ÊÄß
      const localPreference = LocalStorageManager.get<UserLocalePreference>(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );
      const cookiePreferenceStr = CookieManager.get(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );

      if (localPreference && cookiePreferenceStr) {
        try {
          const cookiePreference = JSON.parse(
            cookiePreferenceStr,
          ) as UserLocalePreference;

          if (localPreference.locale !== cookiePreference.locale) {
            issues.push('localStorageÂíåCookie‰∏≠ÁöÑËØ≠Ë®ÄÂÅèÂ•Ω‰∏ç‰∏ÄËá¥');
          }

          if (
            Math.abs(localPreference.timestamp - cookiePreference.timestamp) >
            MINUTE_MS
          ) {
            // 1ÂàÜÈíüÂ∑ÆÂºÇ
            warnings.push('localStorageÂíåCookie‰∏≠ÁöÑÂÅèÂ•ΩÊó∂Èó¥Êà≥Â∑ÆÂºÇËæÉÂ§ß');
          }
        } catch {
          issues.push('Cookie‰∏≠ÁöÑÂÅèÂ•ΩÊï∞ÊçÆÊ†ºÂºèÈîôËØØ');
        }
      }

      // Ê£ÄÊü•Ë¶ÜÁõñËÆæÁΩÆ‰∏ÄËá¥ÊÄß
      const localOverride = LocalStorageManager.get<Locale>(
        STORAGE_KEYS.USER_LOCALE_OVERRIDE,
      );
      const cookieOverride = CookieManager.get(
        STORAGE_KEYS.USER_LOCALE_OVERRIDE,
      );

      if (localOverride && cookieOverride && localOverride !== cookieOverride) {
        issues.push('localStorageÂíåCookie‰∏≠ÁöÑËØ≠Ë®ÄË¶ÜÁõñËÆæÁΩÆ‰∏ç‰∏ÄËá¥');
      }

      if (issues.length === ZERO) {
        return {
          success: true,
          timestamp: Date.now(),
          data: { issues, warnings },
        };
      }
        return {
          success: false,
          error: `ÂèëÁé∞ ${issues.length} ‰∏™‰∏ÄËá¥ÊÄßÈóÆÈ¢ò`,
          timestamp: Date.now(),
          data: { issues, warnings },
        };

    } catch (error) {
      return {
        success: false,
        error: `Êï∞ÊçÆ‰∏ÄËá¥ÊÄßÊ£ÄÊü•Â§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
