/**
 * ÂÅèÂ•ΩÁºìÂ≠òÁÆ°ÁêÜÂô®
 * Preference cache manager
 */
export class PreferenceCacheManager {
  private static cache: Map<string, UserLocalePreference> = new Map();
  private static cacheTimestamp = ZERO;
  private static readonly CACHE_TTL = COUNT_FIVE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW; // 5 minutes

  /**
   * Ëé∑ÂèñÁºìÂ≠òÁöÑÂÅèÂ•Ω
   * Get cached preference
   */
  static getCachedPreference(key: string): UserLocalePreference | null {
    if (Date.now() - this.cacheTimestamp > this.CACHE_TTL) {
      this.cache.clear();
      return null;
    }

    return this.cache.get(key) || null;
  }

  /**
   * Êõ¥Êñ∞ÁºìÂ≠ò
   * Update cache
   */
  static updateCache(key: string, preference: UserLocalePreference): void {
    this.cache.set(key, preference);
    this.cacheTimestamp = Date.now();
  }

  /**
   * Ê∏ÖÈô§ÁºìÂ≠ò
   * Clear cache
   */
  static clearCache(key?: string): void {
    if (key) {
      this.cache.delete(key);
    } else {
      this.cache.clear();
      this.cacheTimestamp = ZERO;
    }
  }

  /**
   * Ëé∑ÂèñÁºìÂ≠òÁä∂ÊÄÅ
   * Get cache status
   */
  static getCacheStatus(): {
    size: number;
    age: number;
    isExpired: boolean;
    keys: string[];
  } {
    const age = Date.now() - this.cacheTimestamp;
    const isExpired = age > this.CACHE_TTL;

    return {
      size: this.cache.size,
      age,
      isExpired,
      keys: Array.from(this.cache.keys()),
    };
  }

  /**
   * È¢ÑÁÉ≠ÁºìÂ≠ò
   * Warm up cache
   */
  static warmUpCache(): void {
    const preference = getUserPreference();
    if (preference.success && preference.data) {
      this.updateCache('locale_preference', preference.data);
    }
  }
}

// ==================== Êï∞ÊçÆÂêåÊ≠•ÂäüËÉΩ ====================

/**
 * ÂêåÊ≠•ÂÅèÂ•ΩÊï∞ÊçÆ
 * Sync preference data
 */
export function syncPreferenceData(): StorageOperationResult<{
  localStorage: UserLocalePreference | null;
  cookies: Locale | null;
  synced: boolean;
}> {
  const startTime = Date.now();

  try {
    // Ëé∑ÂèñÂêÑÂ≠òÂÇ®Ê∫êÁöÑÊï∞ÊçÆ
    const localPreference =
      LocalStorageManager.get<UserLocalePreference>('locale_preference');
    const cookieLocale = CookieManager.get('locale_preference');

    let synced = false;
    let primaryPreference: UserLocalePreference | null = null;

    // Á°ÆÂÆö‰∏ªË¶ÅÊï∞ÊçÆÊ∫ê
    if (localPreference && validatePreferenceData(localPreference).isValid) {
      primaryPreference = localPreference;

      // ÂêåÊ≠•Âà∞ cookies
      if (cookieLocale !== localPreference.locale) {
        CookieManager.set('locale_preference', localPreference.locale);
        synced = true;
      }
    } else if (cookieLocale) {
      // ‰ªé cookies ÂàõÂª∫ÂÅèÂ•ΩÂπ∂ÂêåÊ≠•Âà∞ localStorage
      primaryPreference = {
        locale: cookieLocale as Locale,
        source: 'browser',
        confidence: MAGIC_0_7,
        timestamp: Date.now(),
        metadata: { syncedFrom: 'cookies' },
      };

      const saveResult = saveUserPreference(primaryPreference);
      synced = saveResult.success;
    }

    // Êõ¥Êñ∞ÁºìÂ≠ò
    if (primaryPreference) {
      PreferenceCacheManager.updateCache(
        'locale_preference',
        primaryPreference,
      );
    }

    return {
      success: true,
      data: {
        localStorage: localPreference,
        cookies: cookieLocale as Locale | null,
        synced,
      },
      source: 'sync',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Ê£ÄÊü•Êï∞ÊçÆ‰∏ÄËá¥ÊÄß
 * Check data consistency
 */
export function checkDataConsistency(): {
  isConsistent: boolean;
  issues: string[];
  recommendations: string[];
} {
  const issues: string[] = [];
  const recommendations: string[] = [];

  try {
    const localPreference =
      LocalStorageManager.get<UserLocalePreference>('locale_preference');
    const cookieLocale = CookieManager.get('locale_preference');

    // Ê£ÄÊü• localStorage Êï∞ÊçÆÊúâÊïàÊÄß
    if (localPreference) {
      const validation = validatePreferenceData(localPreference);
      if (!validation.isValid) {
        issues.push(
          `Invalid localStorage data: ${validation.errors.join(', ')}`,
        );
        recommendations.push('Clear and recreate localStorage preference data');
      }
    }

    // Ê£ÄÊü•Êï∞ÊçÆ‰∏ÄËá¥ÊÄß
    if (localPreference && cookieLocale) {
      if (localPreference.locale !== cookieLocale) {
        issues.push(
          `Locale mismatch: localStorage(${localPreference.locale}) vs cookies(${cookieLocale})`,
        );
        recommendations.push('Sync data between localStorage and cookies');
      }
    } else if (!localPreference && !cookieLocale) {
      issues.push('No preference data found in any storage');
      recommendations.push('Initialize default preference data');
    }

    // Ê£ÄÊü•ÁºìÂ≠ò‰∏ÄËá¥ÊÄß
    const cachedPreference =
      PreferenceCacheManager.getCachedPreference('locale_preference');
    if (cachedPreference && localPreference) {
      if (
        cachedPreference.locale !== localPreference.locale ||
        Math.abs(cachedPreference.timestamp - localPreference.timestamp) > ANIMATION_DURATION_VERY_SLOW
      ) {
        issues.push('Cache data is inconsistent with localStorage');
        recommendations.push('Clear and refresh cache');
      }
    }

    return {
      isConsistent: issues.length === ZERO,
      issues,
      recommendations,
    };
  } catch (error) {
    return {
      isConsistent: false,
      issues: [
        `Error checking consistency: ${error instanceof Error ? error.message : 'Unknown error'}`,
      ],
      recommendations: ['Investigate storage access issues'],
    };
  }
}

/**
 * ‰øÆÂ§çÊï∞ÊçÆ‰∏ç‰∏ÄËá¥
 * Fix data inconsistency
 */
export function fixDataInconsistency(): StorageOperationResult<{
  fixed: boolean;
  actions: string[];
}> {
  const startTime = Date.now();
  const actions: string[] = [];

  try {
    const consistency = checkDataConsistency();

    if (consistency.isConsistent) {
      return {
        success: true,
        data: {
          fixed: false,
          actions: ['No issues found'],
        },
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    }

    // Ê∏ÖÈô§Êó†ÊïàÁöÑÁºìÂ≠ò
    PreferenceCacheManager.clearCache();
    actions.push('Cleared cache');

    // Ëé∑ÂèñÊúÄÂèØÈù†ÁöÑÊï∞ÊçÆÊ∫ê
    const localPreference =
      LocalStorageManager.get<UserLocalePreference>('locale_preference');
    const cookieLocale = CookieManager.get('locale_preference');

    let authoritative: UserLocalePreference | null = null;

    // Á°ÆÂÆöÊùÉÂ®ÅÊï∞ÊçÆÊ∫ê
    if (localPreference && validatePreferenceData(localPreference).isValid) {
      authoritative = localPreference;
      actions.push('Using localStorage as authoritative source');
    } else if (cookieLocale) {
      authoritative = {
        locale: cookieLocale as Locale,
        source: 'browser',
        confidence: MAGIC_0_6,
        timestamp: Date.now(),
        metadata: { recoveredFrom: 'cookies' },
      };
      actions.push('Recovered from cookies');
    } else {
      // ÂàõÂª∫ÈªòËÆ§ÂÅèÂ•Ω
      authoritative = {
        locale: 'en',
        source: 'default',
        confidence: MAGIC_0_5,
        timestamp: Date.now(),
        metadata: { createdBy: 'recovery' },
      };
      actions.push('Created default preference');
    }

    // ÂêåÊ≠•Âà∞ÊâÄÊúâÂ≠òÂÇ®
    if (authoritative) {
      const saveResult = saveUserPreference(authoritative);
      if (saveResult.success) {
        actions.push('Synced to all storage locations');
      }
    }

    return {
      success: true,
      data: {
        fixed: true,
        actions,
      },
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Ëé∑ÂèñÂ≠òÂÇ®‰ΩøÁî®ÊÉÖÂÜµ
 * Get storage usage
 */
export function getStorageUsage(): {
  localStorage: {
    available: boolean;
    size: number;
    quota: number;
  };
  cookies: {
    available: boolean;
    size: number;
    count: number;
  };
  cache: {
    size: number;
    age: number;
    isExpired: boolean;
  };
} {
  const usage = {
    localStorage: {
      available: false,
      size: ZERO,
      quota: ZERO,
    },
    cookies: {
      available: false,
      size: ZERO,
      count: ZERO,
    },
    cache: {
      size: ZERO,
      age: ZERO,
      isExpired: false,
    },
  };

  // Ê£ÄÊü• localStorage
  try {
    if (typeof localStorage !== 'undefined') {
      usage.localStorage.available = true;

      // ‰º∞ÁÆó‰ΩøÁî®Â§ßÂ∞è
      let totalSize = ZERO;
      for (const key in localStorage) {
        if (localStorage.hasOwnProperty(key)) {
          totalSize += localStorage[key].length + key.length;
        }
      }
      usage.localStorage.size = totalSize;

      // Â∞ùËØïËé∑ÂèñÈÖçÈ¢ù‰ø°ÊÅØ
      if ('storage' in navigator && 'estimate' in navigator.storage) {
        navigator.storage
          .estimate()
          .then((estimate) => {
            usage.localStorage.quota = estimate.quota || ZERO;
          })
          .catch(() => {
            // ÂøΩÁï•ÈîôËØØ
          });
      }
    }
  } catch {
    // localStorage ‰∏çÂèØÁî®
  }

  // Ê£ÄÊü• cookies
  try {
    if (typeof document !== 'undefined') {
      usage.cookies.available = true;
      usage.cookies.size = document.cookie.length;
      usage.cookies.count = document.cookie.split(';').length;
    }
  } catch {
    // cookies ‰∏çÂèØÁî®
  }

  // Ê£ÄÊü•ÁºìÂ≠ò
  const cacheStatus = PreferenceCacheManager.getCacheStatus();
  usage.cache = {
    size: cacheStatus.size,
    age: cacheStatus.age,
    isExpired: cacheStatus.isExpired,
  };

  return usage;
}

/**
 * ‰ºòÂåñÂ≠òÂÇ®ÊÄßËÉΩ
 * Optimize storage performance
 */
export function optimizeStoragePerformance(): StorageOperationResult<{
  optimized: boolean;
  actions: string[];
  performance: {
    before: number;
    after: number;
    improvement: number;
  };
}> {
  const startTime = Date.now();
  const actions: string[] = [];

  try {
    const beforeTime = Date.now();

    // Ê∏ÖÁêÜËøáÊúüÁºìÂ≠ò
    const cacheStatus = PreferenceCacheManager.getCacheStatus();
    if (cacheStatus.isExpired) {
      PreferenceCacheManager.clearCache();
      actions.push('Cleared expired cache');
    }

    // È¢ÑÁÉ≠ÁºìÂ≠ò
    PreferenceCacheManager.warmUpCache();
    actions.push('Warmed up cache');

    // ÂêåÊ≠•Êï∞ÊçÆ
    const syncResult = syncPreferenceData();
    if (syncResult.success && syncResult.data?.synced) {
      actions.push('Synced storage data');
    }

    const afterTime = Date.now();
    const improvement = beforeTime - afterTime;

    return {
      success: true,
      data: {
        optimized: actions.length > ZERO,
        actions,
        performance: {
          before: beforeTime,
          after: afterTime,
          improvement,
        },
      },
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Áî®Êà∑ËØ≠Ë®ÄÂÅèÂ•ΩÊ†∏ÂøÉÁÆ°ÁêÜ
 * User Locale Preference Core Management
 *
 * Ë¥üË¥£Áî®Êà∑ÂÅèÂ•ΩÁöÑÂü∫Á°ÄÊìç‰ΩúÔºö‰øùÂ≠ò„ÄÅËé∑Âèñ„ÄÅÈ™åËØÅÂíåÊ†∏ÂøÉÊï∞ÊçÆÁÆ°ÁêÜ
 */

'use client';

import { DEC_0_01, MAGIC_0_5, MAGIC_0_7 } from "@/constants/decimal";
import { ANIMATION_DURATION_VERY_SLOW, COUNT_PAIR, COUNT_TRIPLE, ONE, ZERO } from "@/constants/magic-numbers";
import { CookieManager } from '@/lib/locale-storage-cookie';
import { LocalStorageManager } from '@/lib/locale-storage-local';
import { isUserLocalePreference } from '@/lib/locale-storage-types';
import type { Locale } from '@/types/i18n';
import type {
  StorageOperationResult,
  UserLocalePreference,
  ValidationResult,
} from '@/lib/locale-storage-types';

// ==================== Êï∞ÊçÆÈ™åËØÅÂäüËÉΩ ====================

/**
 * È™åËØÅÂÅèÂ•ΩÊï∞ÊçÆ
 * Validate preference data
 */
export function validatePreferenceData(preference: unknown): ValidationResult {
  if (!isUserLocalePreference(preference)) {
    return {
      isValid: false,
      errors: ['Invalid preference data structure'],
      warnings: [],
    };
  }

  const errors: string[] = [];

  // È™åËØÅËØ≠Ë®Ä‰ª£Á†Å
  if (!preference.locale || typeof preference.locale !== 'string') {
    errors.push('Invalid locale');
  }

  // È™åËØÅÊù•Ê∫ê
  if (!preference.source || typeof preference.source !== 'string') {
    errors.push('Invalid source');
  }

  // È™åËØÅÁΩÆ‰ø°Â∫¶
  if (
    typeof preference.confidence !== 'number' ||
    preference.confidence < ZERO ||
    preference.confidence > ONE
  ) {
    errors.push('Invalid confidence value (must be between 0 and 1)');
  }

  // È™åËØÅÊó∂Èó¥Êà≥
  if (typeof preference.timestamp !== 'number' || preference.timestamp <= ZERO) {
    errors.push('Invalid timestamp');
  }

  // È™åËØÅÂÖÉÊï∞ÊçÆÔºàÂ¶ÇÊûúÂ≠òÂú®Ôºâ
  if (preference.metadata && typeof preference.metadata !== 'object') {
    errors.push('Invalid metadata');
  }

  return {
    isValid: errors.length === ZERO,
    errors,
    warnings: [],
  };
}

/**
 * ÂàõÂª∫ÈªòËÆ§ÂÅèÂ•Ω
 * Create default preference
 */
export function createDefaultPreference(
  locale: Locale = 'en',
): UserLocalePreference {
  return {
    locale,
    source: 'default',
    confidence: MAGIC_0_5,
    timestamp: Date.now(),
    metadata: {},
  };
}

/**
 * ËßÑËåÉÂåñÂÅèÂ•ΩÊï∞ÊçÆ
 * Normalize preference data
 */
export function normalizePreference(
  preference: UserLocalePreference,
): UserLocalePreference {
  return {
    locale: preference.locale,
    source: preference.source,
    confidence: Math.max(ZERO, Math.min(ONE, preference.confidence)),
    timestamp: preference.timestamp || Date.now(),
    metadata: preference.metadata || {},
  };
}

// ==================== Ê†∏ÂøÉÂÅèÂ•ΩÁÆ°ÁêÜ ====================

/**
 * ‰øùÂ≠òÁî®Êà∑ËØ≠Ë®ÄÂÅèÂ•Ω
 * Save user locale preference
 */
export function saveUserPreference(
  preference: UserLocalePreference,
): StorageOperationResult<UserLocalePreference> {
  const startTime = Date.now();

  try {
    // È™åËØÅÂÅèÂ•ΩÊï∞ÊçÆ
    const validation = validatePreferenceData(preference);
    if (!validation.isValid) {
      return {
        success: false,
        error: `Invalid preference data: ${validation.errors.join(', ')}`,
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    }

    // ËßÑËåÉÂåñÊï∞ÊçÆ
    const normalizedPreference = normalizePreference(preference);

    // ‰øùÂ≠òÂà∞ localStorage
    LocalStorageManager.set('locale_preference', normalizedPreference);

    // ‰øùÂ≠òÂà∞ cookiesÔºàÁî®‰∫é SSRÔºâ
    CookieManager.set('locale_preference', normalizedPreference.locale);

    // ‰∏§‰∏™Â≠òÂÇ®Êìç‰ΩúÈÉΩËøîÂõûvoidÔºåÊàë‰ª¨ÂÅáËÆæÊàêÂäü
    // Â¶ÇÊûúÊúâÈîôËØØÔºå‰ºöÂú®try-catch‰∏≠ÊçïËé∑
    return {
      success: true,
      data: normalizedPreference,
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Ëé∑ÂèñÁî®Êà∑ËØ≠Ë®ÄÂÅèÂ•Ω
 * Get user locale preference
 */
export function getUserPreference(): StorageOperationResult<UserLocalePreference> {
  const startTime = Date.now();

  try {
    // È¶ñÂÖàÂ∞ùËØï‰ªé localStorage Ëé∑Âèñ
    const localPreference =
      LocalStorageManager.get<UserLocalePreference>('locale_preference');

    if (localPreference && validatePreferenceData(localPreference).isValid) {
      return {
        success: true,
        data: localPreference,
        source: 'localStorage',
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    }

    // Â¶ÇÊûú localStorage Ê≤°ÊúâÔºåÂ∞ùËØï‰ªé cookies Ëé∑Âèñ
    const cookieLocale = CookieManager.get('locale_preference');

    if (cookieLocale) {
      const cookiePreference: UserLocalePreference = {
        locale: cookieLocale as Locale,
        source: 'fallback',
        confidence: MAGIC_0_7,
        timestamp: Date.now(),
        metadata: { source: 'cookie_fallback' },
      };

      // ÂêåÊ≠•Âà∞ localStorage
      const _syncResult = saveUserPreference(cookiePreference);
      // ÂêåÊ≠•ÁªìÊûúÂ∑≤‰øùÂ≠ò‰ΩÜÂú®Ê≠§Â§ÑÊú™Áõ¥Êé•‰ΩøÁî®

      return {
        success: true,
        data: cookiePreference,
        source: 'cookies',
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    }

    // Â¶ÇÊûúÈÉΩÊ≤°ÊúâÔºåËøîÂõûÈªòËÆ§ÂÅèÂ•Ω
    const defaultPreference = createDefaultPreference();

    return {
      success: true,
      data: defaultPreference,
      source: 'default',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Êõ¥Êñ∞ÂÅèÂ•ΩÁΩÆ‰ø°Â∫¶
 * Update preference confidence
 */
export function updatePreferenceConfidence(
  confidence: number,
): StorageOperationResult<UserLocalePreference> {
  const currentResult = getUserPreference();

  if (!currentResult.success || !currentResult.data) {
    return {
      success: false,
      error: 'Failed to get current preference',
      timestamp: Date.now(),
    };
  }

  const updatedPreference: UserLocalePreference = {
    ...currentResult.data,
    confidence: Math.max(ZERO, Math.min(ONE, confidence)),
    timestamp: Date.now(),
  };

  return saveUserPreference(updatedPreference);
}

/**
 * Ê£ÄÊü•ÂÅèÂ•ΩÊòØÂê¶Â≠òÂú®
 * Check if preference exists
 */
export function hasUserPreference(): boolean {
  const localPreference =
    LocalStorageManager.get<UserLocalePreference>('locale_preference');
  const cookieLocale = CookieManager.get('locale_preference');

  return (
    (localPreference && validatePreferenceData(localPreference).isValid) ||
    Boolean(cookieLocale)
  );
}

/**
 * Ëé∑ÂèñÂÅèÂ•ΩÊù•Ê∫ê‰ºòÂÖàÁ∫ß
 * Get preference source priority
 */
export function getPreferenceSourcePriority(): Array<{
  source: string;
  available: boolean;
  priority: number;
}> {
  const localPreference =
    LocalStorageManager.get<UserLocalePreference>('locale_preference');
  const cookieLocale = CookieManager.get('locale_preference');

  return [
    {
      source: 'localStorage',
      available: Boolean(localPreference && validatePreferenceData(localPreference).isValid),
      priority: ONE,
    },
    {
      source: 'cookies',
      available: Boolean(cookieLocale),
      priority: COUNT_PAIR,
    },
    {
      source: 'default',
      available: true,
      priority: COUNT_TRIPLE,
    },
  ];
}

/**
 * ÊØîËæÉ‰∏§‰∏™ÂÅèÂ•Ω
 * Compare two preferences
 */
export function comparePreferences(
  pref1: UserLocalePreference,
  pref2: UserLocalePreference,
): {
  isEqual: boolean;
  differences: string[];
} {
  const differences: string[] = [];

  if (pref1.locale !== pref2.locale) {
    differences.push(`locale: ${pref1.locale} vs ${pref2.locale}`);
  }

  if (pref1.source !== pref2.source) {
    differences.push(`source: ${pref1.source} vs ${pref2.source}`);
  }

  if (Math.abs(pref1.confidence - pref2.confidence) > DEC_0_01) {
    differences.push(`confidence: ${pref1.confidence} vs ${pref2.confidence}`);
  }

  if (Math.abs(pref1.timestamp - pref2.timestamp) > ANIMATION_DURATION_VERY_SLOW) {
    differences.push(`timestamp: ${pref1.timestamp} vs ${pref2.timestamp}`);
  }

  return {
    isEqual: differences.length === ZERO,
    differences,
  };
}

/**
 * Ëé∑ÂèñÂÅèÂ•ΩÊëòË¶Å
 * Get preference summary
 */
export function getPreferenceSummary(): {
  hasPreference: boolean;
  locale: Locale | null;
  source: string | null;
  confidence: number | null;
  age: number | null;
  isValid: boolean;
} {
  const result = getUserPreference();

  if (!result.success || !result.data) {
    return {
      hasPreference: false,
      locale: null,
      source: null,
      confidence: null,
      age: null,
      isValid: false,
    };
  }

  const preference = result.data;
  const age = Date.now() - preference.timestamp;

  return {
    hasPreference: true,
    locale: preference.locale,
    source: preference.source,
    confidence: preference.confidence,
    age,
    isValid: validatePreferenceData(preference).isValid,
  };
}

/**
 * Ê∏ÖÈô§Áî®Êà∑ÂÅèÂ•Ω
