/**
 * ÂÅèÂ•Ω‰∫ã‰ª∂ÁÆ°ÁêÜÂô®
 * Preference Event Manager
 */

'use client';

import { ONE, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';
import type {
  StorageEvent,
  StorageEventListener,
} from '@/lib/locale-storage-types';

/**
 * ÂÅèÂ•Ω‰∫ã‰ª∂ÁÆ°ÁêÜÂô®
 * Preference event manager
 */
export class PreferenceEventManager {
  private static eventListeners: Map<string, StorageEventListener[]> =
    new Map();
  private static eventHistory: StorageEvent[] = [];
  private static readonly MAX_EVENT_HISTORY = PERCENTAGE_FULL;

  /**
   * Ê∑ªÂä†‰∫ã‰ª∂ÁõëÂê¨Âô®
   * Add event listener
   */
  static addEventListener(
    eventType: string,
    listener: StorageEventListener,
  ): void {
    if (!this.eventListeners.has(eventType)) {
      this.eventListeners.set(eventType, []);
    }
    this.eventListeners.get(eventType)!.push(listener);
  }

  /**
   * ÁßªÈô§‰∫ã‰ª∂ÁõëÂê¨Âô®
   * Remove event listener
   */
  static removeEventListener(
    eventType: string,
    listener: StorageEventListener,
  ): void {
    const listeners = this.eventListeners.get(eventType);
    if (listeners) {
      const index = listeners.indexOf(listener);
      if (index > -ONE) {
        listeners.splice(index, ONE);
      }
    }
  }

  /**
   * ÁßªÈô§ÊâÄÊúâ‰∫ã‰ª∂ÁõëÂê¨Âô®
   * Remove all event listeners
   */
  static removeAllListeners(eventType?: string): void {
    if (eventType) {
      this.eventListeners.delete(eventType);
    } else {
      this.eventListeners.clear();
    }
  }

  /**
   * ÂèëÂá∫‰∫ã‰ª∂
   * Emit event
   */
  static emitEvent(event: StorageEvent): void {
    // ËÆ∞ÂΩï‰∫ã‰ª∂ÂéÜÂè≤
    this.recordEvent(event);

    // ÂèëÈÄÅÁªôÁâπÂÆöÁ±ªÂûãÁöÑÁõëÂê¨Âô®
    const listeners = this.eventListeners.get(event.type);
    if (listeners) {
      listeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error(`Error in preference event listener for ${event.type}`, {
            error: error as Error,
          });
        }
      });
    }

    // ÂèëÈÄÅÁªôÈÄöÁî®ÁõëÂê¨Âô®
    const allListeners = this.eventListeners.get('*');
    if (allListeners) {
      allListeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error('Error in universal preference event listener', {
            error: error as Error,
          });
        }
      });
    }
  }

  /**
   * ËÆ∞ÂΩï‰∫ã‰ª∂ÂéÜÂè≤
   * Record event history
   */
  private static recordEvent(event: StorageEvent): void {
    this.eventHistory.unshift(event);

    // ÈôêÂà∂‰∫ã‰ª∂ÂéÜÂè≤ÈïøÂ∫¶
    if (this.eventHistory.length > this.MAX_EVENT_HISTORY) {
      this.eventHistory = this.eventHistory.slice(ZERO, this.MAX_EVENT_HISTORY);
    }
  }

  /**
   * Ëé∑Âèñ‰∫ã‰ª∂ÂéÜÂè≤
   * Get event history
   */
  static getEventHistory(limit?: number): StorageEvent[] {
    return limit ? this.eventHistory.slice(ZERO, limit) : [...this.eventHistory];
  }

  /**
   * Ê∏ÖÈô§‰∫ã‰ª∂ÂéÜÂè≤
   * Clear event history
   */
  static clearEventHistory(): void {
    this.eventHistory = [];
  }

  /**
   * Ëé∑ÂèñÁõëÂê¨Âô®ÁªüËÆ°
   * Get listener statistics
   */
  static getListenerStats(): {
    totalListeners: number;
    eventTypes: string[];
    listenersByType: Record<string, number>;
  } {
    let totalListeners = ZERO;
    const listenersByType: Record<string, number> = {};

    for (const [eventType, listeners] of this.eventListeners.entries()) {
      const count = listeners.length;
      totalListeners += count;
      listenersByType[eventType] = count;
    }

    return {
      totalListeners,
      eventTypes: Array.from(this.eventListeners.keys()),
      listenersByType,
    };
  }
}

/**
 * ÂÅèÂ•ΩÁªüËÆ°ÂàÜÊûê
 * Preference Statistics Analyzer
 */

'use client';

import { ANIMATION_DURATION_VERY_SLOW, HOURS_PER_DAY, ONE, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { getPreferenceHistory } from '@/lib/locale-storage-preference-events/history-manager';
import type { Locale } from '@/types/i18n';

/**
 * Ëé∑ÂèñÂÅèÂ•ΩÂèòÂåñÁªüËÆ°
 * Get preference change statistics
 */
export function getPreferenceChangeStats(): {
  totalChanges: number;
  localeChanges: Record<Locale, number>;
  sourceChanges: Record<string, number>;
  averageConfidence: number;
  lastChange: number | null;
  changeFrequency: number; // changes per day
} {
  const history = getPreferenceHistory();

  const stats = {
    totalChanges: history.length,
    localeChanges: {} as Record<Locale, number>,
    sourceChanges: {} as Record<string, number>,
    averageConfidence: ZERO,
    lastChange: null as number | null,
    changeFrequency: ZERO,
  };

  if (history.length === ZERO) {
    return stats;
  }

  let totalConfidence = ZERO;

  history.forEach((pref) => {
    // ÁªüËÆ°ËØ≠Ë®ÄÂèòÂåñ
    stats.localeChanges[pref.locale] =
      (stats.localeChanges[pref.locale] || ZERO) + ONE;

    // ÁªüËÆ°Êù•Ê∫êÂèòÂåñ
    stats.sourceChanges[pref.source] =
      (stats.sourceChanges[pref.source] || ZERO) + ONE;

    // Á¥ØËÆ°ÁΩÆ‰ø°Â∫¶
    totalConfidence += pref.confidence;
  });

  stats.averageConfidence = totalConfidence / history.length;
  stats.lastChange = history[ZERO]?.timestamp || ZERO;

  // ËÆ°ÁÆóÂèòÂåñÈ¢ëÁéá
  if (history.length > ONE) {
    const timeSpan =
      (history[ZERO]?.timestamp || ZERO) -
      (history[history.length - ONE]?.timestamp || ZERO);
    const days = timeSpan / (HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW);
    stats.changeFrequency = days > ZERO ? history.length / days : ZERO;
  }

  return stats;
}

/**
 * ‰∫ã‰ª∂Á≥ªÁªüÁÆ°ÁêÜ
 * Event System Management
