 */

/**
 * ËÆæÁΩÆÈªòËÆ§‰∫ã‰ª∂ÁõëÂê¨Âô®
 * Setup default event listeners
 */
export function setupDefaultListeners(
  options: {
    enableConsoleLog?: boolean;
    enableHistoryRecording?: boolean;
  } = {},
): void {
  const { enableConsoleLog = true, enableHistoryRecording = true } = options;

  if (enableConsoleLog) {
    PreferenceEventManager.addEventListener('*', consoleLogListener);
  }

  if (enableHistoryRecording) {
    PreferenceEventManager.addEventListener(
      'preference_saved',
      historyRecordingListener,
    );
  }
}

/**
 * Ê∏ÖÁêÜ‰∫ã‰ª∂Á≥ªÁªü
 * Cleanup event system
 */
export function cleanupEventSystem(): void {
  PreferenceEventManager.removeAllListeners();
  PreferenceEventManager.clearEventHistory();
}

/**
 * Ëé∑Âèñ‰∫ã‰ª∂Á≥ªÁªüÁä∂ÊÄÅ
 * Get event system status
 */
export function getEventSystemStatus(): {
  isActive: boolean;
  listenerStats: ReturnType<typeof PreferenceEventManager.getListenerStats>;
  eventHistoryCount: number;
  lastEventTime: number | null;
} {
  const listenerStats = PreferenceEventManager.getListenerStats();
  const eventHistory = PreferenceEventManager.getEventHistory(ONE);

  return {
    isActive: listenerStats.totalListeners > ZERO,
    listenerStats,
    eventHistoryCount: PreferenceEventManager.getEventHistory().length,
    lastEventTime:
      eventHistory.length > ZERO ? eventHistory[ZERO]?.timestamp || null : null,
  };
}

/**
 * Áî®Êà∑ËØ≠Ë®ÄÂÅèÂ•ΩË¶ÜÁõñÁÆ°ÁêÜ
 * User Locale Preference Override Management
 *
 * Ë¥üË¥£Áî®Êà∑ÊâãÂä®Ë¶ÜÁõñËØ≠Ë®ÄËÆæÁΩÆÁöÑÂäüËÉΩÔºåÂåÖÊã¨ËÆæÁΩÆ„ÄÅËé∑Âèñ„ÄÅÊ∏ÖÈô§Ë¶ÜÁõñ
 */

'use client';

import { MAGIC_0_8 } from "@/constants/decimal";
import { ONE, PERCENTAGE_HALF, ZERO } from "@/constants/magic-numbers";
import { CookieManager } from '@/lib/locale-storage-cookie';
import { LocalStorageManager } from '@/lib/locale-storage-local';
import type { Locale } from '@/types/i18n';
import {
  getUserPreference,
  saveUserPreference,
} from '@/lib/locale-storage-preference-core';
import type {
  StorageOperationResult,
  UserLocalePreference,
} from '@/lib/locale-storage-types';

// ==================== Áî®Êà∑Ë¶ÜÁõñÁÆ°ÁêÜ ====================

/**
 * ËÆæÁΩÆÁî®Êà∑ËØ≠Ë®ÄË¶ÜÁõñ
 * Set user locale override
 */
export function setUserOverride(
  locale: Locale,
  metadata?: Record<string, unknown>,
): StorageOperationResult<UserLocalePreference> {
  const preference: UserLocalePreference = {
    locale,
    source: 'user',
    confidence: ONE, // Áî®Êà∑ÊâãÂä®ÈÄâÊã©ÔºåÁΩÆ‰ø°Â∫¶ÊúÄÈ´ò
    timestamp: Date.now(),
    metadata: {
      ...metadata,
      isOverride: true,
      originalSource: 'user_manual',
    },
  };

  // ‰øùÂ≠òÂÅèÂ•Ω
  const saveResult = saveUserPreference(preference);

  if (saveResult.success) {
    // ÂêåÊó∂‰øùÂ≠òË¶ÜÁõñÊ†áËÆ∞Âà∞ÂçïÁã¨ÁöÑÂ≠òÂÇ®ÈîÆ
    LocalStorageManager.set('user_locale_override', locale);
    CookieManager.set('user_locale_override', locale);
  }

  return saveResult;
}

/**
 * Ëé∑ÂèñÁî®Êà∑ËØ≠Ë®ÄË¶ÜÁõñ
 * Get user locale override
 */
export function getUserOverride(): StorageOperationResult<Locale> {
  const startTime = Date.now();

  try {
    // È¶ñÂÖàÊ£ÄÊü•‰∏ìÈó®ÁöÑË¶ÜÁõñÂ≠òÂÇ®
    const localOverride = LocalStorageManager.get<Locale>(
      'user_locale_override',
    );

    if (localOverride) {
      return {
        success: true,
        data: localOverride,
        source: 'localStorage',
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    }

    // Ê£ÄÊü• cookies ‰∏≠ÁöÑË¶ÜÁõñ
    const cookieOverride = CookieManager.get('user_locale_override');

    if (cookieOverride) {
      // ÂêåÊ≠•Âà∞ localStorage
      LocalStorageManager.set('user_locale_override', cookieOverride);

      return {
        success: true,
        data: cookieOverride as Locale,
        source: 'cookies',
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    }

    // Ê£ÄÊü•‰∏ªÂÅèÂ•ΩÊòØÂê¶‰∏∫Áî®Êà∑Ë¶ÜÁõñ
    const preferenceResult = getUserPreference();

    if (
      preferenceResult.success &&
      preferenceResult.data &&
      preferenceResult.data.source === 'user_override'
    ) {
      return {
        success: true,
        data: preferenceResult.data.locale,
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
        ...(preferenceResult.source && { source: preferenceResult.source }),
      };
    }

    // Ê≤°ÊúâÊâæÂà∞Ë¶ÜÁõñ
    return {
      success: false,
      error: 'No user override found',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Ê∏ÖÈô§Áî®Êà∑ËØ≠Ë®ÄË¶ÜÁõñ
 * Clear user locale override
 */
export function clearUserOverride(): StorageOperationResult<void> {
  const startTime = Date.now();

  try {
    // Ê∏ÖÈô§‰∏ìÈó®ÁöÑË¶ÜÁõñÂ≠òÂÇ®
    LocalStorageManager.remove('user_locale_override');
    CookieManager.remove('user_locale_override');

    // Ê£ÄÊü•‰∏ªÂÅèÂ•ΩÊòØÂê¶‰∏∫Ë¶ÜÁõñÔºåÂ¶ÇÊûúÊòØÂàôÊ∏ÖÈô§
    const preferenceResult = getUserPreference();

    if (
      preferenceResult.success &&
      preferenceResult.data &&
      preferenceResult.data.source === 'user_override'
    ) {
      // ÂàõÂª∫‰∏Ä‰∏™Êñ∞ÁöÑÈùûË¶ÜÁõñÂÅèÂ•Ω
      const newPreference: UserLocalePreference = {
        ...preferenceResult.data,
        source: 'auto',
        confidence: MAGIC_0_8,
        timestamp: Date.now(),
        metadata: {
          ...preferenceResult.data.metadata,
          isOverride: false,
          clearedAt: Date.now(),
        },
      };

      saveUserPreference(newPreference);
    }

    return {
      success: true,
      data: undefined,
      source: 'both',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * Ê£ÄÊü•ÊòØÂê¶ÊúâÁî®Êà∑Ë¶ÜÁõñ
 * Check if user override exists
 */
export function hasUserOverride(): boolean {
  const localOverride = LocalStorageManager.get<Locale>('user_locale_override');
  const cookieOverride = CookieManager.get('user_locale_override');

  if (localOverride || cookieOverride) {
    return true;
  }

  // Ê£ÄÊü•‰∏ªÂÅèÂ•Ω
  const preferenceResult = getUserPreference();
  return (
    preferenceResult.success &&
    preferenceResult.data?.source === 'user_override'
  );
}

/**
 * Ëé∑ÂèñË¶ÜÁõñÂéÜÂè≤
 * Get override history
 */
export function getOverrideHistory(): Array<{
  locale: Locale;
  timestamp: number;
  action: 'set' | 'clear';
  metadata?: Record<string, unknown>;
}> {
  const history: Array<{
    locale: Locale;
    timestamp: number;
    action: 'set' | 'clear';
    metadata?: Record<string, unknown>;
  }> = [];

  // ‰ªé localStorage Ëé∑ÂèñÂéÜÂè≤ËÆ∞ÂΩï
  const storedHistory = LocalStorageManager.get<
    Array<{
      locale: Locale;
      timestamp: number;
      action: 'set' | 'clear';
      metadata?: Record<string, unknown>;
    }>
  >('override_history');

  if (storedHistory && Array.isArray(storedHistory)) {
    history.push(...storedHistory);
  }

  return history.sort((a, b) => b.timestamp - a.timestamp);
}

/**
 * ËÆ∞ÂΩïË¶ÜÁõñÊìç‰Ωú
 * Record override operation
 */
export function recordOverrideOperation(
  locale: Locale,
  action: 'set' | 'clear',
  metadata?: Record<string, unknown>,
): void {
  const history = getOverrideHistory();

  const newEntry = {
    locale,
    timestamp: Date.now(),
    action,
    ...(metadata && { metadata }),
  };

  history.unshift(newEntry);

  // ÈôêÂà∂ÂéÜÂè≤ËÆ∞ÂΩïÊï∞Èáè
  const maxHistory = PERCENTAGE_HALF;
  if (history.length > maxHistory) {
    history.splice(maxHistory);
  }

  LocalStorageManager.set('override_history', history);
}

/**
 * Ëé∑ÂèñË¶ÜÁõñÁªüËÆ°
 * Get override statistics
 */
export function getOverrideStats(): {
  totalOverrides: number;
  currentOverride: Locale | null;
  lastOverrideTime: number | null;
  mostUsedLocale: Locale | null;
  overrideFrequency: Record<Locale, number>;
} {
  const history = getOverrideHistory();
  const currentOverride = getUserOverride();

  const stats = {
    totalOverrides: ZERO,
    currentOverride:
      currentOverride.success && currentOverride.data
        ? currentOverride.data
        : null,
    lastOverrideTime: null as number | null,
    mostUsedLocale: null as Locale | null,
    overrideFrequency: {} as Record<Locale, number>,
  };

  if (history.length === ZERO) {
    return stats;
  }

  // ÁªüËÆ°Ë¶ÜÁõñÊìç‰Ωú
  const setOperations = history.filter((entry) => entry.action === 'set');
  stats.totalOverrides = setOperations.length;

  if (setOperations.length > ZERO) {
    stats.lastOverrideTime = setOperations[ZERO]?.timestamp || null;

    // ÁªüËÆ°ËØ≠Ë®Ä‰ΩøÁî®È¢ëÁéá
    setOperations.forEach((entry) => {
      stats.overrideFrequency[entry.locale] =
        (stats.overrideFrequency[entry.locale] || ZERO) + ONE;
    });

    // ÊâæÂá∫ÊúÄÂ∏∏Áî®ÁöÑËØ≠Ë®Ä
    let maxCount = ZERO;
    for (const [locale, count] of Object.entries(stats.overrideFrequency)) {
      if (count > maxCount) {
        maxCount = count;
        stats.mostUsedLocale = locale as Locale;
      }
    }
  }

  return stats;
}

/**
 * Ê∏ÖÈô§Ë¶ÜÁõñÂéÜÂè≤
 * Clear override history
 */
export function clearOverrideHistory(): StorageOperationResult<void> {
  const startTime = Date.now();

  try {
    LocalStorageManager.remove('override_history');

    return {
      success: true,
      data: undefined,
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

/**
 * ÂØºÂá∫Ë¶ÜÁõñÊï∞ÊçÆ
 * Export override data
 */
export function exportOverrideData(): {
  currentOverride: Locale | null;
  history: ReturnType<typeof getOverrideHistory>;
  stats: ReturnType<typeof getOverrideStats>;
  exportTime: number;
} {
  const currentOverride = getUserOverride();

  return {
    currentOverride:
      currentOverride.success && currentOverride.data
        ? currentOverride.data
        : null,
    history: getOverrideHistory(),
    stats: getOverrideStats(),
    exportTime: Date.now(),
  };
}

/**
 * ÂØºÂÖ•Ë¶ÜÁõñÊï∞ÊçÆ
 * Import override data
 */
export function importOverrideData(data: {
  currentOverride?: Locale | null;
  history?: ReturnType<typeof getOverrideHistory>;
}): StorageOperationResult<void> {
  const startTime = Date.now();

  try {
    // ÂØºÂÖ•ÂéÜÂè≤ËÆ∞ÂΩï
    if (data.history && Array.isArray(data.history)) {
      LocalStorageManager.set('override_history', data.history);
    }

    // ËÆæÁΩÆÂΩìÂâçË¶ÜÁõñ
    if (data.currentOverride) {
      const setResult = setUserOverride(data.currentOverride, {
        importedAt: Date.now(),
      });

      if (!setResult.success) {
        return {
          success: false,
          error: `Failed to set imported override: ${setResult.error}`,
          timestamp: Date.now(),
          responseTime: Date.now() - startTime,
        };
      }
    }

    return {
      success: true,
      data: undefined,
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

