 * Environment check tool (backward compatible)
 */
export function checkEnvironmentCompatibility(): {
  isCompatible: boolean;
  issues: string[];
  recommendations: string[];
} {
  const issues: string[] = [];
  const recommendations: string[] = [];

  // Ê£ÄÊü•ÊµãËØïÁéØÂ¢ÉÈÖçÁΩÆ
  if (process.env.PLAYWRIGHT_TEST === 'true') {
    if (process.env.NEXT_PUBLIC_DISABLE_REACT_SCAN !== 'true') {
      issues.push('ÊµãËØïÁéØÂ¢É‰∏≠ React Scan Êú™Ë¢´Á¶ÅÁî®');
      recommendations.push('ËÆæÁΩÆ NEXT_PUBLIC_DISABLE_REACT_SCAN=true');
    }
  }

  // Ê£ÄÊü•ÂºÄÂèëÁéØÂ¢ÉÈÖçÁΩÆ
  if (process.env.NODE_ENV === 'development') {
    if (process.env.NEXT_PUBLIC_DISABLE_REACT_SCAN === 'true') {
      recommendations.push(
        'ÂºÄÂèëÁéØÂ¢É‰∏≠ React Scan Ë¢´Á¶ÅÁî®ÔºåËÄÉËôëÂêØÁî®‰ª•Ëé∑ÂæóÊÄßËÉΩÁõëÊéß',
      );
    }
  }

  return {
    isCompatible: issues.length === ZERO,
