/**
 * Bundle Analyzer ÊÄßËÉΩÁõëÊéßÈõÜÊàê
 * Bundle Analyzer Performance Monitoring Integration
 *
 * Êèê‰æõ‰∏éBundle AnalyzerÂ∑•ÂÖ∑ÁöÑÈõÜÊàêÈí©Â≠êÔºåÁî®‰∫éÁõëÊéßÊâìÂåÖÂ§ßÂ∞èÂíå‰ºòÂåñ
 */

import { ANIMATION_DURATION_SLOW, ANIMATION_DURATION_VERY_SLOW, BYTES_PER_KB, COUNT_FIVE, COUNT_PAIR, COUNT_TEN, MAGIC_0_8, MAGIC_512, MAGIC_8, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, PERCENTAGE_QUARTER, ZERO } from "@/constants/magic-numbers";
import { KB, MB } from '@/constants/units';
import { logger } from '@/lib/logger';
import type {
  PerformanceConfig,
  PerformanceMetrics,
} from '@/lib/performance-monitoring-types';

/**
 * Bundle Analyzer ÈõÜÊàêÈí©Â≠êËøîÂõûÁ±ªÂûã
 * Bundle Analyzer integration hook return type
 */
export interface BundleAnalyzerIntegration {
  enabled: boolean;
  recordBundleSize: (
    bundleName: string,
    size: number,
    gzipSize?: number,
  ) => void;
  recordChunkInfo: (chunkName: string, size: number, modules: string[]) => void;
  generateSizeReport: () => Record<string, unknown>;
}

/**
 * Bundle Analyzer ÈõÜÊàêÈí©Â≠ê
 * Bundle Analyzer integration hook
 */
export function useBundleAnalyzerIntegration(
  config: PerformanceConfig,
  recordMetric: (metric: Omit<PerformanceMetrics, 'timestamp'>) => void,
): BundleAnalyzerIntegration {
  const bundleData = new Map<string, { size: number; gzipSize?: number }>();

  return {
    enabled: config.bundleAnalyzer.enabled,

    recordBundleSize: (bundleName: string, size: number, gzipSize?: number) => {
      if (!config.bundleAnalyzer.enabled) return;

      bundleData.set(bundleName, {
        size,
        ...(gzipSize !== undefined && { gzipSize }),
      });

      recordMetric({
        source: 'bundle-analyzer',
        type: 'bundle',
        data: {
          bundleName,
          size,
          gzipSize,
          timestamp: Date.now(),
        },
        tags: ['bundle-analyzer', 'bundle-size'],
        priority: 'medium',
      });
    },

    recordChunkInfo: (chunkName: string, size: number, modules: string[]) => {
      if (!config.bundleAnalyzer.enabled) return;

      recordMetric({
        source: 'bundle-analyzer',
        type: 'bundle',
        data: {
          chunkName,
          size,
          moduleCount: modules.length,
          modules: modules.slice(ZERO, COUNT_TEN), // Âè™ËÆ∞ÂΩïÂâçCOUNT_TEN‰∏™Ê®°Âùó
          timestamp: Date.now(),
        },
        tags: ['bundle-analyzer', 'chunk-info'],
        priority: 'low',
      });
    },

    generateSizeReport: () => {
      return Object.fromEntries(bundleData);
    },
  };
}

/**
 * Bundle Analyzer ÈÖçÁΩÆÈ™åËØÅ
 * Bundle Analyzer configuration validation
 */
export function validateBundleAnalyzerConfig(config: PerformanceConfig): {
  isValid: boolean;
  errors: string[];
  warnings: string[];
} {
  const errors: string[] = [];
  const warnings: string[] = [];

  if (!config.bundleAnalyzer) {
    errors.push('Bundle Analyzer configuration is missing');
    return { isValid: false, errors, warnings };
  }

  if (typeof config.bundleAnalyzer.enabled !== 'boolean') {
    errors.push('Bundle Analyzer enabled must be a boolean');
  }

  if (config.bundleAnalyzer.enabled) {
    if (
      config.bundleAnalyzer.port &&
      (typeof config.bundleAnalyzer.port !== 'number' ||
        config.bundleAnalyzer.port <= ZERO)
    ) {
      warnings.push('Bundle Analyzer port should be a positive number');
    }

    if (
      config.bundleAnalyzer.reportDir &&
      typeof config.bundleAnalyzer.reportDir !== 'string'
    ) {
      warnings.push('Bundle Analyzer reportDir should be a string');
    }
  }

  return {
    isValid: errors.length === ZERO,
    errors,
    warnings,
  };
}

/**
 * Bundle Analyzer ÊÄßËÉΩÂàÜÊûêÂô®
 * Bundle Analyzer performance analyzer
 */
export class BundleAnalyzerAnalyzer {
  private bundles = new Map<
    string,
    {
      size: number;
      gzipSize?: number;
      timestamp: number;
    }
  >();

  private chunks = new Map<
    string,
    {
      size: number;
      modules: string[];
      timestamp: number;
    }
  >();

  private config: PerformanceConfig;

  constructor(config: PerformanceConfig) {
    this.config = config;
  }

  /**
   * ËÆ∞ÂΩïBundleÂ§ßÂ∞è
   * Record bundle size
   */
  recordBundleSize(bundleName: string, size: number, gzipSize?: number): void {
    if (!this.config.bundleAnalyzer.enabled) return;

    this.bundles.set(bundleName, {
      size,
      ...(gzipSize !== undefined && { gzipSize }),
      timestamp: Date.now(),
    });

    // Ê£ÄÊü•ÊòØÂê¶Ë∂ÖËøáÂ§ßÂ∞èÈôêÂà∂ (‰ΩøÁî®bundleÈÖçÁΩÆÁöÑÈòàÂÄº)
    const maxSize = this.config.bundle?.thresholds?.size || MB; // 1MB
    if (size > maxSize) {
      logger.warn(`Bundle ${bundleName} exceeds size limit`, {
        size: this.formatSize(size),
        maxSize: this.formatSize(maxSize),
      });
    }
  }

  /**
   * ËÆ∞ÂΩïChunk‰ø°ÊÅØ
   * Record chunk information
   */
  recordChunkInfo(chunkName: string, size: number, modules: string[]): void {
    if (!this.config.bundleAnalyzer.enabled) return;

    this.chunks.set(chunkName, {
      size,
      modules,
      timestamp: Date.now(),
    });

    // Ê£ÄÊü•ÊòØÂê¶Ë∂ÖËøáÂ§ßÂ∞èÈôêÂà∂ (‰ΩøÁî®bundleÈÖçÁΩÆÁöÑÈòàÂÄº)
    const maxSize = this.config.bundle?.thresholds?.size || MAGIC_512 * KB; // 512KB
    if (size > maxSize) {
      logger.warn(`Chunk ${chunkName} exceeds size limit`, {
        size: this.formatSize(size),
        maxSize: this.formatSize(maxSize),
      });
    }
  }

  /**
   * ÁîüÊàêBundleÂàÜÊûêÊä•Âëä
   * Generate bundle analysis report
   */
  generateBundleReport(): {
    totalBundles: number;
    totalSize: number;
    totalGzipSize: number;
    largestBundles: Array<{
      name: string;
      size: number;
      gzipSize?: number;
      compressionRatio?: number;
    }>;
    recommendations: string[];
  } {
    const bundles = Array.from(this.bundles.entries());
    const totalSize = bundles.reduce((sum, [, bundle]) => sum + bundle.size, ZERO);
    const totalGzipSize = bundles.reduce(
      (sum, [, bundle]) => sum + (bundle.gzipSize || ZERO),
      ZERO,
    );

    const largestBundles = bundles
      .map(([name, bundle]) => ({
        name,
        size: bundle.size,
        ...(bundle.gzipSize !== undefined && { gzipSize: bundle.gzipSize }),
        ...(bundle.gzipSize !== undefined && {
          compressionRatio: bundle.gzipSize / bundle.size,
        }),
      }))
      .sort((a, b) => b.size - a.size)
      .slice(ZERO, COUNT_TEN);

    const recommendations: string[] = [];

    // ÁîüÊàê‰ºòÂåñÂª∫ËÆÆ
    if (totalSize > COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB) {
      // 5MB
      recommendations.push(
        'Total bundle size is large. Consider code splitting and lazy loading.',
      );
    }

    const largeBundles = largestBundles.filter((b) => b.size > BYTES_PER_KB * BYTES_PER_KB); // 1MB
    if (largeBundles.length > ZERO) {
      recommendations.push(
        `${largeBundles.length} bundles are larger than 1MB. Consider splitting them.`,
      );
    }

    const poorCompressionBundles = largestBundles.filter(
      (b) => b.compressionRatio && b.compressionRatio > MAGIC_0_8,
    );
    if (poorCompressionBundles.length > ZERO) {
      recommendations.push(
        `${poorCompressionBundles.length} bundles have poor compression ratios. Check for duplicate code.`,
      );
    }

    return {
      totalBundles: bundles.length,
      totalSize,
      totalGzipSize,
      largestBundles,
      recommendations,
    };
  }

  /**
   * ÁîüÊàêChunkÂàÜÊûêÊä•Âëä
   * Generate chunk analysis report
   */
  generateChunkReport(): {
    totalChunks: number;
    totalSize: number;
    largestChunks: Array<{
      name: string;
      size: number;
      moduleCount: number;
      topModules: string[];
    }>;
    duplicateModules: Array<{
      module: string;
      chunks: string[];
      totalSize: number;
    }>;
  } {
    const chunks = Array.from(this.chunks.entries());
    const totalSize = chunks.reduce((sum, [, chunk]) => sum + chunk.size, ZERO);

    const largestChunks = chunks
      .map(([name, chunk]) => ({
        name,
        size: chunk.size,
        moduleCount: chunk.modules.length,
        topModules: chunk.modules.slice(ZERO, COUNT_FIVE),
      }))
      .sort((a, b) => b.size - a.size)
      .slice(ZERO, COUNT_TEN);

    // Êü•ÊâæÈáçÂ§çÊ®°Âùó
    const moduleChunkMap = new Map<string, string[]>();
    chunks.forEach(([chunkName, chunk]) => {
      chunk.modules.forEach((module) => {
        if (!moduleChunkMap.has(module)) {
          moduleChunkMap.set(module, []);
        }
        moduleChunkMap.get(module)!.push(chunkName);
      });
    });

    const duplicateModules = Array.from(moduleChunkMap.entries())
      .filter(([, chunks]) => chunks.length > ONE)
      .map(([module, chunks]) => ({
        module,
        chunks,
        totalSize: chunks.length * ANIMATION_DURATION_VERY_SLOW, // ‰º∞ÁÆóÂ§ßÂ∞è
      }))
      .sort((a, b) => b.totalSize - a.totalSize)
      .slice(ZERO, COUNT_TEN);

    return {
      totalChunks: chunks.length,
      totalSize,
      largestChunks,
      duplicateModules,
    };
  }

  /**
   * Ê†ºÂºèÂåñÊñá‰ª∂Â§ßÂ∞è
   * Format file size
   */
  private formatSize(bytes: number): string {
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = ZERO;

    while (size >= BYTES_PER_KB && unitIndex < units.length - ONE) {
      size /= BYTES_PER_KB;
      unitIndex += ONE;
    }

    return `${size.toFixed(COUNT_PAIR)} ${units[unitIndex]}`;
  }

  /**
   * Ëé∑Âèñ‰ºòÂåñÂª∫ËÆÆ
   * Get optimization suggestions
   */
  getOptimizationSuggestions(): string[] {
    const suggestions: string[] = [];
    const bundleReport = this.generateBundleReport();
    const chunkReport = this.generateChunkReport();

    // Bundle‰ºòÂåñÂª∫ËÆÆ
    if (bundleReport.totalSize > COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB) {
      suggestions.push(
        'Consider implementing code splitting to reduce initial bundle size',
      );
    }

    if (bundleReport.largestBundles.length > ZERO) {
      const largest = bundleReport.largestBundles[ZERO];
      if (largest && largest.size > COUNT_PAIR * BYTES_PER_KB * BYTES_PER_KB) {
        suggestions.push(
          `Bundle "${largest.name}" is very large (${this.formatSize(largest.size)}). Consider splitting it.`,
        );
      }
    }

    // Chunk‰ºòÂåñÂª∫ËÆÆ
    if (chunkReport.duplicateModules.length > ZERO) {
      suggestions.push(
        `Found ${chunkReport.duplicateModules.length} duplicate modules. Consider creating shared chunks.`,
      );
    }

    if (chunkReport.totalChunks > PERCENTAGE_HALF) {
      suggestions.push(
        'Large number of chunks detected. Consider consolidating smaller chunks.',
      );
    }

    return suggestions;
  }

  /**
   * ÈáçÁΩÆÊâÄÊúâÊï∞ÊçÆ
   * Reset all data
   */
  reset(): void {
    this.bundles.clear();
    this.chunks.clear();
  }
}

/**
 * Bundle Analyzer Â∑•ÂÖ∑ÂáΩÊï∞
 * Bundle Analyzer utility functions
 */
export const BundleAnalyzerUtils = {
  /**
   * ËÆ°ÁÆóÂéãÁº©ÊØî
   * Calculate compression ratio
   */
  calculateCompressionRatio(
    originalSize: number,
    compressedSize: number,
  ): number {
    return compressedSize / originalSize;
  },

  /**
   * Ëé∑ÂèñÂ§ßÂ∞èÁ≠âÁ∫ß
   * Get size rating
   */
  getSizeRating(size: number): 'small' | 'medium' | 'large' | 'huge' {
    if (size < PERCENTAGE_FULL * BYTES_PER_KB) return 'small'; // < 100KB
    if (size < ANIMATION_DURATION_SLOW * BYTES_PER_KB) return 'medium'; // < 500KB
    if (size < BYTES_PER_KB * BYTES_PER_KB) return 'large'; // < 1MB
    return 'huge'; // >= 1MB
  },

  /**
   * ‰º∞ÁÆóÂä†ËΩΩÊó∂Èó¥
   * Estimate loading time
   */
  estimateLoadingTime(
    size: number,
    connectionSpeed: 'slow' | 'fast' | 'average' = 'average',
  ): number {
    const speeds = {
      slow: (BYTES_PER_KB * BYTES_PER_KB) / MAGIC_8, // 1 Mbps in bytes per second
      average: (COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB) / MAGIC_8, // COUNT_FIVE Mbps
      fast: (PERCENTAGE_QUARTER * BYTES_PER_KB * BYTES_PER_KB) / MAGIC_8, // PERCENTAGE_QUARTER Mbps
    };

    return (size / speeds[connectionSpeed]) * ANIMATION_DURATION_VERY_SLOW; // Return in milliseconds
  },
} as const;

/**
 * Web Vitals ÂíåÁéØÂ¢ÉÊ£ÄÊü•ÊÄßËÉΩÁõëÊéßÈõÜÊàê
 * Web Vitals and Environment Check Performance Monitoring Integration
 *
 * Êèê‰æõ‰∏éWeb VitalsÂ∑•ÂÖ∑ÁöÑÈõÜÊàêÈí©Â≠êÂíåÁéØÂ¢ÉÂÖºÂÆπÊÄßÊ£ÄÊü•ÂäüËÉΩ
 */

import { COUNT_PAIR, MAGIC_0_9, MAGIC_1_1, OFFSET_NEGATIVE_EXTRA_LARGE, OFFSET_NEGATIVE_LARGE, OFFSET_NEGATIVE_MEDIUM, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, ZERO } from "@/constants/magic-numbers";
import type {
  PerformanceConfig,
  PerformanceMetrics,
} from '@/lib/performance-monitoring-types';
import {
  isDevelopmentEnvironment,
  isTestEnvironment,
} from '@/lib/performance-monitoring-types';

/**
 * Web Vitals ÈõÜÊàêÈí©Â≠êËøîÂõûÁ±ªÂûã
 * Web Vitals integration hook return type
 */
export interface WebVitalsIntegration {
  enabled: boolean;
  recordWebVital: (
    name: string,
    value: number,
    rating: 'good' | 'needs-improvement' | 'poor',
  ) => void;
  recordCustomMetric: (name: string, value: number, unit?: string) => void;
}

/**
 * Web Vitals ÈõÜÊàêÈí©Â≠ê
 * Web Vitals integration hook
 */
export function useWebVitalsIntegration(
  config: PerformanceConfig,
  recordMetric: (metric: Omit<PerformanceMetrics, 'timestamp'>) => void,
): WebVitalsIntegration {
  return {
    enabled: config.webVitals?.enabled || false,

    recordWebVital: (
      name: string,
      value: number,
      rating: 'good' | 'needs-improvement' | 'poor',
    ) => {
      if (!config.webVitals?.enabled) return;

      recordMetric({
        source: 'web-vitals',
        type: 'page',
        data: {
          name,
          value,
          rating,
          timestamp: Date.now(),
        },
        tags: ['web-vitals', name.toLowerCase()],
        priority: rating === 'poor' ? 'high' : 'medium',
      });
    },

    recordCustomMetric: (name: string, value: number, unit = 'ms') => {
      if (!config.webVitals?.enabled) return;

      recordMetric({
        source: 'custom',
        type: 'page',
        data: {
          name,
          value,
          unit,
          timestamp: Date.now(),
        },
        tags: ['custom-metric'],
        priority: 'low',
      });
    },
  };
}

/**
 * ÁéØÂ¢ÉÂÖºÂÆπÊÄßÊ£ÄÊü•ÁªìÊûú
 * Environment compatibility check result
 */
export interface EnvironmentCompatibilityResult {
  isCompatible: boolean;
  issues: string[];
  recommendations: string[];
  environment: string;
  warnings: string[];
}

/**
 * ÁéØÂ¢ÉÂÖºÂÆπÊÄßÊ£ÄÊü•
 * Environment compatibility check
 */
export function checkEnvironmentCompatibility(): EnvironmentCompatibilityResult {
  const issues: string[] = [];
  const recommendations: string[] = [];
  const warnings: string[] = [];
  const environment = process.env.NODE_ENV || 'development';

  // Ê£ÄÊü•ÊµãËØïÁéØÂ¢ÉÈÖçÁΩÆ
  if (isTestEnvironment()) {
    if (process.env.NEXT_PUBLIC_DISABLE_REACT_SCAN !== 'true') {
      issues.push('ÊµãËØïÁéØÂ¢É‰∏≠ React Scan Êú™Ë¢´Á¶ÅÁî®');
      recommendations.push('ËÆæÁΩÆ NEXT_PUBLIC_DISABLE_REACT_SCAN=true');
    }

    if (process.env.NEXT_PUBLIC_ENABLE_WEB_EVAL_AGENT !== 'true') {
      warnings.push('ÊµãËØïÁéØÂ¢É‰∏≠ Web Eval Agent Êú™ÂêØÁî®');
      recommendations.push('ËÄÉËôëËÆæÁΩÆ NEXT_PUBLIC_ENABLE_WEB_EVAL_AGENT=true');
    }
  }

  // Ê£ÄÊü•ÂºÄÂèëÁéØÂ¢ÉÈÖçÁΩÆ
  if (isDevelopmentEnvironment()) {
    if (process.env.NEXT_PUBLIC_DISABLE_REACT_SCAN === 'true') {
      warnings.push('ÂºÄÂèëÁéØÂ¢É‰∏≠ React Scan Ë¢´Á¶ÅÁî®');
      recommendations.push('ËÄÉËôëÂêØÁî® React Scan ‰ª•Ëé∑ÂæóÊÄßËÉΩÁõëÊéß');
    }

    if (!process.env.ANALYZE && process.env.NODE_ENV === 'development') {
      recommendations.push(
        'ËÄÉËôëÂÆöÊúüËøêË°å ANALYZE=true npm run build Êù•ÂàÜÊûêÂåÖÂ§ßÂ∞è',
      );
    }
  }

  // Ê£ÄÊü•Áîü‰∫ßÁéØÂ¢ÉÈÖçÁΩÆ
  if (environment === 'production') {
    if (process.env.NEXT_PUBLIC_DISABLE_REACT_SCAN !== 'true') {
      issues.push('Áîü‰∫ßÁéØÂ¢É‰∏≠ React Scan Êú™Ë¢´Á¶ÅÁî®');
      recommendations.push(
        'Âú®Áîü‰∫ßÁéØÂ¢É‰∏≠ËÆæÁΩÆ NEXT_PUBLIC_DISABLE_REACT_SCAN=true',
      );
    }

    if (process.env.ANALYZE === 'true') {
      warnings.push('Áîü‰∫ßÁéØÂ¢É‰∏≠ÂêØÁî®‰∫Ü Bundle Analyzer');
      recommendations.push('Áîü‰∫ßÁéØÂ¢É‰∏≠Â∫îÁ¶ÅÁî® Bundle Analyzer');
    }
  }

  // Ê£ÄÊü•ÂøÖË¶ÅÁöÑÁéØÂ¢ÉÂèòÈáè
  const requiredEnvVars = ['NODE_ENV'];
  for (const envVar of requiredEnvVars) {
    if (!process.env[envVar]) {
      issues.push(`Áº∫Â∞ëÂøÖË¶ÅÁöÑÁéØÂ¢ÉÂèòÈáè: ${envVar}`);
      recommendations.push(`ËÆæÁΩÆÁéØÂ¢ÉÂèòÈáè ${envVar}`);
    }
  }

  return {
    isCompatible: issues.length === ZERO,
    issues,
    recommendations,
    environment,
    warnings,
  };
}

/**
 * ÊÄßËÉΩÁõëÊéßÂÅ•Â∫∑Ê£ÄÊü•
 * Performance monitoring health check
 */
export function performHealthCheck(config: PerformanceConfig): {
  isHealthy: boolean;
  status: Record<string, 'healthy' | 'warning' | 'error'>;
  details: Record<string, string>;
} {
  const status: Record<string, 'healthy' | 'warning' | 'error'> = {};
  const details: Record<string, string> = {};

  // Ê£ÄÊü• React Scan Áä∂ÊÄÅ
  if (config.reactScan.enabled) {
    if (isTestEnvironment()) {
      status.reactScan = 'error';
      details.reactScan = 'React Scan should be disabled in test environment';
    } else if (isDevelopmentEnvironment()) {
      status.reactScan = 'healthy';
      details.reactScan = 'React Scan is properly configured for development';
    } else {
      status.reactScan = 'warning';
      details.reactScan =
        'React Scan is enabled in non-development environment';
    }
  } else {
    status.reactScan = 'healthy';
    details.reactScan = 'React Scan is disabled';
  }

  // Ê£ÄÊü• Web Eval Agent Áä∂ÊÄÅ
  if (config.webEvalAgent.enabled) {
    if (isTestEnvironment()) {
      status.webEvalAgent = 'healthy';
      details.webEvalAgent =
        'Web Eval Agent is properly configured for testing';
    } else {
      status.webEvalAgent = 'warning';
      details.webEvalAgent =
        'Web Eval Agent is enabled outside test environment';
    }
  } else {
    status.webEvalAgent = 'healthy';
    details.webEvalAgent = 'Web Eval Agent is disabled';
  }

  // Ê£ÄÊü• Bundle Analyzer Áä∂ÊÄÅ
  status.bundleAnalyzer = config.bundleAnalyzer.enabled ? 'healthy' : 'healthy';
  details.bundleAnalyzer = config.bundleAnalyzer.enabled
    ? 'Bundle Analyzer is enabled'
    : 'Bundle Analyzer is disabled';

  // Ê£ÄÊü• Size Limit Áä∂ÊÄÅ
  status.sizeLimit = config.sizeLimit.enabled ? 'healthy' : 'warning';
  details.sizeLimit = config.sizeLimit.enabled
    ? 'Size Limit monitoring is active'
    : 'Size Limit monitoring is disabled';

  const isHealthy = Object.values(status).every((s) => s !== 'error');

  return {
    isHealthy,
    status,
    details,
  };
}

/**
 * Web Vitals ÈÖçÁΩÆÈ™åËØÅ
 * Web Vitals configuration validation
 */
export function validateWebVitalsConfig(config: PerformanceConfig): {
  isValid: boolean;
  errors: string[];
  warnings: string[];
} {
  const errors: string[] = [];
  const warnings: string[] = [];

  if (config.webVitals) {
    if (typeof config.webVitals.enabled !== 'boolean') {
      errors.push('Web Vitals enabled must be a boolean');
    }

    if (config.webVitals.enabled) {
      if (
        config.webVitals.reportAllChanges &&
        typeof config.webVitals.reportAllChanges !== 'boolean'
      ) {
        warnings.push('Web Vitals reportAllChanges should be a boolean');
      }

      if (config.webVitals.thresholds) {
        const {thresholds} = config.webVitals;

        if (
          thresholds.lcp &&
          (typeof thresholds.lcp !== 'number' || thresholds.lcp <= ZERO)
        ) {
          warnings.push('Web Vitals LCP threshold should be a positive number');
        }

        if (
          thresholds.fid &&
          (typeof thresholds.fid !== 'number' || thresholds.fid <= ZERO)
        ) {
          warnings.push('Web Vitals FID threshold should be a positive number');
        }

        if (
          thresholds.cls &&
          (typeof thresholds.cls !== 'number' || thresholds.cls <= ZERO)
        ) {
          warnings.push('Web Vitals CLS threshold should be a positive number');
        }
      }
    }
  }

  return {
    isValid: errors.length === ZERO,
    errors,
    warnings,
  };
}

/**
 * Web Vitals ÊÄßËÉΩÂàÜÊûêÂô®
 * Web Vitals performance analyzer
 */
export class WebVitalsAnalyzer {
  private vitals = new Map<
    string,
    {
      values: number[];
      ratings: Array<'good' | 'needs-improvement' | 'poor'>;
      lastUpdated: number;
    }
  >();

  private customMetrics = new Map<
    string,
    {
      values: number[];
      unit: string;
      lastUpdated: number;
    }
  >();

  private config: PerformanceConfig;

  constructor(config: PerformanceConfig) {
    this.config = config;
  }

  /**
   * ËÆ∞ÂΩïWeb VitalÊåáÊ†á
   * Record Web Vital metric
   */
  recordWebVital(
    name: string,
    value: number,
    rating: 'good' | 'needs-improvement' | 'poor',
  ): void {
    if (!this.config.webVitals?.enabled) return;

    const current = this.vitals.get(name) || {
      values: [],
      ratings: [],
      lastUpdated: ZERO,
    };

    current.values.push(value);
    current.ratings.push(rating);
    current.lastUpdated = Date.now();

    this.vitals.set(name, current);

    // ‰øùÊåÅÊï∞ÁªÑÂ§ßÂ∞èÂú®ÂêàÁêÜËåÉÂõ¥ÂÜÖ
    if (current.values.length > PERCENTAGE_FULL) {
      current.values = current.values.slice(OFFSET_NEGATIVE_EXTRA_LARGE);
      current.ratings = current.ratings.slice(OFFSET_NEGATIVE_EXTRA_LARGE);
    }
  }

  /**
   * ËÆ∞ÂΩïËá™ÂÆö‰πâÊåáÊ†á
   * Record custom metric
   */
  recordCustomMetric(name: string, value: number, unit = 'ms'): void {
    if (!this.config.webVitals?.enabled) return;

    const current = this.customMetrics.get(name) || {
      values: [],
      unit,
      lastUpdated: ZERO,
    };

    current.values.push(value);
    current.lastUpdated = Date.now();

    this.customMetrics.set(name, current);

    // ‰øùÊåÅÊï∞ÁªÑÂ§ßÂ∞èÂú®ÂêàÁêÜËåÉÂõ¥ÂÜÖ
    if (current.values.length > PERCENTAGE_FULL) {
      current.values = current.values.slice(OFFSET_NEGATIVE_EXTRA_LARGE);
    }
  }

  /**
   * Ëé∑ÂèñWeb VitalsÊä•Âëä
   * Get Web Vitals report
   */
  getWebVitalsReport(): {
    vitals: Record<
      string,
      {
        average: number;
        latest: number;
        trend: 'improving' | 'stable' | 'degrading';
        rating: 'good' | 'needs-improvement' | 'poor';
      }
    >;
    score: number;
    recommendations: string[];
  } {
    const vitals: Record<
      string,
      {
        average: number;
        latest: number;
        trend: 'improving' | 'stable' | 'degrading';
        rating: 'good' | 'needs-improvement' | 'poor';
      }
    > = {};
    let totalScore = ZERO;
    let vitalCount = ZERO;

    for (const [name, data] of this.vitals.entries()) {
      if (data.values.length === ZERO) continue;

      const average =
        data.values.reduce((sum, val) => sum + val, ZERO) / data.values.length;
      const latest = data.values[data.values.length - ONE] ?? ZERO;
      const latestRating = data.ratings[data.ratings.length - ONE] ?? 'poor';

      // ËÆ°ÁÆóË∂ãÂäø
      let trend: 'improving' | 'stable' | 'degrading' = 'stable';
      if (data.values.length >= COUNT_PAIR) {
        const recent = data.values.slice(OFFSET_NEGATIVE_MEDIUM);
        const older = data.values.slice(OFFSET_NEGATIVE_LARGE, OFFSET_NEGATIVE_MEDIUM);
        if (recent.length > ZERO && older.length > ZERO) {
          const recentAvg =
            recent.reduce((sum, val) => sum + val, ZERO) / recent.length;
          const olderAvg =
            older.reduce((sum, val) => sum + val, ZERO) / older.length;

          if (recentAvg < olderAvg * MAGIC_0_9) trend = 'improving';
          else if (recentAvg > olderAvg * MAGIC_1_1) trend = 'degrading';
        }
      }

      vitals[name] = {
        average,
        latest,
        trend,
        rating: latestRating,
      };

      // ËÆ°ÁÆóÂàÜÊï∞
      const ratingScore =
        latestRating === 'good'
          ? PERCENTAGE_FULL
          : latestRating === 'needs-improvement'
            ? PERCENTAGE_HALF
            : ZERO;
      totalScore += ratingScore;
      vitalCount += ONE;
    }

    const score = vitalCount > ZERO ? totalScore / vitalCount : ZERO;

    // ÁîüÊàêÂª∫ËÆÆ
    const recommendations: string[] = [];
    for (const [name, data] of Object.entries(vitals)) {
      if (data.rating === 'poor') {
        recommendations.push(
          `${name} needs improvement (current: ${data.latest.toFixed(COUNT_PAIR)})`,
        );
      }
      if (data.trend === 'degrading') {
        recommendations.push(`${name} performance is degrading over time`);
      }
    }

    return {
      vitals,
      score,
      recommendations,
    };
  }

  /**
   * ÈáçÁΩÆÊâÄÊúâÊï∞ÊçÆ
   * Reset all data
   */
  reset(): void {
    this.vitals.clear();
    this.customMetrics.clear();
  }
}

import { ANIMATION_DURATION_NORMAL, ANIMATION_DURATION_VERY_SLOW, COUNT_TEN, HTTP_BAD_REQUEST_CONST, HTTP_OK_CONST, MAGIC_0_9, OFFSET_NEGATIVE_EXTRA_LARGE, OFFSET_NEGATIVE_MASSIVE, ONE, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';
import type {
  PerformanceConfig,
  PerformanceMetrics,
} from '@/lib/performance-monitoring-types';

/**
 * Web Eval Agent ÈõÜÊàêÈí©Â≠êËøîÂõûÁ±ªÂûã
 * Web Eval Agent integration hook return type
 */
export interface WebEvalAgentIntegration {
  enabled: boolean;
  recordUserInteraction: (
    action: string,
    timing: number,
    success: boolean,
    details?: Record<string, unknown>,
  ) => void;
  recordNetworkRequest: (
    url: string,
    method: string,
    status: number,
    timing: number,
    size?: number,
  ) => void;
  recordPageLoad: (url: string, timing: Record<string, number>) => void;
  recordError: (error: Error, context?: Record<string, unknown>) => void;
}

/**
 * Web Eval Agent ÈõÜÊàêÈí©Â≠ê
 * Web Eval Agent integration hook
 */
export function useWebEvalAgentIntegration(
  config: PerformanceConfig,
  recordMetric: (metric: Omit<PerformanceMetrics, 'timestamp'>) => void,
): WebEvalAgentIntegration {
  return {
    enabled: config.webEvalAgent.enabled,

    recordUserInteraction: (
      action: string,
      timing: number,
      success: boolean,
      details = {},
    ) => {
      if (!config.webEvalAgent.enabled) return;

      recordMetric({
        source: 'web-eval-agent',
        type: 'user-interaction',
        data: {
          action,
          timing,
          success,
          ...details,
          timestamp: Date.now(),
        },
        tags: ['web-eval-agent', 'user-interaction'],
        priority: success ? 'medium' : 'high',
      });
    },

    recordNetworkRequest: (
      url: string,
      method: string,
      status: number,
      timing: number,
      size = ZERO,
    ) => {
      if (!config.webEvalAgent.enabled || !config.webEvalAgent.captureNetwork)
        return;

      recordMetric({
        source: 'web-eval-agent',
        type: 'network',
        data: {
          url,
          method,
          status,
          timing,
          size,
          isSuccess: status >= 200 && status < ANIMATION_DURATION_NORMAL,
          timestamp: Date.now(),
        },
        tags: ['web-eval-agent', 'network-request'],
        priority: timing > 1000 ? 'high' : 'medium',
      });
    },

    recordPageLoad: (url: string, timing: Record<string, number>) => {
      if (!config.webEvalAgent.enabled) return;

      recordMetric({
        source: 'web-eval-agent',
        type: 'page',
        data: {
          url,
          ...timing,
          timestamp: Date.now(),
        },
        tags: ['web-eval-agent', 'page-load'],
        priority: 'medium',
      });
    },

    recordError: (error: Error, context = {}) => {
      if (!config.webEvalAgent.enabled || !config.webEvalAgent.captureLogs)
        return;

      recordMetric({
        source: 'web-eval-agent',
        type: 'user-interaction',
        data: {
          error: {
            name: error.name,
            message: error.message,
            stack: error.stack,
          },
          context,
          timestamp: Date.now(),
        },
        tags: ['web-eval-agent', 'error'],
        priority: 'critical',
      });
    },
  };
}

/**
 * Web Eval Agent ÈÖçÁΩÆÈ™åËØÅ
 * Web Eval Agent configuration validation
 */
export function validateWebEvalAgentConfig(config: PerformanceConfig): {
  isValid: boolean;
  errors: string[];
  warnings: string[];
} {
  const errors: string[] = [];
  const warnings: string[] = [];

  if (!config.webEvalAgent) {
    errors.push('Web Eval Agent configuration is missing');
    return { isValid: false, errors, warnings };
  }

  if (typeof config.webEvalAgent.enabled !== 'boolean') {
    errors.push('Web Eval Agent enabled must be a boolean');
  }

  if (config.webEvalAgent.enabled) {
    if (typeof config.webEvalAgent.captureNetwork !== 'boolean') {
      warnings.push('Web Eval Agent captureNetwork should be a boolean');
    }

    if (typeof config.webEvalAgent.captureLogs !== 'boolean') {
      warnings.push('Web Eval Agent captureLogs should be a boolean');
    }

    if (
      config.webEvalAgent.maxInteractionsPerSession &&
      (typeof config.webEvalAgent.maxInteractionsPerSession !== 'number' ||
        config.webEvalAgent.maxInteractionsPerSession <= ZERO)
    ) {
      warnings.push(
        'Web Eval Agent maxInteractionsPerSession should be a positive number',
      );
    }
  }

  return {
    isValid: errors.length === ZERO,
    errors,
    warnings,
  };
}

/**
 * Web Eval Agent ÊÄßËÉΩÂàÜÊûêÂô®
 * Web Eval Agent performance analyzer
 */
export class WebEvalAgentAnalyzer {
  private interactions = new Map<
    string,
    {
      count: number;
      totalTime: number;
      successCount: number;
      lastInteraction: number;
    }
  >();

  private networkRequests: Array<{
    url: string;
    method: string;
    status: number;
    timing: number;
    size: number;
    timestamp: number;
  }> = [];

  private pageLoads: Array<{
    url: string;
    timing: Record<string, number>;
    timestamp: number;
  }> = [];

  private errors: Array<{
    error: Error;
    context: Record<string, unknown>;
    timestamp: number;
  }> = [];

  private config: PerformanceConfig;

  constructor(config: PerformanceConfig) {
    this.config = config;
  }

  /**
   * ËÆ∞ÂΩïÁî®Êà∑‰∫§‰∫í
   * Record user interaction
   */
  recordInteraction(
    action: string,
    timing: number,
    success: boolean,
    _details?: Record<string, unknown>,
  ): void {
    if (!this.config.webEvalAgent.enabled) return;

    const current = this.interactions.get(action) || {
      count: ZERO,
      totalTime: ZERO,
      successCount: ZERO,
      lastInteraction: ZERO,
    };

    current.count += ONE;
    current.totalTime += timing;
    if (success) current.successCount += ONE;
    current.lastInteraction = Date.now();

    this.interactions.set(action, current);

    // Ê£ÄÊü•ÊòØÂê¶Ë∂ÖËøá‰ºöËØùÈôêÂà∂
    const maxInteractions =
      this.config.webEvalAgent.maxInteractionsPerSession || ANIMATION_DURATION_VERY_SLOW;
    if (current.count > maxInteractions) {
      logger.warn(`Interaction ${action} has exceeded session limit`, {
        maxInteractions,
      });
    }
  }

  /**
   * ËÆ∞ÂΩïÁΩëÁªúËØ∑Ê±Ç
   * Record network request
   */
  recordNetworkRequest(
    url: string,
    method: string,
    status: number,
    timing: number,
    size = ZERO,
  ): void {
    if (
      !this.config.webEvalAgent.enabled ||
      !this.config.webEvalAgent.captureNetwork
    )
      return;

    this.networkRequests.push({
      url,
      method,
      status,
      timing,
      size,
      timestamp: Date.now(),
    });

    // ‰øùÊåÅÊï∞ÁªÑÂ§ßÂ∞èÂú®ÂêàÁêÜËåÉÂõ¥ÂÜÖ
    if (this.networkRequests.length > 1000) {
      this.networkRequests = this.networkRequests.slice(OFFSET_NEGATIVE_MASSIVE);
    }
  }

  /**
   * ËÆ∞ÂΩïÈ°µÈù¢Âä†ËΩΩ
   * Record page load
   */
  recordPageLoad(url: string, timing: Record<string, number>): void {
    if (!this.config.webEvalAgent.enabled) return;

    this.pageLoads.push({
      url,
      timing,
      timestamp: Date.now(),
    });

    // ‰øùÊåÅÊï∞ÁªÑÂ§ßÂ∞èÂú®ÂêàÁêÜËåÉÂõ¥ÂÜÖ
    if (this.pageLoads.length > PERCENTAGE_FULL) {
      this.pageLoads = this.pageLoads.slice(OFFSET_NEGATIVE_EXTRA_LARGE);
    }
  }

  /**
   * ËÆ∞ÂΩïÈîôËØØ
   * Record error
   */
  recordError(error: Error, context: Record<string, unknown> = {}): void {
    if (
      !this.config.webEvalAgent.enabled ||
      !this.config.webEvalAgent.captureLogs
    )
      return;

    this.errors.push({
      error,
      context,
      timestamp: Date.now(),
    });

    // ‰øùÊåÅÊï∞ÁªÑÂ§ßÂ∞èÂú®ÂêàÁêÜËåÉÂõ¥ÂÜÖ
    if (this.errors.length > PERCENTAGE_FULL) {
      this.errors = this.errors.slice(OFFSET_NEGATIVE_EXTRA_LARGE);
    }
  }

  /**
   * Ëé∑ÂèñÁî®Êà∑‰∫§‰∫íÊä•Âëä
   * Get user interaction report
   */
  getInteractionReport(): {
    totalInteractions: number;
    uniqueActions: number;
    averageResponseTime: number;
    successRate: number;
    topSlowActions: Array<{
      action: string;
      averageTime: number;
      count: number;
      successRate: number;
    }>;
    recommendations: string[];
  } {
    const interactions = Array.from(this.interactions.entries());
    const totalInteractions = interactions.reduce(
      (sum, [, metrics]) => sum + metrics.count,
      ZERO,
    );
    const totalTime = interactions.reduce(
      (sum, [, metrics]) => sum + metrics.totalTime,
      ZERO,
    );
    const totalSuccesses = interactions.reduce(
      (sum, [, metrics]) => sum + metrics.successCount,
      ZERO,
    );

    const topSlowActions = interactions
      .map(([action, metrics]) => ({
        action,
        averageTime: metrics.totalTime / metrics.count,
        count: metrics.count,
        successRate: metrics.successCount / metrics.count,
      }))
      .sort((a, b) => b.averageTime - a.averageTime)
      .slice(ZERO, COUNT_TEN);

    const recommendations: string[] = [];

    // ÁîüÊàêÂª∫ËÆÆ
    if (topSlowActions.length > ZERO) {
      const slowestAction = topSlowActions[ZERO];
      if (slowestAction && slowestAction.averageTime > ANIMATION_DURATION_VERY_SLOW) {
        recommendations.push(
          `Action "${slowestAction.action}" is slow (${slowestAction.averageTime.toFixed(ZERO)}ms average)`,
        );
      }
    }

    const lowSuccessActions = topSlowActions.filter((a) => a.successRate < MAGIC_0_9);
    if (lowSuccessActions.length > ZERO) {
      recommendations.push(
        `${lowSuccessActions.length} actions have low success rates. Consider improving error handling.`,
      );
    }

    return {
      totalInteractions,
      uniqueActions: interactions.length,
      averageResponseTime: totalTime / totalInteractions || ZERO,
      successRate: totalSuccesses / totalInteractions || ZERO,
      topSlowActions,
      recommendations,
    };
  }

  /**
   * Ëé∑ÂèñÁΩëÁªúÊÄßËÉΩÊä•Âëä
   * Get network performance report
   */
  getNetworkReport(): {
    totalRequests: number;
    averageResponseTime: number;
    successRate: number;
    totalDataTransferred: number;
    slowestRequests: Array<{
      url: string;
      method: string;
      timing: number;
      status: number;
    }>;
    errorRequests: Array<{
      url: string;
      method: string;
      status: number;
      timing: number;
    }>;
  } {
    const totalRequests = this.networkRequests.length;
    const totalTime = this.networkRequests.reduce(
      (sum, req) => sum + req.timing,
      ZERO,
    );
    const successfulRequests = this.networkRequests.filter(
      (req) => req.status >= HTTP_OK_CONST && req.status < ANIMATION_DURATION_NORMAL,
    );
    const totalDataTransferred = this.networkRequests.reduce(
      (sum, req) => sum + req.size,
      ZERO,
    );

    const slowestRequests = [...this.networkRequests]
      .sort((a, b) => b.timing - a.timing)
      .slice(ZERO, COUNT_TEN)
      .map((req) => ({
        url: req.url,
        method: req.method,
        timing: req.timing,
        status: req.status,
      }));

    const errorRequests = this.networkRequests
      .filter((req) => req.status >= HTTP_BAD_REQUEST_CONST)
      .map((req) => ({
        url: req.url,
        method: req.method,
        status: req.status,
        timing: req.timing,
      }));

    return {
      totalRequests,
      averageResponseTime: totalTime / totalRequests || ZERO,
      successRate: successfulRequests.length / totalRequests || ZERO,
      totalDataTransferred,
      slowestRequests,
      errorRequests,
    };
  }

  /**
   * Ëé∑ÂèñÈ°µÈù¢Âä†ËΩΩÊä•Âëä
   * Get page load report
   */
  getPageLoadReport(): {
    totalPageLoads: number;
    averageLoadTime: number;
    slowestPages: Array<{
      url: string;
      loadTime: number;
      timestamp: number;
    }>;
  } {
    const totalPageLoads = this.pageLoads.length;
    const totalLoadTime = this.pageLoads.reduce((sum, page) => {
      return (
        sum + (page.timing.loadComplete || page.timing.domContentLoaded || ZERO)
      );
    }, ZERO);

    const slowestPages = this.pageLoads
      .map((page) => ({
        url: page.url,
        loadTime: page.timing.loadComplete || page.timing.domContentLoaded || ZERO,
        timestamp: page.timestamp,
      }))
      .sort((a, b) => b.loadTime - a.loadTime)
      .slice(ZERO, COUNT_TEN);

    return {
      totalPageLoads,
      averageLoadTime: totalLoadTime / totalPageLoads || ZERO,
      slowestPages,
    };
  }

  /**
   * ÈáçÁΩÆÊâÄÊúâÊï∞ÊçÆ
   * Reset all data
   */
  reset(): void {
    this.interactions.clear();
    this.networkRequests.length = ZERO;
    this.pageLoads.length = ZERO;
    this.errors.length = ZERO;
  }
}

