import { MAGIC_20 } from "@/constants/count";
import { ANIMATION_DURATION_VERY_SLOW, COUNT_FIVE, COUNT_PAIR, COUNT_TEN, ONE, PERCENTAGE_FULL, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { MINUTE_MS } from "@/constants/time";

/**
 * ÈÄüÁéáÈôêÂà∂Â∑•ÂÖ∑
 * Rate limiting utilities
 */

/**
 * Rate limiting constants
 */
const RATE_LIMIT_CONSTANTS = {
  DEFAULT_MAX_REQUESTS: COUNT_TEN,
  DEFAULT_WINDOW_MS: MINUTE_MS, // 1 minute
  CLEANUP_INTERVAL_MINUTES: COUNT_FIVE,
  MINUTES_TO_MS: SECONDS_PER_MINUTE,
  SECONDS_TO_MS: ANIMATION_DURATION_VERY_SLOW,
} as const;

/**
 * Rate limit entry interface
 */
interface RateLimitEntry {
  count: number;
  resetTime: number;
}

/**
 * Rate limit store
 */
const rateLimitStore = new Map<string, RateLimitEntry>();

/**
 * Rate limiting utility
 */
export function rateLimit(
  identifier: string,
  maxRequests: number = RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS,
  windowMs: number = RATE_LIMIT_CONSTANTS.DEFAULT_WINDOW_MS,
): boolean {
  const now = Date.now();
  const entry = rateLimitStore.get(identifier);

  if (!entry || now > entry.resetTime) {
    // First request or window expired
    rateLimitStore.set(identifier, {
      count: ONE,
      resetTime: now + windowMs,
    });
    return true;
  }

  if (entry.count >= maxRequests) {
    // Rate limit exceeded
    return false;
  }

  // Increment count
  entry.count += ONE;
  rateLimitStore.set(identifier, entry);
  return true;
}

/**
 * Get rate limit status for an identifier
 */
export function getRateLimitStatus(identifier: string): {
  remaining: number;
  resetTime: number;
  isLimited: boolean;
} {
  const now = Date.now();
  const entry = rateLimitStore.get(identifier);

  if (!entry || now > entry.resetTime) {
    return {
      remaining: RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS - ONE,
      resetTime: now + RATE_LIMIT_CONSTANTS.DEFAULT_WINDOW_MS,
      isLimited: false,
    };
  }

  const remaining = Math.max(
    ZERO,
    RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS - entry.count,
  );
  return {
    remaining,
    resetTime: entry.resetTime,
    isLimited: remaining === ZERO,
  };
}

/**
 * Clean up expired rate limit entries
 */
export function cleanupRateLimit(): void {
  const now = Date.now();
  for (const [key, entry] of rateLimitStore.entries()) {
    if (now > entry.resetTime) {
      rateLimitStore.delete(key);
    }
  }
}

/**
 * Reset rate limit for a specific identifier
 */
export function resetRateLimit(identifier: string): void {
  rateLimitStore.delete(identifier);
}

/**
 * Get all active rate limit entries (for debugging)
 */
export function getActiveLimits(): Array<{
  identifier: string;
  count: number;
  resetTime: number;
  remaining: number;
}> {
  const now = Date.now();
  const active: Array<{
    identifier: string;
    count: number;
    resetTime: number;
    remaining: number;
  }> = [];

  for (const [identifier, entry] of rateLimitStore.entries()) {
    if (now <= entry.resetTime) {
      active.push({
        identifier,
        count: entry.count,
        resetTime: entry.resetTime,
        remaining: Math.max(
          ZERO,
          RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS - entry.count,
        ),
      });
    }
  }

  return active;
}

/**
 * Advanced rate limiting with different tiers
 */
export interface RateLimitTier {
  name: string;
  maxRequests: number;
  windowMs: number;
}

const defaultTiers: Record<string, RateLimitTier> = {
  strict: {
    name: 'strict',
    maxRequests: COUNT_FIVE,
    windowMs: MINUTE_MS, // 1 minute
  },
  normal: {
    name: 'normal',
    maxRequests: COUNT_TEN,
    windowMs: MINUTE_MS, // 1 minute
  },
  relaxed: {
    name: 'relaxed',
    maxRequests: MAGIC_20,
    windowMs: MINUTE_MS, // 1 minute
  },
  premium: {
    name: 'premium',
    maxRequests: PERCENTAGE_FULL,
    windowMs: MINUTE_MS, // 1 minute
  },
};

/**
 * Rate limit with tier support
 */
export function rateLimitWithTier(
  identifier: string,
  tierName: keyof typeof defaultTiers = 'normal',
): boolean {
  const tier = defaultTiers[tierName];
  if (!tier) {
    throw new Error(`Unknown rate limit tier: ${tierName}`);
  }

  return rateLimit(identifier, tier.maxRequests, tier.windowMs);
}

/**
 * Create custom rate limit tier
 */
export function createRateLimitTier(
  name: string,
  maxRequests: number,
  windowMs: number,
): RateLimitTier {
  return { name, maxRequests, windowMs };
}

/**
 * Sliding window rate limiter
 */
interface SlidingWindowEntry {
  timestamps: number[];
}

const slidingWindowStore = new Map<string, SlidingWindowEntry>();

/**
 * Sliding window rate limiting
 */
export function slidingWindowRateLimit(
  identifier: string,
  maxRequests: number = RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS,
  windowMs: number = RATE_LIMIT_CONSTANTS.DEFAULT_WINDOW_MS,
): boolean {
  const now = Date.now();
  const windowStart = now - windowMs;

  let entry = slidingWindowStore.get(identifier);
  if (!entry) {
    entry = { timestamps: [] };
    slidingWindowStore.set(identifier, entry);
  }

  // Remove old timestamps outside the window
  entry.timestamps = entry.timestamps.filter(
    (timestamp) => timestamp > windowStart,
  );

  // Check if we're within the limit
  if (entry.timestamps.length >= maxRequests) {
    return false;
  }

  // Add current timestamp
  entry.timestamps.push(now);
  return true;
}

/**
 * Clean up sliding window entries
 */
export function cleanupSlidingWindow(): void {
  const now = Date.now();
  const maxAge = RATE_LIMIT_CONSTANTS.DEFAULT_WINDOW_MS * COUNT_PAIR; // Keep entries for 2x window size

  for (const [key, entry] of slidingWindowStore.entries()) {
    const oldestTimestamp = Math.min(...entry.timestamps);
    if (now - oldestTimestamp > maxAge) {
      slidingWindowStore.delete(key);
    }
  }
}

// Clean up rate limit entries every 5 minutes
if (typeof setInterval !== 'undefined') {
  setInterval(
    () => {
      cleanupRateLimit();
      cleanupSlidingWindow();
    },
    RATE_LIMIT_CONSTANTS.CLEANUP_INTERVAL_MINUTES *
      RATE_LIMIT_CONSTANTS.MINUTES_TO_MS *
      RATE_LIMIT_CONSTANTS.SECONDS_TO_MS,
  );
}

import { MAGIC_6 } from "@/constants/count";
import { ANIMATION_DURATION_VERY_SLOW, COUNT_PAIR, HEX_MASK_6_BITS, HEX_MASK_BIT_6, HEX_MASK_HIGH_BIT, HEX_MASK_LOW_NIBBLE, MAGIC_12, MAGIC_16, MAGIC_20, MAGIC_32, MAGIC_48, MAGIC_64, MAGIC_8, MAGIC_HEX_3, MAGIC_HEX_8, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";

/**
 * ÂÆâÂÖ®‰ª§ÁâåÁîüÊàêÂ∑•ÂÖ∑
 * Security token generation utilities
 */

/**
 * Token generation constants
 */
const TOKEN_CONSTANTS = {
  // Token generation
  DEFAULT_TOKEN_LENGTH: MAGIC_32,
  HEX_RADIX: COUNT_PAIR,
  HEX_PAD_LENGTH: COUNT_PAIR,
  HEX_BASE: MAGIC_16,
} as const;

/**
 * Generate a secure random string
 */
export function generateSecureToken(
  length: number = TOKEN_CONSTANTS.DEFAULT_TOKEN_LENGTH,
): string {
  if (typeof crypto !== 'undefined' && crypto.getRandomValues) {
    // Generate half the length in bytes since each byte becomes COUNT_PAIR hex characters
    const byteLength = Math.ceil(length / TOKEN_CONSTANTS.HEX_RADIX);
    const array = new Uint8Array(byteLength);
    crypto.getRandomValues(array);
    const hex = Array.from(array, (byte) =>
      byte
        .toString(TOKEN_CONSTANTS.HEX_BASE)
        .padStart(TOKEN_CONSTANTS.HEX_PAD_LENGTH, '0'),
    ).join('');
    return hex.substring(ZERO, length);
  }

  // Fallback for environments without crypto.getRandomValues
  const chars =
    'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  let result = '';
  for (let i = ZERO; i < length; i++) {
    result += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return result;
}

/**
 * Generate a secure random UUID v4
 */
export function generateUUID(): string {
  if (typeof crypto !== 'undefined' && crypto.getRandomValues) {
    const array = new Uint8Array(MAGIC_16);
    crypto.getRandomValues(array);

    // Set version (4) and variant bits
    array[MAGIC_6] = (array[MAGIC_6]! & HEX_MASK_LOW_NIBBLE) | HEX_MASK_BIT_6;
    array[MAGIC_8] = (array[MAGIC_8]! & HEX_MASK_6_BITS) | HEX_MASK_HIGH_BIT;

    const hex = Array.from(array, (byte) =>
      byte.toString(MAGIC_16).padStart(COUNT_PAIR, '0'),
    ).join('');

    return [
      hex.substring(ZERO, MAGIC_8),
      hex.substring(MAGIC_8, MAGIC_12),
      hex.substring(MAGIC_12, MAGIC_16),
      hex.substring(MAGIC_16, MAGIC_20),
      hex.substring(MAGIC_20, MAGIC_32),
    ].join('-');
  }

  // Fallback UUID generation
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
    const r = (Math.random() * MAGIC_16) | 0;
    const v = c === 'x' ? r : (r & MAGIC_HEX_3) | MAGIC_HEX_8;
    return v.toString(MAGIC_16);
  });
}

/**
 * Generate a secure random API key
 */
export function generateApiKey(prefix: string = 'sk'): string {
  const randomPart = generateSecureToken(MAGIC_48);
  return `${prefix}_${randomPart}`;
}

/**
 * Generate a secure session token
 */
export function generateSessionToken(): string {
  return generateSecureToken(MAGIC_64);
}

/**
 * Generate a secure CSRF token
 */
export function generateCsrfToken(): string {
  return generateSecureToken(MAGIC_32);
}

/**
 * Generate a secure nonce for CSP
 */
export function generateNonce(): string {
  return generateSecureToken(MAGIC_16);
}

/**
 * Generate a secure one-time password (OTP)
 */
export function generateOTP(length: number = MAGIC_6): string {
  const digits = '0123456789';
  let result = '';

  if (typeof crypto !== 'undefined' && crypto.getRandomValues) {
    const array = new Uint8Array(length);
    crypto.getRandomValues(array);

    for (let i = ZERO; i < length; i++) {
      result += digits[array[i]! % digits.length];
    }
  } else {
    // Fallback
    for (let i = ZERO; i < length; i++) {
      result += digits[Math.floor(Math.random() * digits.length)];
    }
  }

  return result;
}

/**
 * Generate a secure verification code (alphanumeric)
 */
export function generateVerificationCode(length: number = MAGIC_8): string {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
  let result = '';

  if (typeof crypto !== 'undefined' && crypto.getRandomValues) {
    const array = new Uint8Array(length);
    crypto.getRandomValues(array);

    for (let i = ZERO; i < length; i++) {
      result += chars[array[i]! % chars.length];
    }
  } else {
    // Fallback
    for (let i = ZERO; i < length; i++) {
      result += chars[Math.floor(Math.random() * chars.length)];
    }
  }

  return result;
}

/**
 * Validate token format
 */
export function isValidToken(token: string, expectedLength?: number): boolean {
  if (typeof token !== 'string' || token.length === ZERO) {
    return false;
  }

  // Check if token contains only valid characters (alphanumeric and some special chars)
  const validTokenRegex = /^[a-zA-Z0-9_-]+$/;
  if (!validTokenRegex.test(token)) {
    return false;
  }

  // Check length if specified
  if (expectedLength && token.length !== expectedLength) {
    return false;
  }

  return true;
}

/**
 * Validate UUID format
 */
export function isValidUUID(uuid: string): boolean {
  const uuidRegex =
    /^[0-9a-f]{MAGIC_8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{MAGIC_12}$/i;
  return uuidRegex.test(uuid);
}

/**
 * Generate a secure random salt for password hashing
 */
export function generateSalt(length: number = MAGIC_16): string {
  return generateSecureToken(length * COUNT_PAIR); // Double length for hex representation
}

/**
 * Token expiration utilities
 */
export interface TokenWithExpiry {
  token: string;
  expiresAt: number;
}

/**
 * Create a token with expiration
 */
export function createTokenWithExpiry(
  tokenLength: number = MAGIC_32,
  expiryMinutes: number = SECONDS_PER_MINUTE,
): TokenWithExpiry {
  return {
    token: generateSecureToken(tokenLength),
    expiresAt: Date.now() + expiryMinutes * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW,
  };
}

/**
 * Check if token is expired
 */
export function isTokenExpired(tokenWithExpiry: TokenWithExpiry): boolean {
  return Date.now() > tokenWithExpiry.expiresAt;
}

import { INPUT_VALIDATION_CONSTANTS } from "@/constants/security-constants";
const {EMAIL_MAX_LENGTH} = INPUT_VALIDATION_CONSTANTS;
import { ANIMATION_DURATION_VERY_SLOW, ZERO } from "@/constants/magic-numbers";

/**
 * ÂÆâÂÖ®È™åËØÅÂ∑•ÂÖ∑
 * Security validation utilities
 */

/**
 * Security constants for validation
 */
const VALIDATION_CONSTANTS = {
  // Email validation
  MAX_EMAIL_LENGTH: INPUT_VALIDATION_CONSTANTS.EMAIL_MAX_LENGTH,
} as const;

/**
 * Sanitize user input to prevent XSS attacks
 */
export function sanitizeInput(input: string): string {
  if (typeof input !== 'string') {
    return '';
  }

  return input
    .replace(/[<>]/g, '') // Remove angle brackets
    .replace(/javascript:/gi, '') // Remove javascript: protocol
    .replace(/on\w+=/gi, '') // Remove event handlers
    .replace(/data:/gi, '') // Remove data: protocol
    .trim();
}

/**
 * Validate email address format
 */
export function isValidEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return (
    emailRegex.test(email) &&
    email.length <= VALIDATION_CONSTANTS.MAX_EMAIL_LENGTH
  );
}

/**
 * Validate URL format and protocol
 */
export function isValidUrl(
  url: string,
  allowedProtocols: string[] = ['http:', 'https:'],
): boolean {
  try {
    const urlObj = new URL(url);
    return allowedProtocols.includes(urlObj.protocol);
  } catch {
    return false;
  }
}

/**
 * Validate and sanitize file path to prevent path traversal attacks
 */
export function sanitizeFilePath(filePath: string): string {
  if (typeof filePath !== 'string') {
    return '';
  }

  // Remove dangerous path components
  return filePath
    .replace(/\.\./g, '') // Remove parent directory references
    .replace(/[<>:"|?*]/g, '') // Remove invalid filename characters
    .replace(/^\/+/, '') // Remove leading slashes
    .trim();
}

/**
 * Validate input length
 */
export function validateInputLength(
  input: string,
  minLength: number = ZERO,
  maxLength: number = ANIMATION_DURATION_VERY_SLOW,
): { valid: boolean; error?: string } {
  if (typeof input !== 'string') {
    return { valid: false, error: 'Input must be a string' };
  }

  if (input.length < minLength) {
    return {
      valid: false,
      error: `Input must be at least ${minLength} characters`,
    };
  }

  if (input.length > maxLength) {
    return {
      valid: false,
      error: `Input must be no more than ${maxLength} characters`,
    };
  }

  return { valid: true };
}

/**
 * Check if string contains only allowed characters
 */
export function validateCharacters(
  input: string,
  allowedPattern: RegExp,
): { valid: boolean; error?: string } {
  if (typeof input !== 'string') {
    return { valid: false, error: 'Input must be a string' };
  }

  if (!allowedPattern.test(input)) {
    return { valid: false, error: 'Input contains invalid characters' };
  }

  return { valid: true };
}

/**
 * Validate phone number format
 */
export function isValidPhoneNumber(phone: string): boolean {
  // International phone number validation
  const phoneRegex = /^[+]?[1-9][\d]{0,15}$/;
  const cleanPhone = phone.replace(/[\s\-()]/g, '');
  return phoneRegex.test(cleanPhone);
}

/**
 * Validate and sanitize HTML content
 */
export function sanitizeHtml(html: string): string {
  if (typeof html !== 'string') {
    return '';
  }

  // Basic HTML sanitization - remove script tags and dangerous attributes
  return html
    .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
    .replace(/<iframe\b[^<]*(?:(?!<\/iframe>)<[^<]*)*<\/iframe>/gi, '')
    .replace(/on\w+\s*=\s*["'][^"']*["']/gi, '')
    .replace(/javascript:/gi, '')
    .replace(/data:/gi, '')
    .trim();
}

/**
 * Validate JSON string
 */
export function isValidJson(jsonString: string): boolean {
  try {
    JSON.parse(jsonString);
    return true;
  } catch {
    return false;
  }
}

/**
 * Validate and sanitize user input for database queries
 */
export function sanitizeForDatabase(input: string): string {
  if (typeof input !== 'string') {
    return '';
  }

  // Basic SQL injection prevention
  return input
    .replace(/['";\\]/g, '') // Remove quotes and backslashes
    .replace(/--/g, '') // Remove SQL comments
    .replace(/\/\*/g, '') // Remove block comment start
    .replace(/\*\//g, '') // Remove block comment end
    .trim();
}

