 * Theme analytics core class
 */
import { MAGIC_0_1 } from "@/constants/decimal";
import { ANIMATION_DURATION_VERY_SLOW, COUNT_FIVE, COUNT_TRIPLE, DAYS_PER_MONTH, HOURS_PER_DAY, PERCENTAGE_FULL, PERCENTAGE_HALF, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { ThemeAnalyticsUtils } from '@/lib/theme-analytics-utils';
import * as Sentry from '@sentry/nextjs';
import type {
  ThemeAnalyticsConfig,
  ThemePerformanceMetrics,
  ThemePerformanceSummary,
  ThemeSwitchPattern,
  ThemeUsageStats,
} from '@/lib/theme-analytics-types';

/**
 * ‰∏ªÈ¢òÂàÜÊûêÁÆ°ÁêÜÂô®
 * Ë¥üË¥£Êî∂ÈõÜ„ÄÅÂàÜÊûêÂíåÊä•Âëä‰∏ªÈ¢òÁõ∏ÂÖ≥ÁöÑÊÄßËÉΩÂíå‰ΩøÁî®Êï∞ÊçÆ
 */
export class ThemeAnalytics {
  private config: ThemeAnalyticsConfig;
  private performanceMetrics: ThemePerformanceMetrics[] = [];
  private usageStats: Map<string, ThemeUsageStats> = new Map();
  private switchPatterns: ThemeSwitchPattern[] = [];
  private currentTheme: string = 'system';
  private sessionStartTime: number = Date.now();
  private lastSwitchTime: number = ZERO;

  /**
   * Ëé∑ÂèñÂΩìÂâç‰∏ªÈ¢ò
   */
  getCurrentTheme(): string {
    return this.currentTheme;
  }

  constructor(config?: Partial<ThemeAnalyticsConfig>) {
    this.config = {
      enabled: process.env.NODE_ENV === 'production',
      performanceThreshold: PERCENTAGE_FULL, // 100ms
      sampleRate: MAGIC_0_1, // 10% ÈááÊ†∑
      enableDetailedTracking: true,
      enableUserBehaviorAnalysis: true,
      ...config,
    };

    // ÂàùÂßãÂåñSentryËá™ÂÆö‰πâÊ†áÁ≠æ
    if (this.config.enabled) {
      Sentry.setTag('feature', 'theme-analytics');
    }
  }

  /**
   * ËÆ∞ÂΩï‰∏ªÈ¢òÂàáÊç¢ÊÄßËÉΩ
   */
  recordThemeSwitch(
    fromTheme: string,
    toTheme: string,
    startTime: number,
    endTime: number,
    supportsViewTransitions: boolean = false,
  ): void {
    if (
      !this.config.enabled ||
      !ThemeAnalyticsUtils.shouldSample(this.config.sampleRate)
    ) {
      return;
    }

    const switchDuration = endTime - startTime;
    const now = Date.now();

    const metrics: ThemePerformanceMetrics = {
      switchDuration,
      fromTheme,
      toTheme,
      timestamp: now,
      userAgent: typeof navigator !== 'undefined' ? navigator.userAgent : '',
      viewportSize: ThemeAnalyticsUtils.getViewportSize(),
      supportsViewTransitions,
    };

    // Â≠òÂÇ®ÊÄßËÉΩÊåáÊ†á
    this.performanceMetrics.push(metrics);
    this.lastSwitchTime = now;

    // Êõ¥Êñ∞‰ΩøÁî®ÁªüËÆ°
    ThemeAnalyticsUtils.updateUsageStats(
      this.usageStats,
      toTheme,
      now,
      this.lastSwitchTime,
    );

    // ÂèëÈÄÅÂà∞Sentry
    ThemeAnalyticsUtils.sendPerformanceMetrics(metrics);

    // Ê£ÄÊü•ÊÄßËÉΩÈòàÂÄº
    if (switchDuration > this.config.performanceThreshold) {
      ThemeAnalyticsUtils.reportPerformanceIssue(metrics);
    }

    // ÂàÜÊûêÂàáÊç¢Ê®°Âºè
    if (this.config.enableUserBehaviorAnalysis) {
      ThemeAnalyticsUtils.analyzeSwitchPattern(
        this.switchPatterns,
        fromTheme,
        toTheme,
        switchDuration,
      );
    }

    // Ê∏ÖÁêÜÊóßÊï∞ÊçÆ
    this.cleanupOldData();
  }

  /**
   * ËÆ∞ÂΩï‰∏ªÈ¢òÂÅèÂ•Ω
   */
  recordThemePreference(theme: string): void {
    if (!this.config.enabled) return;

    this.currentTheme = theme;

    // ÂèëÈÄÅÁî®Êà∑ÂÅèÂ•ΩÊï∞ÊçÆÂà∞Sentry
    Sentry.setUser({
      themePreference: theme,
    });

    Sentry.addBreadcrumb({
      category: 'theme',
      message: `User selected theme: ${theme}`,
      level: 'info',
      data: {
        theme,
        timestamp: Date.now(),
      },
    });

    // ËÆ∞ÂΩï‰∏ªÈ¢òÂÅèÂ•Ω‰∫ã‰ª∂
    Sentry.addBreadcrumb({
      category: 'user-preference',
      message: `Theme preference set to ${theme}`,
      level: 'info',
      data: {
        theme,
        sessionDuration: Date.now() - this.sessionStartTime,
      },
    });
  }

  /**
   * Ëé∑ÂèñÊÄßËÉΩÊëòË¶Å
   */
  getPerformanceSummary(): ThemePerformanceSummary {
    return ThemeAnalyticsUtils.generatePerformanceSummary(
      this.performanceMetrics,
      this.usageStats,
    );
  }

  /**
   * Ëé∑Âèñ‰∏ªÈ¢ò‰ΩøÁî®ÁªüËÆ°
   */
  getUsageStatistics(): ThemeUsageStats[] {
    return Array.from(this.usageStats.values()).sort(
      (a, b) => b.count - a.count,
    );
  }

  /**
   * ÂèëÈÄÅÊÄßËÉΩÊä•ÂëäÂà∞Sentry
   */
  sendPerformanceReport(): void {
    if (!this.config.enabled) return;

    const summary = this.getPerformanceSummary();
    const usageStats = this.getUsageStatistics();

    // ÂèëÈÄÅÊÄßËÉΩÊëòË¶Å
    Sentry.addBreadcrumb({
      category: 'theme-analytics',
      message: 'Theme performance report',
      level: 'info',
      data: ThemeAnalyticsUtils.formatPerformanceData(summary),
    });

    // ÂèëÈÄÅ‰ΩøÁî®ÁªüËÆ°
    Sentry.setContext('theme-usage', {
      statistics: usageStats.slice(ZERO, COUNT_FIVE), // Âè™ÂèëÈÄÅÂâçCOUNT_FIVE‰∏™ÊúÄÂ∏∏Áî®ÁöÑ‰∏ªÈ¢ò
      totalThemes: usageStats.length,
      sessionDuration: Date.now() - this.sessionStartTime,
    });

    // ÂèëÈÄÅÂàáÊç¢Ê®°ÂºèÂàÜÊûê
    if (
      this.config.enableUserBehaviorAnalysis &&
      this.switchPatterns.length > ZERO
    ) {
      const topPatterns = this.switchPatterns
        .sort((a, b) => b.frequency - a.frequency)
        .slice(ZERO, COUNT_TRIPLE);

      Sentry.setContext('theme-patterns', {
        topPatterns: topPatterns.map((p) => ({
          sequence: p.sequence.join(' ‚Üí '),
          frequency: p.frequency,
          avgDuration: Math.round(p.avgDuration),
        })),
      });
    }

    // ÂèëÈÄÅËá™ÂÆö‰πâ‰∫ã‰ª∂
    Sentry.captureMessage('Theme Analytics Report', 'info');
  }

  /**
   * ÈáçÁΩÆÂàÜÊûêÊï∞ÊçÆ
   */
  reset(): void {
    this.performanceMetrics = [];
    this.usageStats.clear();
    this.switchPatterns = [];
    this.sessionStartTime = Date.now();
    this.lastSwitchTime = ZERO;
  }

  /**
   * Ëé∑ÂèñÈÖçÁΩÆ
   */
  getConfig(): ThemeAnalyticsConfig {
    return { ...this.config };
  }

  /**
   * Êõ¥Êñ∞ÈÖçÁΩÆ
   */
  updateConfig(newConfig: Partial<ThemeAnalyticsConfig>): void {
    this.config = { ...this.config, ...newConfig };
  }

  /**
   * Ê∏ÖÁêÜÊóßÊï∞ÊçÆ
   */
  private cleanupOldData(): void {
    const hoursInDay = HOURS_PER_DAY;
    const minutesInHour = SECONDS_PER_MINUTE;
    const secondsInMinute = SECONDS_PER_MINUTE;
    const millisecondsInSecond = ANIMATION_DURATION_VERY_SLOW;
    const maxAge =
      hoursInDay * minutesInHour * secondsInMinute * millisecondsInSecond; // 24Â∞èÊó∂

    ThemeAnalyticsUtils.cleanupOldData(this.performanceMetrics, maxAge);

    // Ê∏ÖÁêÜ‰ΩøÁî®ÁªüËÆ°‰∏≠ÁöÑËøáÊúüÊï∞ÊçÆ
    const cutoffTime = Date.now() - maxAge;
    for (const [theme, stats] of this.usageStats.entries()) {
      if (stats.lastUsed < cutoffTime) {
        this.usageStats.delete(theme);
      }
    }

    // Ê∏ÖÁêÜÂàáÊç¢Ê®°ÂºèÊï∞ÊçÆÔºà‰øùÁïôÊúÄËøëÁöÑÊ®°ÂºèÔºâ
    if (this.switchPatterns.length > PERCENTAGE_HALF) {
      this.switchPatterns = this.switchPatterns
        .sort((a, b) => b.frequency - a.frequency)
        .slice(ZERO, DAYS_PER_MONTH);
    }
  }
}

/**
 * ‰∏ªÈ¢òÂàÜÊûêÂ∑•ÂÖ∑ÂáΩÊï∞
 * Theme analytics utility functions
 */

import { ANIMATION_DURATION_VERY_SLOW, COUNT_PAIR, HOURS_PER_DAY, ONE, PERCENTAGE_FULL, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import * as Sentry from '@sentry/nextjs';
import type {
  ThemePerformanceMetrics,
  ThemePerformanceSummary,
  ThemeSwitchPattern,
  ThemeUsageStats,
} from '@/lib/theme-analytics-types';

/**
 * ‰∏ªÈ¢òÂàÜÊûêÂ∑•ÂÖ∑Á±ª
 */
export class ThemeAnalyticsUtils {
  /**
   * Ê£ÄÊü•ÊòØÂê¶Â∫îËØ•ÈááÊ†∑
   */
  static shouldSample(sampleRate: number): boolean {
    // ‰ΩøÁî®crypto.getRandomValues()Êõø‰ª£Math.random()‰ª•ÊèêÈ´òÂÆâÂÖ®ÊÄß
    if (typeof crypto !== 'undefined' && crypto.getRandomValues) {
      const array = new Uint32Array(ONE);
      crypto.getRandomValues(array);
      const maxUint32 = ZERO;
      const randomValue = (array[ZERO] || ZERO) / maxUint32;
      return randomValue < sampleRate;
    }

    // ÈôçÁ∫ßÂà∞Math.random()
    return Math.random() < sampleRate;
  }

  /**
   * Ëé∑ÂèñËßÜÂè£Â§ßÂ∞è
   */
  static getViewportSize(): { width: number; height: number } {
    if (typeof window !== 'undefined') {
      return {
        width: window.innerWidth,
        height: window.innerHeight,
      };
    }
    return { width: ZERO, height: ZERO };
  }

  /**
   * ÂèëÈÄÅÊÄßËÉΩÊåáÊ†áÂà∞Sentry
   */
  static sendPerformanceMetrics(metrics: ThemePerformanceMetrics): void {
    Sentry.addBreadcrumb({
      category: 'theme-performance',
      message: `Theme switched from ${metrics.fromTheme} to ${metrics.toTheme}`,
      level: 'info',
      data: {
        duration: metrics.switchDuration,
        supportsViewTransitions: metrics.supportsViewTransitions,
      },
    });
  }

  /**
   * Êä•ÂëäÊÄßËÉΩÈóÆÈ¢ò
   */
  static reportPerformanceIssue(metrics: ThemePerformanceMetrics): void {
    Sentry.captureMessage(
      `Slow theme switch detected: ${metrics.switchDuration}ms`,
      'warning',
    );

    Sentry.setContext('theme-performance', {
      fromTheme: metrics.fromTheme,
      toTheme: metrics.toTheme,
      duration: metrics.switchDuration,
      viewportSize: metrics.viewportSize,
      supportsViewTransitions: metrics.supportsViewTransitions,
    });
  }

  /**
   * Êõ¥Êñ∞‰ΩøÁî®ÁªüËÆ°
   */
  static updateUsageStats(
    usageStats: Map<string, ThemeUsageStats>,
    theme: string,
    timestamp: number,
    lastSwitchTime: number,
  ): void {
    const existing = usageStats.get(theme);
    if (existing) {
      existing.count += ONE;
      existing.lastUsed = timestamp;
      existing.sessionDuration += timestamp - lastSwitchTime;
    } else {
      usageStats.set(theme, {
        theme,
        count: ONE,
        lastUsed: timestamp,
        sessionDuration: ZERO,
      });
    }
  }

  /**
   * ÂàÜÊûêÂàáÊç¢Ê®°Âºè
   */
  static analyzeSwitchPattern(
    switchPatterns: ThemeSwitchPattern[],
    fromTheme: string,
    toTheme: string,
    duration: number,
  ): void {
    const sequence = [fromTheme, toTheme];
    const sequenceKey = sequence.join('-');

    const existing = switchPatterns.find(
      (p) => p.sequence.join('-') === sequenceKey,
    );

    if (existing) {
      existing.frequency += ONE;
      existing.avgDuration = (existing.avgDuration + duration) / COUNT_PAIR;
    } else {
      switchPatterns.push({
        sequence,
        frequency: ONE,
        avgDuration: duration,
        timeOfDay: new Date().getHours(),
      });
    }
  }

  /**
   * Ê∏ÖÁêÜÊóßÊï∞ÊçÆ
   */
  static cleanupOldData(
    performanceMetrics: ThemePerformanceMetrics[],
    maxAge: number = HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW, // HOURS_PER_DAYÂ∞èÊó∂
  ): void {
    const cutoffTime = Date.now() - maxAge;

    // Ê∏ÖÁêÜÊÄßËÉΩÊåáÊ†á
    const validMetrics = performanceMetrics.filter(
      (metric) => metric.timestamp > cutoffTime,
    );

    // Ê∏ÖÁ©∫ÂéüÊï∞ÁªÑÂπ∂Ê∑ªÂä†ÊúâÊïàÊï∞ÊçÆ
    performanceMetrics.length = ZERO;
    performanceMetrics.push(...validMetrics);
  }

  /**
   * ÁîüÊàêÊÄßËÉΩÊëòË¶Å
   */
  static generatePerformanceSummary(
    performanceMetrics: ThemePerformanceMetrics[],
    usageStats: Map<string, ThemeUsageStats>,
  ): ThemePerformanceSummary {
    if (performanceMetrics.length === ZERO) {
      return {
        totalSwitches: ZERO,
        averageDuration: ZERO,
        slowSwitches: ZERO,
        fastestSwitch: ZERO,
        slowestSwitch: ZERO,
        mostUsedTheme: 'system',
        viewTransitionSupport: false,
      };
    }

    const durations = performanceMetrics.map((m) => m.switchDuration);
    const averageDuration =
      durations.reduce((a, b) => a + b, ZERO) / durations.length;
    const slowSwitches = durations.filter((d) => d > PERCENTAGE_FULL).length; // Ë∂ÖËøá100msÁöÑÂàáÊç¢
    const fastestSwitch = Math.min(...durations);
    const slowestSwitch = Math.max(...durations);

    // ÊâæÂá∫ÊúÄÂ∏∏Áî®ÁöÑ‰∏ªÈ¢ò
    const usageArray = Array.from(usageStats.values());
    const mostUsedTheme =
      usageArray.length > ZERO
        ? usageArray.reduce((a, b) => (a.count > b.count ? a : b)).theme
        : 'system';

    // Ê£ÄÊü•View TransitionsÊîØÊåÅ
    const viewTransitionSupport = performanceMetrics.some(
      (m) => m.supportsViewTransitions,
    );

    return {
      totalSwitches: performanceMetrics.length,
      averageDuration,
      slowSwitches,
      fastestSwitch,
      slowestSwitch,
      mostUsedTheme,
      viewTransitionSupport,
    };
  }

  /**
   * Ê†ºÂºèÂåñÊÄßËÉΩÊï∞ÊçÆÁî®‰∫éÊä•Âëä
   */
  static formatPerformanceData(
    summary: ThemePerformanceSummary,
  ): Record<string, unknown> {
    return {
      total_switches: summary.totalSwitches,
      avg_duration_ms: Math.round(summary.averageDuration),
      slow_switches: summary.slowSwitches,
      fastest_switch_ms: summary.fastestSwitch,
      slowest_switch_ms: summary.slowestSwitch,
      most_used_theme: summary.mostUsedTheme,
      view_transition_support: summary.viewTransitionSupport,
    };
  }
}

import { MAGIC_85 } from "@/constants/count";
import { MAGIC_0_8 } from "@/constants/decimal";
import { logger } from '@/lib/logger';
import { TranslationQualityManager } from '@/lib/translation-manager-quality';
import { TranslationManagerSecurity } from '@/lib/translation-manager-security';
import type { Locale } from '@/types/i18n';
import type {
  QualityReport,
  QualityScore,
  TranslationManagerConfig,
  TranslationQualityCheck,
  ValidationReport,
} from '@/types/translation-manager';

/**
 * ÁøªËØëÁÆ°ÁêÜÂô®Ê†∏ÂøÉÁ±ª
 */
export class TranslationManagerCore implements TranslationQualityCheck {
  private config: TranslationManagerConfig;
  private translations: Partial<Record<Locale, Record<string, unknown>>> = {};
  private qualityManager: TranslationQualityManager;

  constructor(config: TranslationManagerConfig) {
    this.config = config;
    this.qualityManager = new TranslationQualityManager(config);
  }

  /**
   * Âä†ËΩΩÁøªËØëÊñá‰ª∂
   */
  private async loadTranslations(): Promise<void> {
    const fs = await import('fs');
    const path = await import('path');

    for (const locale of this.config.locales) {
      const filePath = path.join(this.config.messagesDir, `${locale}.json`);

      try {
        const content = await fs.promises.readFile(filePath, 'utf-8');
        const translations = JSON.parse(content) as Record<string, unknown>;
        TranslationManagerSecurity.setTranslationsForLocale(
          this.translations,
          locale,
          translations,
        );
      } catch (error) {
        logger.warn(`Failed to load translations for ${locale}`, {
          error: error as Error,
        });
        TranslationManagerSecurity.setTranslationsForLocale(
          this.translations,
          locale,
          {},
        );
      }
    }
  }

  /**
   * ÂàùÂßãÂåñLingo.devÈõÜÊàê
   */
  private initializeLingoIntegration(): void {
    if (!this.config.lingo.apiKey || !this.config.lingo.projectId) {
      logger.warn(
        'Lingo.dev integration enabled but missing API key or project ID',
      );
    } else {
      logger.info('Lingo.dev integration initialized');
    }
  }

  /**
   * ÂàùÂßãÂåñÁøªËØëÁÆ°ÁêÜÂô®
   */
  async initialize(): Promise<void> {
    try {
      await this.loadTranslations();

      if (this.config.lingo.enabled) {
        this.initializeLingoIntegration();
      }

      logger.info('Translation manager initialized successfully');
    } catch (error) {
      logger.error('Failed to initialize translation manager', {
        error: error as Error,
      });
      throw error;
    }
  }

  /**
   * È™åËØÅÁøªËØë
   */
  async validateTranslations(): Promise<ValidationReport> {
    return this.qualityManager.validateTranslations(this.translations);
  }

  /**
   * Ê£ÄÊü•LingoÁøªËØëË¥®Èáè
   */
  async checkLingoTranslation(
    _key: string,
    _aiTranslation: string,
    _humanTranslation?: string,
  ): Promise<QualityScore> {
    // ÂßîÊâòÁªôË¥®ÈáèÁÆ°ÁêÜÂô®
    // ÁÆÄÂçïÁöÑË¥®ÈáèÊ£ÄÊü•ÂÆûÁé∞
    return {
      score: MAGIC_85,
      confidence: MAGIC_0_8,
      issues: [],
      suggestions: [],
    };
  }

  /**
   * È™åËØÅÁøªËØë‰∏ÄËá¥ÊÄß
   */
  async validateTranslationConsistency(
    _translations: Record<string, string>,
  ): Promise<ValidationReport> {
    // ÂßîÊâòÁªôË¥®ÈáèÁÆ°ÁêÜÂô®
    return this.qualityManager.validateTranslations(_translations);
  }

  /**
   * ÁîüÊàêË¥®ÈáèÊä•Âëä
   */
  async generateQualityReport(): Promise<QualityReport> {
    return this.qualityManager.generateQualityReport(this.translations);
  }

  /**
   * Ëé∑ÂèñÊåáÂÆöËØ≠Ë®ÄÁöÑÁøªËØëÊï∞ÊçÆ
   */
  getTranslations(locale: Locale): Record<string, unknown> {
    return TranslationManagerSecurity.getTranslationsForLocale(
      this.translations,
      locale,
    );
  }

  /**
   * ËÆæÁΩÆÊåáÂÆöËØ≠Ë®ÄÁöÑÁøªËØëÊï∞ÊçÆ
   */
  setTranslations(locale: Locale, translations: Record<string, unknown>): void {
    TranslationManagerSecurity.setTranslationsForLocale(
      this.translations,
      locale,
      translations,
    );
  }

  /**
   * Ëé∑ÂèñÊâÄÊúâÁøªËØëÊï∞ÊçÆ
   */
  getAllTranslations(): Partial<Record<Locale, Record<string, unknown>>> {
    return { ...this.translations };
  }

  /**
   * ÈáçÊñ∞Âä†ËΩΩÁøªËØëÊñá‰ª∂
   */
  async reload(): Promise<void> {
    this.translations = {};
    await this.loadTranslations();
  }

  /**
   * Ëé∑ÂèñÈÖçÁΩÆ‰ø°ÊÅØ
   */
  getConfig(): TranslationManagerConfig {
    return { ...this.config };
  }

  /**
   * Êõ¥Êñ∞ÈÖçÁΩÆ‰ø°ÊÅØ
   */
  updateConfig(newConfig: Partial<TranslationManagerConfig>): void {
    this.config = { ...this.config, ...newConfig };
    this.qualityManager = new TranslationQualityManager(this.config);
  }

  /**
   * Ê£ÄÊü•ÁøªËØëÈîÆÊòØÂê¶Â≠òÂú®
   */
  hasTranslation(locale: Locale, key: string): boolean {
    if (!TranslationManagerSecurity.isValidTranslationKey(key)) {
      return false;
    }

    const translations = this.getTranslations(locale);
    return (
      TranslationManagerSecurity.getNestedValue(translations, key) !== undefined
    );
  }

  /**
   * Ëé∑ÂèñÁøªËØëÂÄº
   */
  getTranslation(locale: Locale, key: string): unknown {
    if (!TranslationManagerSecurity.isValidTranslationKey(key)) {
      return undefined;
    }

    const translations = this.getTranslations(locale);
    return TranslationManagerSecurity.getNestedValue(translations, key);
  }

  /**
   * ËÆæÁΩÆÁøªËØëÂÄº
   */
  setTranslation(locale: Locale, key: string, value: unknown): void {
    if (!TranslationManagerSecurity.isValidTranslationKey(key)) {
      logger.warn(`Invalid translation key: ${key}`);
      return;
    }

    const translations = this.getTranslations(locale);
    const updatedTranslations = TranslationManagerSecurity.mergeTranslations(
      translations,
      {
        [key]: value,
      },
    );

    this.setTranslations(locale, updatedTranslations);
  }

  /**
   * Âà†Èô§ÁøªËØëÈîÆ
   */
  deleteTranslation(locale: Locale, key: string): void {
    if (!TranslationManagerSecurity.isValidTranslationKey(key)) {
      logger.warn(`Invalid translation key: ${key}`);
      return;
    }

    const translations = this.getTranslations(locale);
    const updatedTranslations = { ...translations };
    delete updatedTranslations[key];

    this.setTranslations(locale, updatedTranslations);
  }

  /**
   * Ëé∑ÂèñÁøªËØëÁªüËÆ°‰ø°ÊÅØ
   */
  getStatistics(): Record<
    Locale,
    { total: number; translated: number; missing: number }
  > {
    const stats: Record<
      Locale,
      { total: number; translated: number; missing: number }
    > = {} as Record<
      Locale,
      { total: number; translated: number; missing: number }
    >;

    for (const locale of this.config.locales) {
      const translations = this.getTranslations(locale);
      const keys = Object.keys(translations);
      const translatedKeys = keys.filter((key) => {
        const value = TranslationManagerSecurity.getNestedValue(
          translations,
          key,
        );
        return value !== undefined && value !== null && value !== '';
      });

      stats[locale] = {
        total: keys.length,
        translated: translatedKeys.length,
        missing: keys.length - translatedKeys.length,
      };
    }

    return stats;
  }
}

import { MAGIC_0_8 } from "@/constants/decimal";
import { QUALITY_SCORING } from '@/constants/i18n-constants';
import { COUNT_FIVE, COUNT_TRIPLE, PERCENTAGE_FULL, PERCENTAGE_HALF, ZERO } from "@/constants/magic-numbers";
import { TranslationManagerSecurity } from '@/lib/translation-manager-security';
import { TranslationQualityChecker } from '@/lib/translation-quality-checker';
import type { Locale } from '@/types/i18n';
import type {
  LocaleQualityReport,
  QualityIssue,
  QualityReport,
  QualityScore,
  QualityTrend,
  TranslationManagerConfig,
  ValidationReport,
} from '@/types/translation-manager';
import {
  calculateConfidence,
  flattenTranslations,
  generateRecommendations,
  generateSuggestions,
  isEmptyTranslation,
} from '@/lib/translation-utils';

/**
 * ÁøªËØëË¥®ÈáèÁÆ°ÁêÜÂô®
 * Ë¥üË¥£ÁøªËØëË¥®ÈáèÊ£ÄÊü•„ÄÅËØÑÂàÜÂíåÊä•ÂëäÁîüÊàê
 */
export class TranslationQualityManager {
  private config: TranslationManagerConfig;
  private qualityChecker: TranslationQualityChecker;

  constructor(config: TranslationManagerConfig) {
    this.config = config;
    this.qualityChecker = new TranslationQualityChecker(config, {});
  }

  /**
   * È™åËØÅÁøªËØë
   */
  async validateTranslations(
    translations: Partial<Record<Locale, Record<string, unknown>>>,
  ): Promise<ValidationReport> {
    const issues: QualityIssue[] = [];
    const byLocale: Record<Locale, LocaleQualityReport> = {} as Record<
      Locale,
      LocaleQualityReport
    >;

    for (const locale of this.config.locales) {
      const localeTranslations =
        TranslationManagerSecurity.getTranslationsForLocale(
          translations,
          locale,
        );
      const flatTranslations = flattenTranslations(localeTranslations);

      // Ê£ÄÊü•Áº∫Â§±ÁöÑÁøªËØë
      const missingKeys = this.findMissingTranslations(
        flatTranslations,
        locale,
      );
      issues.push(...missingKeys);

      // Ê£ÄÊü•Á©∫ÁøªËØë
      const emptyKeys = this.findEmptyTranslations(flatTranslations, locale);
      issues.push(...emptyKeys);

      // Ë¥®ÈáèÊ£ÄÊü• - ÂØπÊØè‰∏™ÁøªËØëÈîÆËøõË°åÊ£ÄÊü•
      const qualityIssues: QualityIssue[] = [];
      for (const [key, translation] of Object.entries(flatTranslations)) {
        if (translation && typeof translation === 'string') {
          const qualityResult = await this.qualityChecker.checkLingoTranslation(
            key,
            translation,
          );
          qualityIssues.push(...qualityResult.issues);
        }
      }
      issues.push(...qualityIssues);

      // ÁîüÊàêÊú¨Âú∞ÂåñÊä•Âëä
      const qualityScore = this.calculateQualityScore(
        flatTranslations,
        qualityIssues,
      );
      const totalKeys = Object.keys(flatTranslations).length;
      const translatedKeysCount = Object.keys(flatTranslations).filter(
        (key) =>
          !isEmptyTranslation(
            TranslationManagerSecurity.getTranslationValue(
              flatTranslations,
              key,
            ) || '',
          ),
      ).length;

      const localeReport: LocaleQualityReport = {
        locale,
        totalKeys,
        validKeys: translatedKeysCount,
        translatedKeys: translatedKeysCount,
        missingKeys: missingKeys.length,
        emptyKeys: emptyKeys.length,
        issues: qualityIssues,
        score: qualityScore.score,
        timestamp: new Date().toISOString(),
        confidence: qualityScore.confidence,
        suggestions: generateSuggestions(qualityIssues),
      };

      TranslationManagerSecurity.setQualityScoreForLocale(
        byLocale,
        locale,
        qualityScore,
      );
    }

    const overallScore =
      issues.length === ZERO ? PERCENTAGE_FULL : Math.max(ZERO, PERCENTAGE_FULL - issues.length * COUNT_FIVE);

    return {
      isValid: issues.length === ZERO,
      score: overallScore,
      issues,
      recommendations: generateRecommendations(issues),
      timestamp: new Date().toISOString(),
      byLocale,
      summary: {
        totalIssues: issues.length,
        criticalIssues: issues.filter((issue) => issue.severity === 'critical')
          .length,
        warningIssues: issues.filter((issue) => issue.severity === 'warning')
          .length,
        infoIssues: issues.filter((issue) => issue.severity === 'info').length,
      },
    };
  }

  /**
   * ÁîüÊàêË¥®ÈáèÊä•Âëä
   */
  async generateQualityReport(
    translations: Partial<Record<Locale, Record<string, unknown>>>,
  ): Promise<QualityReport> {
    const validation = await this.validateTranslations(translations);
    const trends = await this.getQualityTrends();

    // ËÆ°ÁÆóÊï¥‰ΩìË¥®ÈáèÂàÜÊï∞
    const overall: QualityScore = {
      score: validation.score,
      confidence: MAGIC_0_8,
      issues: validation.issues,
      suggestions: generateSuggestions(validation.issues),
    };

    // ‰ªéÈ™åËØÅÊä•Âëä‰∏≠ÊèêÂèñbyLocaleÊï∞ÊçÆ
    const byLocale = {} as Record<Locale, QualityScore>;
    Object.entries(validation.byLocale).forEach(([locale, report]) => {
      byLocale[locale as Locale] = {
        score: report.score,
        confidence: report.confidence,
        issues: report.issues,
        suggestions: report.suggestions,
      };
    });

    return {
      overall,
      byLocale,
      timestamp: new Date().toISOString(),
      validation,
      trends,
      recommendations: generateRecommendations(validation.issues),
      suggestions: generateSuggestions(validation.issues),
    };
  }

  /**
   * Êü•ÊâæÁº∫Â§±ÁöÑÁøªËØë
   */
  private findMissingTranslations(
    flatTranslations: Record<string, string>,
    locale: Locale,
  ): QualityIssue[] {
    const issues: QualityIssue[] = [];
    const requiredKeys = this.config.requiredKeys || [];

    for (const key of requiredKeys) {
      if (!TranslationManagerSecurity.isValidTranslationKey(key)) {
        continue;
      }

      const value = TranslationManagerSecurity.getTranslationValue(
        flatTranslations,
        key,
      );
      if (!value) {
        issues.push({
          type: 'missing',
          severity: 'critical',
          locale,
          key,
          message: `Missing translation for key: ${key}`,
          suggestion: `Add translation for key "${key}" in ${locale} locale`,
        });
      }
    }

    return issues;
  }

  /**
   * Êü•ÊâæÁ©∫ÁøªËØë
   */
  private findEmptyTranslations(
    flatTranslations: Record<string, string>,
    locale: Locale,
  ): QualityIssue[] {
    const issues: QualityIssue[] = [];

    for (const key in flatTranslations) {
      if (!Object.prototype.hasOwnProperty.call(flatTranslations, key)) {
        continue;
      }

      if (!TranslationManagerSecurity.isValidTranslationKey(key)) {
        continue;
      }

      const value = TranslationManagerSecurity.getTranslationValue(
        flatTranslations,
        key,
      );
      if (value && isEmptyTranslation(value)) {
        issues.push({
          type: 'missing',
          severity: 'medium',
          locale,
          key,
          message: `Empty translation for key: ${key}`,
          suggestion: `Provide content for key "${key}" in ${locale} locale`,
        });
      }
    }

    return issues;
  }

  /**
   * ËÆ°ÁÆóË¥®ÈáèËØÑÂàÜ
   */
  private calculateQualityScore(
    flatTranslations: Record<string, string>,
    qualityIssues: QualityIssue[],
  ): QualityScore {
    const totalKeys = Object.keys(flatTranslations).length;
    const translatedKeys = Object.keys(flatTranslations).filter((key) => {
      const value = TranslationManagerSecurity.getTranslationValue(
        flatTranslations,
        key,
      );
      return value && !isEmptyTranslation(value);
    }).length;

    const completeness = totalKeys > ZERO ? (translatedKeys / totalKeys) * PERCENTAGE_FULL : ZERO;
    const accuracy = this.calculateAccuracy(qualityIssues);
    const consistency = this.calculateConsistency(flatTranslations);

    const overall = (completeness + accuracy + consistency) / COUNT_TRIPLE;

    return {
      score: Math.round(overall * PERCENTAGE_FULL) / PERCENTAGE_FULL,
      confidence: calculateConfidence(qualityIssues),
      issues: qualityIssues,
      suggestions: generateSuggestions(qualityIssues),
      grammar: Math.round(accuracy * PERCENTAGE_FULL) / PERCENTAGE_FULL,
      consistency: Math.round(consistency * PERCENTAGE_FULL) / PERCENTAGE_FULL,
    };
  }

  /**
   * ËÆ°ÁÆóÂáÜÁ°ÆÊÄßËØÑÂàÜ
   */
  private calculateAccuracy(qualityIssues: QualityIssue[]): number {
    const criticalIssues = qualityIssues.filter(
      (issue) => issue.severity === 'critical',
    ).length;
    const warningIssues = qualityIssues.filter(
      (issue) => issue.severity === 'warning',
    ).length;

    // Âü∫Á°ÄÂàÜÊï∞
    let score = PERCENTAGE_FULL;

    // Êâ£ÂàÜËßÑÂàô
    score -= criticalIssues * QUALITY_SCORING.CRITICAL_PENALTY;
    score -= warningIssues * QUALITY_SCORING.WARNING_PENALTY;

    return Math.max(ZERO, score);
  }

  /**
   * ËÆ°ÁÆó‰∏ÄËá¥ÊÄßËØÑÂàÜ
   */
  private calculateConsistency(
    flatTranslations: Record<string, string>,
  ): number {
    // ÁÆÄÂåñÁöÑ‰∏ÄËá¥ÊÄßÊ£ÄÊü•
    // ÂÆûÈôÖÂÆûÁé∞ÂèØ‰ª•ÂåÖÊã¨ÊúØËØ≠‰∏ÄËá¥ÊÄß„ÄÅÊ†ºÂºè‰∏ÄËá¥ÊÄßÁ≠â
    const values = Object.values(flatTranslations);
    const uniqueValues = new Set(values);

    // Â¶ÇÊûúÊúâÈáçÂ§çÁöÑÁøªËØëÂÄºÔºåÂèØËÉΩÂ≠òÂú®‰∏ÄËá¥ÊÄßÈóÆÈ¢ò
    const duplicateRatio = (values.length - uniqueValues.size) / values.length;

    return Math.max(ZERO, PERCENTAGE_FULL - duplicateRatio * PERCENTAGE_HALF);
  }

  /**
   * Ëé∑ÂèñË¥®ÈáèË∂ãÂäø
   */
  private async getQualityTrends(): Promise<QualityTrend[]> {
    // ËøôÈáåÂèØ‰ª•ÂÆûÁé∞Ë¥®ÈáèË∂ãÂäøÂàÜÊûê
    // ‰æãÂ¶Ç‰ªéÂéÜÂè≤Êï∞ÊçÆ‰∏≠Ëé∑ÂèñË¥®ÈáèÂèòÂåñË∂ãÂäø
    return Promise.resolve([]);
  }
}

import { HTTP_OK, ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';
import type { Locale } from '@/types/i18n';
import type { QualityScore } from '@/types/translation-manager';

/**
 * ÁøªËØëÁÆ°ÁêÜÂô®ÂÆâÂÖ®Â∑•ÂÖ∑Á±ª
 * Êèê‰æõÂÆâÂÖ®ÁöÑÊï∞ÊçÆËÆøÈóÆÂíåËÆæÁΩÆÊñπÊ≥ïÔºåÈò≤Ê≠¢ Object Injection Sink
 */
export class TranslationManagerSecurity {
  /**
   * ÂÆâÂÖ®Âú∞Ëé∑ÂèñÊåáÂÆöËØ≠Ë®ÄÁöÑÁøªËØëÊï∞ÊçÆ
   * ‰ΩøÁî®Á±ªÂûãÂÆâÂÖ®ÁöÑÊñπÂºèÈÅøÂÖç Object Injection Sink
   */
  static getTranslationsForLocale(
    translations: Partial<Record<Locale, Record<string, unknown>>>,
    locale: Locale,
  ): Record<string, unknown> {
    // ‰ΩøÁî® switch ËØ≠Âè•Êõø‰ª£Âä®ÊÄÅÂ±ûÊÄßËÆøÈóÆ
    switch (locale) {
      case 'en':
        return translations.en || {};
      case 'zh':
        return translations.zh || {};
      default:
        return {};
    }
  }

  /**
   * ÂÆâÂÖ®Âú∞ËÆæÁΩÆÊåáÂÆöËØ≠Ë®ÄÁöÑÁøªËØëÊï∞ÊçÆ
   */
  static setTranslationsForLocale(
    translations: Partial<Record<Locale, Record<string, unknown>>>,
    locale: Locale,
    data: Record<string, unknown>,
  ): void {
    // ‰ΩøÁî® switch ËØ≠Âè•Êõø‰ª£Âä®ÊÄÅÂ±ûÊÄßËÆøÈóÆ
    switch (locale) {
      case 'en':
        translations.en = data;
        break;
      case 'zh':
        translations.zh = data;
        break;
      default:
        // ÂøΩÁï•‰∏çÊîØÊåÅÁöÑËØ≠Ë®Ä
        break;
    }
  }

  /**
   * ÂÆâÂÖ®Âú∞ËÆæÁΩÆË¥®ÈáèËØÑÂàÜÊï∞ÊçÆ
   */
  static setQualityScoreForLocale(
    byLocale: Record<Locale, QualityScore>,
    locale: Locale,
    score: QualityScore,
  ): void {
    // ‰ΩøÁî® switch ËØ≠Âè•Êõø‰ª£Âä®ÊÄÅÂ±ûÊÄßËÆøÈóÆ
    switch (locale) {
      case 'en':
        byLocale.en = score;
        break;
      case 'zh':
        byLocale.zh = score;
        break;
      default:
        // ÂøΩÁï•‰∏çÊîØÊåÅÁöÑËØ≠Ë®Ä
        break;
    }
  }

  /**
   * ÂÆâÂÖ®Âú∞ËÆæÁΩÆÁøªËØëÁªìÊûú
   * È™åËØÅÈîÆÂêç‰ª•Èò≤Ê≠¢ Object Injection Sink
   */
  static setTranslationResult(
    result: Record<string, string>,
    key: string,
    translation: string,
  ): void {
    // È™åËØÅÈîÆÂêçÊ†ºÂºèÔºåÂè™ÂÖÅËÆ∏Â≠óÊØç„ÄÅÊï∞Â≠ó„ÄÅÁÇπ„ÄÅ‰∏ãÂàíÁ∫øÂíåËøûÂ≠óÁ¨¶
    if (!/^[a-zA-Z0-9._-]+$/.test(key)) {
      logger.warn(`Invalid translation key format: ${key}`);
      return;
    }

    // È™åËØÅÈîÆÂêçÈïøÂ∫¶
    if (key.length > HTTP_OK) {
      logger.warn(`Translation key too long: ${key}`);
      return;
    }

    // ÂÆâÂÖ®Âú∞ËÆæÁΩÆÂÄº
    result[key] = translation;
  }

  /**
   * ÂÆâÂÖ®Âú∞Ëé∑ÂèñÊâÅÂπ≥ÂåñÁøªËØëÂÄº
   */
  static getTranslationValue(
    flatTranslations: Record<string, string>,
    key: string,
  ): string | undefined {
    // È™åËØÅÈîÆÂêçÊ†ºÂºè
    if (!/^[a-zA-Z0-9._-]+$/.test(key)) {
      logger.warn(`Invalid translation key format: ${key}`);
      return undefined;
    }

    // È™åËØÅÈîÆÂêçÈïøÂ∫¶
    if (key.length > HTTP_OK) {
      logger.warn(`Translation key too long: ${key}`);
      return undefined;
    }

    // ‰ΩøÁî® Object.prototype.hasOwnProperty ÂÆâÂÖ®Ê£ÄÊü•
    if (Object.prototype.hasOwnProperty.call(flatTranslations, key)) {
      return flatTranslations[key];
    }

    return undefined;
  }

  /**
   * È™åËØÅÁøªËØëÈîÆÂêçÊòØÂê¶ÂÆâÂÖ®
   */
  static isValidTranslationKey(key: string): boolean {
    // Ê£ÄÊü•Ê†ºÂºè
    if (!/^[a-zA-Z0-9._-]+$/.test(key)) {
      return false;
    }

    // Ê£ÄÊü•ÈïøÂ∫¶
    if (key.length > HTTP_OK) {
      return false;
    }

    // Ê£ÄÊü•ÊòØÂê¶‰∏∫Á©∫
    if (key.trim().length === ZERO) {
      return false;
    }

    return true;
  }

  /**
   * ÂÆâÂÖ®Âú∞ÂêàÂπ∂ÁøªËØëÂØπË±°
   */
  static mergeTranslations(
    target: Record<string, unknown>,
    source: Record<string, unknown>,
  ): Record<string, unknown> {
    const result = { ...target };

    for (const key in source) {
      if (Object.prototype.hasOwnProperty.call(source, key)) {
        if (this.isValidTranslationKey(key)) {
          result[key] = source[key];
        } else {
          logger.warn(`Skipping invalid translation key: ${key}`);
        }
      }
    }

    return result;
  }

  /**
   * ÂÆâÂÖ®Âú∞Ëé∑ÂèñÂµåÂ•óÂØπË±°ÂÄº
   */
  static getNestedValue(obj: Record<string, unknown>, path: string): unknown {
    const keys = path.split('.');
    let current = obj;

    for (const key of keys) {
      if (!this.isValidTranslationKey(key)) {
        return undefined;
      }

      if (
        current &&
        typeof current === 'object' &&
        Object.prototype.hasOwnProperty.call(current, key)
      ) {
        current = current[key] as Record<string, unknown>;
      } else {
        return undefined;
      }
    }

    return current;
  }
}

import {
  PERFORMANCE_THRESHOLDS,
  QUALITY_WEIGHTS,
  VALIDATION_THRESHOLDS,
} from '@/constants/i18n-constants';
import { ONE, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import type { Locale } from '@/types/i18n';
import '@/types/translation-manager';
import type {
  LocaleQualityReport,
  QualityIssue,
  QualityScore,
  TranslationManagerConfig,
  ValidationReport,
} from '@/types/translation-manager';
import {
  calculateConfidence,
  checkTerminologyConsistency,
  extractPlaceholders,
  flattenTranslations,
  generateRecommendations,
  generateSuggestions,
  getNestedValue,
} from '@/lib/translation-utils';

/**
 * ÁøªËØëË¥®ÈáèÊ£ÄÊü•Âô®
 */
export class TranslationQualityChecker {
  private config: TranslationManagerConfig;
  private translations: Partial<Record<Locale, Record<string, unknown>>> = {};
  private qualityCache: Map<string, QualityScore> = new Map();
  private terminologyMap: Map<string, string> = new Map();

  constructor(
    config: TranslationManagerConfig,
    translations: Partial<Record<Locale, Record<string, unknown>>>,
  ) {
    this.config = config;
    this.translations = translations;
  }

  /**
   * Ê£ÄÊü•Lingo.devÁøªËØëË¥®Èáè
   */
  async checkLingoTranslation(
    key: string,
    aiTranslation: string,
    humanTranslation?: string,
  ): Promise<QualityScore> {
    const cacheKey = `${key}:${aiTranslation}`;

    // Ê£ÄÊü•ÁºìÂ≠ò
    if (this.qualityCache.has(cacheKey)) {
      return this.qualityCache.get(cacheKey)!;
    }

    const issues: QualityIssue[] = [];
    let score = PERCENTAGE_FULL;

    // Âü∫Á°ÄË¥®ÈáèÊ£ÄÊü•
    if (aiTranslation.trim().length === ZERO) {
      issues.push({
        type: 'length',
        severity: 'critical',
        message: 'Translation is empty',
        suggestion: 'Provide a non-empty translation',
      });
      score -= VALIDATION_THRESHOLDS.EMPTY_TRANSLATION_PENALTY;
    }

    // Âç†‰ΩçÁ¨¶Ê£ÄÊü•
    const aiPlaceholders = extractPlaceholders(aiTranslation);
    if (humanTranslation) {
      const humanPlaceholders = extractPlaceholders(humanTranslation);

      if (
        JSON.stringify(aiPlaceholders.sort()) !==
        JSON.stringify(humanPlaceholders.sort())
      ) {
        issues.push({
          type: 'placeholder',
          severity: 'high',
          message: 'Placeholder mismatch between AI and human translation',
          suggestion: 'Ensure all placeholders are preserved',
        });
        score -= VALIDATION_THRESHOLDS.PLACEHOLDER_MISMATCH_PENALTY;
      }
    }

    // ÈïøÂ∫¶ÊØî‰æãÊ£ÄÊü•
    if (humanTranslation) {
      const lengthRatio = aiTranslation.length / humanTranslation.length;
      if (
        lengthRatio > VALIDATION_THRESHOLDS.MAX_LENGTH_RATIO ||
        lengthRatio < VALIDATION_THRESHOLDS.MIN_LENGTH_RATIO
      ) {
        issues.push({
          type: 'length',
          severity: 'medium',
          message: `Translation length ratio is unusual: ${lengthRatio.toFixed(QUALITY_WEIGHTS.LENGTH_PENALTY / QUALITY_WEIGHTS.LENGTH_PENALTY)}`,
          suggestion: 'Review translation for completeness and accuracy',
        });
        score -= QUALITY_WEIGHTS.GRAMMAR_PENALTY;
      }
    }

    // ÊúØËØ≠‰∏ÄËá¥ÊÄßÊ£ÄÊü•
    const terminologyIssues = await checkTerminologyConsistency(
      key,
      aiTranslation,
      this.terminologyMap,
    );
    issues.push(...terminologyIssues);
    score -= terminologyIssues.length * QUALITY_WEIGHTS.LENGTH_PENALTY;

    const qualityScore: QualityScore = {
      score: Math.max(ZERO, score),
      confidence: calculateConfidence(issues),
      issues,
      suggestions: generateSuggestions(issues),
    };

    // ÁºìÂ≠òÁªìÊûú
    this.qualityCache.set(cacheKey, qualityScore);

    return qualityScore;
  }

  /**
   * È™åËØÅÁøªËØë‰∏ÄËá¥ÊÄß
   */
  validateTranslationConsistency(
    translations: Record<string, string>,
  ): Promise<ValidationReport> {
    const issues: QualityIssue[] = [];
    let totalScore = ZERO;
    let validTranslations = ZERO;

    for (const [key, translation] of Object.entries(translations)) {
      if (!translation || translation.trim().length === ZERO) {
        issues.push({
          type: 'consistency',
          severity: 'high',
          message: `Missing translation for key: ${key}`,
        });
        continue;
      }

      // Ê£ÄÊü•Âç†‰ΩçÁ¨¶‰∏ÄËá¥ÊÄß
      const placeholders = extractPlaceholders(translation);
      const expectedPlaceholders = this.getExpectedPlaceholders(key);

      if (
        JSON.stringify(placeholders.sort()) !==
        JSON.stringify(expectedPlaceholders.sort())
      ) {
        issues.push({
          type: 'placeholder',
          severity: 'medium',
          message: `Placeholder inconsistency in key: ${key}`,
        });
      }

      validTranslations += ONE;
      totalScore += PERFORMANCE_THRESHOLDS.MAX_MEMORY_USAGE; // Âü∫Á°ÄÂàÜÊï∞
    }

    const averageScore =
      validTranslations > ZERO ? totalScore / validTranslations : ZERO;
    const finalScore = Math.max(
      ZERO,
      averageScore - issues.length * QUALITY_WEIGHTS.LENGTH_PENALTY,
    );

    return Promise.resolve({
      isValid:
        issues.filter(
          (issue) => issue.severity === 'critical' || issue.severity === 'high',
        ).length === ZERO,
      score: finalScore,
      issues,
      recommendations: generateRecommendations(issues),
      timestamp: new Date().toISOString(),
      byLocale: {} as Record<Locale, LocaleQualityReport>,
      summary: {
        totalIssues: issues.length,
        criticalIssues: issues.filter((issue) => issue.severity === 'critical')
          .length,
        warningIssues: issues.filter((issue) => issue.severity === 'warning')
          .length,
        infoIssues: issues.filter((issue) => issue.severity === 'info').length,
      },
    });
  }

  /**
   * Ëé∑ÂèñÈ¢ÑÊúüÂç†‰ΩçÁ¨¶
   */
  private getExpectedPlaceholders(key: string): string[] {
    // ‰ªéÈªòËÆ§ËØ≠Ë®ÄËé∑ÂèñÈ¢ÑÊúüÂç†‰ΩçÁ¨¶
    const defaultTranslation = getNestedValue(
      this.translations[this.config.defaultLocale] || {},
      key,
    );

    if (!defaultTranslation) {
      return [];
    }

    return extractPlaceholders(defaultTranslation);
  }

  /**
   * Ëé∑ÂèñÁøªËØëÈîÆÊÄªÊï∞
   */
  getTotalTranslationKeys(): number {
    const defaultTranslations =
      this.translations[this.config.defaultLocale] || {};
    return Object.keys(flattenTranslations(defaultTranslations)).length;
  }

  /**
   * ËÆæÁΩÆÊúØËØ≠Êò†Â∞Ñ
   */
  setTerminologyMap(terminologyMap: Map<string, string>): void {
    this.terminologyMap = terminologyMap;
  }

  /**
   * Ê∏ÖÈô§Ë¥®ÈáèÁºìÂ≠ò
   */
  clearQualityCache(): void {
    this.qualityCache.clear();
  }

  /**
   * Ëé∑ÂèñÁºìÂ≠òÁªüËÆ°
   */
  getCacheStats(): { size: number; hitRate: number } {
    return {
      size: this.qualityCache.size,
      hitRate: ZERO, // ÈúÄË¶ÅÂÆûÁé∞ÂëΩ‰∏≠ÁéáÁªüËÆ°
    };
  }
}

/**
 * ÁøªËØëË¥®ÈáèÊ£ÄÊü•ÂíåAIÁøªËØëÈ™åËØÅÂ∑•ÂÖ∑
 * Êèê‰æõÁøªËØëË¥®ÈáèËØÑ‰º∞„ÄÅAIÁøªËØëÈ™åËØÅÂíåË¥®ÈáèÂü∫ÂáÜÂØπÊØîÂäüËÉΩ
 */
import { MAGIC_12, MAGIC_15, MAGIC_8 } from "@/constants/count";
import { DEC_0_05, MAGIC_0_1, MAGIC_0_2, MAGIC_0_8 } from "@/constants/decimal";
import { COUNT_FIVE, ONE, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { TranslationBenchmarks } from '@/lib/translation-benchmarks';
import { TranslationManager } from '@/lib/translation-manager';
import { TranslationValidators } from '@/lib/translation-validators';
import type { Locale } from '@/types/i18n';
import type {
  BatchTranslationInput,
  QualityComparison,
  QualityIssue,
  QualityScore,
  TranslationManagerConfig,
  ValidationReport,
} from '@/types/translation-manager';

// ÂØºÂÖ•ÊãÜÂàÜÁöÑÊ®°Âùó
export type {
  AITranslationService, BatchTranslationInput, QualityBenchmark,
  QualityComparison,
  ValidationResult
} from '@/lib/translation-quality-types';

/**
 * ÁøªËØëË¥®ÈáèÂàÜÊûêÂô®
 */
export class TranslationQualityAnalyzer {
  private translationManager: TranslationManager;
  private benchmarks: TranslationBenchmarks;

  constructor(config: TranslationManagerConfig) {
    this.translationManager = new TranslationManager(config);
    this.benchmarks = new TranslationBenchmarks();
  }

  /**
   * ÂàùÂßãÂåñË¥®ÈáèÂàÜÊûêÂô®
   */
  async initialize(): Promise<void> {
    await this.translationManager.initialize();
    this.benchmarks.initialize();
  }

  /**
   * È™åËØÅAIÁøªËØëË¥®Èáè
   */
  async validateAITranslation(
    key: string,
    originalText: string,
    aiTranslation: string,
    targetLocale: Locale,
    humanTranslation?: string,
  ): Promise<QualityScore> {
    const issues: QualityIssue[] = [];
    let totalPenalty = ZERO;

    // Âü∫Á°ÄÈ™åËØÅ
    const basicResult = TranslationValidators.performBasicValidation(
      originalText,
      aiTranslation,
    );
    issues.push(...basicResult.issues);
    totalPenalty += basicResult.penalty;

    // ËØ≠Ë®ÄÁâπÂÆöÈ™åËØÅ
    const languageResult =
      TranslationValidators.performLanguageSpecificValidation(
        aiTranslation,
        targetLocale,
      );
    issues.push(...languageResult.issues);
    totalPenalty += languageResult.penalty;

    // ‰∏ä‰∏ãÊñá‰∏ÄËá¥ÊÄßÈ™åËØÅ
    const contextResult = await this.validateContextConsistency(
      key,
      aiTranslation,
      targetLocale,
    );
    issues.push(...contextResult.issues);
    totalPenalty += contextResult.penalty;

    // ‰∏é‰∫∫Â∑•ÁøªËØëÂØπÊØîÔºàÂ¶ÇÊûúÊèê‰æõÔºâ
    if (humanTranslation) {
      const humanResult = TranslationValidators.compareWithHumanTranslation(
        aiTranslation,
        humanTranslation,
      );
      issues.push(...humanResult.issues);
      totalPenalty += humanResult.penalty;
    }

    // ËÆ°ÁÆóÊúÄÁªàÂàÜÊï∞
    const baseScore = PERCENTAGE_FULL;
    const finalScore = Math.max(ZERO, baseScore - totalPenalty);

    const confidence = this.calculateConfidence(
      issues,
      Boolean(humanTranslation),
    );
    const suggestions = this.generateImprovementSuggestions(issues);

    return {
      score: finalScore,
      confidence,
      issues,
      suggestions,
      // ÂàÜÁ±ªÂàÜÊï∞
      grammar: Math.max(
        ZERO,
        PERCENTAGE_FULL - issues.filter((i) => i.type === 'grammar').length * 10,
      ),
      consistency: Math.max(
        ZERO,
        PERCENTAGE_FULL - issues.filter((i) => i.type === 'consistency').length * MAGIC_15,
      ),
      terminology: Math.max(
        ZERO,
        PERCENTAGE_FULL - issues.filter((i) => i.type === 'terminology').length * MAGIC_12,
      ),
      fluency: Math.max(
        ZERO,
        PERCENTAGE_FULL - issues.filter((i) => i.type === 'fluency').length * MAGIC_8,
      ),
    };
  }

  /**
   * ÊâπÈáèÈ™åËØÅÁøªËØëË¥®Èáè
   */
  async batchValidateTranslations(
    translations: BatchTranslationInput[],
  ): Promise<QualityScore[]> {
    const results: QualityScore[] = [];

    for (const translation of translations) {
      const result = await this.validateAITranslation(
        translation.key,
        translation.original,
        translation.translated,
        translation.locale,
        translation.humanReference,
      );
      results.push(result);
    }

    return results;
  }

  /**
   * ‰∏éË¥®ÈáèÂü∫ÂáÜÂØπÊØî
   */
  compareWithBenchmark(
    currentScore: QualityScore,
    locale: Locale,
  ): QualityComparison {
    const comparison = this.benchmarks.compareWithBenchmark(
      currentScore,
      locale,
    );
    // Á°Æ‰øùÂåÖÂê´ÊâÄÊúâÂøÖÈúÄÂ±ûÊÄß
    return {
      baseline: (comparison as any)?.baseline || {
        score: ZERO,
        confidence: ZERO,
        issues: [],
        suggestions: [],
      },
      current: (comparison as any)?.current || currentScore,
      improvement: (comparison as any)?.improvement || ZERO,
      degradation: (comparison as any)?.degradation || ZERO,
      recommendations: (comparison as any)?.recommendations || [],
    };
  }

  /**
   * ÁîüÊàêË¥®ÈáèÊä•Âëä
   */
  async generateQualityReport(_locale?: Locale): Promise<ValidationReport> {
    return this.translationManager.validateTranslations();
  }

  /**
   * È™åËØÅ‰∏ä‰∏ãÊñá‰∏ÄËá¥ÊÄß
   */
  private async validateContextConsistency(
    key: string,
    translation: string,
    locale: Locale,
  ): Promise<{ issues: QualityIssue[]; penalty: number }> {
    const issues: QualityIssue[] = [];
    let penalty = ZERO;

    // Ê£ÄÊü•ÊúØËØ≠‰∏ÄËá¥ÊÄß
    const terminologyIssues = this.checkTerminologyConsistency(
      key,
      translation,
      locale,
    );
    issues.push(...terminologyIssues);
    penalty += terminologyIssues.length * COUNT_FIVE;

    // Ê£ÄÊü•‰∏ä‰∏ãÊñáÁõ∏ÂÖ≥ÊÄß
    const contextIssues = TranslationValidators.checkContextRelevance(
      key,
      translation,
    );
    issues.push(...contextIssues);
    penalty += contextIssues.length * MAGIC_8;

    return { issues, penalty };
  }

  /**
   * Ê£ÄÊü•ÊúØËØ≠‰∏ÄËá¥ÊÄß
   */
  private checkTerminologyConsistency(
    _key: string,
    _translation: string,
    _locale: Locale,
  ): QualityIssue[] {
    // ËøôÈáåÂèØ‰ª•ÂÆûÁé∞ÊúØËØ≠ËØçÂÖ∏Ê£ÄÊü•
    return [];
  }

  /**
   * ËÆ°ÁÆóÁΩÆ‰ø°Â∫¶
   */
  private calculateConfidence(
    issues: QualityIssue[],
    hasHumanReference: boolean,
  ): number {
    let confidence = MAGIC_0_8; // Âü∫Á°ÄÁΩÆ‰ø°Â∫¶

    if (hasHumanReference) {
      confidence += MAGIC_0_2; // Êúâ‰∫∫Â∑•ÂèÇËÄÉÊèêÈ´òÁΩÆ‰ø°Â∫¶
    }

    // Ê†πÊçÆÈóÆÈ¢ò‰∏•ÈáçÁ®ãÂ∫¶Ë∞ÉÊï¥ÁΩÆ‰ø°Â∫¶
    const highSeverityIssues = issues.filter(
      (i) => i.severity === 'high',
    ).length;
    const mediumSeverityIssues = issues.filter(
      (i) => i.severity === 'medium',
    ).length;

    confidence -= highSeverityIssues * MAGIC_0_1;
    confidence -= mediumSeverityIssues * DEC_0_05;

    return Math.max(MAGIC_0_1, Math.min(ONE, confidence));
  }

  /**
   * ÁîüÊàêÊîπËøõÂª∫ËÆÆ
   */
  private generateImprovementSuggestions(issues: QualityIssue[]): string[] {
    const suggestions = new Set<string>();

    issues.forEach((issue) => {
      if (issue.suggestion) {
        suggestions.add(issue.suggestion);
      }
    });

    return Array.from(suggestions);
  }
}

/**
 * ÁøªËØëÈ™åËØÅÂô®Â∑•ÂÖ∑Á±ª
 */
import { MAGIC_15, MAGIC_8 } from "@/constants/count";
import { MAGIC_0_3, MAGIC_0_6 } from "@/constants/decimal";
import { VALIDATION_THRESHOLDS } from '@/constants/i18n-constants';
import { COUNT_FIVE, COUNT_TEN, COUNT_TRIPLE, ONE, PERCENTAGE_QUARTER, ZERO } from "@/constants/magic-numbers";
import type { ValidationResult } from '@/lib/translation-quality-types';
import type { Locale } from '@/types/i18n';
import type { QualityIssue } from '@/types/translation-manager';

export class TranslationValidators {
  /**
   * Âü∫Á°ÄÈ™åËØÅ
   */
  static performBasicValidation(
    original: string,
    translation: string,
  ): ValidationResult {
    const issues: QualityIssue[] = [];
    let penalty = ZERO;

    // Ê£ÄÊü•Á©∫ÁøªËØë
    if (!translation.trim()) {
      issues.push({
        type: 'missing',
        severity: 'high',
        message: 'Translation is empty',
        suggestion: 'Provide a translation for this text',
      });
      penalty += VALIDATION_THRESHOLDS.EMPTY_TRANSLATION_PENALTY;
    }

    // Ê£ÄÊü•Âç†‰ΩçÁ¨¶
    const originalPlaceholders = this.extractPlaceholders(original);
    const translatedPlaceholders = this.extractPlaceholders(translation);

    if (originalPlaceholders.length !== translatedPlaceholders.length) {
      issues.push({
        type: 'placeholder',
        severity: 'high',
        message: 'Placeholder count mismatch',
        suggestion: 'Ensure all placeholders are preserved in translation',
      });
      penalty += VALIDATION_THRESHOLDS.PLACEHOLDER_MISMATCH_PENALTY;
    }

    // Ê£ÄÊü•Áº∫Â§±ÁöÑÂç†‰ΩçÁ¨¶
    const missingPlaceholders = originalPlaceholders.filter(
      (ph) => !translatedPlaceholders.includes(ph),
    );
    if (missingPlaceholders.length > ZERO) {
      issues.push({
        type: 'placeholder',
        severity: 'high',
        message: `Missing placeholders: ${missingPlaceholders.join(', ')}`,
        suggestion: 'Add missing placeholders to translation',
      });
      penalty +=
        VALIDATION_THRESHOLDS.LENGTH_DIFFERENCE_THRESHOLD *
        missingPlaceholders.length;
    }

    // Ê£ÄÊü•ÈïøÂ∫¶Â∑ÆÂºÇ
    const lengthRatio = translation.length / original.length;
    if (
      lengthRatio > VALIDATION_THRESHOLDS.MAX_LENGTH_RATIO ||
      lengthRatio < VALIDATION_THRESHOLDS.MIN_LENGTH_RATIO
    ) {
      issues.push({
        type: 'length',
        severity: 'medium',
        message: 'Translation length significantly different from original',
        suggestion: 'Review translation for completeness and conciseness',
      });
      penalty += COUNT_TEN;
    }

    return { issues, penalty };
  }

  /**
   * ËØ≠Ë®ÄÁâπÂÆöÈ™åËØÅ
   */
  static performLanguageSpecificValidation(
    translation: string,
    locale: Locale,
  ): ValidationResult {
    const issues: QualityIssue[] = [];
    let penalty = ZERO;

    switch (locale) {
      case 'zh':
        // ‰∏≠ÊñáÁâπÂÆöÊ£ÄÊü•
        if (this.containsTraditionalChinese(translation)) {
          issues.push({
            type: 'language',
            severity: 'medium',
            message: 'Contains traditional Chinese characters',
            suggestion: 'Use simplified Chinese for zh locale',
          });
          penalty += COUNT_FIVE;
        }
        break;

      case 'en':
        // Ëã±ÊñáÁâπÂÆöÊ£ÄÊü•
        if (this.hasGrammarIssues(translation)) {
          issues.push({
            type: 'grammar',
            severity: 'medium',
            message: 'Potential grammar issues detected',
            suggestion: 'Review grammar and sentence structure',
          });
          penalty += MAGIC_8;
        }
        break;
    }

    return { issues, penalty };
  }

  /**
   * ‰∏é‰∫∫Â∑•ÁøªËØëÂØπÊØî
   */
  static compareWithHumanTranslation(
    aiTranslation: string,
    humanTranslation: string,
  ): ValidationResult {
    const issues: QualityIssue[] = [];
    let penalty = ZERO;

    const similarity = this.calculateSimilarity(
      aiTranslation,
      humanTranslation,
    );

    if (similarity < MAGIC_0_3) {
      issues.push({
        type: 'accuracy',
        severity: 'high',
        message: 'AI translation significantly differs from human reference',
        suggestion: 'Review translation accuracy and meaning preservation',
      });
      penalty += PERCENTAGE_QUARTER;
    } else if (similarity < MAGIC_0_6) {
      issues.push({
        type: 'accuracy',
        severity: 'medium',
        message: 'AI translation moderately differs from human reference',
        suggestion: 'Consider adjusting translation for better accuracy',
      });
      penalty += MAGIC_15;
    }

    // Ê£ÄÊü•ÂÖ≥ÈîÆÊúØËØ≠
    const humanTerms = this.extractKeyTerms(humanTranslation);
    const aiTerms = this.extractKeyTerms(aiTranslation);
    const missingTerms = humanTerms.filter((term) => !aiTerms.includes(term));

    if (missingTerms.length > ZERO) {
      issues.push({
        type: 'terminology',
        severity: 'medium',
        message: `Missing key terms: ${missingTerms.slice(ZERO, COUNT_TRIPLE).join(', ')}`,
        suggestion: 'Include important terminology from reference translation',
      });
      penalty += COUNT_TEN;
    }

    return { issues, penalty };
  }

  /**
   * Ê£ÄÊü•‰∏ä‰∏ãÊñáÁõ∏ÂÖ≥ÊÄß
   */
  static checkContextRelevance(
    key: string,
    translation: string,
  ): QualityIssue[] {
    const issues: QualityIssue[] = [];

    // Ê†πÊçÆÈîÆÂêçÊ£ÄÊü•‰∏ä‰∏ãÊñáÁõ∏ÂÖ≥ÊÄß
    if (key.includes('error') && !this.containsErrorTerms(translation)) {
      issues.push({
        type: 'context',
        severity: 'medium',
        message: 'Error context not reflected in translation',
        suggestion: 'Include error-related terminology',
      });
    }

    return issues;
  }

  // Â∑•ÂÖ∑ÊñπÊ≥ï
  private static extractPlaceholders(text: string): string[] {
    const matches = text.match(/\{[^}]+\}/g) || [];
    return matches.map((match) => match.slice(ONE, -ONE));
  }

  private static containsTraditionalChinese(text: string): boolean {
    // ÁÆÄÂçïÁöÑÁπÅ‰ΩìÂ≠óÊ£ÄÊµã
    const traditionalChars = /[ÁπÅÈ´îÂ≠óÊ™¢Ê∏¨]/;
    return traditionalChars.test(text);
  }

  private static hasGrammarIssues(text: string): boolean {
    // ÁÆÄÂçïÁöÑËØ≠Ê≥ïÊ£ÄÊü•
    const commonIssues = /\b(a a|the the|and and|or or)\b/i;
    return commonIssues.test(text);
  }

  private static containsErrorTerms(text: string): boolean {
    const errorTerms = ['error', 'failed', 'invalid', 'ÈîôËØØ', 'Â§±Ë¥•', 'Êó†Êïà'];
    return errorTerms.some((term) =>
      text.toLowerCase().includes(term.toLowerCase()),
    );
  }

  private static calculateSimilarity(text1: string, text2: string): number {
    const words1 = text1.toLowerCase().split(/\s+/);
    const words2 = text2.toLowerCase().split(/\s+/);

    const intersection = words1.filter((word) => words2.includes(word));
    const union = [...new Set([...words1, ...words2])];

    return intersection.length / union.length;
  }

  private static extractKeyTerms(text: string): string[] {
    return text.split(/\s+/).filter((word) => word.length > COUNT_TRIPLE);
  }
}

/**
 * Âçï‰ΩçËΩ¨Êç¢Â∑•ÂÖ∑Â∫ì
 * 
 * Áî®‰∫éÊõø‰ª£Á°¨ÁºñÁ†ÅÁöÑÊó∂Èó¥„ÄÅÂ∞∫ÂØ∏„ÄÅÁôæÂàÜÊØîÁ≠âÊï∞ÂÄºÂ∏∏Èáè
 * Êèê‰æõËØ≠‰πâÂåñÁöÑÂçï‰ΩçËΩ¨Êç¢ÂáΩÊï∞ÔºåÊèêÂçá‰ª£Á†ÅÂèØËØªÊÄß
 * 
 * @example
 * ```typescript
 * // Êó∂Èó¥Áõ∏ÂÖ≥
 * setTimeout(callback, seconds(5)); // Êõø‰ª£ 5000
 * setInterval(poll, minutes(2));    // Êõø‰ª£ 120000
 * 
 * // ÁôæÂàÜÊØîÁõ∏ÂÖ≥
 * opacity: percent(85);             // Êõø‰ª£ 0.85
 * width: `${percent(50) * 100}%`;   // Êõø‰ª£ 50
 * 
 * // Â∞∫ÂØ∏Áõ∏ÂÖ≥
 * padding: pixels(16);              // Êõø‰ª£ 16
