 * ```
 */

// ============================================================================
// Êó∂Èó¥Âçï‰ΩçËΩ¨Êç¢
// ============================================================================

/**
 * Â∞ÜÁßíËΩ¨Êç¢‰∏∫ÊØ´Áßí
 * @param seconds ÁßíÊï∞
 * @returns ÊØ´ÁßíÊï∞
 * 
 * @example
 * ```typescript
 * setTimeout(callback, seconds(5));     // 5000ms
 * delay(seconds(2.5));                  // 2500ms
 * ```
 */
export const seconds = (seconds: number): number => seconds * 1_000;

/**
 * Â∞ÜÂàÜÈíüËΩ¨Êç¢‰∏∫ÊØ´Áßí
 * @param minutes ÂàÜÈíüÊï∞
 * @returns ÊØ´ÁßíÊï∞
 * 
 * @example
 * ```typescript
 * setInterval(poll, minutes(2));        // 120000ms
 * setTimeout(cleanup, minutes(0.5));    // 30000ms
 * ```
 */
export const minutes = (minutes: number): number => minutes * 60_000;

/**
 * Â∞ÜÂ∞èÊó∂ËΩ¨Êç¢‰∏∫ÊØ´Áßí
 * @param hours Â∞èÊó∂Êï∞
 * @returns ÊØ´ÁßíÊï∞
 * 
 * @example
 * ```typescript
 * const cacheExpiry = hours(24);        // 86400000ms
 * const sessionTimeout = hours(2);      // 7200000ms
 * ```
 */
export const hours = (hours: number): number => hours * 3_600_000;

/**
 * Â∞ÜÂ§©ËΩ¨Êç¢‰∏∫ÊØ´Áßí
 * @param days Â§©Êï∞
 * @returns ÊØ´ÁßíÊï∞
 * 
 * @example
 * ```typescript
 * const weekInMs = days(7);             // 604800000ms
 * const monthInMs = days(30);           // 2592000000ms
 * ```
 */
export const days = (days: number): number => days * 86_400_000;

// ============================================================================
// ÁôæÂàÜÊØîÂíåÊØî‰æãËΩ¨Êç¢
// ============================================================================

/**
 * Â∞ÜÁôæÂàÜÊØîËΩ¨Êç¢‰∏∫Â∞èÊï∞Ôºà0-1‰πãÈó¥Ôºâ
 * @param percentage ÁôæÂàÜÊØîÊï∞ÂÄºÔºà0-100Ôºâ
 * @returns Â∞èÊï∞ÂÄºÔºà0-1Ôºâ
 * 
 * @example
 * ```typescript
 * opacity: percent(85);                 // 0.85
 * transform: `scale(${percent(120)})`;  // 1.2
 * ```
 */
export const percent = (percentage: number): number => percentage / 100;

/**
 * Â∞ÜÂ∞èÊï∞ËΩ¨Êç¢‰∏∫ÁôæÂàÜÊØî
 * @param decimal Â∞èÊï∞ÂÄºÔºà0-1Ôºâ
 * @returns ÁôæÂàÜÊØîÊï∞ÂÄºÔºà0-100Ôºâ
 * 
 * @example
 * ```typescript
 * const progress = toPercent(0.75);     // 75
 * const completion = toPercent(0.95);   // 95
 * ```
 */
export const toPercent = (decimal: number): number => decimal * 100;

/**
 * Â∞ÜÂàÜÊï∞ËΩ¨Êç¢‰∏∫Â∞èÊï∞
 * @param numerator ÂàÜÂ≠ê
 * @param denominator ÂàÜÊØç
 * @returns Â∞èÊï∞ÂÄº
 * 
 * @example
 * ```typescript
 * const ratio = fraction(3, 4);         // 0.75
 * const portion = fraction(1, 3);       // 0.333...
 * ```
 */
export const fraction = (numerator: number, denominator: number): number => 
  numerator / denominator;

// ============================================================================
// Â∞∫ÂØ∏Âçï‰ΩçËΩ¨Êç¢
// ============================================================================

/**
 * ÂÉèÁ¥†ÂÄºÔºàËØ≠‰πâÂåñÊ†áËØÜÔºâ
 * @param px ÂÉèÁ¥†Êï∞ÂÄº
 * @returns ÂÉèÁ¥†Êï∞ÂÄºÔºàÂéüÊ†∑ËøîÂõûÔºåÁî®‰∫éËØ≠‰πâÊ†áËØÜÔºâ
 * 
 * @example
 * ```typescript
 * padding: pixels(16);                  // 16
 * margin: pixels(8);                    // 8
 * ```
 */
