'use client';

import { COUNT_800, MAGIC_1800, MAGIC_2500, MAGIC_4000 } from "@/constants/count";
import { MAGIC_0_1, MAGIC_0_25 } from "@/constants/decimal";
import { ANIMATION_DURATION_NORMAL, ANIMATION_DURATION_SLOW, HTTP_OK, ONE, PERCENTAGE_FULL, THREE_SECONDS_MS, ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';

/**
 * Êâ©Â±ïÁöÑPerformanceEntryÊé•Âè£ÔºåÂåÖÂê´Layout ShiftÁâπÂÆöÂ±ûÊÄß
 */
interface LayoutShiftEntry extends PerformanceEntry {
  hadRecentInput: boolean;
  value: number;
}

/**
 * Êâ©Â±ïÁöÑPerformanceEntryÊé•Âè£ÔºåÂåÖÂê´First Input DelayÁâπÂÆöÂ±ûÊÄß
 */
interface FirstInputEntry extends PerformanceEntry {
  processingStart: number;
}

/**
 * Web Vitals ÊÄßËÉΩÊåáÊ†áÊé•Âè£
 */
export interface WebVitalsMetrics {
  /** Á¥ØÁßØÂ∏ÉÂ±ÄÂÅèÁßª (Cumulative Layout Shift) */
  cls: number;
  /** È¶ñÊ¨°ËæìÂÖ•Âª∂Ëøü (First Input Delay) */
  fid: number;
  /** ÊúÄÂ§ßÂÜÖÂÆπÁªòÂà∂ (Largest Contentful Paint) */
  lcp: number;
  /** È¶ñÊ¨°ÂÜÖÂÆπÁªòÂà∂ (First Contentful Paint) */
  fcp?: number;
  /** È¶ñÂ≠óËäÇÊó∂Èó¥ (Time to First Byte) */
  ttfb?: number;
  /** ‰∫§‰∫íÂà∞‰∏ãÊ¨°ÁªòÂà∂ (Interaction to Next Paint) */
  inp?: number;
}

/**
 * ÊÄßËÉΩÊåáÊ†áËØÑÁ∫ß
 */
export interface MetricRating {
  value: number;
  rating: 'good' | 'needs-improvement' | 'poor';
}

/**
 * Web Vitals ÊÄßËÉΩÁõëÊéßÁ±ª
 *
 * Âü∫‰∫éÁé∞ÊúâÊÄßËÉΩÁõëÊéßÁªÑ‰ª∂Ê®°ÂºèÔºåÊèê‰æõÊ†∏ÂøÉWeb VitalsÊåáÊ†áÁöÑÊî∂ÈõÜÂíåÂàÜÊûê„ÄÇ
 * ÊîØÊåÅÂºÄÂèëÁéØÂ¢ÉÊòæÁ§∫ÂíåÁîü‰∫ßÁéØÂ¢ÉÈùôÈªòÊî∂ÈõÜ„ÄÇ
 */
export class WebVitalsMonitor {
  private static instance: WebVitalsMonitor;
  private metrics: Partial<WebVitalsMetrics> = {};
  private observers: PerformanceObserver[] = [];
  private isInitialized = false;

  // Web Vitals ÈòàÂÄºÈÖçÁΩÆ
  private static readonly THRESHOLDS = {
    CLS: { good: MAGIC_0_1, poor: MAGIC_0_25 },
    FID: { good: PERCENTAGE_FULL, poor: ANIMATION_DURATION_NORMAL },
    LCP: { good: MAGIC_2500, poor: MAGIC_4000 },
    FCP: { good: MAGIC_1800, poor: THREE_SECONDS_MS },
    TTFB: { good: COUNT_800, poor: MAGIC_1800 },
    INP: { good: HTTP_OK, poor: ANIMATION_DURATION_SLOW },
  } as const;

  private constructor() {
    this.initialize();
  }

  /**
   * Ëé∑ÂèñÂçï‰æãÂÆû‰æã
   */
  static getInstance(): WebVitalsMonitor {
    if (!WebVitalsMonitor.instance) {
      WebVitalsMonitor.instance = new WebVitalsMonitor();
    }
    return WebVitalsMonitor.instance;
  }

  /**
   * ÂàùÂßãÂåñÁõëÊéß
   */
  private initialize(): void {
    if (this.isInitialized || typeof window === 'undefined') {
      return;
    }

    try {
      this.setupPerformanceObservers();
      this.setupNavigationTiming();
      this.isInitialized = true;
    } catch (error) {
      logger.warn('Web Vitals monitoring initialization failed', {
        error: error instanceof Error ? error.message : String(error),
      });
    }
  }

  /**
   * ËÆæÁΩÆÊÄßËÉΩËßÇÂØüÂô®
   */
  private setupPerformanceObservers(): void {
    // CLS (Cumulative Layout Shift)
    if ('PerformanceObserver' in window) {
      try {
        const clsObserver = new PerformanceObserver((list) => {
          let clsValue = ZERO;
          for (const entry of list.getEntries()) {
            const layoutShiftEntry = entry as LayoutShiftEntry;
            if (!layoutShiftEntry.hadRecentInput) {
              clsValue += layoutShiftEntry.value;
            }
          }
          this.recordCLS(clsValue);
        });
        clsObserver.observe({ type: 'layout-shift', buffered: true });
        this.observers.push(clsObserver);
      } catch (error) {
        logger.warn('CLS observer setup failed', {
          error: error instanceof Error ? error.message : String(error),
        });
      }

      // LCP (Largest Contentful Paint)
      try {
        const lcpObserver = new PerformanceObserver((list) => {
          const entries = list.getEntries();
          const lastEntry = entries[entries.length - ONE];
          if (lastEntry) {
            this.recordLCP(lastEntry.startTime);
          }
        });
        lcpObserver.observe({
          type: 'largest-contentful-paint',
          buffered: true,
        });
        this.observers.push(lcpObserver);
      } catch (error) {
        logger.warn('LCP observer setup failed', {
          error: error instanceof Error ? error.message : String(error),
        });
      }

      // FID (First Input Delay)
      try {
        const fidObserver = new PerformanceObserver((list) => {
          for (const entry of list.getEntries()) {
            const firstInputEntry = entry as FirstInputEntry;
            this.recordFID(firstInputEntry.processingStart - entry.startTime);
          }
        });
        fidObserver.observe({ type: 'first-input', buffered: true });
        this.observers.push(fidObserver);
      } catch (error) {
        logger.warn('FID observer setup failed', {
          error: error instanceof Error ? error.message : String(error),
        });
      }

      // FCP (First Contentful Paint)
      try {
        const fcpObserver = new PerformanceObserver((list) => {
          for (const entry of list.getEntries()) {
            if (entry.name === 'first-contentful-paint') {
              this.recordFCP(entry.startTime);
            }
          }
        });
        fcpObserver.observe({ type: 'paint', buffered: true });
        this.observers.push(fcpObserver);
      } catch (error) {
        logger.warn('FCP observer setup failed', {
          error: error instanceof Error ? error.message : String(error),
        });
      }
    }
  }

  /**
   * ËÆæÁΩÆÂØºËà™Êó∂Èó¥ÁõëÊéß
   */
  private setupNavigationTiming(): void {
    if ('performance' in window && 'getEntriesByType' in performance) {
      // ‰ΩøÁî® setTimeout Á°Æ‰øùÂØºËà™Êó∂Èó¥Êï∞ÊçÆÂèØÁî®
      setTimeout(() => {
        const navigationEntries = performance.getEntriesByType(
          'navigation',
        ) as PerformanceNavigationTiming[];
        if (navigationEntries.length > ZERO) {
          const [navigation] = navigationEntries;
          if (
            navigation &&
            navigation.responseStart &&
            navigation.requestStart
          ) {
            const ttfb = navigation.responseStart - navigation.requestStart;
            this.recordTTFB(ttfb);
          }
        }
      }, ZERO);
    }
  }

  /**
   * ËÆ∞ÂΩï CLS ÊåáÊ†á
   */
  recordCLS(value: number): void {
    this.metrics.cls = (this.metrics.cls || ZERO) + value;
  }

  /**
   * ËÆ∞ÂΩï FID ÊåáÊ†á
   */
  recordFID(value: number): void {
    if (!this.metrics.fid || value > this.metrics.fid) {
      this.metrics.fid = value;
    }
  }

  /**
   * ËÆ∞ÂΩï LCP ÊåáÊ†á
   */
  recordLCP(value: number): void {
    this.metrics.lcp = value;
  }

  /**
   * ËÆ∞ÂΩï FCP ÊåáÊ†á
   */
  recordFCP(value: number): void {
    this.metrics.fcp = value;
  }

  /**
   * ËÆ∞ÂΩï TTFB ÊåáÊ†á
   */
  recordTTFB(value: number): void {
    this.metrics.ttfb = value;
  }

  /**
   * Ëé∑ÂèñÊÄßËÉΩÊåáÊ†áËØÑÁ∫ß
   */
  private getMetricRating(
    metric: keyof typeof WebVitalsMonitor.THRESHOLDS,
    value: number,
  ): MetricRating {
    // ÂÆâÂÖ®ÁöÑÂ±ûÊÄßËÆøÈóÆÔºåÈò≤Ê≠¢ÂØπË±°Ê≥®ÂÖ•ÊîªÂáª
    const allowedMetrics = ['CLS', 'FID', 'LCP', 'FCP', 'TTFB', 'INP'] as const;
    if (!allowedMetrics.includes(metric as (typeof allowedMetrics)[number])) {
      throw new Error(`Invalid metric: ${metric}`);
    }
    const safeThresholds = new Map(Object.entries(WebVitalsMonitor.THRESHOLDS));
    const thresholds = safeThresholds.get(metric) || { good: ZERO, poor: ZERO };
    let rating: 'good' | 'needs-improvement' | 'poor';

    if (value <= thresholds.good) {
      rating = 'good';
    } else if (value <= thresholds.poor) {
      rating = 'needs-improvement';
    } else {
      rating = 'poor';
    }

    return { value, rating };
  }

  /**
   * Ëé∑ÂèñÊÄßËÉΩÊëòË¶Å
   */
  getPerformanceSummary(): {
    metrics: WebVitalsMetrics;
    ratings: Record<string, MetricRating>;
    score: number;
  } {
    const metrics: WebVitalsMetrics = {
      cls: this.metrics.cls || ZERO,
      fid: this.metrics.fid || ZERO,
      lcp: this.metrics.lcp || ZERO,
      ...(this.metrics.fcp !== undefined && { fcp: this.metrics.fcp }),
      ...(this.metrics.ttfb !== undefined && { ttfb: this.metrics.ttfb }),
      ...(this.metrics.inp !== undefined && { inp: this.metrics.inp }),
    };

    const ratings = {
      cls: this.getMetricRating('CLS', metrics.cls),
      fid: this.getMetricRating('FID', metrics.fid),
      lcp: this.getMetricRating('LCP', metrics.lcp),
      ...(metrics.fcp && { fcp: this.getMetricRating('FCP', metrics.fcp) }),
      ...(metrics.ttfb && { ttfb: this.getMetricRating('TTFB', metrics.ttfb) }),
    };

    // ËÆ°ÁÆóÊÄª‰ΩìÊÄßËÉΩËØÑÂàÜ
    const ratingValues = Object.values(ratings);
    const goodCount = ratingValues.filter((r) => r.rating === 'good').length;
    const score = Math.round((goodCount / ratingValues.length) * PERCENTAGE_FULL);

    return { metrics, ratings, score };
  }

  /**
   * ÂèëÈÄÅÊÄßËÉΩÊä•Âëä
   */
  sendReport(): void {
    const summary = this.getPerformanceSummary();

    // ÂºÄÂèëÁéØÂ¢ÉËæìÂá∫Âà∞Êó•Âøó
    if (process.env.NODE_ENV === 'development') {
      logger.info('Web Vitals Performance Report', {
        ratings: summary.ratings,
        score: summary.score,
        metrics: summary.metrics,
      });
    }

    // Áîü‰∫ßÁéØÂ¢ÉÂèØ‰ª•ÂèëÈÄÅÂà∞ÂàÜÊûêÊúçÂä°
    // ËøôÈáåÂèØ‰ª•ÈõÜÊàê Google Analytics„ÄÅVercel Analytics Á≠â
  }

  /**
   * ÈáçÁΩÆÁõëÊéßÂô®Áä∂ÊÄÅ
   */
  reset(): void {
    this.metrics = {};
    this.cleanup();
    this.initialize();
  }

  /**
   * Ê∏ÖÁêÜÁõëÊéßÂô®
   */
  cleanup(): void {
    this.observers.forEach((observer) => {
      try {
        observer.disconnect();
      } catch (error) {
        logger.warn('Failed to disconnect performance observer', {
          error: error instanceof Error ? error.message : String(error),
        });
      }
    });
    this.observers = [];
    this.isInitialized = false;
  }
}

