 * Êèê‰æõÈÄöÁî®ÁöÑÂ∑•ÂÖ∑ÊñπÊ≥ïÂíåËæÖÂä©ÂáΩÊï∞
 */

import { ONE, ZERO } from "@/constants/magic-numbers";
import { WEB_VITALS_CONSTANTS } from '@/constants/test-constants';
import { PERFORMANCE_THRESHOLDS } from '@/lib/web-vitals/constants';
import type { DetailedWebVitals, PerformanceBaseline } from '@/lib/web-vitals/types';

/**
 * ÊÄßËÉΩÁõëÊéßÂ∑•ÂÖ∑Á±ª
 */
export class MonitoringUtils {
  /**
   * Ëé∑ÂèñÊåáÊ†áÁä∂ÊÄÅÁöÑËæÖÂä©ÂáΩÊï∞
   */
  static getStandardMetricStatus(
    value: number,
    goodThreshold: number,
    needsImprovementThreshold: number,
  ): string {
    if (value <= goodThreshold) return 'üü¢';
    if (value <= needsImprovementThreshold) return 'üü°';
    return 'üî¥';
  }

  /**
   * Ëé∑ÂèñÊåáÊ†áÁä∂ÊÄÅ
   */
  static getMetricStatus(metric: string, value: number): string {
    const thresholds = PERFORMANCE_THRESHOLDS;

    // ÂÆö‰πâÊåáÊ†áÈòàÂÄºÊò†Â∞Ñ
    const metricThresholds = {
      cls: {
        good: thresholds.CLS_GOOD,
        needsImprovement: thresholds.CLS_NEEDS_IMPROVEMENT,
      },
      fid: {
        good: thresholds.FID_GOOD,
        needsImprovement: thresholds.FID_NEEDS_IMPROVEMENT,
      },
      lcp: {
        good: thresholds.LCP_GOOD,
        needsImprovement: thresholds.LCP_NEEDS_IMPROVEMENT,
      },
      fcp: {
        good: thresholds.FCP_GOOD,
        needsImprovement: WEB_VITALS_CONSTANTS.FCP_NEEDS_IMPROVEMENT_THRESHOLD,
      },
      ttfb: {
        good: thresholds.TTFB_GOOD,
        needsImprovement: thresholds.TTFB_NEEDS_IMPROVEMENT,
      },
    };

    const threshold = metricThresholds[metric as keyof typeof metricThresholds];
    if (!threshold) {
      return '';
    }

    return MonitoringUtils.getStandardMetricStatus(
      value,
      threshold.good,
      threshold.needsImprovement,
    );
  }

  /**
   * Âà§Êñ≠ÊòØÂê¶Â∫îËØ•‰øùÂ≠òÂü∫ÂáÜÊï∞ÊçÆ
   */
  static shouldSaveBaseline(
    _metrics: DetailedWebVitals,
    baseline: PerformanceBaseline | null,
  ): boolean {
    if (!baseline) return true; // Ê≤°ÊúâÂü∫ÂáÜÊï∞ÊçÆÊó∂ÊÄªÊòØ‰øùÂ≠ò

    // Â¶ÇÊûúË∑ùÁ¶ª‰∏äÊ¨°Âü∫ÂáÜË∂ÖËøá24Â∞èÊó∂Ôºå‰øùÂ≠òÊñ∞Âü∫ÂáÜ
    const hoursSinceBaseline =
      (Date.now() - baseline.timestamp) /
      (WEB_VITALS_CONSTANTS.MILLISECONDS_PER_SECOND *
        WEB_VITALS_CONSTANTS.SECONDS_PER_MINUTE *
        WEB_VITALS_CONSTANTS.MINUTES_PER_HOUR);
    return hoursSinceBaseline > WEB_VITALS_CONSTANTS.BASELINE_REFRESH_HOURS;
  }

  /**
   * È™åËØÅÊåáÊ†áÊï∞ÊçÆÊúâÊïàÊÄß
   */
  static isValidMetrics(metrics: DetailedWebVitals): boolean {
    return metrics.lcp > ZERO && metrics.fcp > ZERO && metrics.ttfb > ZERO;
  }

  /**
   * ÊèêÂèñÈ°µÈù¢Ê†áËØÜÁ¨¶
   */
  static extractPageIdentifier(url: string): string {
    try {
      const urlObj = new URL(url);
      return urlObj.pathname;
    } catch {
      return url;
    }
  }

  /**
   * ÊèêÂèñlocale
   */
  static extractLocale(url: string): string {
    const match = url.match(/\/([a-z]{2})(?:\/|$)/);
    return match?.[ONE] ?? 'en';
  }

  /**
   * ËÆ°ÁÆóÊÄßËÉΩÁ≠âÁ∫ß
   */
  static calculateGrade(score: number): 'A' | 'B' | 'C' | 'D' | 'F' {
    if (score >= WEB_VITALS_CONSTANTS.GRADE_A_THRESHOLD) return 'A';
    if (score >= WEB_VITALS_CONSTANTS.GRADE_B_THRESHOLD) return 'B';
    if (score >= WEB_VITALS_CONSTANTS.GRADE_C_THRESHOLD) return 'C';
    if (score >= WEB_VITALS_CONSTANTS.GRADE_D_THRESHOLD) return 'D';
    return 'F';
  }
}

/**
 * Web Vitals Êî∂ÈõÜÂô®ÂàÜÊûêÂô®
 * Web Vitals collector analyzer
 */

'use client';

import { BYTES_PER_KB, COUNT_FIVE, COUNT_PAIR, COUNT_TEN, COUNT_TRIPLE, DAYS_PER_MONTH, MAGIC_15, MAGIC_1800, MAGIC_600, MAGIC_800, ONE, PERCENTAGE_FULL, PERCENTAGE_QUARTER, THREE_SECONDS_MS, ZERO } from "@/constants/magic-numbers";
import { WEB_VITALS_CONSTANTS } from '@/constants/test-constants';
import { PERFORMANCE_THRESHOLDS } from '@/lib/web-vitals/constants';
import type { DetailedWebVitals } from '@/lib/web-vitals/types';

/**
 * Web Vitals ÊÄßËÉΩÂàÜÊûêÂô®
 * Ë¥üË¥£ÊÄßËÉΩÊåáÊ†áÂàÜÊûêÂíåËØäÊñ≠Êä•ÂëäÁîüÊàê
 */
export class WebVitalsCollectorAnalyzer {
  /**
   * ÂàÜÊûê CLS ÊåáÊ†áÂπ∂ÁîüÊàêÈóÆÈ¢òÂíåÂª∫ËÆÆ
   */
  protected analyzeCLS(
    cls: number,
    issues: string[],
    recommendations: string[],
  ): void {
    if (cls > PERFORMANCE_THRESHOLDS.CLS_NEEDS_IMPROVEMENT) {
      issues.push(`Á¥ØÁßØÂ∏ÉÂ±ÄÂÅèÁßª (CLS) ËøáÈ´ò: ${cls.toFixed(COUNT_TRIPLE)}`);
      recommendations.push(
        '‰∏∫ÂõæÁâáÂíåÂπøÂëäËÆæÁΩÆÊòéÁ°ÆÁöÑÂ∞∫ÂØ∏ÔºåÈÅøÂÖçÂä®ÊÄÅÂÜÖÂÆπÊèíÂÖ•ÂØºËá¥Â∏ÉÂ±ÄÂÅèÁßª',
      );
      recommendations.push(
        '‰ΩøÁî® CSS aspect-ratio Â±ûÊÄßÈ¢ÑÁïôÁ©∫Èó¥ÔºåÈÅøÂÖçÂÜÖÂÆπÂä†ËΩΩÊó∂ÁöÑÂ∏ÉÂ±ÄÂèòÂåñ',
      );
      recommendations.push(
        'Á°Æ‰øùÂ≠ó‰ΩìÂä†ËΩΩ‰∏ç‰ºöÂØºËá¥ÊñáÊú¨ÈáçÊñ∞Â∏ÉÂ±ÄÔºå‰ΩøÁî® font-display: swap',
      );
    }
  }

  /**
   * ÂàÜÊûê LCP ÊåáÊ†áÂπ∂ÁîüÊàêÈóÆÈ¢òÂíåÂª∫ËÆÆ
   */
  protected analyzeLCP(
    lcp: number,
    issues: string[],
    recommendations: string[],
  ): void {
    if (lcp > PERFORMANCE_THRESHOLDS.LCP_NEEDS_IMPROVEMENT) {
      issues.push(`ÊúÄÂ§ßÂÜÖÂÆπÁªòÂà∂ (LCP) ËøáÊÖ¢: ${lcp.toFixed(ZERO)}ms`);
      recommendations.push('‰ºòÂåñÂõæÁâáÂä†ËΩΩÔºå‰ΩøÁî®Áé∞‰ª£ÂõæÁâáÊ†ºÂºèÂ¶Ç WebP');
      recommendations.push('ÂáèÂ∞ëÊúçÂä°Âô®ÂìçÂ∫îÊó∂Èó¥Ôºå‰ºòÂåñÂêéÁ´ØÊÄßËÉΩ');
      recommendations.push('‰ΩøÁî® CDN Âä†ÈÄüËµÑÊ∫êÂä†ËΩΩ');
    }
  }

  /**
   * ÂàÜÊûê FID ÊåáÊ†áÂπ∂ÁîüÊàêÈóÆÈ¢òÂíåÂª∫ËÆÆ
   */
  protected analyzeFID(
    fid: number,
    issues: string[],
    recommendations: string[],
  ): void {
    if (fid > PERFORMANCE_THRESHOLDS.FID_NEEDS_IMPROVEMENT) {
      issues.push(`È¶ñÊ¨°ËæìÂÖ•Âª∂Ëøü (FID) ËøáÈ´ò: ${fid.toFixed(ZERO)}ms`);
      recommendations.push('ÂáèÂ∞ë JavaScript ÊâßË°åÊó∂Èó¥ÔºåËÄÉËôë‰ª£Á†ÅÂàÜÂâ≤ÂíåÊáíÂä†ËΩΩ');
      recommendations.push('‰ºòÂåñÁ¨¨‰∏âÊñπËÑöÊú¨ÔºåÂª∂ËøüÈùûÂÖ≥ÈîÆËÑöÊú¨ÁöÑÂä†ËΩΩ');
      recommendations.push('‰ΩøÁî® Web Workers Â§ÑÁêÜËÆ°ÁÆóÂØÜÈõÜÂûã‰ªªÂä°');
    }
  }

  /**
   * ÂàÜÊûêËµÑÊ∫êÂä†ËΩΩÊÄßËÉΩÂπ∂ÁîüÊàêÈóÆÈ¢òÂíåÂª∫ËÆÆ
   */
  protected analyzeResourceTiming(
    resourceTiming: DetailedWebVitals['resourceTiming'],
    issues: string[],
    recommendations: string[],
  ): void {
    if (
      resourceTiming.slowResources.length > ZERO &&
      resourceTiming.slowResources[ZERO]!.duration > 500
    ) {
      issues.push(
        `ÂèëÁé∞ÊÖ¢ÈÄüËµÑÊ∫ê: ${resourceTiming.slowResources[ZERO]!.name} (${resourceTiming.slowResources[ZERO]!.duration.toFixed(ZERO)}ms)`,
      );
      recommendations.push('‰ºòÂåñÊÖ¢ÈÄüËµÑÊ∫êÁöÑÂä†ËΩΩÔºåËÄÉËôëÂéãÁº©Êàñ‰ΩøÁî® CDN');
    }

    if (resourceTiming.totalSize > COUNT_PAIR * BYTES_PER_KB * BYTES_PER_KB) {
      // 2MB
      issues.push(
        `ÊÄªËµÑÊ∫êÂ§ßÂ∞èËøáÂ§ß: ${(resourceTiming.totalSize / BYTES_PER_KB / BYTES_PER_KB).toFixed(ONE)}MB`,
      );
      recommendations.push('ÂáèÂ∞ëËµÑÊ∫êÂ§ßÂ∞èÔºåÂêØÁî® gzip ÂéãÁº©');
    }
  }

  /**
   * ËÆ°ÁÆóÊÄßËÉΩËØÑÂàÜ
   */
  protected calculatePerformanceScore(metrics: DetailedWebVitals): number {
    const { PERFECT_SCORE } = WEB_VITALS_CONSTANTS;
    let score = PERFECT_SCORE;

    // CLS ËØÑÂàÜ
    if (metrics.cls > PERFORMANCE_THRESHOLDS.CLS_NEEDS_IMPROVEMENT) {
      score -= PERCENTAGE_QUARTER;
    } else if (metrics.cls > PERFORMANCE_THRESHOLDS.CLS_GOOD) {
      score -= COUNT_TEN;
    }

    // LCP ËØÑÂàÜ
    if (metrics.lcp > PERFORMANCE_THRESHOLDS.LCP_NEEDS_IMPROVEMENT) {
      score -= DAYS_PER_MONTH;
    } else if (metrics.lcp > PERFORMANCE_THRESHOLDS.LCP_GOOD) {
      score -= MAGIC_15;
    }

    // FID ËØÑÂàÜ
    if (metrics.fid > PERFORMANCE_THRESHOLDS.FID_NEEDS_IMPROVEMENT) {
      score -= PERCENTAGE_QUARTER;
    } else if (metrics.fid > PERFORMANCE_THRESHOLDS.FID_GOOD) {
      score -= COUNT_TEN;
    }

    // FCP ËØÑÂàÜ
    if (metrics.fcp > THREE_SECONDS_MS) {
      score -= COUNT_TEN;
    } else if (metrics.fcp > MAGIC_1800) {
      score -= COUNT_FIVE;
    }

    // TTFB ËØÑÂàÜ
    if (metrics.ttfb > MAGIC_800) {
      score -= COUNT_TEN;
    } else if (metrics.ttfb > MAGIC_600) {
      score -= COUNT_FIVE;
    }

    return Math.max(ZERO, score);
  }

  /**
   * ÊâßË°åÊâÄÊúâÊÄßËÉΩÊåáÊ†áÂàÜÊûê
   */
  protected performMetricsAnalysis(
    metrics: DetailedWebVitals,
    issues: string[],
    recommendations: string[],
  ): void {
    this.analyzeCLS(metrics.cls, issues, recommendations);
    this.analyzeLCP(metrics.lcp, issues, recommendations);
    this.analyzeFID(metrics.fid, issues, recommendations);
    this.analyzeResourceTiming(metrics.resourceTiming, issues, recommendations);
  }

  /**
   * ÊûÑÂª∫ËØäÊñ≠Êä•ÂëäÁªìÊûúÂØπË±°
   */
  protected buildDiagnosticResult(
    metrics: DetailedWebVitals,
    issues: string[],
    recommendations: string[],
    score: number,
  ): {
    metrics: DetailedWebVitals;
    analysis: {
      issues: string[];
      recommendations: string[];
      score: number;
    };
  } {
    return {
      metrics,
      analysis: {
        issues,
        recommendations,
        score,
      },
    };
  }

  /**
   * ÁîüÊàêÂÆåÊï¥ÁöÑËØäÊñ≠Êä•Âëä
   */
  public generateDiagnosticReport(metrics: DetailedWebVitals): {
    metrics: DetailedWebVitals;
    analysis: {
      issues: string[];
      recommendations: string[];
      score: number;
    };
  } {
    const issues: string[] = [];
    const recommendations: string[] = [];

    // ÊâßË°åÊâÄÊúâÂàÜÊûê
    this.performMetricsAnalysis(metrics, issues, recommendations);

    // ËÆ°ÁÆóÊÄßËÉΩËØÑÂàÜ
    const score = this.calculatePerformanceScore(metrics);

    // ÊûÑÂª∫Âπ∂ËøîÂõûÁªìÊûú
    return this.buildDiagnosticResult(metrics, issues, recommendations, score);
  }

  /**
   * ÂàÜÊûêÊÄßËÉΩË∂ãÂäø
   */
  public analyzePerformanceTrend(
    currentMetrics: DetailedWebVitals,
    previousMetrics: DetailedWebVitals,
  ): {
    trend: 'improving' | 'declining' | 'stable';
    changes: Array<{
      metric: string;
      current: number;
      previous: number;
      change: number;
      changePercent: number;
    }>;
  } {
    const changes = [
      {
        metric: 'LCP',
        current: currentMetrics.lcp,
        previous: previousMetrics.lcp,
        change: currentMetrics.lcp - previousMetrics.lcp,
        changePercent:
          previousMetrics.lcp > ZERO
            ? ((currentMetrics.lcp - previousMetrics.lcp) /
                previousMetrics.lcp) *
              PERCENTAGE_FULL
            : ZERO,
      },
      {
        metric: 'FID',
        current: currentMetrics.fid,
        previous: previousMetrics.fid,
        change: currentMetrics.fid - previousMetrics.fid,
        changePercent:
          previousMetrics.fid > ZERO
            ? ((currentMetrics.fid - previousMetrics.fid) /
                previousMetrics.fid) *
              PERCENTAGE_FULL
            : ZERO,
      },
      {
        metric: 'CLS',
        current: currentMetrics.cls,
        previous: previousMetrics.cls,
        change: currentMetrics.cls - previousMetrics.cls,
        changePercent:
          previousMetrics.cls > ZERO
            ? ((currentMetrics.cls - previousMetrics.cls) /
                previousMetrics.cls) *
              PERCENTAGE_FULL
            : ZERO,
      },
    ];

    // ËÆ°ÁÆóÊÄª‰ΩìË∂ãÂäø
    const improvingCount = changes.filter((c) => c.change < ZERO).length;
    const decliningCount = changes.filter((c) => c.change > ZERO).length;

    let trend: 'improving' | 'declining' | 'stable';
    if (improvingCount > decliningCount) {
      trend = 'improving';
    } else if (decliningCount > improvingCount) {
      trend = 'declining';
    } else {
      trend = 'stable';
    }

    return { trend, changes };
  }
}

/**
 * Web Vitals Êî∂ÈõÜÂô®Âü∫Á°ÄÁ±ª
 * Web Vitals collector base class
 */

'use client';

import { COUNT_TEN, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { WebVitalsObservers } from '@/lib/web-vitals/observers';
import type { DetailedWebVitals } from '@/lib/web-vitals/types';

/**
 * Web Vitals Êî∂ÈõÜÂô®Âü∫Á°ÄÁ±ª
 * Ë¥üË¥£Âü∫Á°ÄÊï∞ÊçÆÊî∂ÈõÜÂíåÂàùÂßãÂåñ
 */
export class WebVitalsCollectorBase {
  protected metrics: Partial<DetailedWebVitals> = {};
  protected webVitalsObservers: WebVitalsObservers;
  protected isCollecting = false;

  constructor() {
    this.webVitalsObservers = new WebVitalsObservers(this.metrics);
    this.initializeCollection();
  }

  protected initializeCollection() {
    if (typeof window === 'undefined' || this.isCollecting) return;

    this.isCollecting = true;
    this.collectBasicPageInfo();
    this.collectDeviceInfo();
    this.collectNetworkInfo();
    this.collectNavigationTiming();
    this.collectResourceTiming();
    this.collectWebVitals();
  }

  protected collectBasicPageInfo() {
    this.metrics.page = {
      url: window.location.href,
      referrer: document.referrer,
      title: document.title,
      timestamp: Date.now(),
    };
  }

  protected collectDeviceInfo() {
    const nav = navigator as Navigator & {
      deviceMemory?: number;
      hardwareConcurrency?: number;
    };
    this.metrics.device = {
      userAgent: navigator.userAgent,
      ...(nav.deviceMemory !== undefined && { memory: nav.deviceMemory }),
      ...(nav.hardwareConcurrency !== undefined && {
        cores: nav.hardwareConcurrency,
      }),
      viewport: {
        width: window.innerWidth,
        height: window.innerHeight,
      },
    };
  }

  protected collectNetworkInfo() {
    const nav = navigator as Navigator & {
      connection?: {
        effectiveType: string;
        downlink: number;
        rtt: number;
        saveData: boolean;
      };
    };
    if (nav.connection) {
      this.metrics.connection = {
        effectiveType: nav.connection.effectiveType,
        downlink: nav.connection.downlink,
        rtt: nav.connection.rtt,
        saveData: nav.connection.saveData,
      };
    }
  }

  protected collectNavigationTiming() {
    const navigation = performance.getEntriesByType(
      'navigation',
    )[ZERO] as PerformanceNavigationTiming;
    if (navigation) {
      // ‰ΩøÁî® startTime ‰Ωú‰∏∫Âü∫ÂáÜÊó∂Èó¥ÁÇπÔºàÁõ∏ÂΩì‰∫é navigationStartÔºâ
      this.metrics.fcp = navigation.responseEnd - navigation.startTime;
      this.metrics.domContentLoaded =
        navigation.domContentLoadedEventEnd - navigation.startTime;
      this.metrics.loadComplete =
        navigation.loadEventEnd - navigation.startTime;
      this.metrics.ttfb = navigation.responseStart - navigation.startTime;
    }
  }

  protected collectResourceTiming() {
    const resources = performance.getEntriesByType(
      'resource',
    ) as PerformanceResourceTiming[];

    const slowResources = resources
      .filter((resource) => resource.duration > PERCENTAGE_FULL) // Ë∂ÖËøá100msÁöÑËµÑÊ∫ê
      .map((resource) => ({
        name: resource.name,
        duration: resource.duration,
        size: resource.transferSize || ZERO,
        type: this.getResourceType(resource.name),
      }))
      .sort((a, b) => b.duration - a.duration)
      .slice(ZERO, COUNT_TEN); // Âè™‰øùÁïôÂâçCOUNT_TEN‰∏™ÊúÄÊÖ¢ÁöÑËµÑÊ∫ê

    this.metrics.resourceTiming = {
      totalResources: resources.length,
      slowResources,
      totalSize: resources.reduce((sum, r) => sum + (r.transferSize || ZERO), ZERO),
      totalDuration: resources.reduce((sum, r) => sum + r.duration, ZERO),
    };
  }

  protected getResourceType(url: string): string {
    if (url.includes('.js')) return 'JavaScript';
    if (url.includes('.css')) return 'CSS';
    if (url.match(/\.(jpg|jpeg|png|gif|webp|svg)$/i)) return 'Image';
    if (url.match(/\.(woff|woff2|ttf|otf)$/i)) return 'Font';
    if (url.includes('api/') || url.includes('/api/')) return 'API';
    return 'Other';
  }

  protected collectWebVitals() {
    // ÂêØÂä®ÊâÄÊúâ Web Vitals ËßÇÂØüÂô®
    this.webVitalsObservers.startAllObservers();
  }

  /**
   * Ëé∑ÂèñÈªòËÆ§ÁöÑËµÑÊ∫êÊó∂Èó¥‰ø°ÊÅØ
   */
  protected getDefaultResourceTiming() {
    return {
      totalResources: ZERO,
      slowResources: [],
      totalSize: ZERO,
      totalDuration: ZERO,
    };
  }

  /**
   * Ëé∑ÂèñÈªòËÆ§ÁöÑËøûÊé•‰ø°ÊÅØ
   */
  protected getDefaultConnection() {
    return {
      effectiveType: 'unknown' as const,
      downlink: ZERO,
      rtt: ZERO,
      saveData: false,
    };
  }

  /**
   * Ëé∑ÂèñÈªòËÆ§ÁöÑËÆæÂ§á‰ø°ÊÅØ
   */
  protected getDefaultDevice() {
    const {deviceMemory} = (navigator as Navigator & { deviceMemory?: number });
    const {hardwareConcurrency} = navigator;

    return {
      ...(deviceMemory !== undefined && { memory: deviceMemory }),
      ...(hardwareConcurrency !== undefined && { cores: hardwareConcurrency }),
      userAgent: navigator.userAgent,
      viewport: {
        width: window?.innerWidth || ZERO,
        height: window?.innerHeight || ZERO,
      },
    };
  }

  /**
   * Ëé∑ÂèñÈªòËÆ§ÁöÑÈ°µÈù¢‰ø°ÊÅØ
   */
  protected getDefaultPage() {
    return {
      url: '',
      referrer: '',
      title: '',
      timestamp: Date.now(),
    };
  }

  public getDetailedMetrics(): DetailedWebVitals {
    // Á°Æ‰øùÊâÄÊúâÂøÖÈúÄÁöÑÂ≠óÊÆµÈÉΩÊúâÈªòËÆ§ÂÄº
    return {
      cls: this.metrics.cls || ZERO,
      fid: this.metrics.fid || ZERO,
      lcp: this.metrics.lcp || ZERO,
      fcp: this.metrics.fcp || ZERO,
      firstPaint: this.metrics.firstPaint || ZERO,
      ttfb: this.metrics.ttfb || ZERO,
      domContentLoaded: this.metrics.domContentLoaded || ZERO,
      loadComplete: this.metrics.loadComplete || ZERO,
      page: this.metrics.page || this.getDefaultPage(),
      device: this.metrics.device || this.getDefaultDevice(),
      connection: this.metrics.connection || this.getDefaultConnection(),
      resourceTiming:
        this.metrics.resourceTiming || this.getDefaultResourceTiming(),
    };
  }

  /**
   * Ëé∑ÂèñÊî∂ÈõÜÁä∂ÊÄÅÔºà‰æõÂ≠êÁ±ªËÆøÈóÆÔºâ
   */
  protected get collectingStatus(): boolean {
    return this.isCollecting;
  }

  public cleanup() {
    this.webVitalsObservers.cleanup();
    this.isCollecting = false;
  }
}

/**
 * WhatsApp Business API Ê†∏ÂøÉÊúçÂä°Á±ª
 * Êï¥ÂêàÊ∂àÊÅØÂèëÈÄÅÂíåÂ™í‰ΩìÂ§ÑÁêÜÂäüËÉΩ
