import { MAGIC_4096 } from "@/constants/count";
import { BYTES_PER_KB, COUNT_PAIR, COUNT_TEN, COUNT_TRIPLE, HOURS_PER_DAY, HTTP_OK, MAGIC_20, MAGIC_256, MAGIC_36, MAGIC_72, MAGIC_9, ZERO } from "@/constants/magic-numbers";

/**
 * WhatsApp Business API Â∑•ÂÖ∑ÂáΩÊï∞
 * Êèê‰æõÈ™åËØÅ„ÄÅÊ†ºÂºèÂåñÁ≠âËæÖÂä©ÂäüËÉΩ
 */

/**
 * WhatsApp Â∑•ÂÖ∑Á±ª
 */
export class WhatsAppUtils {
  /**
   * È™åËØÅÁîµËØùÂè∑Á†ÅÊ†ºÂºè
   */
  static validatePhoneNumber(phoneNumber: string): boolean {
    // Âü∫Êú¨È™åËØÅÔºöÂ∫îËØ•ÂåÖÂê´ÂõΩÂÆ∂‰ª£Á†ÅÔºåÂè™ÂåÖÂê´Êï∞Â≠ó
    const phoneRegex = /^\d{COUNT_TEN,15}$/;
    return phoneRegex.test(phoneNumber.replace(/\D/g, ''));
  }

  /**
   * Ê†ºÂºèÂåñÁîµËØùÂè∑Á†ÅÔºàÁßªÈô§ÈùûÊï∞Â≠óÂ≠óÁ¨¶Ôºâ
   */
  static formatPhoneNumber(phoneNumber: string): string {
    return phoneNumber.replace(/\D/g, '');
  }

  /**
   * Ê£ÄÊü•Ê∂àÊÅØÈïøÂ∫¶ÈôêÂà∂
   */
  static validateMessageLength(
    message: string,
    maxLength: number = MAGIC_4096,
  ): boolean {
    return message.length <= maxLength;
  }

  /**
   * È™åËØÅÊ®°ÊùøÂèÇÊï∞
   */
  static validateTemplateParameters(parameters: string[]): boolean {
    return parameters.every(
      (param) =>
        typeof param === 'string' && param.length > ZERO && param.length <= BYTES_PER_KB,
    );
  }

  /**
   * È™åËØÅÊåâÈíÆÈÖçÁΩÆ
   */
  static validateButtons(
    buttons: Array<{ id: string; title: string }>,
  ): boolean {
    if (buttons.length === ZERO || buttons.length > COUNT_TRIPLE) {
      return false;
    }

    return buttons.every(
      (button) =>
        typeof button.id === 'string' &&
        typeof button.title === 'string' &&
        button.id.length > ZERO &&
        button.id.length <= MAGIC_256 &&
        button.title.length > ZERO &&
        button.title.length <= MAGIC_20,
    );
  }

  /**
   * È™åËØÅÂàóË°®Ë°åÈÖçÁΩÆ
   */
  static validateListRows(
    rows: Array<{ id: string; title: string; description?: string }>,
  ): boolean {
    if (rows.length === ZERO || rows.length > COUNT_TEN) {
      return false;
    }

    return rows.every(
      (row) =>
        typeof row.id === 'string' &&
        typeof row.title === 'string' &&
        row.id.length > ZERO &&
        row.id.length <= HTTP_OK &&
        row.title.length > ZERO &&
        row.title.length <= HOURS_PER_DAY &&
        (!row.description ||
          (row.description.length > ZERO && row.description.length <= MAGIC_72)),
    );
  }

  /**
   * ÁîüÊàêÂîØ‰∏ÄÁöÑÊ∂àÊÅØID
   */
  static generateMessageId(): string {
    return `msg_${Date.now()}_${Math.random().toString(MAGIC_36).substr(COUNT_PAIR, MAGIC_9)}`;
  }

  /**
   * Ê£ÄÊü•URLÊ†ºÂºè
   */
  static validateUrl(url: string): boolean {
    try {
      new URL(url);
      return true;
    } catch {
      return false;
    }
  }

  /**
   * Ê£ÄÊü•Â™í‰ΩìÁ±ªÂûã
   */
  static validateMediaType(type: string): boolean {
    const validTypes = ['image', 'document', 'audio', 'video', 'sticker'];
    return validTypes.includes(type);
  }
}

import { env } from '@/../env.mjs';
import { COUNT_TEN, ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';
import WhatsApp from 'whatsapp';

/**
 * WhatsApp webhookÊ∂àÊÅØ‰ΩìÁ±ªÂûãÂÆö‰πâ
 */
interface WhatsAppWebhookBody {
  entry?: Array<{
    changes?: Array<{
      value?: {
        messages?: Array<{
          from: string;
          text?: { body: string };
        }>;
      };
    }>;
  }>;
}

/**
 * WhatsApp Business API ÊúçÂä°Á±ª
 * Êèê‰æõÂèëÈÄÅÊ∂àÊÅØ„ÄÅÂ§ÑÁêÜ webhook Á≠âÂäüËÉΩ
 */
export class WhatsAppService {
  private client: WhatsApp;

  constructor() {
    if (!env.WHATSAPP_ACCESS_TOKEN) {
      throw new Error('WHATSAPP_ACCESS_TOKEN is required');
    }

    // WhatsApp constructor expects phoneNumberId as number
    const phoneNumberId = env.WHATSAPP_PHONE_NUMBER_ID
      ? parseInt(env.WHATSAPP_PHONE_NUMBER_ID, COUNT_TEN)
      : ZERO;
    this.client = new WhatsApp(phoneNumberId);
    // Set token separately if needed
    if (env.WHATSAPP_ACCESS_TOKEN) {
      (this.client as { token?: string }).token = env.WHATSAPP_ACCESS_TOKEN;
    }
  }

  /**
   * ÂèëÈÄÅÊñáÊú¨Ê∂àÊÅØ
   */
  async sendTextMessage(to: string, message: string) {
    try {
      // WhatsApp API expects recipient as second parameter
      const recipient = parseInt(to, COUNT_TEN);
      const response = await this.client.messages.text(
        {
          body: message,
        },
        recipient,
      );
      return response;
    } catch (_error) {
      // ÂøΩÁï•ÈîôËØØÂèòÈáè
      logger.error(
        'Failed to send WhatsApp message',
        {},
        _error instanceof Error ? _error : new Error(String(_error)),
      );
      throw _error;
    }
  }

  /**
   * ÂèëÈÄÅÊ®°ÊùøÊ∂àÊÅØ
   */
  async sendTemplateMessage(
    to: string,
    templateName: string,
    languageCode: string = 'en',
    components?: Array<Record<string, unknown>>,
  ) {
    try {
      // WhatsApp API expects recipient as second parameter
      const recipient = parseInt(to, COUNT_TEN);
      // WhatsApp template message with correct structure
      const templateObject = {
        name: templateName,
        language: {
          policy: 'deterministic' as const,
          code: languageCode,
        },
        ...(components && { components }),
      };
      const response = await this.client.messages.template(
        templateObject as unknown as Parameters<
          typeof this.client.messages.template
        >[0],
        recipient,
      );
      return response;
    } catch (_error) {
      // ÂøΩÁï•ÈîôËØØÂèòÈáè
      logger.error(
        'Failed to send WhatsApp template message',
        {},
        _error instanceof Error ? _error : new Error(String(_error)),
      );
      throw _error;
    }
  }

  /**
   * È™åËØÅ webhook Á≠æÂêç
   */
  verifyWebhook(mode: string, token: string, challenge: string): string | null {
    if (mode === 'subscribe' && token === env.WHATSAPP_WEBHOOK_VERIFY_TOKEN) {
      return challenge;
    }
    return null;
  }

  /**
   * Â§ÑÁêÜÊé•Êî∂Âà∞ÁöÑÊ∂àÊÅØ
   */
  async handleIncomingMessage(body: WhatsAppWebhookBody) {
    try {
      const entry = body.entry?.[ZERO];
      const changes = entry?.changes?.[ZERO];
      const value = changes?.value;

      if (value?.messages) {
        const [message] = value.messages;
        if (message) {
          const { from } = message;
          const messageBody = message.text?.body;

          // ‰ΩøÁî®loggerÊõø‰ª£console.log
          logger.info(`Received WhatsApp message from ${from}: ${messageBody}`);

          // ËøôÈáåÂèØ‰ª•Ê∑ªÂä†Ëá™Âä®ÂõûÂ§çÈÄªËæë
          if (messageBody) {
            await this.sendAutoReply(from, messageBody);
          }
        }
      }

      return { success: true };
    } catch (_error) {
      // ÂøΩÁï•ÈîôËØØÂèòÈáè
      logger.error(
        'Failed to handle incoming WhatsApp message',
        {},
        _error instanceof Error ? _error : new Error(String(_error)),
      );
      throw _error;
    }
  }

  /**
   * Ëá™Âä®ÂõûÂ§çÈÄªËæë
   */
  private async sendAutoReply(to: string, receivedMessage: string) {
    // ÁÆÄÂçïÁöÑËá™Âä®ÂõûÂ§çÈÄªËæë
    const lowerMessage = receivedMessage.toLowerCase();

    let replyMessage = '';

    if (lowerMessage.includes('hello') || lowerMessage.includes('hi')) {
      replyMessage =
        'Hello! Thank you for contacting us. How can we help you today?';
    } else if (lowerMessage.includes('help')) {
      replyMessage =
        "We're here to help! Please describe your question or concern.";
    } else if (
      lowerMessage.includes('price') ||
      lowerMessage.includes('cost')
    ) {
      replyMessage =
        'For pricing information, please visit our website or contact our sales team.';
    } else {
      replyMessage =
        'Thank you for your message. Our team will get back to you soon!';
    }

    await this.sendTextMessage(to, replyMessage);
  }
}

