let whatsappService: WhatsAppService | null = null;

export function getWhatsAppService(): WhatsAppService {
  if (!whatsappService) {
    whatsappService = new WhatsAppService();
  }
  return whatsappService;
}

// Ê∂àÊÅØÁ±ªÂûãÂÆö‰πâ
export interface WhatsAppMessage {
  to: string;
  type: 'text' | 'template' | 'media';
  content: {
    body?: string;
    templateName?: string;
    languageCode?: string;
    components?: Array<Record<string, unknown>>;
    mediaUrl?: string;
    caption?: string;
  };
}

// ÂèëÈÄÅÊ∂àÊÅØÁöÑÈÄöÁî®ÂáΩÊï∞
export function sendWhatsAppMessage(message: WhatsAppMessage) {
  const service = getWhatsAppService();

  switch (message.type) {
    case 'text':
      return service.sendTextMessage(message.to, message.content.body!);

    case 'template':
      return service.sendTemplateMessage(
        message.to,
        message.content.templateName!,
        message.content.languageCode,
        message.content.components,
      );

    default:
      throw new Error(`Unsupported message type: ${message.type}`);
  }
}

/**
 * ÈõÜ‰∏≠URLÁîüÊàêÊúçÂä°
 * Áªü‰∏ÄÁÆ°ÁêÜÊâÄÊúâURLÁîüÊàêÈÄªËæëÔºåÁ°Æ‰øù‰∏ÄËá¥ÊÄßÂíåÁ±ªÂûãÂÆâÂÖ®
 */

/* eslint-disable security/detect-object-injection */

import type { Locale } from '@/config/paths';
import {
  getLocalizedPath,
  LOCALES_CONFIG,
  PATHS_CONFIG,
  SITE_CONFIG,
  type PageType,
} from '@/config/paths';
import { ONE, ZERO } from "@/constants/magic-numbers";
import { SEO_CONSTANTS } from '@/constants/seo-constants';

// URLÁîüÊàêÈÄâÈ°πÊé•Âè£
export interface URLGeneratorOptions {
  includeLocale?: boolean;
  absolute?: boolean;
  trailingSlash?: boolean;
  protocol?: 'http' | 'https';
  host?: string;
}

// ÈªòËÆ§ÈÄâÈ°π
const DEFAULT_OPTIONS: Required<URLGeneratorOptions> = {
  includeLocale: true,
  absolute: false,
  trailingSlash: false,
  protocol: 'https',
  host: '',
};

// hreflangÈìæÊé•Êé•Âè£
export interface HreflangLink {
  href: string;
  hreflang: string;
}

// SitemapÊù°ÁõÆÊé•Âè£
export interface SitemapEntry {
  loc: string;
  changefreq?:
    | 'always'
    | 'hourly'
    | 'daily'
    | 'weekly'
    | 'monthly'
    | 'yearly'
    | 'never';
  priority?: number;
  lastmod?: string;
  alternateRefs?: HreflangLink[];
}

/**
 * Ê†∏ÂøÉURLÁîüÊàêÂô®Á±ª
 */
export class URLGenerator {
  private readonly baseUrl: string;
  private readonly defaultLocale: Locale;
  private readonly locales: readonly Locale[];

  constructor() {
    this.baseUrl = SITE_CONFIG.baseUrl;
    this.defaultLocale = LOCALES_CONFIG.defaultLocale;
    this.locales = LOCALES_CONFIG.locales;
  }

  /**
   * ÁîüÊàêÈ°µÈù¢URL
   */
  generatePageURL(
    pageType: PageType,
    locale: Locale,
    options: URLGeneratorOptions = {},
  ): string {
    const opts = { ...DEFAULT_OPTIONS, ...options };

    // Ëé∑ÂèñÊú¨Âú∞ÂåñË∑ØÂæÑ
    const localizedPath = getLocalizedPath(pageType, locale);

    // ÊûÑÂª∫Ë∑ØÂæÑ
    let path = '';

    // Ê∑ªÂä†ËØ≠Ë®ÄÂâçÁºÄÔºàÂ¶ÇÊûúÈúÄË¶Å‰∏î‰∏çÊòØÈªòËÆ§ËØ≠Ë®ÄÔºâ
    if (opts.includeLocale && locale !== this.defaultLocale) {
      path += `/${locale}`;
    }

    // Ê∑ªÂä†È°µÈù¢Ë∑ØÂæÑÔºàÂØπ‰∫é‰∏ªÈ°µÔºåÂ¶ÇÊûúÂ∑≤ÊúâËØ≠Ë®ÄÂâçÁºÄÂàô‰∏çÊ∑ªÂä†È¢ùÂ§ñÁöÑÊñúÊù†Ôºâ
    if (localizedPath !== '/' || path === '') {
      path += localizedPath;
    }

    // Ê∑ªÂä†Â∞æÈÉ®ÊñúÊù†
    if (opts.trailingSlash && !path.endsWith('/') && path !== '') {
      path += '/';
    }

    // ËøîÂõûÁªùÂØπÊàñÁõ∏ÂØπURL
    if (opts.absolute) {
      const host = opts.host || this.baseUrl.replace(/^https?:\/\//, '');
      return `${opts.protocol}://${host}${path}`;
    }

    return path || '/';
  }

  /**
   * ÁîüÊàêËßÑËåÉÂåñURLÔºàÁî®‰∫éSEOÔºâ
   */
  generateCanonicalURL(pageType: PageType, locale: Locale): string {
    return this.generatePageURL(pageType, locale, {
      absolute: true,
      includeLocale: true,
      trailingSlash: false,
    });
  }

  /**
   * ÁîüÊàêÊâÄÊúâËØ≠Ë®ÄÁâàÊú¨ÁöÑURLÊò†Â∞Ñ
   */
  generateLanguageAlternates(pageType: PageType): Record<Locale, string> {
    const alternates: Record<string, string> = {};

    this.locales.forEach((locale) => {
      alternates[locale] = this.generateCanonicalURL(pageType, locale);
    });

    return alternates as Record<Locale, string>;
  }

  /**
   * ÁîüÊàêhreflangÈìæÊé•Êï∞ÁªÑ
   */
  generateHreflangLinks(pageType: PageType): HreflangLink[] {
    const links: HreflangLink[] = [];

    this.locales.forEach((locale) => {
      links.push({
        href: this.generateCanonicalURL(pageType, locale),
        hreflang: locale,
      });
    });

    // Ê∑ªÂä†x-defaultÈìæÊé•ÔºàÊåáÂêëÈªòËÆ§ËØ≠Ë®ÄÔºâ
    links.push({
      href: this.generateCanonicalURL(pageType, this.defaultLocale),
      hreflang: 'x-default',
    });

    return links;
  }

  /**
   * ÁîüÊàêsitemapÊù°ÁõÆ
   */
  generateSitemapEntry(
    pageType: PageType,
    locale: Locale,
    options: {
      changefreq?: SitemapEntry['changefreq'];
      priority?: number;
      lastmod?: string;
    } = {},
  ): SitemapEntry {
    const url = this.generateCanonicalURL(pageType, locale);
    const alternateRefs = this.generateHreflangLinks(pageType);

    return {
      loc: url,
      changefreq:
        options.changefreq || SEO_CONSTANTS.URL_GENERATION.DEFAULT_CHANGEFREQ,
      priority:
        options.priority || SEO_CONSTANTS.URL_GENERATION.DEFAULT_PAGE_PRIORITY,
      lastmod: options.lastmod || new Date().toISOString(),
      alternateRefs,
    };
  }

  /**
   * ÁîüÊàêÊâÄÊúâÈ°µÈù¢ÁöÑsitemapÊù°ÁõÆ
   */
  generateAllSitemapEntries(): SitemapEntry[] {
    const entries: SitemapEntry[] = [];

    // ‰∏∫ÊØè‰∏™È°µÈù¢Á±ªÂûãÂíåËØ≠Ë®ÄÁîüÊàêÊù°ÁõÆ
    Object.keys(PATHS_CONFIG).forEach((pageType) => {
      this.locales.forEach((locale) => {
        const entry = this.generateSitemapEntry(pageType as PageType, locale, {
          changefreq:
            pageType === 'home'
              ? SEO_CONSTANTS.URL_GENERATION.HOME_CHANGEFREQ
              : SEO_CONSTANTS.URL_GENERATION.DEFAULT_CHANGEFREQ,
          priority:
            pageType === 'home'
              ? SEO_CONSTANTS.URL_GENERATION.HOME_PAGE_PRIORITY
              : SEO_CONSTANTS.URL_GENERATION.DEFAULT_PAGE_PRIORITY,
        });
        entries.push(entry);
      });
    });

    return entries;
  }

  /**
   * Ê†πÊçÆË∑ØÂæÑÂèçÂêëÁîüÊàêÈ°µÈù¢‰ø°ÊÅØ
   */
  parseURLToPageInfo(url: string): {
    pageType: PageType | null;
    locale: Locale;
    isValid: boolean;
  } {
    // ÁßªÈô§ÂçèËÆÆÂíåÂüüÂêç
    let path = url.replace(/^https?:\/\/[^/]+/, '');

    // ÁßªÈô§Êü•ËØ¢ÂèÇÊï∞ÂíåÈîöÁÇπ
    const pathWithoutQuery = path.split('?')[ZERO] || '';
    path = pathWithoutQuery.split('#')[ZERO] || '';

    // Ê£ÄÊµãËØ≠Ë®Ä
    let locale: Locale = this.defaultLocale;
    let cleanPath = path;

    // Ê£ÄÊü•ÊòØÂê¶ÊúâËØ≠Ë®ÄÂâçÁºÄ
    const localeMatch = path.match(/^\/([a-z]{2})(?=\/|$)/);
    if (localeMatch && this.locales.includes(localeMatch[ONE] as Locale)) {
      locale = localeMatch[ONE] as Locale;
      cleanPath = path.replace(/^\/[a-z]{2}/, '') || '/';
    }

    // Êü•ÊâæÈ°µÈù¢Á±ªÂûã
    let pageType: PageType | null = null;

    if (cleanPath === '/' || cleanPath === '') {
      pageType = 'home';
    } else {
      // Êü•ÊâæÂåπÈÖçÁöÑÈ°µÈù¢Á±ªÂûã
      for (const [type, paths] of Object.entries(PATHS_CONFIG)) {
        if (
          Object.prototype.hasOwnProperty.call(paths, locale) &&
          paths[locale] === cleanPath
        ) {
          pageType = type as PageType;
          break;
        }
      }
    }

    return {
      pageType,
      locale,
      isValid: pageType !== null,
    };
  }

  /**
   * È™åËØÅURLÊòØÂê¶ÊúâÊïà
   */
  isValidURL(url: string): boolean {
    const { isValid } = this.parseURLToPageInfo(url);
    return isValid;
  }

  /**
   * Ëé∑ÂèñÂü∫Á°ÄURL
   */
  getBaseURL(): string {
    return this.baseUrl;
  }

  /**
   * Ëé∑ÂèñÊîØÊåÅÁöÑËØ≠Ë®ÄÂàóË°®
   */
  getSupportedLocales(): readonly Locale[] {
    return this.locales;
  }

  /**
   * Ëé∑ÂèñÈªòËÆ§ËØ≠Ë®Ä
   */
  getDefaultLocale(): Locale {
    return this.defaultLocale;
  }
}

