/**
 * ÂÖ±‰∫´Â∑•ÂÖ∑ÂáΩÊï∞Â∫ì
 * Êèê‰æõÈ°πÁõÆ‰∏≠Â∏∏Áî®ÁöÑÂ∑•ÂÖ∑ÂáΩÊï∞ÔºåÂåÖÊã¨Êó•ÊúüÊ†ºÂºèÂåñ„ÄÅÈÇÆÁÆ±È™åËØÅÁ≠â
 */

import { ZERO } from "@/constants/magic-numbers";
import { EMAIL_VALIDATION } from '@/constants/react-scan';

/**
 * Ê†ºÂºèÂåñÊó•Êúü‰∏∫ISOÂ≠óÁ¨¶‰∏≤Ê†ºÂºè (YYYY-MM-DD)
 * @param date - Ë¶ÅÊ†ºÂºèÂåñÁöÑÊó•ÊúüÂØπË±°
 * @returns ISOÊ†ºÂºèÁöÑÊó•ÊúüÂ≠óÁ¨¶‰∏≤
 * @example
 * ```typescript
 * const today = new Date();
 * const formatted = formatDate(today); // "2025-07-29"
 * ```
 */
export function formatDate(date: Date): string {
  return date.toISOString().split('T')[ZERO]!;
}

/**
 * È™åËØÅÈÇÆÁÆ±Âú∞ÂùÄÊ†ºÂºèÊòØÂê¶Ê≠£Á°Æ
 * ‰ΩøÁî®Ê†áÂáÜÁöÑÈÇÆÁÆ±Ê≠£ÂàôË°®ËææÂºèËøõË°åÈ™åËØÅ
 * @param email - Ë¶ÅÈ™åËØÅÁöÑÈÇÆÁÆ±Âú∞ÂùÄ
 * @returns ÊòØÂê¶‰∏∫ÊúâÊïàÈÇÆÁÆ±Ê†ºÂºè
 * @example
 * ```typescript
 * validateEmail("user@example.com"); // true
 * validateEmail("invalid-email");    // false
 * validateEmail("test@domain.co.uk"); // true
 * ```
 */
/**
 * È™åËØÅÈÇÆÁÆ±Âü∫Êú¨Ê†ºÂºè
 */
function validateEmailBasicFormat(email: string): boolean {
  if (!email || typeof email !== 'string') {
    return false;
  }

  // Ê£ÄÊü•ÊòØÂê¶ÂåÖÂê´Á©∫Ê†ºÊàñËøûÁª≠ÁÇπÂè∑
  return !(email.includes(' ') || email.includes('..'));
}

/**
 * È™åËØÅÈÇÆÁÆ±Êú¨Âú∞ÈÉ®ÂàÜÔºà@ÂâçÈù¢Ôºâ
 */
function validateLocalPart(localPart: string): boolean {
  if (
    !localPart ||
    localPart.length === ZERO ||
    localPart.length > EMAIL_VALIDATION.LOCAL_PART_MAX_LENGTH
  ) {
    return false;
  }

  // Ê£ÄÊü•‰∏çËÉΩ‰ª•ÁÇπÂè∑ÂºÄÂ§¥ÊàñÁªìÂ∞æ
  if (localPart.startsWith('.') || localPart.endsWith('.')) {
    return false;
  }

  // Âü∫Êú¨Â≠óÁ¨¶Ê£ÄÊü•
  const validLocalChars = /^[a-zA-Z0-9._+-]+$/;
  return validLocalChars.test(localPart);
}

/**
 * È™åËØÅÈÇÆÁÆ±ÂüüÂêçÈÉ®ÂàÜÔºà@ÂêéÈù¢Ôºâ
 */
function validateDomainPart(domainPart: string): boolean {
  if (
    !domainPart ||
    domainPart.length === ZERO ||
    domainPart.length > EMAIL_VALIDATION.DOMAIN_PART_MAX_LENGTH
  ) {
    return false;
  }

  // Ê£ÄÊü•‰∏çËÉΩ‰ª•ÁÇπÂè∑ÂºÄÂ§¥ÊàñÁªìÂ∞æÔºå‰∏îÂøÖÈ°ªÂåÖÂê´ÁÇπÂè∑
  if (
    domainPart.startsWith('.') ||
    domainPart.endsWith('.') ||
    !domainPart.includes('.')
  ) {
    return false;
  }

  // Âü∫Êú¨Â≠óÁ¨¶Ê£ÄÊü•
  const validDomainChars = /^[a-zA-Z0-9.-]+$/;
  return validDomainChars.test(domainPart);
}

export function validateEmail(email: string): boolean {
  // Âü∫Êú¨Ê†ºÂºèÊ£ÄÊü•
  if (!validateEmailBasicFormat(email)) {
    return false;
  }

  // ÂàÜÂâ≤ÈÇÆÁÆ±Âú∞ÂùÄ
  const parts = email.split('@');
  if (parts.length !== EMAIL_VALIDATION.EMAIL_PARTS_COUNT) {
    return false;
  }

  const [localPart, domainPart] = parts;

  // È™åËØÅÊú¨Âú∞ÈÉ®ÂàÜÂíåÂüüÂêçÈÉ®ÂàÜ
  return (
    validateLocalPart(localPart || '') && validateDomainPart(domainPart || '')
  );
}

