  return Boolean(
    response &&
      typeof response === 'object' &&
      (response as Record<string, unknown>).messaging_product === 'whatsapp' &&
      Array.isArray((response as Record<string, unknown>).contacts) &&
      Array.isArray((response as Record<string, unknown>).messages),
  );
}

