#!/usr/bin/env tsx
import { writeFileSync } from 'node:fs';
import { resolve } from 'node:path';
import { ExportAssignment, Project, ts, VariableDeclaration } from 'ts-morph';

interface ConstantInfo {
  export: string;
  module: string;
  value: number;
  filePath: string;
  line: number;
}

// 
// 
// - src/constants/**/* .ts
// - src/config/**/* .ts
// 
async function collectExistingConstants() {
  console.log(' ...');

  // 
  const project = new Project({
    tsConfigFilePath: resolve(process.cwd(), 'tsconfig.json'),
  });

  // 
  const targetPatterns = ['src/constants/**/*.ts', 'src/config/**/*.ts'];

  // 
  for (const pattern of targetPatterns) {
    project.addSourceFilesAtPaths(pattern);
  }

  const sourceFiles = project.getSourceFiles();

  console.log(`  ${sourceFiles.length} ...`);

  const constants: ConstantInfo[] = [];
  const valueToConstants = new Map<number, ConstantInfo[]>();

  for (const sourceFile of sourceFiles) {
    const filePath = sourceFile.getFilePath().replace(`${process.cwd()}/`, '');
    const modulePath = filePath.replace(/^src\//, '@/').replace(/\.ts$/, '');

    console.log(` : ${filePath}`);

    // 
    const exportedDeclarations = sourceFile.getExportedDeclarations();

    for (const [exportName, declarations] of exportedDeclarations) {
      for (const declaration of declarations) {
        if (declaration.getKind() === ts.SyntaxKind.VariableDeclaration) {
          const varDecl = declaration as VariableDeclaration;
          const initializer = varDecl.getInitializer();

          if (initializer && ts.isNumericLiteral(initializer.compilerNode)) {
            const value = parseFloat(initializer.getText());
            const line = varDecl.getStartLineNumber();

            const constantInfo: ConstantInfo = {
              export: exportName,
              module: modulePath,
              value,
              filePath,
              line,
            };

            constants.push(constantInfo);

            if (!valueToConstants.has(value)) {
              valueToConstants.set(value, []);
            }
            valueToConstants.get(value)!.push(constantInfo);

            console.log(`   : ${exportName} = ${value} ( ${line})`);
          }
        }
      }
    }

    // 
    sourceFile.forEachDescendant((node) => {
      if (ts.isPropertyAssignment(node.compilerNode)) {
        const propAssignment = node;
        const initializer = propAssignment.getInitializer();

        if (initializer && ts.isNumericLiteral(initializer.compilerNode)) {
          const value = parseFloat(initializer.getText());
          const propertyName = propAssignment.getName();

          // 
          let parent = propAssignment.getParent();
          while (parent && !ts.isVariableDeclaration(parent.compilerNode)) {
            parent = parent.getParent();
          }

          if (parent && ts.isVariableDeclaration(parent.compilerNode)) {
            const varDecl = parent as VariableDeclaration;
            const varName = varDecl.getName();

            // 
            const exportedDeclarations = sourceFile.getExportedDeclarations();
            if (exportedDeclarations.has(varName)) {
              const line = propAssignment.getStartLineNumber();
              const exportName = `${varName}.${propertyName}`;

              const constantInfo: ConstantInfo = {
                export: exportName,
                module: modulePath,
                value,
                filePath,
                line,
              };

              constants.push(constantInfo);

              if (!valueToConstants.has(value)) {
                valueToConstants.set(value, []);
              }
              valueToConstants.get(value)!.push(constantInfo);

              console.log(
                `   : ${exportName} = ${value} ( ${line})`,
              );
            }
          }
        }
      }
    });
  }

  console.log(`\n :  ${constants.length} `);

  // 
  const candidateMapping: Record<string, any> = {};
  const conflicts: Record<string, ConstantInfo[]> = {};

  for (const [value, constantInfos] of valueToConstants) {
    const valueStr = value.toString();

    if (constantInfos.length === 1) {
      // 
      const info = constantInfos[0];
      candidateMapping[valueStr] = {
        export: info.export,
        module: info.module,
        source: `${info.filePath}:${info.line}`,
      };
    } else {
      // 
      conflicts[valueStr] = constantInfos;

      // 
      const prioritized = prioritizeConstant(constantInfos);
      candidateMapping[valueStr] = {
        export: prioritized.export,
        module: prioritized.module,
        source: `${prioritized.filePath}:${prioritized.line}`,
        alternatives: constantInfos
          .filter((c) => c !== prioritized)
          .map((c) => ({
            export: c.export,
            module: c.module,
            source: `${c.filePath}:${c.line}`,
          })),
      };
    }
  }

  // 
  const outputPath = 'scripts/magic-numbers/existing-constants-analysis.json';
  const result = {
    _comment: '',
    _generated: new Date().toISOString(),
    _stats: {
      totalConstants: constants.length,
      uniqueValues: valueToConstants.size,
      conflicts: Object.keys(conflicts).length,
    },
    candidateMapping,
    conflicts,
    allConstants: constants,
  };

  writeFileSync(outputPath, JSON.stringify(result, null, 2));
  console.log(`\n : ${outputPath}`);

  // 
  if (Object.keys(conflicts).length > 0) {
    console.log('\n  :');
    for (const [value, infos] of Object.entries(conflicts)) {
      console.log(`  ${value}: ${infos.length} `);
      infos.forEach((info) => {
        console.log(`    - ${info.export} (${info.module})`);
      });
    }
  }

  return result;
}

/**
 * 
 *  >  > magic-numbers.ts 
 */
function prioritizeConstant(constants: ConstantInfo[]): ConstantInfo {
  // 
  const priorityOrder = [
    // 
    /constants\/app-constants/,
    /config\/security/,
    /constants\/performance/,

    // 
    /constants\//,
    /config\//,

    // 
    /magic-numbers/,
  ];

  for (const pattern of priorityOrder) {
    const match = constants.find((c) => pattern.test(c.filePath));
    if (match) return match;
  }

  // 
  return constants[0];
}

// 
if (require.main === module) {
  collectExistingConstants().catch((error) => {
    console.error(' :', error);
    process.exit(1);
  });
}

export { collectExistingConstants, type ConstantInfo };

#!/usr/bin/env tsx
import { readFileSync, writeFileSync } from 'node:fs';
import { resolve } from 'node:path';

/**
 * 
 */
function generateConstantName(num: string): string {
  // 
  if (num.includes('e') || num.includes('E')) {
    return `SCIENTIFIC_${num
      .replace(/[e\-\.]/gi, '_')
      .replace(/\+/g, 'PLUS_')
      .toUpperCase()}`;
  }

  // 
  if (num.startsWith('-')) {
    return `NEGATIVE_${generateConstantName(num.slice(1))}`;
  }

  // 
  if (num.includes('.')) {
    const parts = num.split('.');
    const intPart = parts[0] || '0';
    const decPart = parts[1] || '0';

    // 
    const specialDecimals: Record<string, string> = {
      '0.1': 'DECIMAL_TENTH',
      '0.2': 'DECIMAL_TWENTY_PERCENT',
      '0.25': 'DECIMAL_QUARTER',
      '0.3': 'DECIMAL_THIRTY_PERCENT',
      '0.4': 'DECIMAL_FORTY_PERCENT',
      '0.5': 'DECIMAL_HALF',
      '0.6': 'DECIMAL_SIXTY_PERCENT',
      '0.7': 'DECIMAL_SEVENTY_PERCENT',
      '0.75': 'DECIMAL_THREE_QUARTERS',
      '0.8': 'DECIMAL_EIGHTY_PERCENT',
      '0.9': 'DECIMAL_NINETY_PERCENT',
      '0.95': 'DECIMAL_NINETY_FIVE_PERCENT',
      '0.99': 'DECIMAL_NINETY_NINE_PERCENT',
    };

    if (specialDecimals[num]) {
      return specialDecimals[num];
    }

    // 
    if (decPart.length > 6) {
      return `TEST_DECIMAL_${intPart}_${decPart.slice(0, 6)}`;
    }

    return `DECIMAL_${intPart}_${decPart}`;
  }

  // 
  const numValue = parseInt(num);

  // 
  const specialNumbers: Record<string, string> = {
    '0': 'ZERO',
    '1': 'ONE',
    '2': 'TWO',
    '3': 'THREE',
    '4': 'FOUR',
    '5': 'FIVE',
    '6': 'SIX',
    '7': 'SEVEN',
    '8': 'EIGHT',
    '9': 'NINE',
    '10': 'TEN',
    '42': 'ANSWER_TO_EVERYTHING',
    '100': 'ONE_HUNDRED',
    '200': 'HTTP_OK',
    '400': 'HTTP_BAD_REQUEST',
    '401': 'HTTP_UNAUTHORIZED',
    '403': 'HTTP_FORBIDDEN',
    '404': 'HTTP_NOT_FOUND',
    '500': 'HTTP_INTERNAL_SERVER_ERROR',
    '1000': 'ONE_THOUSAND',
    '1024': 'BYTES_PER_KB',
  };

  if (specialNumbers[num]) {
    return specialNumbers[num];
  }

  // HTTP
  if (numValue >= 100 && numValue < 600) {
    return `HTTP_${num}`;
  }

  // 
  if (
    (numValue >= 1000 && numValue <= 65535 && num.endsWith('80')) ||
    num.endsWith('00')
  ) {
    return `PORT_${num}`;
  }

  // 
  if (numValue === 60) return 'SECONDS_PER_MINUTE';
  if (numValue === 3600) return 'SECONDS_PER_HOUR';
  if (numValue === 86400) return 'SECONDS_PER_DAY';
  if (numValue === 86400000) return 'MILLISECONDS_PER_DAY';
  if (numValue === 3600000) return 'MILLISECONDS_PER_HOUR';
  if (numValue === 60000) return 'MILLISECONDS_PER_MINUTE';

  // 
  if (numValue === 320) return 'MOBILE_WIDTH_SMALL';
  if (numValue === 375) return 'MOBILE_WIDTH_MEDIUM';
  if (numValue === 640) return 'TABLET_WIDTH_SMALL';
  if (numValue === 768) return 'TABLET_WIDTH';
  if (numValue === 1024) return 'DESKTOP_WIDTH_SMALL';
  if (numValue === 1280) return 'DESKTOP_WIDTH_MEDIUM';
  if (numValue === 1920) return 'DESKTOP_WIDTH_LARGE';

  // 
  if (numValue === 1048576) return 'BYTES_PER_MB';
  if (numValue === 1073741824) return 'BYTES_PER_GB';

  // 
  if (numValue >= 1000000000)
    return `BILLION_${Math.floor(numValue / 1000000000)}`;
  if (numValue >= 1000000) return `MILLION_${Math.floor(numValue / 1000000)}`;
  if (numValue >= 1000) return `THOUSAND_${Math.floor(numValue / 1000)}`;

  // 
  return `NUMBER_${num.replace(/\./g, '_')}`;
}

/**
 * 
 */
function extractMissingConstants(): string[] {
  try {
    // 
    const { execSync } = require('child_process');
    const output = execSync('tsx scripts/magic-numbers/preflight.ts', {
      encoding: 'utf8',
      cwd: process.cwd(),
    });

    // 
    return [];
  } catch (error: any) {
    const output = error.stdout || error.message || '';

    // 
    const lines = output.split('\n');
    const numbers: string[] = [];

    let inMissingSection = false;
    for (const line of lines) {
      if (line.includes('')) {
        inMissingSection = true;
        continue;
      }

      if (inMissingSection && line.includes(' ')) {
        break;
      }

      if (inMissingSection && line.includes('"') && line.includes(':')) {
        const match = line.match(/"([^"]+)":/);
        if (match) {
          numbers.push(match[1]);
        }
      }
    }

    return numbers;
  }
}

/**
 * 
 */
async function generateConstants() {
  console.log(' ...');

  const missingNumbers = extractMissingConstants();
  console.log(`  ${missingNumbers.length} `);

  if (missingNumbers.length === 0) {
    console.log(' ');
    return;
  }

  // 
  const mappingPath = resolve(__dirname, 'mapping.json');
  let existingMapping: Record<string, string> = {};

  try {
    existingMapping = JSON.parse(readFileSync(mappingPath, 'utf-8'));
  } catch (error) {
    console.log(' ');
  }

  // 
  const newMapping = { ...existingMapping };
  const newConstants: Array<{ name: string; value: string }> = [];

  for (const num of missingNumbers) {
    if (!newMapping[num]) {
      const constantName = generateConstantName(num);
      newMapping[num] = constantName;
      newConstants.push({ name: constantName, value: num });
    }
  }

  // 
  writeFileSync(mappingPath, JSON.stringify(newMapping, null, 2));
  console.log(` : ${Object.keys(newMapping).length} `);

  // 
  const constantsPath = resolve(
    process.cwd(),
    'src/constants/magic-numbers.ts',
  );
  let constantsContent = '';

  try {
    constantsContent = readFileSync(constantsPath, 'utf-8');
  } catch (error) {
    constantsContent =
      '// \n// \n\n';
  }

  // 
  if (newConstants.length > 0) {
    constantsContent += '\n// \n';

    for (const { name, value } of newConstants) {
      constantsContent += `export const ${name} = ${value};\n`;
    }

    writeFileSync(constantsPath, constantsContent);
    console.log(` :  ${newConstants.length} `);
  }

  console.log(' ');
}

// 
if (require.main === module) {
  generateConstants().catch(console.error);
}

#!/usr/bin/env tsx
import { readFileSync, writeFileSync } from 'node:fs';
import { resolve } from 'node:path';

interface ConstantInfo {
  export: string;
  module: string;
  value: number;
  filePath: string;
  line: number;
}

interface MappingEntry {
  export: string;
  module: string;
  source: string;
  alternatives?: Array<{
    export: string;
    module: string;
    source: string;
  }>;
}

/**
 * CODEX
 * 
 */
async function generateEnhancedMapping() {
  console.log(' CODEX...');

  // 
  const analysisPath = 'scripts/magic-numbers/existing-constants-analysis.json';
  const analysisData = JSON.parse(readFileSync(analysisPath, 'utf-8'));

  // CODEX
  const originalMappingPath = 'scripts/magic-numbers/codex-mapping.json';
  const originalMapping = JSON.parse(
    readFileSync(originalMappingPath, 'utf-8'),
  );

  console.log(
    ` CODEX: ${Object.keys(originalMapping).filter((k) => !k.startsWith('_')).length} `,
  );
  console.log(
    ` : ${analysisData._stats.uniqueValues} `,
  );

  // 
  const missingNumbers = [
    '14',
    '17',
    '18',
    '22',
    '23',
    '35',
    '36',
    '64',
    '82',
    '83',
    '84',
    '87',
    '88',
    '130',
    '131',
    '132',
    '133',
    '136',
    '184.704',
    '254',
    '255',
    '368',
    '450',
    '600',
    '890',
    '1005',
    '1010',
    '1020',
    '1080',
    '1180',
    '1250',
    '1300',
    '1400',
    '1412',
    '1450',
    '1900',
    '2200',
    '2800',
    '200100',
  ];

  // 
  const enhancedMapping: Record<string, any> = {
    _comment: 'CODEX',
    _description: '',
    _optimization: '301',
    _updated: new Date().toISOString(),
    _stats: {
      originalCodexConstants: Object.keys(originalMapping).filter(
        (k) => !k.startsWith('_'),
      ).length,
      existingConstants: analysisData._stats.totalConstants,
      uniqueValues: analysisData._stats.uniqueValues,
      missingNumbersFound: 0,
    },
  };

  // 1. CODEX
  for (const [value, constantName] of Object.entries(originalMapping)) {
    if (!value.startsWith('_')) {
      enhancedMapping[value] = {
        export: constantName,
        module: '@/constants/magic-numbers',
        source: 'CODEX',
        type: 'codex-core',
      };
    }
  }

  // 2. 
  let foundCount = 0;
  for (const missingNumber of missingNumbers) {
    const candidateMapping = analysisData.candidateMapping[missingNumber];
    if (candidateMapping) {
      enhancedMapping[missingNumber] = {
        export: candidateMapping.export,
        module: candidateMapping.module,
        source: candidateMapping.source,
        type: 'existing-constant',
        alternatives: candidateMapping.alternatives,
      };
      foundCount++;
      console.log(
        `    ${missingNumber}: ${candidateMapping.export}`,
      );
    } else {
      console.log(`    ${missingNumber} `);
    }
  }

  enhancedMapping._stats.missingNumbersFound = foundCount;

  // 3. 
  const highFrequencyNumbers = ['100', '200', '300', '400', '500', '1000'];
  for (const number of highFrequencyNumbers) {
    if (!enhancedMapping[number] && analysisData.candidateMapping[number]) {
      const candidate = analysisData.candidateMapping[number];
      enhancedMapping[number] = {
        export: candidate.export,
        module: candidate.module,
        source: candidate.source,
        type: 'high-frequency',
        alternatives: candidate.alternatives,
      };
      console.log(`    ${number}: ${candidate.export}`);
    }
  }

  // 
  const outputPath = 'scripts/magic-numbers/enhanced-codex-mapping.json';
  writeFileSync(outputPath, JSON.stringify(enhancedMapping, null, 2));

  console.log(`\n : ${outputPath}`);
  console.log(
    ` : ${Object.keys(enhancedMapping).filter((k) => !k.startsWith('_')).length} `,
  );
  console.log(
    ` : ${foundCount}/${missingNumbers.length} (${Math.round((foundCount / missingNumbers.length) * 100)}%)`,
  );

  // 
  const importSuggestions = generateImportSuggestions(enhancedMapping);
  const suggestionsPath =
    'scripts/magic-numbers/import-optimization-suggestions.json';
  writeFileSync(suggestionsPath, JSON.stringify(importSuggestions, null, 2));
  console.log(` : ${suggestionsPath}`);

  return enhancedMapping;
}

/**
 * 
 */
function generateImportSuggestions(mapping: Record<string, any>) {
  const moduleUsage = new Map<string, string[]>();

  for (const [value, info] of Object.entries(mapping)) {
    if (!value.startsWith('_') && info.module) {
      if (!moduleUsage.has(info.module)) {
        moduleUsage.set(info.module, []);
      }
      moduleUsage.get(info.module)!.push(`${info.export} (${value})`);
    }
  }

  const suggestions = {
    _comment: '',
    _description: '',
    _generated: new Date().toISOString(),
    moduleUsage: Object.fromEntries(moduleUsage),
    importStatements: Array.from(moduleUsage.entries()).map(
      ([module, constants]) => ({
        module,
        constantCount: constants.length,
        suggestedImport: `import { ${constants.map((c) => c.split(' ')[0]).join(', ')} } from '${module}';`,
      }),
    ),
  };

  return suggestions;
}

// 
if (require.main === module) {
  generateEnhancedMapping().catch((error) => {
    console.error(' :', error);
    process.exit(1);
  });
}

export { generateEnhancedMapping };

#!/usr/bin/env tsx
import { readFileSync, writeFileSync } from 'node:fs';
import { resolve } from 'node:path';

/**
 * 
 * 
 */

interface NumberCategory {
  name: string;
  description: string;
  pattern: (num: number, str: string) => boolean;
  generateName: (num: number, str: string) => string;
  priority: number; // 
}

// 
const categories: NumberCategory[] = [
  // HTTP
  {
    name: 'HTTP_STATUS',
    description: 'HTTP',
    pattern: (num) =>
      [
        200, 201, 204, 300, 301, 302, 400, 401, 403, 404, 415, 429, 500, 503,
      ].includes(num),
    generateName: (num) => `HTTP_${num}`,
    priority: 1,
  },

  //  - 
  {
    name: 'TIME_MS',
    description: '',
    pattern: (num) => {
      // 1000, 2000, 3000, 5000, 10000, 30000, 60000
      return num >= 1000 && num <= 86400000 && num % 1000 === 0;
    },
    generateName: (num) => {
      if (num === 1000) return 'SECOND_MS';
      if (num === 2000) return 'TWO_SECONDS_MS';
      if (num === 3000) return 'THREE_SECONDS_MS';
      if (num === 5000) return 'FIVE_SECONDS_MS';
      if (num === 10000) return 'TEN_SECONDS_MS';
      if (num === 30000) return 'THIRTY_SECONDS_MS';
      if (num === 60000) return 'MINUTE_MS';
      if (num === 3600000) return 'HOUR_MS';
      if (num === 86400000) return 'DAY_MS';
      return `TIME_${num}_MS`;
    },
    priority: 2,
  },

  // 
  {
    name: 'BREAKPOINT',
    description: '',
    pattern: (num) =>
      [
        320, 375, 480, 640, 667, 720, 768, 800, 1024, 1080, 1200, 1280, 1536,
        1600, 1920,
      ].includes(num),
    generateName: (num) => {
      const breakpoints: Record<number, string> = {
        320: 'BREAKPOINT_MOBILE_SMALL',
        375: 'BREAKPOINT_MOBILE',
        480: 'BREAKPOINT_MOBILE_LARGE',
        640: 'BREAKPOINT_SM',
        667: 'BREAKPOINT_MOBILE_LANDSCAPE',
        720: 'BREAKPOINT_HD_HEIGHT',
        768: 'BREAKPOINT_MD',
        800: 'BREAKPOINT_TABLET_SMALL',
        1024: 'BREAKPOINT_LG',
        1080: 'BREAKPOINT_HD_HEIGHT_FULL',
        1200: 'BREAKPOINT_DESKTOP',
        1280: 'BREAKPOINT_XL',
        1536: 'BREAKPOINT_2XL',
        1600: 'BREAKPOINT_DESKTOP_LARGE',
        1920: 'BREAKPOINT_FULL_HD',
      };
      return breakpoints[num] || `BREAKPOINT_${num}`;
    },
    priority: 3,
  },

  // 0-1
  {
    name: 'PERCENTAGE_DECIMAL',
    description: '',
    pattern: (num) => num > 0 && num < 1 && num.toString().length <= 5,
    generateName: (num) => {
      const percentageMap: Record<string, string> = {
        '0.01': 'PERCENT_1',
        '0.02': 'PERCENT_2',
        '0.03': 'PERCENT_3',
        '0.05': 'PERCENT_5',
        '0.08': 'PERCENT_8',
        '0.1': 'PERCENT_10',
        '0.12': 'PERCENT_12',
        '0.15': 'PERCENT_15',
        '0.2': 'PERCENT_20',
        '0.25': 'PERCENT_25',
        '0.3': 'PERCENT_30',
        '0.4': 'PERCENT_40',
        '0.45': 'PERCENT_45',
        '0.5': 'PERCENT_50',
        '0.55': 'PERCENT_55',
        '0.6': 'PERCENT_60',
        '0.65': 'PERCENT_65',
        '0.7': 'PERCENT_70',
        '0.75': 'PERCENT_75',
        '0.8': 'PERCENT_80',
        '0.85': 'PERCENT_85',
        '0.9': 'PERCENT_90',
        '0.94': 'PERCENT_94',
        '0.95': 'PERCENT_95',
        '0.96': 'PERCENT_96',
        '0.97': 'PERCENT_97',
        '0.99': 'PERCENT_99',
      };
      return (
        percentageMap[num.toString()] || `OPACITY_${Math.round(num * 100)}`
      );
    },
    priority: 4,
  },

  // 
  {
    name: 'ANIMATION_DURATION',
    description: '',
    pattern: (num) =>
      [
        150, 200, 250, 300, 400, 500, 600, 700, 750, 800, 900, 1000, 1200, 1250,
        1500, 2000,
      ].includes(num),
    generateName: (num) => `ANIMATION_DURATION_${num}`,
    priority: 5,
  },

  // 
  {
    name: 'MEMORY_SIZE',
    description: '',
    pattern: (num) => {
      // 2
      return [
        256, 512, 1024, 2048, 4096, 8192, 16384, 32768, 65536, 131072, 262144,
        524288, 1048576, 2097152, 4194304,
      ].includes(num);
    },
    generateName: (num) => {
      const sizeMap: Record<number, string> = {
        256: 'BYTES_256',
        512: 'BYTES_512',
        1024: 'BYTES_1KB',
        2048: 'BYTES_2KB',
        4096: 'BYTES_4KB',
        8192: 'BYTES_8KB',
        16384: 'BYTES_16KB',
        32768: 'BYTES_32KB',
        65536: 'BYTES_64KB',
        131072: 'BYTES_128KB',
        262144: 'BYTES_256KB',
        524288: 'BYTES_512KB',
        1048576: 'BYTES_1MB',
        2097152: 'BYTES_2MB',
        4194304: 'BYTES_4MB',
      };
      return sizeMap[num] || `MEMORY_SIZE_${num}`;
    },
    priority: 6,
  },

  // 
  {
    name: 'YEAR',
    description: '',
    pattern: (num) => num >= 2020 && num <= 2030 && Number.isInteger(num),
    generateName: (num) => `YEAR_${num}`,
    priority: 7,
  },

  // 
  {
    name: 'ANGLE',
    description: '',
    pattern: (num) =>
      [0, 45, 90, 180, 270, 360].includes(num) || (num > 359 && num < 361),
    generateName: (num) => {
      if (num === 0) return 'ANGLE_ZERO';
      if (num === 45) return 'ANGLE_45_DEG';
      if (num === 90) return 'ANGLE_90_DEG';
      if (num === 180) return 'ANGLE_180_DEG';
      if (num === 270) return 'ANGLE_270_DEG';
      if (num === 360 || (num > 359 && num < 361)) return 'ANGLE_360_DEG';
      return `ANGLE_${num.toString().replace('.', '_')}_DEG`;
    },
    priority: 8,
  },

  // 
  {
    name: 'COORDINATE',
    description: '',
    pattern: (num, str) => {
      //  -90  90 -180  180
      return (
        (num >= -90 && num <= 90 && str.includes('.')) ||
        (num >= -180 && num <= 180 && str.includes('.') && Math.abs(num) > 90)
      );
    },
    generateName: (num, str) => {
      // 
      if (Math.abs(num - 39.9042) < 0.001) return 'COORD_BEIJING_LAT';
      if (Math.abs(num - 116.4074) < 0.001) return 'COORD_BEIJING_LNG';
      if (Math.abs(num - 40.7128) < 0.001) return 'COORD_NYC_LAT';
      if (Math.abs(num - 74.006) < 0.001) return 'COORD_NYC_LNG';
      return `COORDINATE_${str.replace('.', '_').replace('-', 'NEG_')}`;
    },
    priority: 9,
  },

  //  - 
  {
    name: 'TEST_PRECISION',
    description: '',
    pattern: (num, str) => {
      // 
      return str.includes('.') && str.length > 10;
    },
    generateName: (num, str) => `TEST_PRECISION_${str.replace('.', '_')}`,
    priority: 10,
  },

  // 
  {
    name: 'PORT',
    description: '',
    pattern: (num) => num >= 3000 && num <= 9000 && Number.isInteger(num),
    generateName: (num) => `PORT_${num}`,
    priority: 11,
  },

  // 
  {
    name: 'NUMERIC',
    description: '',
    pattern: () => true, // 
    generateName: (num, str) => {
      if (Number.isInteger(num)) {
        return `NUMBER_${num}`;
      }
      return `DECIMAL_${str.replace('.', '_').replace('-', 'NEG_')}`;
    },
    priority: 99,
  },
];

/**
 * 
 */
function analyzeNumber(numStr: string): {
  category: string;
  constantName: string;
  description: string;
} {
  const num = parseFloat(numStr);

  // 
  const sortedCategories = categories.sort((a, b) => a.priority - b.priority);

  for (const category of sortedCategories) {
    if (category.pattern(num, numStr)) {
      return {
        category: category.name,
        constantName: category.generateName(num, numStr),
        description: category.description,
      };
    }
  }

  // 
  return {
    category: 'UNKNOWN',
    constantName: `UNKNOWN_${numStr.replace('.', '_')}`,
    description: '',
  };
}

/**
 * 
 */
function extractNumbersFromPreflight(): string[] {
  try {
    const { execSync } = require('child_process');
    const output = execSync('tsx scripts/magic-numbers/preflight.ts', {
      encoding: 'utf8',
      cwd: process.cwd(),
    });

    // 
    const lines = output.split('\n');
    const numbers: string[] = [];
    let inNumberSection = false;

    for (const line of lines) {
      if (line.includes('')) {
        inNumberSection = true;
        continue;
      }

      if (inNumberSection && line.includes(' :')) {
        break;
      }

      if (inNumberSection && line.trim().startsWith('"')) {
        //   "0.123": "MAGIC_0_123",
        const match = line.match(/"([^"]+)":/);
        if (match) {
          numbers.push(match[1]);
        }
      }
    }

    return numbers;
  } catch (error) {
    // 
    const errorOutput = error.stderr || error.stdout || '';
    const lines = errorOutput.split('\n');
    const numbers: string[] = [];
    let inNumberSection = false;

    for (const line of lines) {
      if (line.includes('')) {
        inNumberSection = true;
        continue;
      }

      if (inNumberSection && line.includes(' :')) {
        break;
      }

      if (inNumberSection && line.trim().startsWith('"')) {
        //   "0.123": "MAGIC_0_123",
        const match = line.match(/"([^"]+)":/);
        if (match) {
          numbers.push(match[1]);
        }
      }
    }

    if (numbers.length === 0) {
      console.error('Failed to extract numbers from preflight:', error.message);
    }

    return numbers;
  }
}

/**
 * 
 */
async function main() {
  console.log(' ...');

  // 
  const numbers = extractNumbersFromPreflight();
  console.log(`  ${numbers.length} `);

  if (numbers.length === 0) {
    console.log(' ');
    return;
  }

  // 
  const analysis: Record<string, any> = {};
  const categoryStats: Record<string, number> = {};

  for (const numStr of numbers) {
    const result = analyzeNumber(numStr);
    analysis[numStr] = result;

    categoryStats[result.category] = (categoryStats[result.category] || 0) + 1;
  }

  // 
  const report = {
    timestamp: new Date().toISOString(),
    summary: {
      totalNumbers: numbers.length,
      categoriesFound: Object.keys(categoryStats).length,
      categoryStats,
    },
    analysis,
    recommendations: generateRecommendations(categoryStats),
  };

  // 
  const reportPath = resolve(__dirname, 'semantic-analysis-report.json');
  writeFileSync(reportPath, JSON.stringify(report, null, 2));

  console.log(' ');
  console.log('');
  console.log(' :');
  Object.entries(categoryStats)
    .sort(([, a], [, b]) => b - a)
    .forEach(([category, count]) => {
      console.log(`  ${category}: ${count} `);
    });

  console.log('');
  console.log(` : ${reportPath}`);

  return report;
}

/**
 * 
 */
function generateRecommendations(stats: Record<string, number>): string[] {
  const recommendations: string[] = [];

  if (stats.TEST_PRECISION > 10) {
    recommendations.push('');
  }

  if (stats.NUMERIC > 50) {
    recommendations.push('');
  }

  if (stats.BREAKPOINT > 5) {
    recommendations.push('');
  }

  return recommendations;
}

// 
if (require.main === module) {
  main().catch((error) => {
    console.error(' :', error);
    process.exit(1);
  });
}

export { analyzeNumber, categories };

#!/usr/bin/env tsx
import { readdirSync, readFileSync, statSync, writeFileSync } from 'node:fs';
import { join, resolve } from 'node:path';

interface ConstantInfo {
  export: string;
  module: string;
  value: number;
  filePath: string;
  line: number;
}

/**
 * 
 * 
 */
async function collectConstants() {
  console.log(' ...');

  const constants: ConstantInfo[] = [];
  const targetDirs = ['src/constants', 'src/config'];

  for (const dir of targetDirs) {
    const fullPath = resolve(process.cwd(), dir);
    try {
      await scanDirectory(fullPath, constants);
    } catch (error) {
      console.log(`   ${dir}: ${error}`);
    }
  }

  console.log(` :  ${constants.length} `);

  // 
  const valueToConstants = new Map<number, ConstantInfo[]>();
  for (const constant of constants) {
    if (!valueToConstants.has(constant.value)) {
      valueToConstants.set(constant.value, []);
    }
    valueToConstants.get(constant.value)!.push(constant);
  }

  // 
  const candidateMapping: Record<string, any> = {};
  const conflicts: Record<string, ConstantInfo[]> = {};

  for (const [value, constantInfos] of valueToConstants) {
    const valueStr = value.toString();

    if (constantInfos.length === 1) {
      const info = constantInfos[0];
      candidateMapping[valueStr] = {
        export: info.export,
        module: info.module,
        source: `${info.filePath}:${info.line}`,
      };
    } else {
      conflicts[valueStr] = constantInfos;
      const prioritized = prioritizeConstant(constantInfos);
      candidateMapping[valueStr] = {
        export: prioritized.export,
        module: prioritized.module,
        source: `${prioritized.filePath}:${prioritized.line}`,
        alternatives: constantInfos
          .filter((c) => c !== prioritized)
          .map((c) => ({
            export: c.export,
            module: c.module,
            source: `${c.filePath}:${c.line}`,
          })),
      };
    }
  }

  // 
  const result = {
    _comment: '',
    _generated: new Date().toISOString(),
    _stats: {
      totalConstants: constants.length,
      uniqueValues: valueToConstants.size,
      conflicts: Object.keys(conflicts).length,
    },
    candidateMapping,
    conflicts,
    allConstants: constants,
  };

  const outputPath = 'scripts/magic-numbers/existing-constants-analysis.json';
  writeFileSync(outputPath, JSON.stringify(result, null, 2));
  console.log(` : ${outputPath}`);

  return result;
}

async function scanDirectory(dirPath: string, constants: ConstantInfo[]) {
  const items = readdirSync(dirPath);

  for (const item of items) {
    const itemPath = join(dirPath, item);
    const stat = statSync(itemPath);

    if (stat.isDirectory()) {
      await scanDirectory(itemPath, constants);
    } else if (item.endsWith('.ts') && !item.endsWith('.d.ts')) {
      await scanFile(itemPath, constants);
    }
  }
}

async function scanFile(filePath: string, constants: ConstantInfo[]) {
  const content = readFileSync(filePath, 'utf-8');
  const lines = content.split('\n');
  const relativePath = filePath.replace(`${process.cwd()}/`, '');
  const modulePath = relativePath.replace(/^src\//, '@/').replace(/\.ts$/, '');

  console.log(` : ${relativePath}`);

  // 
  const patterns = [
    // export const NAME = 123;
    /^export\s+const\s+([A-Z_][A-Z0-9_]*)\s*=\s*([0-9]+(?:\.[0-9]+)?)\s*;?\s*$/,
    // export const NAME: number = 123;
    /^export\s+const\s+([A-Z_][A-Z0-9_]*)\s*:\s*number\s*=\s*([0-9]+(?:\.[0-9]+)?)\s*;?\s*$/,
  ];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    for (const pattern of patterns) {
      const match = line.match(pattern);
      if (match) {
        const [, exportName, valueStr] = match;
        const value = parseFloat(valueStr);

        constants.push({
          export: exportName,
          module: modulePath,
          value,
          filePath: relativePath,
          line: i + 1,
        });

        console.log(`   : ${exportName} = ${value} ( ${i + 1})`);
      }
    }
  }

  // 
  const objectPatterns = [
    // NAME: 123,
    /^\s*([A-Z_][A-Z0-9_]*)\s*:\s*([0-9]+(?:\.[0-9]+)?)\s*,?\s*$/,
  ];

  let inExportedObject = false;
  let currentObjectName = '';

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    // 
    const exportObjectMatch = line.match(
      /^export\s+const\s+([A-Z_][A-Z0-9_]*)\s*=\s*\{/,
    );
    if (exportObjectMatch) {
      inExportedObject = true;
      currentObjectName = exportObjectMatch[1];
      continue;
    }

    // 
    if (inExportedObject && line.includes('}')) {
      inExportedObject = false;
      currentObjectName = '';
      continue;
    }

    // 
    if (inExportedObject) {
      for (const pattern of objectPatterns) {
        const match = line.match(pattern);
        if (match) {
          const [, propertyName, valueStr] = match;
          const value = parseFloat(valueStr);
          const exportName = `${currentObjectName}.${propertyName}`;

          constants.push({
            export: exportName,
            module: modulePath,
            value,
            filePath: relativePath,
            line: i + 1,
          });

          console.log(
            `   : ${exportName} = ${value} ( ${i + 1})`,
          );
        }
      }
    }
  }
}

function prioritizeConstant(constants: ConstantInfo[]): ConstantInfo {
  const priorityOrder = [
    /constants\/app-constants/,
    /config\/security/,
    /constants\/performance/,
    /constants\//,
    /config\//,
    /magic-numbers/,
  ];

  for (const pattern of priorityOrder) {
    const match = constants.find((c) => pattern.test(c.filePath));
    if (match) return match;
  }

  return constants[0];
}

// 
if (require.main === module) {
  collectConstants().catch((error) => {
    console.error(' :', error);
    process.exit(1);
  });
}

export { collectConstants, type ConstantInfo };

