 */
export function PerformanceTestComponent({
  iterations = 1000,
}: {
  iterations?: number;
}) {
  const [isRunning, setIsRunning] = useState(false);
  const [results, setResults] = useState<{
    inefficient: number;
    optimized: number;
  } | null>(null);

  const runPerformanceTest = useCallback(async () => {
    setIsRunning(true);
    setResults(null);

    // 
    const inefficientStart = performance.now();
    for (let i = 0; i < iterations; i++) {
      // 
      for (let j = 0; j < 1000; j++) {
        Math.random();
      }
    }
    const inefficientTime = performance.now() - inefficientStart;

    // 
    const optimizedStart = performance.now();
    let cachedResult = 0;
    for (let j = 0; j < 1000; j++) {
      cachedResult += Math.random();
    }
    for (let i = 0; i < iterations; i++) {
      // 
      void cachedResult;
    }
    const optimizedTime = performance.now() - optimizedStart;

    setResults({
      inefficient: inefficientTime,
      optimized: optimizedTime,
    });
    setIsRunning(false);
  }, [iterations]);

  return (
    <Card>
      <CardHeader>
        <CardTitle></CardTitle>
        <CardDescription></CardDescription>
      </CardHeader>
      <CardContent>
        <div className='space-y-4'>
          <Button
            onClick={runPerformanceTest}
            disabled={isRunning}
            className='w-full'
          >
            {isRunning ? '...' : ` (${iterations} )`}
          </Button>

          {results && (
            <div className='grid gap-4 md:grid-cols-2'>
              <div className='rounded-lg border border-red-200 bg-red-50 p-4'>
                <h4 className='font-semibold text-red-700'></h4>
                <p className='text-2xl font-bold text-red-600'>
                  {results.inefficient.toFixed(2)}ms
                </p>
              </div>
              <div className='rounded-lg border border-green-200 bg-green-50 p-4'>
                <h4 className='font-semibold text-green-700'></h4>
                <p className='text-2xl font-bold text-green-600'>
                  {results.optimized.toFixed(2)}ms
                </p>
              </div>
            </div>
          )}

          {results && (
            <div className='text-center'>
              <p className='text-muted-foreground text-sm'>
                :{' '}
                {(
                  ((results.inefficient - results.optimized) /
                    results.inefficient) *
                  100
                ).toFixed(1)}
                %
              </p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

