  const optimizedObject = useMemo(
    () => ({
      timestamp: Date.now(),
      random: Math.random(),
      count,
    }),
    [count],
  );

  return (
    <Card className='border-green-200 bg-green-50'>
      <CardHeader>
        <CardTitle className='text-green-700'></CardTitle>
        <CardDescription>
           React 
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className='space-y-2'>
          <p>
            <strong>:</strong> {count}
          </p>
          <p>
            <strong>:</strong> {expensiveCalculation.toFixed(4)}
          </p>
          <p>
            <strong>:</strong> {optimizedObject.timestamp}
          </p>
          <p>
            <strong>:</strong> {optimizedObject.random.toFixed(4)}
          </p>
          <Badge variant='secondary'> useMemo </Badge>
        </div>
      </CardContent>
    </Card>
  );
}

/**
 * React Scan 
 */
export function ReactScanStats() {
  const [stats, setStats] = useState<ReactScanStats | null>(null);

  const updateStats = useCallback(() => {
    if (typeof window !== 'undefined') {
      try {
        //  React Scan 
        const reactScan = (window as ReactScanWindow).__REACT_SCAN__;
        const reactScanInternals = reactScan?.ReactScanInternals;

        // 
        let isEnabled = false;
        let totalRenders = 0;
        let componentsTracked = 0;

        if (reactScanInternals) {
          isEnabled = reactScanInternals.enabled ?? false;
          totalRenders = reactScanInternals.totalRenders ?? 0;

          // 
          const fiberRoots = reactScanInternals?.fiberRoots || {};
          componentsTracked = Object.keys(fiberRoots).length;
        }

        setStats({
          enabled: isEnabled,
          totalRenders,
          componentsTracked,
          lastUpdate: new Date().toLocaleTimeString(),
        });
      } catch (error) {
        console.warn(' React Scan :', error);
        setStats({
          enabled: false,
          totalRenders: 0,
          componentsTracked: 0,
          lastUpdate: new Date().toLocaleTimeString(),
        });
      }
    }
  }, []);

  useEffect(() => {
    updateStats();

    // 
    const interval = setInterval(
      updateStats,
      REACT_SCAN_CONFIG.STATS_UPDATE_INTERVAL,
    );

    // 
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.ctrlKey && event.shiftKey && event.key.toLowerCase() === 'x') {
        //  React Scan 
        setTimeout(updateStats, 100);
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      clearInterval(interval);
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [updateStats]);

  if (!stats) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>React Scan </CardTitle>
        </CardHeader>
        <CardContent>
          <p>...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className='flex items-center gap-2'>
          React Scan 
          <Badge variant={stats.enabled ? 'default' : 'secondary'}>
            {stats.enabled ? '' : ''}
          </Badge>
        </CardTitle>
        <CardDescription>
           React Scan 
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className='grid grid-cols-2 gap-4'>
          <div>
            <p className='text-sm font-medium'></p>
            <p className='text-2xl font-bold'>{stats.totalRenders}</p>
          </div>
          <div>
            <p className='text-sm font-medium'></p>
            <p className='text-2xl font-bold'>{stats.componentsTracked}</p>
          </div>
        </div>
        <div className='border-_t mt-4 pt-4'>
          <p className='text-muted-foreground text-xs'>
            : {stats.lastUpdate}
          </p>
        </div>
      </CardContent>
    </Card>
  );
}

